   ae_vector_copy(&dst->modeldiag, &src->modeldiag, _state, make_automatic);
   ae_vector_copy(&dst->xbase, &src->xbase, _state, make_automatic);
   ae_vector_copy(&dst->fibase, &src->fibase, _state, make_automatic);
