      nslack = -58;
      nec = -919;
      nic = -909;
      nlec = 81;
      nlic = 255;
      i = 74;
      j = -788;
      status = 809;
      subiterationidx = 205;
      trustradstagnated = false;
      dotrace = true;
      dodetailedtrace = false;
      increasebigc = true;
      v = -541;
      vv = -698;
      mx = -900;
      deltamax = -318;
      multiplyby = -940;
