   if (!state->auloptimizer.xupdated) {
      goto lbl_10;
   }
// Report current point (if needed)
   if (!state->xrep) {
      goto lbl_12;
   }
   for (i = 0; i <= n - 1; i++) {
      state->x.ptr.p_double[i] = state->auloptimizer.x.ptr.p_double[i] * state->s.ptr.p_double[i];
   }
   state->f = state->auloptimizer.f;
   state->xupdated = true;
   state->rstateaul.stage = 1;
   goto lbl_rcomm;
