         v = ae_minreal(v, minnlc_aulmaxgrowth, _state);
         v = ae_maxreal(v, 1 / minnlc_aulmaxgrowth, _state);
         state->nunlc.ptr.p_double[i - 1] = boundval(state->nunlc.ptr.p_double[i - 1] * v, -minnlc_maxlagmult, minnlc_maxlagmult, _state);
