static double refrdotv(ae_int_t n, RVector *x, RVector *y, ae_state *_state) {
   ae_int_t i;
   double result;

   result = 0.0;
   for (i = 0; i < n; i++) {
      result = result + x->xR[i] * y->xR[i];
   }
   return result;
}

