static ae_int_t testablasfunit_pseudorandominit2(RMatrix *x, ae_int_t iseed, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t result;

   ae_frame_make(_state, &_frame_block);
   NewVector(xr, 0, DT_REAL, _state);
   NewVector(xc, 0, DT_REAL, _state);

   ae_vector_set_length(&xr, x->rows, _state);
   for (i = 0; i < x->rows; i++) {
      xr.xR[i] = ae_sin((double)(13 + 2 * i + iseed), _state);
   }
   iseed = iseed + 1;
   ae_vector_set_length(&xc, x->cols, _state);
   for (j = 0; j < x->cols; j++) {
      xc.xR[j] = ae_sin((double)(17 + 3 * j + iseed), _state);
   }
   iseed = iseed + 1;
   for (i = 0; i < x->rows; i++) {
      for (j = 0; j < x->cols; j++) {
         x->xyR[i][j] = xr.xR[i] + xc.xR[j];
         while (x->xyR[i][j] > 1.0) {
            x->xyR[i][j] = x->xyR[i][j] - 1;
         }
         while (x->xyR[i][j] < -1.0) {
            x->xyR[i][j] = x->xyR[i][j] + 1;
         }
      }
   }
   result = iseed;
   ae_frame_leave(_state);
   return result;
}

static double testablasfunit_rmx3(double r0, double r1, double r2, ae_state *_state) {
   double result;

   result = r0;
   if (r1 > result) {
      result = r1;
   }
   if (r2 > result) {
      result = r2;
   }
   return result;
}

static double testablasfunit_rcmp1(RVector *x, RVector *y, ae_state *_state) {
   ae_int_t i;
   double mx;
   double result;

   ae_assert(x->cnt == y->cnt, "rcmp1: sizes do not match", _state);
   result = 0.0;
   mx = 1.0;
   for (i = 0; i < x->cnt; i++) {
      result = ae_maxreal(result, ae_fabs(x->xR[i] - y->xR[i], _state), _state);
      mx = ae_maxreal(mx, ae_fabs(x->xR[i], _state), _state);
      mx = ae_maxreal(mx, ae_fabs(y->xR[i], _state), _state);
   }
   result = result / mx;
   return result;
}

static double testablasfunit_icmp1(ZVector *x, ZVector *y, ae_state *_state) {
   ae_int_t i;
   double result;

   ae_assert(x->cnt == y->cnt, "rcmp1: sizes do not match", _state);
   result = 0.0;
   for (i = 0; i < x->cnt; i++) {
      result = ae_maxreal(result, ae_fabs((double)(x->xZ[i] - y->xZ[i]), _state), _state);
   }
   return result;
}

static double testablasfunit_bcmp1(BVector *x, BVector *y, ae_state *_state) {
   ae_int_t i;
   double result;

   ae_assert(x->cnt == y->cnt, "rcmp1: sizes do not match", _state);
   result = 0.0;
   for (i = 0; i < x->cnt; i++) {
      if ((x->xB[i] && !y->xB[i]) || (y->xB[i] && !x->xB[i])) {
         result = 1.0;
      }
   }
   return result;
}

static double testablasfunit_rcmp2(RMatrix *x, RMatrix *y, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double mx;
   double result;

   ae_assert(x->rows == y->rows, "rcmp2: rows do not match", _state);
   ae_assert(x->cols == y->cols, "rcmp2: cols do not match", _state);
   result = 0.0;
   mx = 1.0;
   for (i = 0; i < x->rows; i++) {
      for (j = 0; j < x->cols; j++) {
         result = ae_maxreal(result, ae_fabs(x->xyR[i][j] - y->xyR[i][j], _state), _state);
         mx = ae_maxreal(mx, ae_fabs(x->xyR[i][j], _state), _state);
         mx = ae_maxreal(mx, ae_fabs(y->xyR[i][j], _state), _state);
      }
   }
   result = result / mx;
   return result;
}

