// === hqrnd testing unit ===
static void testhqrndunit_calculatemv(RVector *x, ae_int_t n, double *mean, double *means, double *stddev, double *stddevs, ae_state *_state);
static void testhqrndunit_unsetstate(hqrndstate *state, ae_state *_state);

// Function for test HQRNDContinuous function
static bool hqrndcontinuoustest(bool silent, ae_state *_state);

// Function for test HQRNDContinuous function
static bool hqrnddiscretetest(bool silent, ae_state *_state);

bool testhqrnd(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   ae_int_t samplesize;
   double sigmathreshold;
   ae_int_t passcount;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t pass;
   ae_int_t s1;
   ae_int_t s2;
   ae_int_t i1;
   ae_int_t i2;
   double r1;
   double r2;
   ae_int_t am;
   ae_int_t an;
   double mean;
   double means;
   double stddev;
   double stddevs;
   double lambdav;
   bool seederrors;
   bool urerrors;
   double ursigmaerr;
   bool uierrors;
   double uisigmaerr;
   bool normerrors;
   double normsigmaerr;
   bool unit2errors;
   bool experrors;
   double expsigmaerr;
   bool discreteerr;
   bool continuouserr;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(dx, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(bins, 0, DT_INT, _state);
   NewObj(hqrndstate, state, _state);

   waserrors = false;
   sigmathreshold = 7.0;
   samplesize = 100000 + ae_randominteger(100, _state);
   passcount = 50;
   seederrors = false;
   urerrors = false;
   uierrors = false;
   normerrors = false;
   experrors = false;
   unit2errors = false;
   ae_vector_set_length(&x, samplesize - 1 + 1, _state);

// Test seed errors
   for (pass = 1; pass <= passcount; pass++) {
      s1 = 1 + ae_randominteger(32000, _state);
      s2 = 1 + ae_randominteger(32000, _state);
      testhqrndunit_unsetstate(&state, _state);
      hqrndseed(s1, s2, &state, _state);
      i1 = hqrnduniformi(&state, 100, _state);
      testhqrndunit_unsetstate(&state, _state);
      hqrndseed(s1, s2, &state, _state);
      i2 = hqrnduniformi(&state, 100, _state);
      seederrors = seederrors || i1 != i2;
      testhqrndunit_unsetstate(&state, _state);
      hqrndseed(s1, s2, &state, _state);
      r1 = hqrnduniformr(&state, _state);
      testhqrndunit_unsetstate(&state, _state);
      hqrndseed(s1, s2, &state, _state);
      r2 = hqrnduniformr(&state, _state);
      seederrors = seederrors || r1 != r2;
   }

// Test HQRNDRandomize() and real uniform generator
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   ursigmaerr = 0.0;
   for (i = 0; i < samplesize; i++) {
      x.xR[i] = hqrnduniformr(&state, _state);
   }
   for (i = 0; i < samplesize; i++) {
      urerrors = (urerrors || x.xR[i] <= 0.0) || x.xR[i] >= 1.0;
   }
   testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
   if (means != 0.0) {
      ursigmaerr = ae_maxreal(ursigmaerr, ae_fabs((mean - 0.5) / means, _state), _state);
   } else {
      urerrors = true;
   }
   if (stddevs != 0.0) {
      ursigmaerr = ae_maxreal(ursigmaerr, ae_fabs((stddev - ae_sqrt(1.0 / 12.0, _state)) / stddevs, _state), _state);
   } else {
      urerrors = true;
   }
   urerrors = urerrors || ursigmaerr > sigmathreshold;

// Test HQRNDRandomize() and integer uniform
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   uisigmaerr = 0.0;
   for (n = 2; n <= 10; n++) {
      for (i = 0; i < samplesize; i++) {
         x.xR[i] = (double)hqrnduniformi(&state, n, _state);
      }
      for (i = 0; i < samplesize; i++) {
         uierrors = (uierrors || x.xR[i] < 0.0) || x.xR[i] >= (double)n;
      }
      testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
      if (means != 0.0) {
         uisigmaerr = ae_maxreal(uisigmaerr, ae_fabs((mean - 0.5 * (n - 1)) / means, _state), _state);
      } else {
         uierrors = true;
      }
      if (stddevs != 0.0) {
         uisigmaerr = ae_maxreal(uisigmaerr, ae_fabs((stddev - ae_sqrt((ae_sqr((double)n, _state) - 1) / 12, _state)) / stddevs, _state), _state);
      } else {
         uierrors = true;
      }
   }
   uierrors = uierrors || uisigmaerr > sigmathreshold;

// Special 'close-to-limit' test on uniformity of integers
// (straightforward implementation like 'RND mod N' will return
//  non-uniform numbers for N=2/3*LIMIT)
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   uisigmaerr = 0.0;
   n = 1431655708;
   for (i = 0; i < samplesize; i++) {
      x.xR[i] = (double)hqrnduniformi(&state, n, _state);
   }
   for (i = 0; i < samplesize; i++) {
      uierrors = (uierrors || x.xR[i] < 0.0) || x.xR[i] >= (double)n;
   }
   testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
   if (means != 0.0) {
      uisigmaerr = ae_maxreal(uisigmaerr, ae_fabs((mean - 0.5 * (n - 1)) / means, _state), _state);
   } else {
      uierrors = true;
   }
   if (stddevs != 0.0) {
      uisigmaerr = ae_maxreal(uisigmaerr, ae_fabs((stddev - ae_sqrt((ae_sqr((double)n, _state) - 1) / 12, _state)) / stddevs, _state), _state);
   } else {
      uierrors = true;
   }
   uierrors = uierrors || uisigmaerr > sigmathreshold;

// Test normal
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   i = 0;
   while (i < samplesize) {
      hqrndnormal2(&state, &r1, &r2, _state);
      x.xR[i] = r1;
      if (i + 1 < samplesize) {
         x.xR[i + 1] = r2;
      }
      i = i + 2;
   }
   normsigmaerr = 0.0;
   testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
   if (means != 0.0) {
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((mean - 0) / means, _state), _state);
   } else {
      normerrors = true;
   }
   if (stddevs != 0.0) {
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((stddev - 1) / stddevs, _state), _state);
   } else {
      normerrors = true;
   }
   ae_set_error_flag(&normerrors, normsigmaerr > sigmathreshold, __FILE__, __LINE__, "testhqrndunit.ap:230");
   ae_vector_set_length(&x, 0, _state);
   hqrndnormalv(&state, samplesize, &x, _state);
   ae_set_error_flag(&normerrors, x.cnt != samplesize, __FILE__, __LINE__, "testhqrndunit.ap:233");
   ae_set_error_flag(&normerrors, !isfinitevector(&x, samplesize, _state), __FILE__, __LINE__, "testhqrndunit.ap:234");
   if (!normerrors) {

   // Check mean/sigma of the distribution
      testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
      ae_set_error_flag(&normerrors, means == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:241");
      ae_set_error_flag(&normerrors, stddevs == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:242");
      normsigmaerr = 0.0;
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((mean - 0) / coalesce(means, 1.0, _state), _state), _state);
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((stddev - 1) / coalesce(stddevs, 1.0, _state), _state), _state);
      ae_set_error_flag(&normerrors, normsigmaerr > sigmathreshold, __FILE__, __LINE__, "testhqrndunit.ap:246");

   // Check that subsequent differences are normally distributed too
      ae_vector_set_length(&dx, samplesize - 1, _state);
      for (i = 0; i < samplesize - 1; i++) {
         dx.xR[i] = x.xR[i + 1] - x.xR[i];
      }
      testhqrndunit_calculatemv(&dx, samplesize - 1, &mean, &means, &stddev, &stddevs, _state);
      ae_set_error_flag(&normerrors, means == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:255");
      ae_set_error_flag(&normerrors, stddevs == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:256");
      normsigmaerr = 0.0;
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((mean - 0) / coalesce(means, 1.0, _state), _state), _state);
      normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((stddev - ae_sqrt(2.0, _state)) / coalesce(stddevs, 1.0, _state), _state), _state);
      ae_set_error_flag(&normerrors, normsigmaerr > sigmathreshold, __FILE__, __LINE__, "testhqrndunit.ap:260");
   }
   am = 1 + ae_randominteger(ae_round(ae_sqrt((double)samplesize, _state), _state), _state);
   an = 1 + samplesize / am;
   hqrndnormalm(&state, am, an, &a, _state);
   ae_set_error_flag(&normerrors, a.rows != am, __FILE__, __LINE__, "testhqrndunit.ap:265");
   ae_set_error_flag(&normerrors, a.cols != an, __FILE__, __LINE__, "testhqrndunit.ap:266");
   ae_set_error_flag(&normerrors, !apservisfinitematrix(&a, am, an, _state), __FILE__, __LINE__, "testhqrndunit.ap:267");
   ae_vector_set_length(&x, am * an, _state);
   for (i = 0; i < am; i++) {
      for (j = 0; j < an; j++) {
         x.xR[i * an + j] = a.xyR[i][j];
      }
   }
   testhqrndunit_calculatemv(&x, am * an, &mean, &means, &stddev, &stddevs, _state);
   ae_set_error_flag(&normerrors, means == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:273");
   ae_set_error_flag(&normerrors, stddevs == 0.0, __FILE__, __LINE__, "testhqrndunit.ap:274");
   normsigmaerr = 0.0;
   normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((mean - 0) / coalesce(means, 1.0, _state), _state), _state);
   normsigmaerr = ae_maxreal(normsigmaerr, ae_fabs((stddev - 1) / coalesce(stddevs, 1.0, _state), _state), _state);
   ae_set_error_flag(&normerrors, normsigmaerr > sigmathreshold, __FILE__, __LINE__, "testhqrndunit.ap:278");

// Test unit2
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   n = 1000000;
   ae_vector_set_length(&bins, 10, _state);
   for (i = 0; i < bins.cnt; i++) {
      bins.xZ[i] = 0;
   }
   for (pass = 0; pass < n; pass++) {
      hqrndunit2(&state, &r1, &r2, _state);
      ae_set_error_flag(&unit2errors, ae_fabs(r1 * r1 + r2 * r2 - 1, _state) > 100 * ae_machineepsilon, __FILE__, __LINE__, "testhqrndunit.ap:292");
      k = ae_ifloor((ae_atan2(r1, r2, _state) + ae_pi) / (2 * ae_pi) * bins.cnt, _state);
      if (k < 0) {
         k = 0;
      }
      if (k >= bins.cnt) {
         k = bins.cnt - 1;
      }
      bins.xZ[k] = bins.xZ[k] + 1;
   }
   for (i = 0; i < bins.cnt; i++) {
      ae_set_error_flag(&unit2errors, (double)bins.xZ[i] < 0.9 * n / bins.cnt || (double)bins.xZ[i] > 1.1 * n / bins.cnt, __FILE__, __LINE__, "testhqrndunit.ap:301");
   }

// Test exponential
   testhqrndunit_unsetstate(&state, _state);
   hqrndrandomize(&state, _state);
   expsigmaerr = 0.0;
   lambdav = 2 + 5 * ae_randomreal(_state);
   for (i = 0; i < samplesize; i++) {
      x.xR[i] = hqrndexponential(&state, lambdav, _state);
   }
   for (i = 0; i < samplesize; i++) {
      uierrors = uierrors || x.xR[i] < 0.0;
   }
   testhqrndunit_calculatemv(&x, samplesize, &mean, &means, &stddev, &stddevs, _state);
   if (means != 0.0) {
      expsigmaerr = ae_maxreal(expsigmaerr, ae_fabs((mean - 1.0 / lambdav) / means, _state), _state);
   } else {
      experrors = true;
   }
   if (stddevs != 0.0) {
      expsigmaerr = ae_maxreal(expsigmaerr, ae_fabs((stddev - 1.0 / lambdav) / stddevs, _state), _state);
   } else {
      experrors = true;
   }
   experrors = experrors || expsigmaerr > sigmathreshold;

// Discrete/Continuous tests
   discreteerr = hqrnddiscretetest(true, _state);
   continuouserr = hqrndcontinuoustest(true, _state);

// Final report
   waserrors = ((((((seederrors || urerrors) || uierrors) || normerrors) || unit2errors) || experrors) || discreteerr) || continuouserr;
   if (!silent) {
      printf("RNG TEST\n");
      printf("SEED TEST:                               ");
      if (!seederrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("UNIFORM CONTINUOUS:                      ");
      if (!urerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("UNIFORM INTEGER:                         ");
      if (!uierrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("NORMAL:                                  ");
      if (!normerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("UNIT2:                                   ");
      if (!unit2errors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("EXPONENTIAL:                             ");
      if (!experrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("DISCRETE:                                ");
      if (!discreteerr) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("CONTINUOUS:                              ");
      if (!continuouserr) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST SUMMARY: FAILED\n");
      } else {
         printf("TEST SUMMARY: PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Function for test HQRNDContinuous function
static bool hqrndcontinuoustest(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nb;
   ae_int_t samplesize;
   ae_int_t xp;
   ae_int_t i;
   ae_int_t j;
   double v;
   double sigma;
   double sigmamax;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(sample, 0, DT_REAL, _state);
   NewVector(bins, 0, DT_INT, _state);
   NewVector(binbounds, 0, DT_REAL, _state);
   NewObj(hqrndstate, state, _state);

   result = false;

// Test for sample size equal to 1
   ae_vector_set_length(&sample, 1, _state);
   sample.xR[0] = ae_randomreal(_state);
   hqrndrandomize(&state, _state);
   result = result || hqrndcontinuous(&state, &sample, 1, _state) != sample.xR[0];

// Test for larger samples
   xp = 100000;
   sigmamax = 10.0;
   for (samplesize = 2; samplesize <= 5; samplesize++) {

   // 1. Generate random sample with SampleSize points
   // 2. Generate NB=3*(SampleSize-1) bins, with bounds as prescribed by (BinBounds[I],BinBounds[I+1]).
   //    Bin bounds are generated in such a way that value can fall into any bin with same probability
   // 3. Generate many random values
   // 4. Calculate number of values which fall into each bin
   // 5. Bins[I] should have binomial distribution with mean XP/NB and
   //    variance XP*(1/NB)*(1-1/NB)
      nb = 3 * (samplesize - 1);
      sigma = ae_sqrt(xp * (1.0 / nb) * (1 - 1.0 / nb), _state);
      ae_vector_set_length(&sample, samplesize, _state);
      sample.xR[0] = 2 * ae_randomreal(_state) - 1;
      for (i = 0; i < samplesize - 1; i++) {
         sample.xR[i + 1] = sample.xR[i] + 0.1 + ae_randomreal(_state);
      }
      ae_vector_set_length(&bins, nb, _state);
      ae_vector_set_length(&binbounds, nb + 1, _state);
      for (i = 0; i < samplesize - 1; i++) {
         bins.xZ[3 * i + 0] = 0;
         bins.xZ[3 * i + 1] = 0;
         bins.xZ[3 * i + 2] = 0;
         binbounds.xR[3 * i + 0] = sample.xR[i];
         binbounds.xR[3 * i + 1] = sample.xR[i] + (sample.xR[i + 1] - sample.xR[i]) / 3;
         binbounds.xR[3 * i + 2] = sample.xR[i] + (sample.xR[i + 1] - sample.xR[i]) * 2 / 3;
      }
      binbounds.xR[nb] = sample.xR[samplesize - 1];
      hqrndrandomize(&state, _state);
      for (i = 0; i < xp; i++) {
         v = hqrndcontinuous(&state, &sample, samplesize, _state);
         for (j = 0; j < nb; j++) {
            if (v > binbounds.xR[j] && v < binbounds.xR[j + 1]) {
               bins.xZ[j] = bins.xZ[j] + 1;
               break;
            }
         }
      }
      for (i = 0; i < nb; i++) {
         result = result || ae_fabs(bins.xZ[i] - (double)xp / nb, _state) > sigma * sigmamax;
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Function for test HQRNDContinuous function
static bool hqrnddiscretetest(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   double sigma;
   double sigmathreshold;
   double tsample;
   double max;
   double min;
   ae_int_t i;
   ae_int_t j;
   ae_int_t s1;
   ae_int_t s2;
   ae_int_t binscount;
   ae_int_t xp;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(sample, 0, DT_REAL, _state);
   NewVector(nn, 0, DT_INT, _state);
   NewObj(hqrndstate, state, _state);

// We test that all values from discrete sample are generated with same probability.
// To do this, we generate random values many times, then we calculate actual probabilities
// and compare them with theoretical ones.
   max = 100.0;
   min = -100.0;
   xp = 100000;
   sigmathreshold = 10.0;
   for (binscount = 1; binscount <= 5; binscount++) {
      sigma = ae_sqrt(xp * (1.0 / binscount) * (1 - 1.0 / binscount), _state);
      ae_vector_set_length(&nn, binscount, _state);
      for (i = 0; i < binscount; i++) {
         nn.xZ[i] = 0;
      }
      ae_vector_set_length(&sample, binscount, _state);
      sample.xR[0] = (max - min) * ae_randomreal(_state) + min;
      for (i = 1; i < binscount; i++) {
         sample.xR[i] = sample.xR[i - 1] + max * ae_randomreal(_state) + 0.001;
      }
      s1 = 1 + ae_randominteger(32000, _state);
      s2 = 1 + ae_randominteger(32000, _state);
      hqrndseed(s1, s2, &state, _state);
      for (i = 0; i < xp; i++) {
         tsample = hqrnddiscrete(&state, &sample, binscount, _state);
         for (j = 0; j < binscount; j++) {
            if (tsample == sample.xR[j]) {
               nn.xZ[j] = nn.xZ[j] + 1;
               break;
            }
         }
      }
      for (i = 0; i < binscount; i++) {
         if ((double)nn.xZ[i] < (double)xp / binscount - sigmathreshold * sigma || (double)nn.xZ[i] > (double)xp / binscount + sigmathreshold * sigma) {
            if (!silent) {
               printf("HQRNDDiscreteTest::ErrorReport::\n");
               printf("nn[%0d]=%0d;\n   xp/BinsCount=%0.5f;\n   C*sigma=%0.5f\n", (int)i, (int)nn.xZ[i], (double)xp / binscount, sigmathreshold * sigma);
               printf("HQRNDDiscreteTest: test is FAILED!\n");
            }
            result = true;
            ae_frame_leave(_state);
            return result;
         }
      }
      if (!silent) {
         printf("HQRNDDiscreteTest: test is OK.\n");
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

static void testhqrndunit_calculatemv(RVector *x, ae_int_t n, double *mean, double *means, double *stddev, double *stddevs, ae_state *_state) {
   ae_int_t i;
   double v1;
   double v2;
   double variance;

   *mean = 0;
   *means = 0;
   *stddev = 0;
   *stddevs = 0;

   *mean = 0.0;
   *means = 1.0;
   *stddev = 0.0;
   *stddevs = 1.0;
   variance = 0.0;
   if (n <= 1) {
      return;
   }
// Mean
   for (i = 0; i < n; i++) {
      *mean = *mean + x->xR[i];
   }
   *mean = *mean / n;

// Variance (using corrected two-pass algorithm)
   if (n != 1) {
      v1 = 0.0;
      for (i = 0; i < n; i++) {
         v1 = v1 + ae_sqr(x->xR[i] - (*mean), _state);
      }
      v2 = 0.0;
      for (i = 0; i < n; i++) {
         v2 = v2 + (x->xR[i] - (*mean));
      }
      v2 = ae_sqr(v2, _state) / n;
      variance = (v1 - v2) / (n - 1);
      if (variance < 0.0) {
         variance = 0.0;
      }
      *stddev = ae_sqrt(variance, _state);
   }
// Errors
   *means = *stddev / ae_sqrt((double)n, _state);
   *stddevs = *stddev * ae_sqrt(2.0, _state) / ae_sqrt((double)(n - 1), _state);
}

// Unsets HQRNDState structure
static void testhqrndunit_unsetstate(hqrndstate *state, ae_state *_state) {

   state->s1 = 0;
   state->s2 = 0;
   state->magicv = 0;
}

