static void testablasunit_testreflections(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t n;
   ae_int_t m;
   ae_int_t maxmn;
   double tmp;
   double beta;
   double tau;
   double err;
   double mer;
   double mel;
   double meg;
   ae_int_t pass;
   ae_int_t passcount;
   double threshold;
   ae_int_t tasktype;
   double xscale;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(v, 0, DT_REAL, _state);
   NewVector(work, 0, DT_REAL, _state);
   NewMatrix(h, 0, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(b, 0, 0, DT_REAL, _state);
   NewMatrix(c, 0, 0, DT_REAL, _state);

   passcount = 10;
   threshold = 100 * ae_machineepsilon;
   mer = 0.0;
   mel = 0.0;
   meg = 0.0;
   for (pass = 1; pass <= passcount; pass++) {
      for (n = 1; n <= 10; n++) {
         for (m = 1; m <= 10; m++) {

         // Task
            n = 1 + ae_randominteger(10, _state);
            m = 1 + ae_randominteger(10, _state);
            maxmn = ae_maxint(m, n, _state);

         // Initialize
            ae_vector_set_length(&x, maxmn + 1, _state);
            ae_vector_set_length(&v, maxmn + 1, _state);
            ae_vector_set_length(&work, maxmn + 1, _state);
            ae_matrix_set_length(&h, maxmn + 1, maxmn + 1, _state);
            ae_matrix_set_length(&a, maxmn + 1, maxmn + 1, _state);
            ae_matrix_set_length(&b, maxmn + 1, maxmn + 1, _state);
            ae_matrix_set_length(&c, maxmn + 1, maxmn + 1, _state);

         // GenerateReflection, three tasks are possible:
         // * random X
         // * zero X
         // * non-zero X[1], all other are zeros
         // * random X, near underflow scale
         // * random X, near overflow scale
            for (tasktype = 0; tasktype <= 4; tasktype++) {
               xscale = 1.0;
               if (tasktype == 0) {
                  for (i = 1; i <= n; i++) {
                     x.xR[i] = 2 * ae_randomreal(_state) - 1;
                  }
               }
               if (tasktype == 1) {
                  for (i = 1; i <= n; i++) {
                     x.xR[i] = 0.0;
                  }
               }
               if (tasktype == 2) {
                  x.xR[1] = 2 * ae_randomreal(_state) - 1;
                  for (i = 2; i <= n; i++) {
                     x.xR[i] = 0.0;
                  }
               }
               if (tasktype == 3) {
                  for (i = 1; i <= n; i++) {
                     x.xR[i] = (ae_randominteger(21, _state) - 10) * ae_minrealnumber;
                  }
                  xscale = 10 * ae_minrealnumber;
               }
               if (tasktype == 4) {
                  for (i = 1; i <= n; i++) {
                     x.xR[i] = (2 * ae_randomreal(_state) - 1) * ae_maxrealnumber;
                  }
                  xscale = ae_maxrealnumber;
               }
               ae_v_move(&v.xR[1], 1, &x.xR[1], 1, n);
               generatereflection(&v, n, &tau, _state);
               beta = v.xR[1];
               v.xR[1] = 1.0;
               for (i = 1; i <= n; i++) {
                  for (j = 1; j <= n; j++) {
                     if (i == j) {
                        h.xyR[i][j] = 1 - tau * v.xR[i] * v.xR[j];
                     } else {
                        h.xyR[i][j] = -tau * v.xR[i] * v.xR[j];
                     }
                  }
               }
               err = 0.0;
               for (i = 1; i <= n; i++) {
                  tmp = ae_v_dotproduct(&h.xyR[i][1], 1, &x.xR[1], 1, n);
                  if (i == 1) {
                     err = ae_maxreal(err, ae_fabs(tmp - beta, _state), _state);
                  } else {
                     err = ae_maxreal(err, ae_fabs(tmp, _state), _state);
                  }
               }
               meg = ae_maxreal(meg, err / xscale, _state);
            }

         // ApplyReflectionFromTheLeft
            for (i = 1; i <= m; i++) {
               x.xR[i] = 2 * ae_randomreal(_state) - 1;
               v.xR[i] = x.xR[i];
            }
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
                  b.xyR[i][j] = a.xyR[i][j];
               }
            }
            generatereflection(&v, m, &tau, _state);
            beta = v.xR[1];
            v.xR[1] = 1.0;
            applyreflectionfromtheleft(&b, tau, &v, 1, m, 1, n, &work, _state);
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= m; j++) {
                  if (i == j) {
                     h.xyR[i][j] = 1 - tau * v.xR[i] * v.xR[j];
                  } else {
                     h.xyR[i][j] = -tau * v.xR[i] * v.xR[j];
                  }
               }
            }
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  tmp = ae_v_dotproduct(&h.xyR[i][1], 1, &a.xyR[1][j], a.stride, m);
                  c.xyR[i][j] = tmp;
               }
            }
            err = 0.0;
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  err = ae_maxreal(err, ae_fabs(b.xyR[i][j] - c.xyR[i][j], _state), _state);
               }
            }
            mel = ae_maxreal(mel, err, _state);

         // ApplyReflectionFromTheRight
            for (i = 1; i <= n; i++) {
               x.xR[i] = 2 * ae_randomreal(_state) - 1;
               v.xR[i] = x.xR[i];
            }
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
                  b.xyR[i][j] = a.xyR[i][j];
               }
            }
            generatereflection(&v, n, &tau, _state);
            beta = v.xR[1];
            v.xR[1] = 1.0;
            applyreflectionfromtheright(&b, tau, &v, 1, m, 1, n, &work, _state);
            for (i = 1; i <= n; i++) {
               for (j = 1; j <= n; j++) {
                  if (i == j) {
                     h.xyR[i][j] = 1 - tau * v.xR[i] * v.xR[j];
                  } else {
                     h.xyR[i][j] = -tau * v.xR[i] * v.xR[j];
                  }
               }
            }
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  tmp = ae_v_dotproduct(&a.xyR[i][1], 1, &h.xyR[1][j], h.stride, n);
                  c.xyR[i][j] = tmp;
               }
            }
            err = 0.0;
            for (i = 1; i <= m; i++) {
               for (j = 1; j <= n; j++) {
                  err = ae_maxreal(err, ae_fabs(b.xyR[i][j] - c.xyR[i][j], _state), _state);
               }
            }
            mer = ae_maxreal(mer, err, _state);
         }
      }
   }

// Overflow crash test
   ae_vector_set_length(&x, 10 + 1, _state);
   ae_vector_set_length(&v, 10 + 1, _state);
   for (i = 1; i <= 10; i++) {
      v.xR[i] = ae_maxrealnumber * 0.01 * (2 * ae_randomreal(_state) - 1);
   }
   generatereflection(&v, 10, &tau, _state);

// Result
   ae_set_error_flag(errorflag, (meg > threshold || mel > threshold) || mer > threshold, __FILE__, __LINE__, "testablasunit.ap:1956");
   ae_frame_leave(_state);
}

