// Internal subroutine.
// Triangular matrix inversion
//
//   -- LAPACK routine (version 3.0) --
//      Univ. of Tennessee, Univ. of California Berkeley, NAG Ltd.,
//      Courant Institute, Argonne National Lab, and Rice University
//      February 29, 1992
static bool testablasunit_internalcmatrixtrinverse(CMatrix *a, ae_int_t n, bool isupper, bool isunittriangular, ae_state *_state) {
   ae_frame _frame_block;
   bool nounit;
   ae_int_t i;
   ae_int_t j;
   ae_complex v;
   ae_complex ajj;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(t, 0, DT_COMPLEX, _state);

   result = true;
   ae_vector_set_length(&t, n - 1 + 1, _state);

// Test the input parameters.
   nounit = !isunittriangular;
   if (isupper) {

   // Compute inverse of upper triangular matrix.
      for (j = 0; j < n; j++) {
         if (nounit) {
            if (ae_c_eq_d(a->xyC[j][j], 0.0)) {
               result = false;
               ae_frame_leave(_state);
               return result;
            }
            a->xyC[j][j] = ae_c_d_div(1, a->xyC[j][j]);
            ajj = ae_c_neg(a->xyC[j][j]);
         } else {
            ajj = ae_complex_from_i(-1);
         }

      // Compute elements 1:j-1 of j-th column.
         if (j > 0) {
            ae_v_cmove(t.xC, 1, &a->xyC[0][j], a->stride, "N", j);
            for (i = 0; i < j; i++) {
               if (i + 1 < j) {
                  v = ae_v_cdotproduct(&a->xyC[i][i + 1], 1, "N", &t.xC[i + 1], 1, "N", j - i - 1);
               } else {
                  v = ae_complex_from_i(0);
               }
               if (nounit) {
                  a->xyC[i][j] = ae_c_add(v, ae_c_mul(a->xyC[i][i], t.xC[i]));
               } else {
                  a->xyC[i][j] = ae_c_add(v, t.xC[i]);
               }
            }
            ae_v_cmulc(&a->xyC[0][j], a->stride, j, ajj);
         }
      }
   } else {

   // Compute inverse of lower triangular matrix.
      for (j = n - 1; j >= 0; j--) {
         if (nounit) {
            if (ae_c_eq_d(a->xyC[j][j], 0.0)) {
               result = false;
               ae_frame_leave(_state);
               return result;
            }
            a->xyC[j][j] = ae_c_d_div(1, a->xyC[j][j]);
            ajj = ae_c_neg(a->xyC[j][j]);
         } else {
            ajj = ae_complex_from_i(-1);
         }
         if (j + 1 < n) {

         // Compute elements j+1:n of j-th column.
            ae_v_cmove(&t.xC[j + 1], 1, &a->xyC[j + 1][j], a->stride, "N", n - j - 1);
            for (i = j + 1; i < n; i++) {
               if (i > j + 1) {
                  v = ae_v_cdotproduct(&a->xyC[i][j + 1], 1, "N", &t.xC[j + 1], 1, "N", i - j - 1);
               } else {
                  v = ae_complex_from_i(0);
               }
               if (nounit) {
                  a->xyC[i][j] = ae_c_add(v, ae_c_mul(a->xyC[i][i], t.xC[i]));
               } else {
                  a->xyC[i][j] = ae_c_add(v, t.xC[i]);
               }
            }
            ae_v_cmulc(&a->xyC[j + 1][j], a->stride, n - j - 1, ajj);
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

