static void testablasunit_refcmatrixherk(ae_int_t n, ae_int_t k, double alpha, CMatrix *a, ae_int_t ia, ae_int_t ja, ae_int_t optypea, double beta, CMatrix *c, ae_int_t ic, ae_int_t jc, bool isupper, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_complex vc;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(ae, 0, 0, DT_COMPLEX, _state);

   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         if ((isupper && j >= i) || (!isupper && j <= i)) {
            if (beta == 0.0) {
               c->xyC[i + ic][j + jc] = ae_complex_from_i(0);
            } else {
               c->xyC[i + ic][j + jc] = ae_c_mul_d(c->xyC[i + ic][j + jc], beta);
            }
         }
      }
   }
   if (alpha == 0.0) {
      ae_frame_leave(_state);
      return;
   }
   if (n * k > 0) {
      ae_matrix_set_length(&ae, n, k, _state);
   }
   for (i = 0; i < n; i++) {
      for (j = 0; j < k; j++) {
         if (optypea == 0) {
            ae.xyC[i][j] = a->xyC[ia + i][ja + j];
         }
         if (optypea == 2) {
            ae.xyC[i][j] = ae_c_conj(a->xyC[ia + j][ja + i], _state);
         }
      }
   }
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         vc = ae_complex_from_i(0);
         if (k > 0) {
            vc = ae_v_cdotproduct(ae.xyC[i], 1, "N", ae.xyC[j], 1, "Conj", k);
         }
         vc = ae_c_mul_d(vc, alpha);
         if (isupper && j >= i) {
            c->xyC[ic + i][jc + j] = ae_c_add(vc, c->xyC[ic + i][jc + j]);
         }
         if (!isupper && j <= i) {
            c->xyC[ic + i][jc + j] = ae_c_add(vc, c->xyC[ic + i][jc + j]);
         }
      }
   }
   ae_frame_leave(_state);
}

