// Reference SYMV,
// ALGLIB subroutine
static void testablasunit_refrmatrixsymv(ae_int_t n, double alpha, RMatrix *a, ae_int_t ia, ae_int_t ja, bool isupper, RVector *x, ae_int_t ix, double beta, RVector *y, ae_int_t iy, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(b, 0, 0, DT_REAL, _state);

// Convert problem to traditional dense GEMV
   ae_matrix_set_length(&b, n, n, _state);
   if (isupper) {
      for (i = 0; i < n; i++) {
         for (j = i; j < n; j++) {
            b.xyR[i][j] = a->xyR[ia + i][ja + j];
            b.xyR[j][i] = a->xyR[ia + i][ja + j];
         }
      }
   } else {
      for (i = 0; i < n; i++) {
         for (j = 0; j <= i; j++) {
            b.xyR[i][j] = a->xyR[ia + i][ja + j];
            b.xyR[j][i] = a->xyR[ia + i][ja + j];
         }
      }
   }

// Calculate result
   for (i = 0; i < n; i++) {
      v = beta * y->xR[iy + i];
      for (j = 0; j < n; j++) {
         v = v + alpha * b.xyR[i][j] * x->xR[ix + j];
      }
      y->xR[iy + i] = v;
   }
   ae_frame_leave(_state);
}

