// === hblas testing unit ===
bool testhblas(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t maxn;
   ae_int_t i;
   ae_int_t j;
   ae_int_t i1;
   ae_int_t i2;
   bool waserrors;
   double mverr;
   double threshold;
   ae_complex alpha;
   ae_complex v;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_COMPLEX, _state);
   NewMatrix(ua, 0, 0, DT_COMPLEX, _state);
   NewMatrix(la, 0, 0, DT_COMPLEX, _state);
   NewVector(x, 0, DT_COMPLEX, _state);
   NewVector(y1, 0, DT_COMPLEX, _state);
   NewVector(y2, 0, DT_COMPLEX, _state);
   NewVector(y3, 0, DT_COMPLEX, _state);

   mverr = 0.0;
   waserrors = false;
   maxn = 10;
   threshold = 1000 * ae_machineepsilon;

// Test MV
   for (n = 2; n <= maxn; n++) {
      ae_matrix_set_length(&a, n + 1, n + 1, _state);
      ae_matrix_set_length(&ua, n + 1, n + 1, _state);
      ae_matrix_set_length(&la, n + 1, n + 1, _state);
      ae_vector_set_length(&x, n + 1, _state);
      ae_vector_set_length(&y1, n + 1, _state);
      ae_vector_set_length(&y2, n + 1, _state);
      ae_vector_set_length(&y3, n + 1, _state);

   // fill A, UA, LA
      for (i = 1; i <= n; i++) {
         a.xyC[i][i].x = 2 * ae_randomreal(_state) - 1;
         a.xyC[i][i].y = 0.0;
         for (j = i + 1; j <= n; j++) {
            a.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            a.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
            a.xyC[j][i] = ae_c_conj(a.xyC[i][j], _state);
         }
      }
      for (i = 1; i <= n; i++) {
         for (j = 1; j <= n; j++) {
            ua.xyC[i][j] = ae_complex_from_i(0);
         }
      }
      for (i = 1; i <= n; i++) {
         for (j = i; j <= n; j++) {
            ua.xyC[i][j] = a.xyC[i][j];
         }
      }
      for (i = 1; i <= n; i++) {
         for (j = 1; j <= n; j++) {
            la.xyC[i][j] = ae_complex_from_i(0);
         }
      }
      for (i = 1; i <= n; i++) {
         for (j = 1; j <= i; j++) {
            la.xyC[i][j] = a.xyC[i][j];
         }
      }

   // test on different I1, I2
      for (i1 = 1; i1 <= n; i1++) {
         for (i2 = i1; i2 <= n; i2++) {

         // Fill X, choose Alpha
            for (i = 1; i <= i2 - i1 + 1; i++) {
               x.xC[i].x = 2 * ae_randomreal(_state) - 1;
               x.xC[i].y = 2 * ae_randomreal(_state) - 1;
            }
            alpha.x = 2 * ae_randomreal(_state) - 1;
            alpha.y = 2 * ae_randomreal(_state) - 1;

         // calculate A*x, UA*x, LA*x
            for (i = i1; i <= i2; i++) {
               v = ae_v_cdotproduct(&a.xyC[i][i1], 1, "N", &x.xC[1], 1, "N", i2 - i1 + 1);
               y1.xC[i - i1 + 1] = ae_c_mul(alpha, v);
            }
            hermitianmatrixvectormultiply(&ua, true, i1, i2, &x, alpha, &y2, _state);
            hermitianmatrixvectormultiply(&la, false, i1, i2, &x, alpha, &y3, _state);

         // Calculate error
            ae_v_csub(&y2.xC[1], 1, &y1.xC[1], 1, "N", i2 - i1 + 1);
            v = ae_v_cdotproduct(&y2.xC[1], 1, "N", &y2.xC[1], 1, "Conj", i2 - i1 + 1);
            mverr = ae_maxreal(mverr, ae_sqrt(ae_c_abs(v, _state), _state), _state);
            ae_v_csub(&y3.xC[1], 1, &y1.xC[1], 1, "N", i2 - i1 + 1);
            v = ae_v_cdotproduct(&y3.xC[1], 1, "N", &y3.xC[1], 1, "Conj", i2 - i1 + 1);
            mverr = ae_maxreal(mverr, ae_sqrt(ae_c_abs(v, _state), _state), _state);
         }
      }
   }

// report
   waserrors = mverr > threshold;
   if (!silent) {
      printf("TESTING HERMITIAN BLAS\n");
      printf("MV error:                                %5.3e\n", mverr);
      printf("Threshold:                               %5.3e\n", threshold);
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// === creflections testing unit ===
bool testcreflections(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t n;
   ae_int_t m;
   ae_int_t maxmn;
   ae_complex tmp;
   ae_complex beta;
   ae_complex tau;
   double err;
   double mer;
   double mel;
   double meg;
   ae_int_t pass;
   ae_int_t passcount;
   bool waserrors;
   double threshold;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_COMPLEX, _state);
   NewVector(v, 0, DT_COMPLEX, _state);
   NewVector(work, 0, DT_COMPLEX, _state);
   NewMatrix(h, 0, 0, DT_COMPLEX, _state);
   NewMatrix(a, 0, 0, DT_COMPLEX, _state);
   NewMatrix(b, 0, 0, DT_COMPLEX, _state);
   NewMatrix(c, 0, 0, DT_COMPLEX, _state);

   threshold = 1000 * ae_machineepsilon;
   passcount = 1000;
   mer = 0.0;
   mel = 0.0;
   meg = 0.0;
   for (pass = 1; pass <= passcount; pass++) {

   // Task
      n = 1 + ae_randominteger(10, _state);
      m = 1 + ae_randominteger(10, _state);
      maxmn = ae_maxint(m, n, _state);

   // Initialize
      ae_vector_set_length(&x, maxmn + 1, _state);
      ae_vector_set_length(&v, maxmn + 1, _state);
      ae_vector_set_length(&work, maxmn + 1, _state);
      ae_matrix_set_length(&h, maxmn + 1, maxmn + 1, _state);
      ae_matrix_set_length(&a, maxmn + 1, maxmn + 1, _state);
      ae_matrix_set_length(&b, maxmn + 1, maxmn + 1, _state);
      ae_matrix_set_length(&c, maxmn + 1, maxmn + 1, _state);

   // GenerateReflection
      for (i = 1; i <= n; i++) {
         x.xC[i].x = 2 * ae_randomreal(_state) - 1;
         x.xC[i].y = 2 * ae_randomreal(_state) - 1;
         v.xC[i] = x.xC[i];
      }
      complexgeneratereflection(&v, n, &tau, _state);
      beta = v.xC[1];
      v.xC[1] = ae_complex_from_i(1);
      for (i = 1; i <= n; i++) {
         for (j = 1; j <= n; j++) {
            if (i == j) {
               h.xyC[i][j] = ae_c_d_sub(1, ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            } else {
               h.xyC[i][j] = ae_c_neg(ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            }
         }
      }
      err = 0.0;
      for (i = 1; i <= n; i++) {
         tmp = ae_v_cdotproduct(&h.xyC[1][i], h.stride, "Conj", &x.xC[1], 1, "N", n);
         if (i == 1) {
            err = ae_maxreal(err, ae_c_abs(ae_c_sub(tmp, beta), _state), _state);
         } else {
            err = ae_maxreal(err, ae_c_abs(tmp, _state), _state);
         }
      }
      err = ae_maxreal(err, ae_fabs(beta.y, _state), _state);
      meg = ae_maxreal(meg, err, _state);

   // ApplyReflectionFromTheLeft
      for (i = 1; i <= m; i++) {
         x.xC[i].x = 2 * ae_randomreal(_state) - 1;
         x.xC[i].y = 2 * ae_randomreal(_state) - 1;
         v.xC[i] = x.xC[i];
      }
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            a.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            a.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
            b.xyC[i][j] = a.xyC[i][j];
         }
      }
      complexgeneratereflection(&v, m, &tau, _state);
      beta = v.xC[1];
      v.xC[1] = ae_complex_from_i(1);
      complexapplyreflectionfromtheleft(&b, tau, &v, 1, m, 1, n, &work, _state);
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= m; j++) {
            if (i == j) {
               h.xyC[i][j] = ae_c_d_sub(1, ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            } else {
               h.xyC[i][j] = ae_c_neg(ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            }
         }
      }
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            tmp = ae_v_cdotproduct(&h.xyC[i][1], 1, "N", &a.xyC[1][j], a.stride, "N", m);
            c.xyC[i][j] = tmp;
         }
      }
      err = 0.0;
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            err = ae_maxreal(err, ae_c_abs(ae_c_sub(b.xyC[i][j], c.xyC[i][j]), _state), _state);
         }
      }
      mel = ae_maxreal(mel, err, _state);

   // ApplyReflectionFromTheRight
      for (i = 1; i <= n; i++) {
         x.xC[i] = ae_complex_from_d(2 * ae_randomreal(_state) - 1);
         v.xC[i] = x.xC[i];
      }
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            a.xyC[i][j] = ae_complex_from_d(2 * ae_randomreal(_state) - 1);
            b.xyC[i][j] = a.xyC[i][j];
         }
      }
      complexgeneratereflection(&v, n, &tau, _state);
      beta = v.xC[1];
      v.xC[1] = ae_complex_from_i(1);
      complexapplyreflectionfromtheright(&b, tau, &v, 1, m, 1, n, &work, _state);
      for (i = 1; i <= n; i++) {
         for (j = 1; j <= n; j++) {
            if (i == j) {
               h.xyC[i][j] = ae_c_d_sub(1, ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            } else {
               h.xyC[i][j] = ae_c_neg(ae_c_mul(ae_c_mul(tau, v.xC[i]), ae_c_conj(v.xC[j], _state)));
            }
         }
      }
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            tmp = ae_v_cdotproduct(&a.xyC[i][1], 1, "N", &h.xyC[1][j], h.stride, "N", n);
            c.xyC[i][j] = tmp;
         }
      }
      err = 0.0;
      for (i = 1; i <= m; i++) {
         for (j = 1; j <= n; j++) {
            err = ae_maxreal(err, ae_c_abs(ae_c_sub(b.xyC[i][j], c.xyC[i][j]), _state), _state);
         }
      }
      mer = ae_maxreal(mer, err, _state);
   }

// Overflow crash test
   ae_vector_set_length(&x, 10 + 1, _state);
   ae_vector_set_length(&v, 10 + 1, _state);
   for (i = 1; i <= 10; i++) {
      v.xC[i] = ae_complex_from_d(ae_maxrealnumber * 0.01 * (2 * ae_randomreal(_state) - 1));
   }
   complexgeneratereflection(&v, 10, &tau, _state);

// report
   waserrors = (meg > threshold || mel > threshold) || mer > threshold;
   if (!silent) {
      printf("TESTING COMPLEX REFLECTIONS\n");
      printf("Generate error:                          %5.3e\n", meg);
      printf("Apply(L) error:                          %5.3e\n", mel);
      printf("Apply(R) error:                          %5.3e\n", mer);
      printf("Threshold:                               %5.3e\n", threshold);
      printf("Overflow crash test:                     PASSED\n");
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

