// Main unittest subroutine
bool testortfac(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   double threshold;
   ae_int_t mx;
   ae_int_t m;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;
   bool rqrerrors;
   bool rlqerrors;
   bool cqrerrors;
   bool clqerrors;
   bool rbderrors;
   bool rhesserrors;
   bool rtderrors;
   bool ctderrors;
   bool waserrors;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(ra, 0, 0, DT_REAL, _state);
   NewMatrix(ca, 0, 0, DT_COMPLEX, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   waserrors = false;
   rqrerrors = false;
   rlqerrors = false;
   cqrerrors = false;
   clqerrors = false;
   rbderrors = false;
   rhesserrors = false;
   rtderrors = false;
   ctderrors = false;
   threshold = 5 * 1000 * ae_machineepsilon;

// Medium-scale problems with various sparseness profiles
   for (mx = 1; mx <= 3 * matrixtilesizea(_state) + 1; mx++) {

   // Rectangular factorizations: QR, LQ, bidiagonal
   // Matrix types: zero, dense, sparse
      n = 1 + ae_randominteger(mx, _state);
      m = 1 + ae_randominteger(mx, _state);
      if (ae_randomreal(_state) > 0.5) {
         n = mx;
      } else {
         m = mx;
      }
      ae_matrix_set_length(&ra, m, n, _state);
      ae_matrix_set_length(&ca, m, n, _state);
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            ra.xyR[i][j] = 0.0;
            ca.xyC[i][j] = ae_complex_from_i(0);
         }
      }
      testortfacunit_testrqrproblem(&ra, m, n, threshold, &rqrerrors, _state);
      testortfacunit_testrlqproblem(&ra, m, n, threshold, &rlqerrors, _state);
      testortfacunit_testcqrproblem(&ca, m, n, threshold, &cqrerrors, _state);
      testortfacunit_testclqproblem(&ca, m, n, threshold, &clqerrors, _state);
      testortfacunit_testrbdproblem(&ra, m, n, threshold, &rbderrors, _state);
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
         }
      }
      testortfacunit_testrqrproblem(&ra, m, n, threshold, &rqrerrors, _state);
      testortfacunit_testrlqproblem(&ra, m, n, threshold, &rlqerrors, _state);
      testortfacunit_testcqrproblem(&ca, m, n, threshold, &cqrerrors, _state);
      testortfacunit_testclqproblem(&ca, m, n, threshold, &clqerrors, _state);
      testortfacunit_testrbdproblem(&ra, m, n, threshold, &rbderrors, _state);
      testortfacunit_rmatrixfillsparsea(&ra, m, n, 0.95, _state);
      testortfacunit_cmatrixfillsparsea(&ca, m, n, 0.95, _state);
      testortfacunit_testrqrproblem(&ra, m, n, threshold, &rqrerrors, _state);
      testortfacunit_testrlqproblem(&ra, m, n, threshold, &rlqerrors, _state);
      testortfacunit_testcqrproblem(&ca, m, n, threshold, &cqrerrors, _state);
      testortfacunit_testclqproblem(&ca, m, n, threshold, &clqerrors, _state);
      testortfacunit_testrbdproblem(&ra, m, n, threshold, &rbderrors, _state);

   // Square factorizations: Hessenberg, tridiagonal
   // Matrix types: zero, dense, sparse
      ae_matrix_set_length(&ra, mx, mx, _state);
      ae_matrix_set_length(&ca, mx, mx, _state);
      for (i = 0; i < mx; i++) {
         for (j = 0; j < mx; j++) {
            ra.xyR[i][j] = 0.0;
            ca.xyC[i][j] = ae_complex_from_i(0);
         }
      }
      testortfacunit_testrhessproblem(&ra, mx, threshold, &rhesserrors, _state);
      for (i = 0; i < mx; i++) {
         for (j = 0; j < mx; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
         }
      }
      testortfacunit_testrhessproblem(&ra, mx, threshold, &rhesserrors, _state);
      testortfacunit_rmatrixfillsparsea(&ra, mx, mx, 0.95, _state);
      testortfacunit_cmatrixfillsparsea(&ca, mx, mx, 0.95, _state);
      testortfacunit_testrhessproblem(&ra, mx, threshold, &rhesserrors, _state);

   // Symetric factorizations: tridiagonal
   // Matrix types: zero, dense, sparse
      ae_matrix_set_length(&ra, mx, mx, _state);
      ae_matrix_set_length(&ca, mx, mx, _state);
      for (i = 0; i < mx; i++) {
         for (j = 0; j < mx; j++) {
            ra.xyR[i][j] = 0.0;
            ca.xyC[i][j] = ae_complex_from_i(0);
         }
      }
      testortfacunit_testrtdproblem(&ra, mx, threshold, &rtderrors, _state);
      testortfacunit_testctdproblem(&ca, mx, threshold, &ctderrors, _state);
      for (i = 0; i < mx; i++) {
         for (j = i; j < mx; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
            ra.xyR[j][i] = ra.xyR[i][j];
            ca.xyC[j][i] = ae_c_conj(ca.xyC[i][j], _state);
         }
      }
      for (i = 0; i < mx; i++) {
         ca.xyC[i][i] = ae_complex_from_d(2 * ae_randomreal(_state) - 1);
      }
      testortfacunit_testrtdproblem(&ra, mx, threshold, &rtderrors, _state);
      testortfacunit_testctdproblem(&ca, mx, threshold, &ctderrors, _state);
      testortfacunit_rmatrixfillsparsea(&ra, mx, mx, 0.95, _state);
      testortfacunit_cmatrixfillsparsea(&ca, mx, mx, 0.95, _state);
      for (i = 0; i < mx; i++) {
         for (j = i; j < mx; j++) {
            ra.xyR[j][i] = ra.xyR[i][j];
            ca.xyC[j][i] = ae_c_conj(ca.xyC[i][j], _state);
         }
      }
      for (i = 0; i < mx; i++) {
         ca.xyC[i][i] = ae_complex_from_d(2 * ae_randomreal(_state) - 1);
      }
      testortfacunit_testrtdproblem(&ra, mx, threshold, &rtderrors, _state);
      testortfacunit_testctdproblem(&ca, mx, threshold, &ctderrors, _state);
   }

// Large-scale tests
   for (mx = 4 * matrixtilesizeb(_state); mx <= 4 * matrixtilesizeb(_state); mx++) {

   // Rectangular factorizations: QR, LQ, bidiagonal
   // Matrix types: dense
      n = 1 + ae_randominteger(mx, _state);
      m = 1 + ae_randominteger(mx, _state);
      if (ae_randomreal(_state) > 0.5) {
         n = mx;
      } else {
         m = mx;
      }
      ae_matrix_set_length(&ra, m, n, _state);
      ae_matrix_set_length(&ca, m, n, _state);
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
         }
      }
      testortfacunit_testrqrproblem(&ra, m, n, threshold, &rqrerrors, _state);
      testortfacunit_testrlqproblem(&ra, m, n, threshold, &rlqerrors, _state);
      testortfacunit_testcqrproblem(&ca, m, n, threshold, &cqrerrors, _state);
      testortfacunit_testclqproblem(&ca, m, n, threshold, &clqerrors, _state);
      testortfacunit_testrbdproblem(&ra, m, n, threshold, &rbderrors, _state);

   // Square factorizations: Hessenberg, tridiagonal
   // Matrix types: dense
      ae_matrix_set_length(&ra, mx, mx, _state);
      ae_matrix_set_length(&ca, mx, mx, _state);
      for (i = 0; i < mx; i++) {
         for (j = 0; j < mx; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
         }
      }
      testortfacunit_testrhessproblem(&ra, mx, threshold, &rhesserrors, _state);

   // Symetric factorizations: tridiagonal
   // Matrix types: dense
      ae_matrix_set_length(&ra, mx, mx, _state);
      ae_matrix_set_length(&ca, mx, mx, _state);
      for (i = 0; i < mx; i++) {
         for (j = i; j < mx; j++) {
            ra.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].x = 2 * ae_randomreal(_state) - 1;
            ca.xyC[i][j].y = 2 * ae_randomreal(_state) - 1;
            ra.xyR[j][i] = ra.xyR[i][j];
            ca.xyC[j][i] = ae_c_conj(ca.xyC[i][j], _state);
         }
      }
      for (i = 0; i < mx; i++) {
         ca.xyC[i][i] = ae_complex_from_d(2 * ae_randomreal(_state) - 1);
      }
      testortfacunit_testrtdproblem(&ra, mx, threshold, &rtderrors, _state);
      testortfacunit_testctdproblem(&ca, mx, threshold, &ctderrors, _state);
   }

// report
   waserrors = ((((((rqrerrors || rlqerrors) || cqrerrors) || clqerrors) || rbderrors) || rhesserrors) || rtderrors) || ctderrors;
   if (!silent) {
      printf("TESTING ORTFAC UNIT\n");
      printf("RQR ERRORS:                              ");
      if (!rqrerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("RLQ ERRORS:                              ");
      if (!rlqerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("CQR ERRORS:                              ");
      if (!cqrerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("CLQ ERRORS:                              ");
      if (!clqerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("RBD ERRORS:                              ");
      if (!rbderrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("RHESS ERRORS:                            ");
      if (!rhesserrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("RTD ERRORS:                              ");
      if (!rtderrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("CTD ERRORS:                              ");
      if (!ctderrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Diff
static double testortfacunit_rmatrixdiff(RMatrix *a, RMatrix *b, ae_int_t m, ae_int_t n, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double result;

   result = 0.0;
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         result = ae_maxreal(result, ae_fabs(b->xyR[i][j] - a->xyR[i][j], _state), _state);
      }
   }
   return result;
}

