// Matrix multiplication
static void testortfacunit_internalmatrixmatrixmultiply(RMatrix *a, ae_int_t ai1, ae_int_t ai2, ae_int_t aj1, ae_int_t aj2, bool transa, RMatrix *b, ae_int_t bi1, ae_int_t bi2, ae_int_t bj1, ae_int_t bj2, bool transb, RMatrix *c, ae_int_t ci1, ae_int_t ci2, ae_int_t cj1, ae_int_t cj2, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t arows;
   ae_int_t acols;
   ae_int_t brows;
   ae_int_t bcols;
   ae_int_t crows;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t l;
   ae_int_t r;
   double v;
   double beta;
   double alpha;

   ae_frame_make(_state, &_frame_block);
   NewVector(work, 0, DT_REAL, _state);

// Pre-setup
   k = ae_maxint(ai2 - ai1 + 1, aj2 - aj1 + 1, _state);
   k = ae_maxint(k, bi2 - bi1 + 1, _state);
   k = ae_maxint(k, bj2 - bj1 + 1, _state);
   ae_vector_set_length(&work, k + 1, _state);
   beta = 0.0;
   alpha = 1.0;

// Setup
   if (!transa) {
      arows = ai2 - ai1 + 1;
      acols = aj2 - aj1 + 1;
   } else {
      arows = aj2 - aj1 + 1;
      acols = ai2 - ai1 + 1;
   }
   if (!transb) {
      brows = bi2 - bi1 + 1;
      bcols = bj2 - bj1 + 1;
   } else {
      brows = bj2 - bj1 + 1;
      bcols = bi2 - bi1 + 1;
   }
   ae_assert(acols == brows, "MatrixMatrixMultiply: incorrect matrix sizes!", _state);
   if (((arows <= 0 || acols <= 0) || brows <= 0) || bcols <= 0) {
      ae_frame_leave(_state);
      return;
   }
   crows = arows;

// Test WORK
   i = ae_maxint(arows, acols, _state);
   i = ae_maxint(brows, i, _state);
   i = ae_maxint(i, bcols, _state);
   work.xR[1] = 0.0;
   work.xR[i] = 0.0;

// Prepare C
   if (beta == 0.0) {
      for (i = ci1; i <= ci2; i++) {
         for (j = cj1; j <= cj2; j++) {
            c->xyR[i][j] = 0.0;
         }
      }
   } else {
      for (i = ci1; i <= ci2; i++) {
         ae_v_muld(&c->xyR[i][cj1], 1, cj2 - cj1 + 1, beta);
      }
   }

// A*B
   if (!transa && !transb) {
      for (l = ai1; l <= ai2; l++) {
         for (r = bi1; r <= bi2; r++) {
            v = alpha * a->xyR[l][aj1 + r - bi1];
            k = ci1 + l - ai1;
            ae_v_addd(&c->xyR[k][cj1], 1, &b->xyR[r][bj1], 1, cj2 - cj1 + 1, v);
         }
      }
      ae_frame_leave(_state);
      return;
   }
// A*B'
   if (!transa && transb) {
      if (arows * acols < brows * bcols) {
         for (r = bi1; r <= bi2; r++) {
            for (l = ai1; l <= ai2; l++) {
               v = ae_v_dotproduct(&a->xyR[l][aj1], 1, &b->xyR[r][bj1], 1, aj2 - aj1 + 1);
               c->xyR[ci1 + l - ai1][cj1 + r - bi1] = c->xyR[ci1 + l - ai1][cj1 + r - bi1] + alpha * v;
            }
         }
         ae_frame_leave(_state);
         return;
      } else {
         for (l = ai1; l <= ai2; l++) {
            for (r = bi1; r <= bi2; r++) {
               v = ae_v_dotproduct(&a->xyR[l][aj1], 1, &b->xyR[r][bj1], 1, aj2 - aj1 + 1);
               c->xyR[ci1 + l - ai1][cj1 + r - bi1] = c->xyR[ci1 + l - ai1][cj1 + r - bi1] + alpha * v;
            }
         }
         ae_frame_leave(_state);
         return;
      }
   }
// A'*B
   if (transa && !transb) {
      for (l = aj1; l <= aj2; l++) {
         for (r = bi1; r <= bi2; r++) {
            v = alpha * a->xyR[ai1 + r - bi1][l];
            k = ci1 + l - aj1;
            ae_v_addd(&c->xyR[k][cj1], 1, &b->xyR[r][bj1], 1, cj2 - cj1 + 1, v);
         }
      }
      ae_frame_leave(_state);
      return;
   }
// A'*B'
   if (transa && transb) {
      if (arows * acols < brows * bcols) {
         for (r = bi1; r <= bi2; r++) {
            for (i = 1; i <= crows; i++) {
               work.xR[i] = 0.0;
            }
            for (l = ai1; l <= ai2; l++) {
               v = alpha * b->xyR[r][bj1 + l - ai1];
               k = cj1 + r - bi1;
               ae_v_addd(&work.xR[1], 1, &a->xyR[l][aj1], 1, crows, v);
            }
            ae_v_add(&c->xyR[ci1][k], c->stride, &work.xR[1], 1, ci2 - ci1 + 1);
         }
         ae_frame_leave(_state);
         return;
      } else {
         for (l = aj1; l <= aj2; l++) {
            k = ai2 - ai1 + 1;
            ae_v_move(&work.xR[1], 1, &a->xyR[ai1][l], a->stride, k);
            for (r = bi1; r <= bi2; r++) {
               v = ae_v_dotproduct(&work.xR[1], 1, &b->xyR[r][bj1], 1, k);
               c->xyR[ci1 + l - aj1][cj1 + r - bi1] = c->xyR[ci1 + l - aj1][cj1 + r - bi1] + alpha * v;
            }
         }
         ae_frame_leave(_state);
         return;
      }
   }
   ae_frame_leave(_state);
}

// Problem testing
static void testortfacunit_testrqrproblem(RMatrix *a, ae_int_t m, ae_int_t n, double threshold, bool *qrerrors, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(b, 0, 0, DT_REAL, _state);
   NewVector(taub, 0, DT_REAL, _state);
   NewMatrix(q, 0, 0, DT_REAL, _state);
   NewMatrix(r, 0, 0, DT_REAL, _state);
   NewMatrix(q2, 0, 0, DT_REAL, _state);

// Test decompose-and-unpack error
   testortfacunit_rmatrixmakeacopy(a, m, n, &b, _state);
   rmatrixqr(&b, m, n, &taub, _state);
   rmatrixqrunpackq(&b, m, n, &taub, m, &q, _state);
   rmatrixqrunpackr(&b, m, n, &r, _state);
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         v = ae_v_dotproduct(q.xyR[i], 1, &r.xyR[0][j], r.stride, m);
         *qrerrors = *qrerrors || ae_fabs(v - a->xyR[i][j], _state) > threshold;
      }
   }
   for (i = 0; i < m; i++) {
      for (j = 0; j < ae_minint(i, n - 1, _state); j++) {
         *qrerrors = *qrerrors || r.xyR[i][j] != 0.0;
      }
   }
   for (i = 0; i < m; i++) {
      for (j = 0; j < m; j++) {
         v = ae_v_dotproduct(q.xyR[i], 1, q.xyR[j], 1, m);
         if (i == j) {
            v = v - 1;
         }
         *qrerrors = *qrerrors || ae_fabs(v, _state) >= threshold;
      }
   }

// Test for other errors
   k = 1 + ae_randominteger(m, _state);
   rmatrixqrunpackq(&b, m, n, &taub, k, &q2, _state);
   for (i = 0; i < m; i++) {
      for (j = 0; j < k; j++) {
         *qrerrors = *qrerrors || ae_fabs(q2.xyR[i][j] - q.xyR[i][j], _state) > 10 * ae_machineepsilon;
      }
   }
   ae_frame_leave(_state);
}

// Problem testing
static void testortfacunit_testcqrproblem(CMatrix *a, ae_int_t m, ae_int_t n, double threshold, bool *qrerrors, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_complex v;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(b, 0, 0, DT_COMPLEX, _state);
   NewVector(taub, 0, DT_COMPLEX, _state);
   NewMatrix(q, 0, 0, DT_COMPLEX, _state);
   NewMatrix(r, 0, 0, DT_COMPLEX, _state);
   NewMatrix(q2, 0, 0, DT_COMPLEX, _state);

// Test decompose-and-unpack error
   testortfacunit_cmatrixmakeacopy(a, m, n, &b, _state);
   cmatrixqr(&b, m, n, &taub, _state);
   cmatrixqrunpackq(&b, m, n, &taub, m, &q, _state);
   cmatrixqrunpackr(&b, m, n, &r, _state);
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         v = ae_v_cdotproduct(q.xyC[i], 1, "N", &r.xyC[0][j], r.stride, "N", m);
         *qrerrors = *qrerrors || ae_c_abs(ae_c_sub(v, a->xyC[i][j]), _state) > threshold;
      }
   }
   for (i = 0; i < m; i++) {
      for (j = 0; j < ae_minint(i, n - 1, _state); j++) {
         *qrerrors = *qrerrors || ae_c_neq_d(r.xyC[i][j], 0.0);
      }
   }
   for (i = 0; i < m; i++) {
      for (j = 0; j < m; j++) {
         v = ae_v_cdotproduct(q.xyC[i], 1, "N", q.xyC[j], 1, "Conj", m);
         if (i == j) {
            v = ae_c_sub_d(v, 1);
         }
         *qrerrors = *qrerrors || ae_c_abs(v, _state) >= threshold;
      }
   }

// Test for other errors
   k = 1 + ae_randominteger(m, _state);
   cmatrixqrunpackq(&b, m, n, &taub, k, &q2, _state);
   for (i = 0; i < m; i++) {
      for (j = 0; j < k; j++) {
         *qrerrors = *qrerrors || ae_c_abs(ae_c_sub(q2.xyC[i][j], q.xyC[i][j]), _state) > 10 * ae_machineepsilon;
      }
   }
   ae_frame_leave(_state);
}

