// Test whether matrix is SPD
static bool testmatgenunit_isspd(RMatrix *a, ae_int_t n, bool isupper, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double ajj;
   double v;
   bool result;

   ae_frame_make(_state, &_frame_block);
   DupMatrix(a, _state);

//     Test the input parameters.
   ae_assert(n >= 0, "Error in SMatrixCholesky: incorrect function arguments", _state);

//     Quick return if possible
   result = true;
   if (n <= 0) {
      ae_frame_leave(_state);
      return result;
   }
   if (isupper) {

   // Compute the Cholesky factorization A = U'*U.
      for (j = 0; j < n; j++) {

      // Compute U(J,J) and test for non-positive-definiteness.
         v = ae_v_dotproduct(&a->xyR[0][j], a->stride, &a->xyR[0][j], a->stride, j);
         ajj = a->xyR[j][j] - v;
         if (ajj <= 0.0) {
            result = false;
            ae_frame_leave(_state);
            return result;
         }
         ajj = ae_sqrt(ajj, _state);
         a->xyR[j][j] = ajj;

      // Compute elements J+1:N of row J.
         if (j < n - 1) {
            for (i = j + 1; i < n; i++) {
               v = ae_v_dotproduct(&a->xyR[0][i], a->stride, &a->xyR[0][j], a->stride, j);
               a->xyR[j][i] = a->xyR[j][i] - v;
            }
            v = 1 / ajj;
            ae_v_muld(&a->xyR[j][j + 1], 1, n - j - 1, v);
         }
      }
   } else {

   // Compute the Cholesky factorization A = L*L'.
      for (j = 0; j < n; j++) {

      // Compute L(J,J) and test for non-positive-definiteness.
         v = ae_v_dotproduct(a->xyR[j], 1, a->xyR[j], 1, j);
         ajj = a->xyR[j][j] - v;
         if (ajj <= 0.0) {
            result = false;
            ae_frame_leave(_state);
            return result;
         }
         ajj = ae_sqrt(ajj, _state);
         a->xyR[j][j] = ajj;

      // Compute elements J+1:N of column J.
         if (j < n - 1) {
            for (i = j + 1; i < n; i++) {
               v = ae_v_dotproduct(a->xyR[i], 1, a->xyR[j], 1, j);
               a->xyR[i][j] = a->xyR[i][j] - v;
            }
            v = 1 / ajj;
            ae_v_muld(&a->xyR[j + 1][j], a->stride, n - j - 1, v);
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Tests whether A is HPD
static bool testmatgenunit_ishpd(CMatrix *a, ae_int_t n, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t j;
   double ajj;
   ae_complex v;
   double r;
   ae_int_t i;
   bool result;

   ae_frame_make(_state, &_frame_block);
   DupMatrix(a, _state);
   NewVector(t, 0, DT_COMPLEX, _state);
   NewVector(t2, 0, DT_COMPLEX, _state);
   NewVector(t3, 0, DT_COMPLEX, _state);
   NewMatrix(a1, 0, 0, DT_COMPLEX, _state);

   ae_vector_set_length(&t, n - 1 + 1, _state);
   ae_vector_set_length(&t2, n - 1 + 1, _state);
   ae_vector_set_length(&t3, n - 1 + 1, _state);
   result = true;

// Compute the Cholesky factorization A = U'*U.
   for (j = 0; j < n; j++) {

   // Compute U(J,J) and test for non-positive-definiteness.
      v = ae_v_cdotproduct(&a->xyC[0][j], a->stride, "Conj", &a->xyC[0][j], a->stride, "N", j);
      ajj = ae_c_sub(a->xyC[j][j], v).x;
      if (ajj <= 0.0) {
         a->xyC[j][j] = ae_complex_from_d(ajj);
         result = false;
         ae_frame_leave(_state);
         return result;
      }
      ajj = ae_sqrt(ajj, _state);
      a->xyC[j][j] = ae_complex_from_d(ajj);

   // Compute elements J+1:N-1 of row J.
      if (j < n - 1) {
         ae_v_cmove(t2.xC, 1, &a->xyC[0][j], a->stride, "Conj", j);
         ae_v_cmove(&t3.xC[j + 1], 1, &a->xyC[j][j + 1], 1, "N", n - j - 1);
         for (i = j + 1; i < n; i++) {
            v = ae_v_cdotproduct(&a->xyC[0][i], a->stride, "N", t2.xC, 1, "N", j);
            t3.xC[i] = ae_c_sub(t3.xC[i], v);
         }
         ae_v_cmove(&a->xyC[j][j + 1], 1, &t3.xC[j + 1], 1, "N", n - j - 1);
         r = 1 / ajj;
         ae_v_cmuld(&a->xyC[j][j + 1], 1, n - j - 1, r);
      }
   }
   ae_frame_leave(_state);
   return result;
}

// The function check, that upper triangle from symmetric matrix is equal to
// lower triangle.
static bool testmatgenunit_testeult(ae_state *_state) {
   ae_frame _frame_block;
   double c;
   double range;
   double eps;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(b, 0, 0, DT_COMPLEX, _state);

   eps = 2 * ae_machineepsilon;
   range = 100 * (2 * ae_randomreal(_state) - 1);
   for (n = 1; n <= 15; n++) {
      c = 900 * ae_randomreal(_state) + 100;

   // Generate symmetric matrix and check it
      smatrixrndcond(n, c, &a, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(a.xyR[i][j] - a.xyR[j][i], _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
      spdmatrixrndcond(n, c, &a, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(a.xyR[i][j] - a.xyR[j][i], _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
      hmatrixrndcond(n, c, &b, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(b.xyC[i][j].x - b.xyC[j][i].x, _state) > eps || ae_fabs(b.xyC[i][j].y + b.xyC[j][i].y, _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
      hpdmatrixrndcond(n, c, &b, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(b.xyC[i][j].x - b.xyC[j][i].x, _state) > eps || ae_fabs(b.xyC[i][j].y + b.xyC[j][i].y, _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }

   // Prepare symmetric matrix with real values
      for (i = 0; i < n; i++) {
         for (j = i; j < n; j++) {
            a.xyR[i][j] = range * (2 * ae_randomreal(_state) - 1);
         }
      }
      for (i = 0; i < n - 1; i++) {
         for (j = i + 1; j < n; j++) {
            a.xyR[j][i] = a.xyR[i][j];
         }
      }
      smatrixrndmultiply(&a, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(a.xyR[i][j] - a.xyR[j][i], _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }

   // Prepare symmetric matrix with complex values
      for (i = 0; i < n; i++) {
         for (j = i; j < n; j++) {
            b.xyC[i][j].x = range * (2 * ae_randomreal(_state) - 1);
            if (i != j) {
               b.xyC[i][j].y = range * (2 * ae_randomreal(_state) - 1);
            } else {
               b.xyC[i][j].y = 0.0;
            }
         }
      }
      for (i = 0; i < n; i++) {
         for (j = i + 1; j < n; j++) {
            b.xyC[i][j].x = b.xyC[j][i].x;
            b.xyC[i][j].y = -b.xyC[j][i].y;
         }
      }
      hmatrixrndmultiply(&b, n, _state);
      for (i = 0; i < n; i++) {
         b.xyC[i][i].y = 0.0;
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (ae_fabs(b.xyC[i][j].x - b.xyC[j][i].x, _state) > eps || ae_fabs(b.xyC[i][j].y + b.xyC[j][i].y, _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

// SVD condition number
static double testmatgenunit_svdcond(RMatrix *a, ae_int_t n, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double minw;
   double maxw;
   double result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewMatrix(v, 0, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);

   ae_matrix_set_length(&a1, n + 1, n + 1, _state);
   for (i = 1; i <= n; i++) {
      for (j = 1; j <= n; j++) {
         a1.xyR[i][j] = a->xyR[i - 1][j - 1];
      }
   }
   if (!testmatgenunit_obsoletesvddecomposition(&a1, n, n, &w, &v, _state)) {
      result = 0.0;
      ae_frame_leave(_state);
      return result;
   }
   minw = w.xR[1];
   maxw = w.xR[1];
   for (i = 2; i <= n; i++) {
      if (w.xR[i] < minw) {
         minw = w.xR[i];
      }
      if (w.xR[i] > maxw) {
         maxw = w.xR[i];
      }
   }
   result = maxw / minw;
   ae_frame_leave(_state);
   return result;
}

static bool testmatgenunit_obsoletesvddecomposition(RMatrix *a, ae_int_t m, ae_int_t n, RVector *w, RMatrix *v, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nm;
   ae_int_t minmn;
   ae_int_t l;
   ae_int_t k;
   ae_int_t j;
   ae_int_t jj;
   ae_int_t its;
   ae_int_t i;
   double z;
   double y;
   double x;
   double vscale;
   double s;
   double h;
   double g;
   double f;
   double c;
   double anorm;
   bool flag;
   bool result;

   ae_frame_make(_state, &_frame_block);
   SetVector(w);
   SetMatrix(v);
   NewVector(rv1, 0, DT_REAL, _state);

   ae_vector_set_length(&rv1, n + 1, _state);
   ae_vector_set_length(w, n + 1, _state);
   ae_matrix_set_length(v, n + 1, n + 1, _state);
   result = true;
   if (m < n) {
      minmn = m;
   } else {
      minmn = n;
   }
   g = 0.0;
   vscale = 0.0;
   anorm = 0.0;
   l = n;
   for (i = 1; i <= n; i++) {
      l = i + 1;
      rv1.xR[i] = vscale * g;
      g = 0.0;
      s = 0.0;
      vscale = 0.0;
      if (i <= m) {
         for (k = i; k <= m; k++) {
            vscale = vscale + ae_fabs(a->xyR[k][i], _state);
         }
         if (vscale != 0.0) {
            for (k = i; k <= m; k++) {
               a->xyR[k][i] = a->xyR[k][i] / vscale;
               s = s + a->xyR[k][i] * a->xyR[k][i];
            }
            f = a->xyR[i][i];
            g = -testmatgenunit_extsign(ae_sqrt(s, _state), f, _state);
            h = f * g - s;
            a->xyR[i][i] = f - g;
            if (i != n) {
               for (j = l; j <= n; j++) {
                  s = 0.0;
                  for (k = i; k <= m; k++) {
                     s = s + a->xyR[k][i] * a->xyR[k][j];
                  }
                  f = s / h;
                  for (k = i; k <= m; k++) {
                     a->xyR[k][j] = a->xyR[k][j] + f * a->xyR[k][i];
                  }
               }
            }
            for (k = i; k <= m; k++) {
               a->xyR[k][i] = vscale * a->xyR[k][i];
            }
         }
      }
      w->xR[i] = vscale * g;
      g = 0.0;
      s = 0.0;
      vscale = 0.0;
      if (i <= m && i != n) {
         for (k = l; k <= n; k++) {
            vscale = vscale + ae_fabs(a->xyR[i][k], _state);
         }
         if (vscale != 0.0) {
            for (k = l; k <= n; k++) {
               a->xyR[i][k] = a->xyR[i][k] / vscale;
               s = s + a->xyR[i][k] * a->xyR[i][k];
            }
            f = a->xyR[i][l];
            g = -testmatgenunit_extsign(ae_sqrt(s, _state), f, _state);
            h = f * g - s;
            a->xyR[i][l] = f - g;
            for (k = l; k <= n; k++) {
               rv1.xR[k] = a->xyR[i][k] / h;
            }
            if (i != m) {
               for (j = l; j <= m; j++) {
                  s = 0.0;
                  for (k = l; k <= n; k++) {
                     s = s + a->xyR[j][k] * a->xyR[i][k];
                  }
                  for (k = l; k <= n; k++) {
                     a->xyR[j][k] = a->xyR[j][k] + s * rv1.xR[k];
                  }
               }
            }
            for (k = l; k <= n; k++) {
               a->xyR[i][k] = vscale * a->xyR[i][k];
            }
         }
      }
      anorm = testmatgenunit_mymax(anorm, ae_fabs(w->xR[i], _state) + ae_fabs(rv1.xR[i], _state), _state);
   }
   for (i = n; i >= 1; i--) {
      if (i < n) {
         if (g != 0.0) {
            for (j = l; j <= n; j++) {
               v->xyR[j][i] = a->xyR[i][j] / a->xyR[i][l] / g;
            }
            for (j = l; j <= n; j++) {
               s = 0.0;
               for (k = l; k <= n; k++) {
                  s = s + a->xyR[i][k] * v->xyR[k][j];
               }
               for (k = l; k <= n; k++) {
                  v->xyR[k][j] = v->xyR[k][j] + s * v->xyR[k][i];
               }
            }
         }
         for (j = l; j <= n; j++) {
            v->xyR[i][j] = 0.0;
            v->xyR[j][i] = 0.0;
         }
      }
      v->xyR[i][i] = 1.0;
      g = rv1.xR[i];
      l = i;
   }
   for (i = minmn; i >= 1; i--) {
      l = i + 1;
      g = w->xR[i];
      if (i < n) {
         for (j = l; j <= n; j++) {
            a->xyR[i][j] = 0.0;
         }
      }
      if (g != 0.0) {
         g = 1.0 / g;
         if (i != n) {
            for (j = l; j <= n; j++) {
               s = 0.0;
               for (k = l; k <= m; k++) {
                  s = s + a->xyR[k][i] * a->xyR[k][j];
               }
               f = s / a->xyR[i][i] * g;
               for (k = i; k <= m; k++) {
                  a->xyR[k][j] = a->xyR[k][j] + f * a->xyR[k][i];
               }
            }
         }
         for (j = i; j <= m; j++) {
            a->xyR[j][i] = a->xyR[j][i] * g;
         }
      } else {
         for (j = i; j <= m; j++) {
            a->xyR[j][i] = 0.0;
         }
      }
      a->xyR[i][i] = a->xyR[i][i] + 1.0;
   }
   nm = 0;
   for (k = n; k >= 1; k--) {
      for (its = 1; its <= testmatgenunit_maxsvditerations; its++) {
         flag = true;
         for (l = k; l >= 1; l--) {
            nm = l - 1;
            if (ae_fabs(rv1.xR[l], _state) + anorm == anorm) {
               flag = false;
               break;
            }
            if (ae_fabs(w->xR[nm], _state) + anorm == anorm) {
               break;
            }
         }
         if (flag) {
            c = 0.0;
            s = 1.0;
            for (i = l; i <= k; i++) {
               f = s * rv1.xR[i];
               if (ae_fabs(f, _state) + anorm != anorm) {
                  g = w->xR[i];
                  h = testmatgenunit_pythag(f, g, _state);
                  w->xR[i] = h;
                  h = 1.0 / h;
                  c = g * h;
                  s = -f * h;
                  for (j = 1; j <= m; j++) {
                     y = a->xyR[j][nm];
                     z = a->xyR[j][i];
                     a->xyR[j][nm] = y * c + z * s;
                     a->xyR[j][i] = -y * s + z * c;
                  }
               }
            }
         }
         z = w->xR[k];
         if (l == k) {
            if (z < 0.0) {
               w->xR[k] = -z;
               for (j = 1; j <= n; j++) {
                  v->xyR[j][k] = -v->xyR[j][k];
               }
            }
            break;
         }
         if (its == testmatgenunit_maxsvditerations) {
            result = false;
            ae_frame_leave(_state);
            return result;
         }
         x = w->xR[l];
         nm = k - 1;
         y = w->xR[nm];
         g = rv1.xR[nm];
         h = rv1.xR[k];
         f = ((y - z) * (y + z) + (g - h) * (g + h)) / (2.0 * h * y);
         g = testmatgenunit_pythag(f, 1.0, _state);
         f = ((x - z) * (x + z) + h * (y / (f + testmatgenunit_extsign(g, f, _state)) - h)) / x;
         c = 1.0;
         s = 1.0;
         for (j = l; j <= nm; j++) {
            i = j + 1;
            g = rv1.xR[i];
            y = w->xR[i];
            h = s * g;
            g = c * g;
            z = testmatgenunit_pythag(f, h, _state);
            rv1.xR[j] = z;
            c = f / z;
            s = h / z;
            f = x * c + g * s;
            g = -x * s + g * c;
            h = y * s;
            y = y * c;
            for (jj = 1; jj <= n; jj++) {
               x = v->xyR[jj][j];
               z = v->xyR[jj][i];
               v->xyR[jj][j] = x * c + z * s;
               v->xyR[jj][i] = -x * s + z * c;
            }
            z = testmatgenunit_pythag(f, h, _state);
            w->xR[j] = z;
            if (z != 0.0) {
               z = 1.0 / z;
               c = f * z;
               s = h * z;
            }
            f = c * g + s * y;
            x = -s * g + c * y;
            for (jj = 1; jj <= m; jj++) {
               y = a->xyR[jj][j];
               z = a->xyR[jj][i];
               a->xyR[jj][j] = y * c + z * s;
               a->xyR[jj][i] = -y * s + z * c;
            }
         }
         rv1.xR[l] = 0.0;
         rv1.xR[k] = f;
         w->xR[k] = x;
      }
   }
   ae_frame_leave(_state);
   return result;
}

static double testmatgenunit_extsign(double a, double b, ae_state *_state) {
   double result;

   if (b >= 0.0) {
      result = ae_fabs(a, _state);
   } else {
      result = -ae_fabs(a, _state);
   }
   return result;
}

static double testmatgenunit_mymax(double a, double b, ae_state *_state) {
   double result;

   if (a > b) {
      result = a;
   } else {
      result = b;
   }
   return result;
}

static double testmatgenunit_pythag(double a, double b, ae_state *_state) {
   double result;

   if (ae_fabs(a, _state) < ae_fabs(b, _state)) {
      result = ae_fabs(b, _state) * ae_sqrt(1 + ae_sqr(a / b, _state), _state);
   } else {
      result = ae_fabs(a, _state) * ae_sqrt(1 + ae_sqr(b / a, _state), _state);
   }
   return result;
}

// === tsort testing unit ===
static void testtsortunit_unset1di(ZVector *a, ae_state *_state);
static void testtsortunit_testsortresults(RVector *asorted, ZVector *p1, ZVector *p2, RVector *aoriginal, ae_int_t n, bool *waserrors, ae_state *_state);

// Testing tag sort
bool testtsort(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   ae_int_t n;
   ae_int_t i;
   ae_int_t m;
   ae_int_t offs;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t maxn;
   bool distinctvals;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(a, 0, DT_REAL, _state);
   NewVector(a0, 0, DT_REAL, _state);
   NewVector(a1, 0, DT_REAL, _state);
   NewVector(a2, 0, DT_REAL, _state);
   NewVector(a3, 0, DT_REAL, _state);
   NewVector(i1, 0, DT_INT, _state);
   NewVector(i2, 0, DT_INT, _state);
   NewVector(i3, 0, DT_INT, _state);
   NewVector(a4, 0, DT_INT, _state);
   NewVector(a5, 0, DT_INT, _state);
   NewVector(pa4, 0, DT_INT, _state);
   NewVector(ar, 0, DT_REAL, _state);
   NewVector(ar2, 0, DT_REAL, _state);
   NewVector(ai, 0, DT_INT, _state);
   NewVector(p1, 0, DT_INT, _state);
   NewVector(p2, 0, DT_INT, _state);
   NewVector(bufr1, 0, DT_REAL, _state);
   NewVector(bufr2, 0, DT_REAL, _state);
   NewVector(bufi1, 0, DT_INT, _state);

   waserrors = false;
   maxn = 100;
   passcount = 10;

// Test tagsort
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= passcount; pass++) {

      // Pprobably distinct sort:
      // * generate array of integer random numbers.
      //   Because of birthday paradox, random numbers have to be VERY large
      //   in order to avoid situation when we have distinct values.
      // * sort A0 using TagSort and test sort results
      // * now we can use A0 as reference point and test other functions
         testtsortunit_unset1di(&p1, _state);
         testtsortunit_unset1di(&p2, _state);
         ae_vector_set_length(&a, n, _state);
         ae_vector_set_length(&a0, n, _state);
         ae_vector_set_length(&a1, n, _state);
         ae_vector_set_length(&a2, n, _state);
         ae_vector_set_length(&a3, n, _state);
         ae_vector_set_length(&a4, n, _state);
         ae_vector_set_length(&a5, n, _state);
         ae_vector_set_length(&ar, n, _state);
         ae_vector_set_length(&ar2, n, _state);
         ae_vector_set_length(&ai, n, _state);
         for (i = 0; i < n; i++) {
            a.xR[i] = (double)ae_randominteger(100000000, _state);
            a0.xR[i] = a.xR[i];
            a1.xR[i] = a.xR[i];
            a2.xR[i] = a.xR[i];
            a3.xR[i] = a.xR[i];
            a4.xZ[i] = ae_round(a.xR[i], _state);
            a5.xZ[i] = ae_round(a.xR[i], _state);
            ar.xR[i] = (double)i;
            ar2.xR[i] = (double)i;
            ai.xZ[i] = i;
         }
         tagsort(&a0, n, &p1, &p2, _state);
         testtsortunit_testsortresults(&a0, &p1, &p2, &a, n, &waserrors, _state);
         distinctvals = true;
         for (i = 1; i < n; i++) {
            distinctvals = distinctvals && a0.xR[i] != a0.xR[i - 1];
         }
         if (distinctvals) {
            tagsortfasti(&a1, &ai, &bufr1, &bufi1, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = (waserrors || a1.xR[i] != a0.xR[i]) || ai.xZ[i] != p1.xZ[i];
            }
            tagsortfastr(&a2, &ar, &bufr1, &bufr2, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = (waserrors || a2.xR[i] != a0.xR[i]) || ar.xR[i] != (double)p1.xZ[i];
            }
            tagsortfast(&a3, &bufr1, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = waserrors || a3.xR[i] != a0.xR[i];
            }
            tagsortmiddleir(&a4, &ar2, 0, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = (waserrors || (double)a4.xZ[i] != a0.xR[i]) || ar2.xR[i] != (double)p1.xZ[i];
            }
            sortmiddlei(&a5, 0, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = waserrors || (double)a5.xZ[i] != a0.xR[i];
            }
         }
      // Non-distinct sort.
      // We test that keys are correctly reordered, but do NOT test order of values.
         testtsortunit_unset1di(&p1, _state);
         testtsortunit_unset1di(&p2, _state);
         ae_vector_set_length(&a, n, _state);
         ae_vector_set_length(&a0, n, _state);
         ae_vector_set_length(&a1, n, _state);
         ae_vector_set_length(&a2, n, _state);
         ae_vector_set_length(&a3, n, _state);
         ae_vector_set_length(&a4, n, _state);
         ae_vector_set_length(&a5, n, _state);
         ae_vector_set_length(&ar, n, _state);
         ae_vector_set_length(&ar2, n, _state);
         ae_vector_set_length(&ai, n, _state);
         for (i = 0; i < n; i++) {
            a.xR[i] = (double)((n - i) / 2);
            a0.xR[i] = a.xR[i];
            a1.xR[i] = a.xR[i];
            a2.xR[i] = a.xR[i];
            a3.xR[i] = a.xR[i];
            a4.xZ[i] = ae_round(a.xR[i], _state);
            a5.xZ[i] = ae_round(a.xR[i], _state);
            ar.xR[i] = (double)i;
            ar2.xR[i] = (double)i;
            ai.xZ[i] = i;
         }
         tagsort(&a0, n, &p1, &p2, _state);
         testtsortunit_testsortresults(&a0, &p1, &p2, &a, n, &waserrors, _state);
         tagsortfasti(&a1, &ai, &bufr1, &bufi1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a1.xR[i] != a0.xR[i];
         }
         tagsortfastr(&a2, &ar, &bufr1, &bufr2, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a2.xR[i] != a0.xR[i];
         }
         tagsortfast(&a3, &bufr1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a3.xR[i] != a0.xR[i];
         }
         tagsortmiddleir(&a4, &ar2, 0, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || (double)a4.xZ[i] != a0.xR[i];
         }
         sortmiddlei(&a5, 0, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || (double)a5.xZ[i] != a0.xR[i];
         }

      // 'All same' sort
      // We test that keys are correctly reordered, but do NOT test order of values.
         testtsortunit_unset1di(&p1, _state);
         testtsortunit_unset1di(&p2, _state);
         ae_vector_set_length(&a, n, _state);
         ae_vector_set_length(&a0, n, _state);
         ae_vector_set_length(&a1, n, _state);
         ae_vector_set_length(&a2, n, _state);
         ae_vector_set_length(&a3, n, _state);
         ae_vector_set_length(&a4, n, _state);
         ae_vector_set_length(&ar, n, _state);
         ae_vector_set_length(&ar2, n, _state);
         ae_vector_set_length(&ai, n, _state);
         for (i = 0; i < n; i++) {
            a.xR[i] = 0.0;
            a0.xR[i] = a.xR[i];
            a1.xR[i] = a.xR[i];
            a2.xR[i] = a.xR[i];
            a3.xR[i] = a.xR[i];
            a4.xZ[i] = ae_round(a.xR[i], _state);
            ar.xR[i] = (double)i;
            ar2.xR[i] = (double)i;
            ai.xZ[i] = i;
         }
         tagsort(&a0, n, &p1, &p2, _state);
         testtsortunit_testsortresults(&a0, &p1, &p2, &a, n, &waserrors, _state);
         tagsortfasti(&a1, &ai, &bufr1, &bufi1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a1.xR[i] != a0.xR[i];
         }
         tagsortfastr(&a2, &ar, &bufr1, &bufr2, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a2.xR[i] != a0.xR[i];
         }
         tagsortfast(&a3, &bufr1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a3.xR[i] != a0.xR[i];
         }
         tagsortmiddleir(&a4, &ar2, 0, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || (double)a4.xZ[i] != a0.xR[i];
         }

      // 0-1 sort
      // We test that keys are correctly reordered, but do NOT test order of values.
         testtsortunit_unset1di(&p1, _state);
         testtsortunit_unset1di(&p2, _state);
         ae_vector_set_length(&a, n, _state);
         ae_vector_set_length(&a0, n, _state);
         ae_vector_set_length(&a1, n, _state);
         ae_vector_set_length(&a2, n, _state);
         ae_vector_set_length(&a3, n, _state);
         ae_vector_set_length(&a4, n, _state);
         ae_vector_set_length(&ar, n, _state);
         ae_vector_set_length(&ar2, n, _state);
         ae_vector_set_length(&ai, n, _state);
         for (i = 0; i < n; i++) {
            a.xR[i] = (double)ae_randominteger(2, _state);
            a0.xR[i] = a.xR[i];
            a1.xR[i] = a.xR[i];
            a2.xR[i] = a.xR[i];
            a3.xR[i] = a.xR[i];
            a4.xZ[i] = ae_round(a.xR[i], _state);
            ar.xR[i] = (double)i;
            ar2.xR[i] = (double)i;
            ai.xZ[i] = i;
         }
         tagsort(&a0, n, &p1, &p2, _state);
         testtsortunit_testsortresults(&a0, &p1, &p2, &a, n, &waserrors, _state);
         tagsortfasti(&a1, &ai, &bufr1, &bufi1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a1.xR[i] != a0.xR[i];
         }
         tagsortfastr(&a2, &ar, &bufr1, &bufr2, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a2.xR[i] != a0.xR[i];
         }
         tagsortfast(&a3, &bufr1, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || a3.xR[i] != a0.xR[i];
         }
         tagsortmiddleir(&a4, &ar2, 0, n, _state);
         for (i = 0; i < n; i++) {
            waserrors = waserrors || (double)a4.xZ[i] != a0.xR[i];
         }

      // Special test for TagSortMiddleIR: sorting in the middle gives same results
      // as sorting in the beginning of the array
         m = 3 * n;
         offs = ae_randominteger(n, _state);
         ae_vector_set_length(&i1, m, _state);
         ae_vector_set_length(&i2, m, _state);
         ae_vector_set_length(&i3, m, _state);
         ae_vector_set_length(&ar, m, _state);
         ae_vector_set_length(&ar2, m, _state);
         for (i = 0; i < m; i++) {
            i1.xZ[i] = ae_randominteger(100000000, _state);
            i2.xZ[i] = i1.xZ[i];
            i3.xZ[i] = i1.xZ[i];
            ar.xR[i] = (double)i;
            ar2.xR[i] = (double)i;
         }
         for (i = 0; i < n; i++) {
            i1.xZ[i] = i1.xZ[offs + i];
            ar.xR[i] = ar.xR[offs + i];
         }
         tagsortmiddleir(&i1, &ar, 0, n, _state);
         for (i = 1; i < n; i++) {
            distinctvals = distinctvals && i1.xZ[i] != i1.xZ[i - 1];
         }
         if (distinctvals) {
            tagsortmiddleir(&i2, &ar2, offs, n, _state);
            for (i = 0; i < n; i++) {
               waserrors = (waserrors || i2.xZ[offs + i] != i1.xZ[i]) || ar2.xR[offs + i] != ar.xR[i];
            }
         }
      }
   }

// report
   if (!silent) {
      printf("TESTING TAGSORT\n");
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Unsets 1D array.
static void testtsortunit_unset1di(ZVector *a, ae_state *_state) {

   ae_vector_set_length(a, 0 + 1, _state);
   a->xZ[0] = ae_randominteger(3, _state) - 1;
}

static void testtsortunit_testsortresults(RVector *asorted, ZVector *p1, ZVector *p2, RVector *aoriginal, ae_int_t n, bool *waserrors, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   double t;

   ae_frame_make(_state, &_frame_block);
   NewVector(a2, 0, DT_REAL, _state);
   NewVector(f, 0, DT_INT, _state);

   ae_vector_set_length(&a2, n - 1 + 1, _state);
   ae_vector_set_length(&f, n - 1 + 1, _state);

// is set ordered?
   for (i = 0; i < n - 1; i++) {
      *waserrors = *waserrors || asorted->xR[i] > asorted->xR[i + 1];
   }

// P1 correctness
   for (i = 0; i < n; i++) {
      *waserrors = *waserrors || asorted->xR[i] != aoriginal->xR[p1->xZ[i]];
   }
   for (i = 0; i < n; i++) {
      f.xZ[i] = 0;
   }
   for (i = 0; i < n; i++) {
      f.xZ[p1->xZ[i]] = f.xZ[p1->xZ[i]] + 1;
   }
   for (i = 0; i < n; i++) {
      *waserrors = *waserrors || f.xZ[i] != 1;
   }

// P2 correctness
   for (i = 0; i < n; i++) {
      a2.xR[i] = aoriginal->xR[i];
   }
   for (i = 0; i < n; i++) {
      if (p2->xZ[i] != i) {
         t = a2.xR[i];
         a2.xR[i] = a2.xR[p2->xZ[i]];
         a2.xR[p2->xZ[i]] = t;
      }
   }
   for (i = 0; i < n; i++) {
      *waserrors = *waserrors || asorted->xR[i] != a2.xR[i];
   }
   ae_frame_leave(_state);
}

