// Function for testing Level 2 symmetric linear algebra functions.
// Additionally it tests SparseGet() for several matrix formats.
// Returns True on failure.
//
// ALGLIB Project: Copyright 20.01.2014 by Sergey Bochkanov
static bool testlevel2symmetric(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;
   double eps;
   double v;
   double va;
   double vb;
   bool isupper;
   ae_int_t triangletype;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(y0, 0, DT_REAL, _state);
   NewVector(y1, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewObj(sparsematrix, s0, _state);
   NewObj(sparsematrix, s1, _state);
   NewObj(sparsematrix, sa, _state);
   NewObj(sparsegenerator, g, _state);
   NewObj(hqrndstate, rs, _state);

   eps = 10000 * ae_machineepsilon;
   result = false;
   hqrndrandomize(&rs, _state);

// Test linear algebra functions
   for (n = 1; n <= 20; n++) {
      for (triangletype = -1; triangletype <= 1; triangletype++) {
         isupper = hqrnduniformr(&rs, _state) > 0.5;
         if (triangletype < 0) {
            isupper = false;
         }
         if (triangletype > 0) {
            isupper = true;
         }
         testsparseunit_initgenerator(n, n, 0, triangletype, &g, _state);
         while (testsparseunit_generatenext(&g, &a, &sa, _state)) {

         // Convert SA to desired storage format:
         // * S0 stores unmodified copy
         // * S1 stores copy with unmodified triangle corresponding
         //   to IsUpper and another triangle being spoiled by random
         //   trash
            sparsecopytohash(&sa, &s1, _state);
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  if ((j < i && isupper) || (j > i && !isupper)) {
                     sparseset(&s1, i, j, hqrnduniformr(&rs, _state), _state);
                  }
               }
            }
            if (hqrnduniformr(&rs, _state) < 0.5) {
               sparsecopytocrs(&sa, &s0, _state);
               sparseconverttocrs(&s1, _state);
            } else {
               sparsecopytosks(&sa, &s0, _state);
               sparseconverttosks(&s1, _state);
            }

         // Test SparseGet() for SA and S0 against matrix returned in A
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  ae_set_error_flag(&result, ae_fabs(sparseget(&sa, i, j, _state) - a.xyR[i][j], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1311");
                  ae_set_error_flag(&result, ae_fabs(sparseget(&s0, i, j, _state) - a.xyR[i][j], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1312");
                  ae_set_error_flag(&result, (j < i && triangletype == 1) && sparseget(&s0, i, j, _state) != 0.0, __FILE__, __LINE__, "testsparseunit.ap:1313");
                  ae_set_error_flag(&result, (j > i && triangletype == -1) && sparseget(&s0, i, j, _state) != 0.0, __FILE__, __LINE__, "testsparseunit.ap:1314");
               }
            }

         // Before we proceed with testing, update empty triangle of A
         // with its copy from another part of the matrix.
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  if ((j < i && isupper) || (j > i && !isupper)) {
                     a.xyR[i][j] = a.xyR[j][i];
                  }
               }
            }

         // Test SparseSMV
            ae_vector_set_length(&x0, n, _state);
            ae_vector_set_length(&x1, n, _state);
            for (j = 0; j < n; j++) {
               x0.xR[j] = hqrnduniformr(&rs, _state) - 0.5;
               x1.xR[j] = x0.xR[j];
            }
            sparsesmv(&s0, isupper, &x0, &y0, _state);
            ae_set_error_flag(&result, y0.cnt < n, __FILE__, __LINE__, "testsparseunit.ap:1337");
            if (result) {
               ae_frame_leave(_state);
               return result;
            }
            for (i = 0; i < n; i++) {
               v = ae_v_dotproduct(a.xyR[i], 1, x1.xR, 1, n);
               ae_set_error_flag(&result, ae_fabs(v - y0.xR[i], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1343");
            }
            sparsesmv(&s1, isupper, &x0, &y1, _state);
            ae_set_error_flag(&result, y1.cnt < n, __FILE__, __LINE__, "testsparseunit.ap:1346");
            if (result) {
               ae_frame_leave(_state);
               return result;
            }
            for (i = 0; i < n; i++) {
               v = ae_v_dotproduct(a.xyR[i], 1, x1.xR, 1, n);
               ae_set_error_flag(&result, ae_fabs(v - y1.xR[i], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1352");
            }

         // Test SparseVSMV
            ae_vector_set_length(&x0, n, _state);
            ae_vector_set_length(&x1, n, _state);
            for (j = 0; j < n; j++) {
               x0.xR[j] = hqrnduniformr(&rs, _state) - 0.5;
               x1.xR[j] = x0.xR[j];
            }
            vb = 0.0;
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  vb = vb + x1.xR[i] * a.xyR[i][j] * x1.xR[j];
               }
            }
            va = sparsevsmv(&s0, isupper, &x0, _state);
            ae_set_error_flag(&result, ae_fabs(va - vb, _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1370");
            va = sparsevsmv(&s1, isupper, &x0, _state);
            ae_set_error_flag(&result, ae_fabs(va - vb, _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1372");
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Function for testing Level 2 symmetric linear algebra functions.
// Additionally it tests SparseGet() for several matrix formats.
// Returns True on failure.
//
// ALGLIB Project: Copyright 14.10.2011 by Sergey Bochkanov
static bool testlevel3symmetric(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t k;
   ae_int_t i;
   ae_int_t j;
   double eps;
   double v;
   bool isupper;
   ae_int_t triangletype;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(x0, 0, 0, DT_REAL, _state);
   NewMatrix(x1, 0, 0, DT_REAL, _state);
   NewMatrix(y0, 0, 0, DT_REAL, _state);
   NewMatrix(y1, 0, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewObj(sparsematrix, s0, _state);
   NewObj(sparsematrix, s1, _state);
   NewObj(sparsematrix, sa, _state);
   NewObj(sparsegenerator, g, _state);
   NewObj(hqrndstate, rs, _state);

   eps = 10000 * ae_machineepsilon;
   result = false;
   hqrndrandomize(&rs, _state);

// Test linear algebra functions
   for (n = 1; n <= 20; n++) {
      for (triangletype = -1; triangletype <= 1; triangletype++) {
         isupper = hqrnduniformr(&rs, _state) > 0.5;
         if (triangletype < 0) {
            isupper = false;
         }
         if (triangletype > 0) {
            isupper = true;
         }
         testsparseunit_initgenerator(n, n, 0, triangletype, &g, _state);
         while (testsparseunit_generatenext(&g, &a, &sa, _state)) {

         // Choose matrix width K
            k = 1 + hqrnduniformi(&rs, 20, _state);

         // Convert SA to desired storage format:
         // * S0 stores unmodified copy
         // * S1 stores copy with unmodified triangle corresponding
         //   to IsUpper and another triangle being spoiled by random
         //   trash
            sparsecopytohash(&sa, &s1, _state);
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  if ((j < i && isupper) || (j > i && !isupper)) {
                     sparseset(&s1, i, j, hqrnduniformr(&rs, _state), _state);
                  }
               }
            }
            if (hqrnduniformr(&rs, _state) < 0.5) {
               sparsecopytocrs(&sa, &s0, _state);
               sparseconverttocrs(&s1, _state);
            } else {
               sparsecopytosks(&sa, &s0, _state);
               sparseconverttosks(&s1, _state);
            }

         // Test SparseGet() for SA and S0 against matrix returned in A
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  ae_set_error_flag(&result, ae_fabs(sparseget(&sa, i, j, _state) - a.xyR[i][j], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1453");
                  ae_set_error_flag(&result, ae_fabs(sparseget(&s0, i, j, _state) - a.xyR[i][j], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1454");
                  ae_set_error_flag(&result, (j < i && triangletype == 1) && sparseget(&s0, i, j, _state) != 0.0, __FILE__, __LINE__, "testsparseunit.ap:1455");
                  ae_set_error_flag(&result, (j > i && triangletype == -1) && sparseget(&s0, i, j, _state) != 0.0, __FILE__, __LINE__, "testsparseunit.ap:1456");
               }
            }

         // Before we proceed with testing, update empty triangle of A
         // with its copy from another part of the matrix.
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  if ((j < i && isupper) || (j > i && !isupper)) {
                     a.xyR[i][j] = a.xyR[j][i];
                  }
               }
            }

         // Test SparseSMM
            ae_matrix_set_length(&x0, n, k, _state);
            ae_matrix_set_length(&x1, n, k, _state);
            for (i = 0; i < n; i++) {
               for (j = 0; j < k; j++) {
                  x0.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
                  x1.xyR[i][j] = x0.xyR[i][j];
               }
            }
            sparsesmm(&s0, isupper, &x0, k, &y0, _state);
            ae_set_error_flag(&result, y0.rows < n, __FILE__, __LINE__, "testsparseunit.ap:1480");
            ae_set_error_flag(&result, y0.cols < k, __FILE__, __LINE__, "testsparseunit.ap:1481");
            if (result) {
               ae_frame_leave(_state);
               return result;
            }
            for (i = 0; i < n; i++) {
               for (j = 0; j < k; j++) {
                  v = ae_v_dotproduct(a.xyR[i], 1, &x1.xyR[0][j], x1.stride, n);
                  ae_set_error_flag(&result, ae_fabs(v - y0.xyR[i][j], _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1488");
               }
            }
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Function for testing sparse symmetric permutations
//
// ALGLIB Project: Copyright 07.10.2020 by Sergey Bochkanov
static bool testsymmetricperm(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t functype;
   ae_int_t i;
   ae_int_t j;
   bool isupper;
   double eps;
   double nzprob;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewMatrix(da, 0, 0, DT_REAL, _state);
   NewMatrix(db, 0, 0, DT_REAL, _state);
   NewObj(sparsematrix, sa, _state);
   NewObj(sparsematrix, sb, _state);
   NewVector(ptbl, 0, DT_INT, _state);
   NewVector(pprod, 0, DT_INT, _state);

   eps = 10 * ae_machineepsilon;
   result = false;
   hqrndrandomize(&rs, _state);

// Try various N and fill factors
   for (n = 1; n <= 20; n++) {
      nzprob = 1.0;
      while (nzprob >= 0.1 / (n * n)) {

      // Generate matrix with desired fill factor, randomly select one triangle,
      // generate random permutation (in table and product form)
         sparsecreate(n, n, 0, &sa, _state);
         ae_matrix_set_length(&da, n, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               if (hqrnduniformr(&rs, _state) < nzprob) {
                  da.xyR[i][j] = hqrndnormal(&rs, _state);
                  sparseset(&sa, i, j, da.xyR[i][j], _state);
               } else {
                  da.xyR[i][j] = 0.0;
               }
            }
         }
         sparseconverttocrs(&sa, _state);
         isupper = hqrndnormal(&rs, _state) > 0.0;
         for (i = 0; i < n; i++) {
            for (j = 0; j <= i; j++) {
               if (isupper) {
                  da.xyR[i][j] = da.xyR[j][i];
               } else {
                  da.xyR[j][i] = da.xyR[i][j];
               }
            }
         }
         ae_vector_set_length(&ptbl, n, _state);
         ae_vector_set_length(&pprod, n, _state);
         for (i = 0; i < n; i++) {
            ptbl.xZ[i] = i;
         }
         for (i = 0; i < n; i++) {
            pprod.xZ[i] = i + hqrnduniformi(&rs, n - i, _state);
            j = ptbl.xZ[i];
            ptbl.xZ[i] = ptbl.xZ[pprod.xZ[i]];
            ptbl.xZ[pprod.xZ[i]] = j;
         }
         ae_matrix_set_length(&db, n, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               db.xyR[ptbl.xZ[i]][ptbl.xZ[j]] = da.xyR[i][j];
            }
         }

      // Select function to test, run and test
         functype = hqrnduniformi(&rs, 2, _state);
         if (functype == 0) {
            sparsesymmpermtblbuf(&sa, isupper, &ptbl, &sb, _state);
         }
         if (functype == 1) {
            sparsesymmpermtbl(&sa, isupper, &ptbl, &sb, _state);
         }
         ae_set_error_flag(&result, !sparseiscrs(&sb, _state), __FILE__, __LINE__, "testsparseunit.ap:1582");
         ae_set_error_flag(&result, sparsegetnrows(&sb, _state) != n, __FILE__, __LINE__, "testsparseunit.ap:1583");
         ae_set_error_flag(&result, sparsegetncols(&sb, _state) != n, __FILE__, __LINE__, "testsparseunit.ap:1584");
         if (result) {
            ae_frame_leave(_state);
            return result;
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               ae_set_error_flag(&result, (isupper && j >= i) && ae_fabs(db.xyR[i][j] - sparseget(&sb, i, j, _state), _state) > eps, __FILE__, __LINE__, "testsparseunit.ap:1590");
               ae_set_error_flag(&result, (isupper && j < i) && sparseget(&sb, i, j, _state) != 0.0, __FILE__, __LINE__, "testsparseunit.ap:1591");
            }
         }

      // Increase sparsity
         nzprob = 0.75 * nzprob;
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Function for testing Level 2 triangular linear algebra functions.
