// This function does test for SparseEnumerate function.
//
// ALGLIB Project: Copyright 14.03.2012 by Sergey Bochkanov
static bool testsparseunit_enumeratetest(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t m;
   ae_int_t n;
   double r;
   double v;
   ae_int_t ne;
   ae_int_t t0;
   ae_int_t t1;
   ae_int_t counter;
   ae_int_t c;
   ae_int_t hashcrs;
   ae_int_t i;
   ae_int_t j;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(sparsematrix, spa, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(ta, 0, 0, DT_BOOL, _state);

   r = 10.5;
   for (m = 1; m <= 30; m++) {
      for (n = 1; n <= 30; n++) {
         ne = 0;

      // Create matrix with non-zero elements inside the region:
      // 0 <= I<S.M and 0 <= J<S.N
         ae_matrix_set_length(&a, m, n, _state);
         ae_matrix_set_length(&ta, m, n, _state);
         for (i = 0; i < m; i++) {
            for (j = 0; j < n; j++) {
               a.xyR[i][j] = 0.0;
               ta.xyB[i][j] = false;
            }
         }
         for (i = 0; i < m; i++) {
            for (j = 0; j < n; j++) {
               c = ae_randominteger(2, _state);
               if (c == 0) {
                  a.xyR[i][j] = 0.0;
               } else {
                  do {
                     a.xyR[i][j] = r * (2 * ae_randomreal(_state) - 1);
                  } while (a.xyR[i][j] == 0.0);

               // Number of non-zero elements
                  ne = ne + 1;
               }
            }
         }
         for (hashcrs = 0; hashcrs <= 1; hashcrs++) {
            sparsecreate(m, n, m * n, &spa, _state);
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  sparseset(&spa, i, j, a.xyR[i][j], _state);
               }
            }
            if (hashcrs == 1) {
               sparseconverttocrs(&spa, _state);
            }
            t0 = 0;
            t1 = 0;
            counter = 0;
            while (sparseenumerate(&spa, &t0, &t1, &i, &j, &v, _state)) {
               ta.xyB[i][j] = true;
               counter = counter + 1;
               if (v != a.xyR[i][j]) {
                  ae_set_error_flag(&result, true, __FILE__, __LINE__, "testsparseunit.ap:3112");
                  ae_frame_leave(_state);
                  return result;
               }
            }

         // Check that all non-zero elements was enumerated
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (ta.xyB[i][j] && a.xyR[i][j] == 0.0) {
                     ae_set_error_flag(&result, true, __FILE__, __LINE__, "testsparseunit.ap:3124");
                     ae_frame_leave(_state);
                     return result;
                  }
               }
            }
            if (ne != counter) {
               ae_set_error_flag(&result, true, __FILE__, __LINE__, "testsparseunit.ap:3130");
               ae_frame_leave(_state);
               return result;
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

// This function does test for SparseRewriteExisting function.
//
// ALGLIB Project: Copyright 14.03.2012 by Sergey Bochkanov
static bool testsparseunit_rewriteexistingtest(ae_state *_state) {
   ae_frame _frame_block;
   double spaval;
   ae_int_t m;
   ae_int_t n;
   ae_int_t c;
   ae_int_t ne;
   ae_int_t nr;
   double r;
   double v;
   ae_int_t hashcrs;
   ae_int_t i;
   ae_int_t j;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(sparsematrix, spa, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(ta, 0, 0, DT_BOOL, _state);

   r = 20.0;
   for (m = 1; m <= 30; m++) {
      for (n = 1; n <= 30; n++) {
         ae_matrix_set_length(&a, m, n, _state);
         ae_matrix_set_length(&ta, m, n, _state);
         for (hashcrs = 0; hashcrs <= 1; hashcrs++) {
            v = r * (2 * ae_randomreal(_state) - 1);

         // Creating and filling of the matrix
            ne = 0;
            sparsecreate(m, n, m * n, &spa, _state);
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  c = ae_randominteger(2, _state);
                  if (c == 0) {
                     a.xyR[i][j] = 0.0;
                  }
                  if (c == 1) {
                     do {
                        a.xyR[i][j] = r * (2 * ae_randomreal(_state) - 1);
                     } while (a.xyR[i][j] == 0.0);
                     sparseset(&spa, i, j, a.xyR[i][j], _state);
                     ne = ne + 1;
                  }
                  ta.xyB[i][j] = false;
               }
            }
            if (hashcrs == 1) {
               sparseconverttocrs(&spa, _state);
            }
         // Rewrite some elements
            nr = 0;
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  c = ae_randominteger(2, _state);
                  if (c == 1) {
                     ta.xyB[i][j] = sparserewriteexisting(&spa, i, j, v, _state);
                     if (ta.xyB[i][j]) {
                        a.xyR[i][j] = v;
                        nr = nr + 1;
                     }
                  }
               }
            }

         // Now we have to be sure, that all changes had made correctly
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (ta.xyB[i][j]) {
                     spaval = sparseget(&spa, i, j, _state);
                     nr = nr - 1;
                     if (spaval != v || spaval != a.xyR[i][j]) {
                        result = true;
                        ae_frame_leave(_state);
                        return result;
                     }
                  }
               }
            }
            if (nr != 0) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         // Rewrite all elements
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  ta.xyB[i][j] = sparserewriteexisting(&spa, i, j, v, _state);
                  if (ta.xyB[i][j]) {
                     a.xyR[i][j] = v;
                  }
               }
            }
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (ta.xyB[i][j]) {
                     ne = ne - 1;
                  }
               }
            }
            if (ne != 0) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  spaval = sparseget(&spa, i, j, _state);
                  if (ta.xyB[i][j]) {
                     if (spaval != v || spaval != a.xyR[i][j]) {
                        result = true;
                        ae_frame_leave(_state);
                        return result;
                     }
                  } else {
                     if (spaval != 0.0 || spaval != a.xyR[i][j]) {
                        result = true;
                        ae_frame_leave(_state);
                        return result;
                     }
                  }
               }
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

// Test  for  SparseGetRow/GetCompressedRow  function.   It  creates  random
// dense and sparse matrices;  then  get every  row from  sparse matrix  and
// compares it with every row in dense matrix.
//
// On failure sets error flag, on success leaves it unchanged.
//
// ALGLIB Project: Copyright 23.07.2012 by Sergey Bochkanov
static void testsparseunit_testgetrow(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t m;
   ae_int_t n;
   ae_int_t msize;
   ae_int_t nsize;
   ae_int_t nz;
   ae_int_t mtype;
   ae_int_t i;
   ae_int_t j;

   ae_frame_make(_state, &_frame_block);
   NewObj(sparsematrix, s, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(vals, 0, DT_REAL, _state);
   NewVector(mrow, 0, DT_REAL, _state);
   NewVector(colidx, 0, DT_INT, _state);
   NewVector(wasreturned, 0, DT_BOOL, _state);

   msize = 15;
   nsize = 15;
   for (mtype = 1; mtype <= 2; mtype++) {
      for (m = 1; m <= msize; m++) {
         for (n = 1; n <= nsize; n++) {

         // Skip nonrectangular SKS matrices - not supported
            if (mtype == 2 && m != n) {
               continue;
            }
         // Create "reference" and sparse matrices
            ae_matrix_set_length(&a, m, n, _state);
            sparsecreate(m, n, 1, &s, _state);
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (ae_randominteger(5, _state) == 3) {
                     a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
                     sparseset(&s, i, j, a.xyR[i][j], _state);
                  } else {
                     a.xyR[i][j] = 0.0;
                  }
               }
            }

         // Choose matrix type to test
            if (mtype == 1) {
               sparseconverttocrs(&s, _state);
            } else {
               sparseconverttosks(&s, _state);
            }

         // Test SparseGetRow()
            for (i = 0; i < m; i++) {
               sparsegetrow(&s, i, &mrow, _state);
               for (j = 0; j < n; j++) {
                  if (mrow.xR[j] != a.xyR[i][j] || mrow.xR[j] != sparseget(&s, i, j, _state)) {
                     ae_set_error_flag(err, true, __FILE__, __LINE__, "testsparseunit.ap:3355");
                     ae_frame_leave(_state);
                     return;
                  }
               }
            }

         // Test SparseGetCompressedRow()
            ae_vector_set_length(&wasreturned, n, _state);
            for (i = 0; i < m; i++) {
               sparsegetcompressedrow(&s, i, &colidx, &vals, &nz, _state);
               if (nz < 0 || nz > n) {
                  ae_set_error_flag(err, true, __FILE__, __LINE__, "testsparseunit.ap:3369");
                  ae_frame_leave(_state);
                  return;
               }
               for (j = 0; j < n; j++) {
                  wasreturned.xB[j] = false;
               }
               for (j = 0; j < nz; j++) {
                  if (colidx.xZ[j] < 0 || colidx.xZ[j] > n) {
                     ae_set_error_flag(err, true, __FILE__, __LINE__, "testsparseunit.ap:3378");
                     ae_frame_leave(_state);
                     return;
                  }
                  ae_set_error_flag(err, j > 0 && colidx.xZ[j] <= colidx.xZ[j - 1], __FILE__, __LINE__, "testsparseunit.ap:3381");
                  ae_set_error_flag(err, vals.xR[j] != a.xyR[i][colidx.xZ[j]] || vals.xR[j] != sparseget(&s, i, colidx.xZ[j], _state), __FILE__, __LINE__, "testsparseunit.ap:3382");
                  wasreturned.xB[colidx.xZ[j]] = true;
               }
               for (j = 0; j < n; j++) {
                  ae_set_error_flag(err, a.xyR[i][j] != 0.0 && !wasreturned.xB[j], __FILE__, __LINE__, "testsparseunit.ap:3386");
               }
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Test for SparseConvert functions(isn't tested ConvertToCRS function). The
// function  create random  dense and sparse  matrices  in CRS  format. Then
// convert  sparse matrix  to some  format  by CONVERT_TO/COPY_TO  functions,
// then it does  some modification in matrices and compares that marices are
// identical.
//
// NOTE:
//     Result of the function assigned to variable CopyErrors in unit test.
//
// ALGLIB Project: Copyright 23.07.2012 by Sergey Bochkanov
static bool testsparseunit_testconvertsm(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t m;
   ae_int_t n;
   ae_int_t msize;
   ae_int_t nsize;
   double tmp;
   ae_int_t i;
   ae_int_t j;
   ae_int_t vartf;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(sparsematrix, s, _state);
   NewObj(sparsematrix, cs, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(ner, 0, DT_INT, _state);

   msize = 15;
   nsize = 15;
   for (m = 1; m <= msize; m++) {
      for (n = 1; n <= nsize; n++) {
         for (vartf = 0; vartf <= 2; vartf++) {
            ae_matrix_set_length(&a, m, n, _state);
            ae_vector_set_length(&ner, m, _state);
            for (i = 0; i < m; i++) {
               ner.xZ[i] = 0;
               for (j = 0; j < n; j++) {
                  if (ae_randominteger(5, _state) == 3) {
                     ner.xZ[i] = ner.xZ[i] + 1;
                     do {
                        a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
                     } while (a.xyR[i][j] == 0.0);
                  } else {
                     a.xyR[i][j] = 0.0;
                  }
               }
            }

         // Create sparse matrix
            sparsecreatecrs(m, n, &ner, &s, _state);
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (a.xyR[i][j] != 0.0) {
                     a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
                     sparseset(&s, i, j, a.xyR[i][j], _state);
                  }
               }
            }

         // Set matrix type(we have to be sure that all formats
         // converted correctly)
            i = ae_randominteger(2, _state);
            if (i == 0) {
               sparseconverttohash(&s, _state);
            }
            if (i == 1) {
               sparseconverttocrs(&s, _state);
            }
         // Start test
            if (vartf == 0) {
               sparseconverttohash(&s, _state);
               sparsecopy(&s, &cs, _state);
            }
            if (vartf == 1) {
               sparsecopytohash(&s, &cs, _state);
            }
            if (vartf == 2) {
               sparsecopytocrs(&s, &cs, _state);
            }
         // Change some elements in row
            if (vartf != 2) {
               for (i = 0; i < m; i++) {
                  tmp = 2 * ae_randomreal(_state) - 1;
                  j = ae_randominteger(n, _state);
                  a.xyR[i][j] = tmp;
                  sparseset(&cs, i, j, tmp, _state);
                  tmp = 2 * ae_randomreal(_state) - 1;
                  j = ae_randominteger(n, _state);
                  a.xyR[i][j] = a.xyR[i][j] + tmp;
                  sparseadd(&cs, i, j, tmp, _state);
               }
            }
         // Check that A is identical to S
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  if (a.xyR[i][j] != sparseget(&cs, i, j, _state)) {
                     result = true;
                     ae_frame_leave(_state);
                     return result;
                  }
               }
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

