static void sparsegenerator_init(void *_p, ae_state *_state, bool make_automatic) {
   sparsegenerator *p = (sparsegenerator *)_p;
   ae_touch_ptr((void *)p);
   ae_matrix_init(&p->bufa, 0, 0, DT_REAL, _state, make_automatic);
   hqrndstate_init(&p->rs, _state, make_automatic);
   rcommstate_init(&p->rcs, _state, make_automatic);
}

static void sparsegenerator_copy(void *_dst, void *_src, ae_state *_state, bool make_automatic) {
   sparsegenerator *dst = (sparsegenerator *)_dst;
   sparsegenerator *src = (sparsegenerator *)_src;
   dst->n = src->n;
   dst->m = src->m;
   dst->matkind = src->matkind;
   dst->triangle = src->triangle;
   ae_matrix_copy(&dst->bufa, &src->bufa, _state, make_automatic);
   hqrndstate_copy(&dst->rs, &src->rs, _state, make_automatic);
   rcommstate_copy(&dst->rcs, &src->rcs, _state, make_automatic);
}

static void sparsegenerator_free(void *_p, bool make_automatic) {
   sparsegenerator *p = (sparsegenerator *)_p;
   ae_touch_ptr((void *)p);
   ae_matrix_free(&p->bufa, make_automatic);
   hqrndstate_free(&p->rs, make_automatic);
   rcommstate_free(&p->rcs, make_automatic);
}

