// Testing symmetric EVD subroutine
bool testevd(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t j;
   ae_int_t failc;
   ae_int_t runs;
   double failthreshold;
   double threshold;
   double bithreshold;
   bool waserrors;
   bool nserrors;
   bool serrors;
   bool herrors;
   bool tderrors;
   bool sbierrors;
   bool hbierrors;
   bool tdbierrors;
   bool sisymmerrors;
   bool wfailed;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(ra, 0, 0, DT_REAL, _state);

   failthreshold = 0.005;
   threshold = 1.0E-8;
   bithreshold = 1.0E-6;
   nserrors = false;
   serrors = false;
   herrors = false;
   tderrors = false;
   sbierrors = false;
   hbierrors = false;
   tdbierrors = false;
   sisymmerrors = false;
   failc = 0;
   runs = 0;

// Test subspace iteration solver
   testevdunit_testsisymm(&sisymmerrors, _state);

// Test dense solvers
   for (n = 1; n <= ablasblocksize(&ra, _state); n++) {
      testevdunit_testevdset(n, threshold, bithreshold, &failc, &runs, &nserrors, &serrors, &herrors, &tderrors, &sbierrors, &hbierrors, &tdbierrors, _state);
   }
   for (j = 2; j <= 3; j++) {
      for (n = j * ablasblocksize(&ra, _state) - 1; n <= j * ablasblocksize(&ra, _state) + 1; n++) {
         testevdunit_testevdset(n, threshold, bithreshold, &failc, &runs, &nserrors, &serrors, &herrors, &tderrors, &sbierrors, &hbierrors, &tdbierrors, _state);
      }
   }

// report
   wfailed = (double)failc / runs > failthreshold;
   waserrors = (((((((nserrors || serrors) || herrors) || tderrors) || sbierrors) || hbierrors) || tdbierrors) || wfailed) || sisymmerrors;
   if (!silent) {
      printf("TESTING EVD UNIT\n");
      printf("DENSE DIRECT SOLVERS:\n");
      printf("* NS                                     ");
      if (!nserrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* S                                      ");
      if (!serrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* H                                      ");
      if (!herrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* TD                                     ");
      if (!tderrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* SBI                                    ");
      if (!sbierrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* HBI                                    ");
      if (!hbierrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* TDBI                                   ");
      if (!tdbierrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* FAILURE THRESHOLD                      ");
      if (!wfailed) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("ITERATIVE SOLVERS:\n");
      printf("* SUBSPACE ITERATION (S)                 ");
      if (!sisymmerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Sparse fill
//
// Sparcity - sparcity level, in [0,1] (0=dense matrix).
// DiagMAg - magnitude of dense diagonal entries; zero value means that diagonal
// is sparse too, non-zero value means that diagonal is dense
static void testevdunit_rmatrixfillsparsea(RMatrix *a, ae_int_t m, ae_int_t n, double sparcity, double diagmag, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;

   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         if (ae_randomreal(_state) >= sparcity) {
            a->xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         } else {
            a->xyR[i][j] = 0.0;
         }
      }
   }
   if (diagmag > 0.0) {
      for (i = 0; i < ae_minint(m, n, _state); i++) {
         a->xyR[i][i] = diagmag * (2 * ae_randomreal(_state) - 1);
      }
   }
}

