// Tests EVD problem
static void testevdunit_testtdevdproblem(RVector *d, RVector *e, ae_int_t n, double threshold, bool *tderrors, ae_state *_state) {
   ae_frame _frame_block;
   bool wsucc;
   ae_int_t i;
   ae_int_t j;
   double v;
   double worstseparation;
   double requiredseparation;
   double specialthreshold;

   ae_frame_make(_state, &_frame_block);
   NewVector(lambdav, 0, DT_REAL, _state);
   NewVector(ee, 0, DT_REAL, _state);
   NewVector(lambda2, 0, DT_REAL, _state);
   NewMatrix(z, 0, 0, DT_REAL, _state);
   NewMatrix(zref, 0, 0, DT_REAL, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewMatrix(a2, 0, 0, DT_REAL, _state);

   ae_vector_set_length(&lambdav, n - 1 + 1, _state);
   ae_vector_set_length(&lambda2, n - 1 + 1, _state);
   ae_matrix_set_length(&zref, n - 1 + 1, n - 1 + 1, _state);
   ae_matrix_set_length(&a1, n - 1 + 1, n - 1 + 1, _state);
   ae_matrix_set_length(&a2, n - 1 + 1, n - 1 + 1, _state);
   if (n > 1) {
      ae_vector_set_length(&ee, n - 2 + 1, _state);
   }
// Test simple EVD: values and full vectors
   for (i = 0; i < n; i++) {
      lambdav.xR[i] = d->xR[i];
   }
   for (i = 0; i < n - 1; i++) {
      ee.xR[i] = e->xR[i];
   }
   testevdunit_unset2d(&z, _state);
   wsucc = smatrixtdevd(&lambdav, &ee, n, 2, &z, _state);
   if (!wsucc) {
      ae_set_error_flag(tderrors, true, __FILE__, __LINE__, "testevdunit.ap:1113");
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(tderrors, testevdunit_tdtestproduct(d, e, n, &z, &lambdav, _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1116");
   ae_set_error_flag(tderrors, testevdunit_testort(&z, n, _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1117");
   for (i = 0; i < n - 1; i++) {
      if (lambdav.xR[i + 1] < lambdav.xR[i]) {
         ae_set_error_flag(tderrors, true, __FILE__, __LINE__, "testevdunit.ap:1121");
         ae_frame_leave(_state);
         return;
      }
   }
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         zref.xyR[i][j] = z.xyR[i][j];
      }
   }

// Test values only variant
   for (i = 0; i < n; i++) {
      lambda2.xR[i] = d->xR[i];
   }
   for (i = 0; i < n - 1; i++) {
      ee.xR[i] = e->xR[i];
   }
   testevdunit_unset2d(&z, _state);
   wsucc = smatrixtdevd(&lambda2, &ee, n, 0, &z, _state);
   if (!wsucc) {
      ae_set_error_flag(tderrors, true, __FILE__, __LINE__, "testevdunit.ap:1139");
      ae_frame_leave(_state);
      return;
   }
   for (i = 0; i < n; i++) {
      ae_set_error_flag(tderrors, ae_fabs(lambda2.xR[i] - lambdav.xR[i], _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1143");
   }

// Test multiplication variant
   for (i = 0; i < n; i++) {
      lambda2.xR[i] = d->xR[i];
   }
   for (i = 0; i < n - 1; i++) {
      ee.xR[i] = e->xR[i];
   }
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         a1.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         a2.xyR[i][j] = a1.xyR[i][j];
      }
   }
   wsucc = smatrixtdevd(&lambda2, &ee, n, 1, &a1, _state);
   if (!wsucc) {
      ae_set_error_flag(tderrors, true, __FILE__, __LINE__, "testevdunit.ap:1161");
      ae_frame_leave(_state);
      return;
   }
   for (i = 0; i < n; i++) {
      ae_set_error_flag(tderrors, ae_fabs(lambda2.xR[i] - lambdav.xR[i], _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1165");
   }
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         v = ae_v_dotproduct(a2.xyR[i], 1, &zref.xyR[0][j], zref.stride, n);

      // next line is a bit complicated because
      // depending on algorithm used we can get either
      // z or -z as eigenvector. so we compare result
      // with both A*ZRef and -A*ZRef
         ae_set_error_flag(tderrors, ae_fabs(v - a1.xyR[i][j], _state) > threshold && ae_fabs(v + a1.xyR[i][j], _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1177");
      }
   }

// Test first row variant.
//
// NOTE: this test is special because ZNeeded=3 is ALGLIB-specific feature
//       which is NOT supported by Intel MKL. Thus, MKL-capable version of
//       ALGLIB will use different algorithms for ZNeeded=3 and for ZNeeded<3.
//
//       In most cases it is OK, but when problem happened to be degenerate
//       (two close eigenvalues), Z computed by ALGLIB may be different from
//       Z computed by MKL (up to arbitrary rotation), which will lead to
//       failure of the test, because ZNeeded=2 is used as reference value
//       for ZNeeded=3.
//
//       That's why this test is performed only for well-separated matrices,
//       and with custom threshold.
   requiredseparation = 1.0E-6;
   specialthreshold = 1.0E-6;
   worstseparation = ae_maxrealnumber;
   for (i = 0; i < n - 1; i++) {
      worstseparation = ae_minreal(worstseparation, ae_fabs(lambdav.xR[i + 1] - lambdav.xR[i], _state), _state);
   }
   if (worstseparation > requiredseparation) {
      for (i = 0; i < n; i++) {
         lambda2.xR[i] = d->xR[i];
      }
      for (i = 0; i < n - 1; i++) {
         ee.xR[i] = e->xR[i];
      }
      testevdunit_unset2d(&z, _state);
      wsucc = smatrixtdevd(&lambda2, &ee, n, 3, &z, _state);
      if (!wsucc) {
         ae_set_error_flag(tderrors, true, __FILE__, __LINE__, "testevdunit.ap:1211");
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(tderrors, ae_fabs(lambda2.xR[i] - lambdav.xR[i], _state) > threshold, __FILE__, __LINE__, "testevdunit.ap:1216");

      // next line is a bit complicated because
      // depending on algorithm used we can get either
      // z or -z as eigenvector. so we compare result
      // with both z and -z
         ae_set_error_flag(tderrors, ae_fabs(z.xyR[0][i] - zref.xyR[0][i], _state) > specialthreshold && ae_fabs(z.xyR[0][i] + zref.xyR[0][i], _state) > specialthreshold, __FILE__, __LINE__, "testevdunit.ap:1224");
      }
   }
   ae_frame_leave(_state);
}

