// Function for testing dense Cholesky updates
// Sets error flag to True on errors, does not change it on success.
//
// ALGLIB Project: Copyright 16.01.1014 by Sergey Bochkanov
static void testtrfacunit_testdensecholeskyupdates(bool *spdupderrorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   double pfix;
   ae_int_t i;
   ae_int_t j;
   bool isupper;
   double tol;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a0, 0, 0, DT_REAL, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewVector(u, 0, DT_REAL, _state);
   NewVector(fix, 0, DT_BOOL, _state);
   NewVector(bufr, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Settings
   tol = 1.0E-8;

// Test rank-1 updates
//
// For each matrix size in 1..30 select sparse update vector with probability of element
// being non-zero equal to 1/2.
   for (n = 1; n <= 30; n++) {

   // Generate two matrices A0=A1, fill one triangle with SPD matrix,
   // another one with trash. Prepare vector U.
      isupper = hqrnduniformr(&rs, _state) < 0.5;
      spdmatrixrndcond(n, 1.0E4, &a0, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if ((j < i && isupper) || (j > i && !isupper)) {
               a0.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
            }
         }
      }
      ae_matrix_set_length(&a1, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            a1.xyR[i][j] = a0.xyR[i][j];
         }
      }
      ae_vector_set_length(&u, n, _state);
      for (i = 0; i < n; i++) {
         if (hqrnduniformr(&rs, _state) <= 0.5) {
            u.xR[i] = hqrnduniformr(&rs, _state) - 0.5;
         } else {
            u.xR[i] = 0.0;
         }
      }

   // Factorize and compare:
   // * A0 is factorized as follows: first with full Cholesky, then
   //   we call SPDMatrixCholeskyUpdateAdd1
   // * A1 is transformed explicitly before factorization with full Cholesky
   //
   // We randomly test either SPDMatrixCholeskyUpdateFix() or its
   // buffered version, SPDMatrixCholeskyUpdateFixBuf()
      ae_set_error_flag(spdupderrorflag, !spdmatrixcholesky(&a0, n, isupper, _state), __FILE__, __LINE__, "testtrfacunit.ap:1772");
      if (*spdupderrorflag) {
         ae_frame_leave(_state);
         return;
      }
      if (hqrnduniformr(&rs, _state) < 0.5) {
         spdmatrixcholeskyupdateadd1(&a0, n, isupper, &u, _state);
      } else {
         spdmatrixcholeskyupdateadd1buf(&a0, n, isupper, &u, &bufr, _state);
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if ((j >= i && isupper) || (j <= i && !isupper)) {
               a1.xyR[i][j] = a1.xyR[i][j] + u.xR[i] * u.xR[j];
            }
         }
      }
      ae_set_error_flag(spdupderrorflag, !spdmatrixcholesky(&a1, n, isupper, _state), __FILE__, __LINE__, "testtrfacunit.ap:1783");
      if (*spdupderrorflag) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            ae_set_error_flag(spdupderrorflag, ae_fabs(a0.xyR[i][j] - a1.xyR[i][j], _state) > tol, __FILE__, __LINE__, "testtrfacunit.ap:1788");
         }
      }
   }

// Test variable fixing functions.
//
// For each matrix size in 1..30 select PFix - probability of each variable being fixed,
// and perform test.
   for (n = 1; n <= 30; n++) {

   // Generate two matrices A0=A1, fill one triangle with SPD matrix,
   // another one with trash. Prepare vector Fix.
      pfix = (double)hqrnduniformi(&rs, n + 1, _state) / n;
      isupper = hqrnduniformr(&rs, _state) < 0.5;
      spdmatrixrndcond(n, 1.0E4, &a0, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if ((j < i && isupper) || (j > i && !isupper)) {
               a0.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
            }
         }
      }
      ae_matrix_set_length(&a1, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            a1.xyR[i][j] = a0.xyR[i][j];
         }
      }
      ae_vector_set_length(&fix, n, _state);
      for (i = 0; i < n; i++) {
         fix.xB[i] = hqrnduniformr(&rs, _state) <= pfix;
      }

   // Factorize and compare:
   // * A0 is factorized as follows: first with full Cholesky, then
   //   variables are fixed with SPDMatrixCholeskyUpdateFix
   // * A1 is fixed explicitly before factorization with full Cholesky
   //
   // We randomly test either SPDMatrixCholeskyUpdateFix() or its
   // buffered version, SPDMatrixCholeskyUpdateFixBuf()
      ae_set_error_flag(spdupderrorflag, !spdmatrixcholesky(&a0, n, isupper, _state), __FILE__, __LINE__, "testtrfacunit.ap:1827");
      if (*spdupderrorflag) {
         ae_frame_leave(_state);
         return;
      }
      if (hqrnduniformr(&rs, _state) < 0.5) {
         spdmatrixcholeskyupdatefixbuf(&a0, n, isupper, &fix, &bufr, _state);
      } else {
         spdmatrixcholeskyupdatefix(&a0, n, isupper, &fix, _state);
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if ((j >= i && isupper) || (j <= i && !isupper)) {
               if (fix.xB[i] || fix.xB[j]) {
                  if (i == j) {
                     a1.xyR[i][j] = 1.0;
                  } else {
                     a1.xyR[i][j] = 0.0;
                  }
               }
            }
         }
      }
      ae_set_error_flag(spdupderrorflag, !spdmatrixcholesky(&a1, n, isupper, _state), __FILE__, __LINE__, "testtrfacunit.ap:1844");
      if (*spdupderrorflag) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            ae_set_error_flag(spdupderrorflag, ae_fabs(a0.xyR[i][j] - a1.xyR[i][j], _state) > tol, __FILE__, __LINE__, "testtrfacunit.ap:1849");
         }
      }
   }
   ae_frame_leave(_state);
}

// === polynomialsolver testing unit ===
// Test
bool testpolynomialsolver(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool wereerrors;
   double eps;
   ae_int_t n;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(a, 0, DT_REAL, _state);
   NewVector(x, 0, DT_COMPLEX, _state);
   NewObj(polynomialsolverreport, rep, _state);

   wereerrors = false;

// Basic tests
   eps = 1.0E-6;
   n = 1;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 2.0;
   a.xR[1] = 3.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   ae_set_error_flag(&wereerrors, ae_fabs(x.xC[0].x + 2.0 / 3.0, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:33");
   ae_set_error_flag(&wereerrors, x.xC[0].y != 0.0, __FILE__, __LINE__, "testpolynomialsolverunit.ap:34");
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:35");
   n = 2;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 1.0;
   a.xR[1] = -2.0;
   a.xR[2] = 1.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   ae_set_error_flag(&wereerrors, ae_c_abs(ae_c_sub_d(x.xC[0], 1), _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:43");
   ae_set_error_flag(&wereerrors, ae_c_abs(ae_c_sub_d(x.xC[1], 1), _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:44");
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:45");
   n = 2;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 2.0;
   a.xR[1] = -3.0;
   a.xR[2] = 1.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   if (x.xC[0].x < x.xC[1].x) {
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[0].x - 1, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:55");
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[1].x - 2, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:56");
   } else {
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[0].x - 2, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:60");
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[1].x - 1, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:61");
   }
   ae_set_error_flag(&wereerrors, x.xC[0].y != 0.0, __FILE__, __LINE__, "testpolynomialsolverunit.ap:63");
   ae_set_error_flag(&wereerrors, x.xC[1].y != 0.0, __FILE__, __LINE__, "testpolynomialsolverunit.ap:64");
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:65");
   n = 2;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 1.0;
   a.xR[1] = 0.0;
   a.xR[2] = 1.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   ae_set_error_flag(&wereerrors, ae_c_abs(ae_c_add_d(ae_c_mul(x.xC[0], x.xC[0]), 1.0), _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:73");
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:74");
   n = 4;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 0.0;
   a.xR[1] = 0.0;
   a.xR[2] = 0.0;
   a.xR[3] = 0.0;
   a.xR[4] = 1.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[0], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:84");
   ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[1], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:85");
   ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[2], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:86");
   ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[3], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:87");
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:88");
   n = 2;
   ae_vector_set_length(&a, n + 1, _state);
   a.xR[0] = 0.0;
   a.xR[1] = 3.0;
   a.xR[2] = 2.0;
   polynomialsolve(&a, n, &x, &rep, _state);
   if (x.xC[0].x > x.xC[1].x) {
      ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[0], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:98");
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[1].x + 3.0 / 2.0, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:99");
      ae_set_error_flag(&wereerrors, x.xC[1].y != 0.0, __FILE__, __LINE__, "testpolynomialsolverunit.ap:100");
   } else {
      ae_set_error_flag(&wereerrors, ae_c_neq_d(x.xC[1], 0.0), __FILE__, __LINE__, "testpolynomialsolverunit.ap:104");
      ae_set_error_flag(&wereerrors, ae_fabs(x.xC[0].x + 3.0 / 2.0, _state) > eps, __FILE__, __LINE__, "testpolynomialsolverunit.ap:105");
      ae_set_error_flag(&wereerrors, x.xC[0].y != 0.0, __FILE__, __LINE__, "testpolynomialsolverunit.ap:106");
   }
   ae_set_error_flag(&wereerrors, rep.maxerr > 100 * ae_machineepsilon, __FILE__, __LINE__, "testpolynomialsolverunit.ap:108");
   if (!silent) {
      printf("TESTING POLYNOMIAL SOLVER\n");
      if (wereerrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
   }
   result = !wereerrors;
   ae_frame_leave(_state);
   return result;
}

// === bdsvd testing unit ===
static void testbdsvdunit_fillidentity(RMatrix *a, ae_int_t n, ae_state *_state);
static void testbdsvdunit_fillsparsede(RVector *d, RVector *e, ae_int_t n, double sparcity, ae_state *_state);
static void testbdsvdunit_getbdsvderror(RVector *d, RVector *e, ae_int_t n, bool isupper, RMatrix *u, RMatrix *c, RVector *w, RMatrix *vt, double *materr, double *orterr, bool *wsorted, ae_state *_state);
static void testbdsvdunit_checksvdmultiplication(RVector *d, RVector *e, ae_int_t n, bool isupper, RMatrix *u, RMatrix *c, RVector *w, RMatrix *vt, double *err, ae_state *_state);
static void testbdsvdunit_testbdsvdproblem(RVector *d, RVector *e, ae_int_t n, double *materr, double *orterr, bool *wsorted, bool *wfailed, ae_int_t *failcount, ae_int_t *succcount, ae_state *_state);

// Testing bidiagonal SVD decomposition subroutine
bool testbdsvd(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t maxn;
   ae_int_t i;
   ae_int_t pass;
   bool waserrors;
   bool wsorted;
   bool wfailed;
   double materr;
   double orterr;
   double threshold;
   double failr;
   ae_int_t failcount;
   ae_int_t succcount;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(d, 0, DT_REAL, _state);
   NewVector(e, 0, DT_REAL, _state);
   NewMatrix(mempty, 0, 0, DT_REAL, _state);

   failcount = 0;
   succcount = 0;
   materr = 0.0;
   orterr = 0.0;
   wsorted = true;
   wfailed = false;
   waserrors = false;
   maxn = 15;
   threshold = 5 * 100 * ae_machineepsilon;
   ae_vector_set_length(&d, maxn - 1 + 1, _state);
   ae_vector_set_length(&e, maxn - 2 + 1, _state);

// special case: zero divide matrix
// unfixed LAPACK routine should fail on this problem
   n = 7;
   d.xR[0] = -6.96462904751731892700e-01;
   d.xR[1] = 0.00000000000000000000e+00;
   d.xR[2] = -5.73827770385971991400e-01;
   d.xR[3] = -6.62562624399371191700e-01;
   d.xR[4] = 5.82737148001782223600e-01;
   d.xR[5] = 3.84825263580925003300e-01;
   d.xR[6] = 9.84087420830525472200e-01;
   e.xR[0] = -7.30307931760612871800e-02;
   e.xR[1] = -2.30079042939542843800e-01;
   e.xR[2] = -6.87824621739351216300e-01;
   e.xR[3] = -1.77306437707837570600e-02;
   e.xR[4] = 1.78285126526551632000e-15;
   e.xR[5] = -4.89434737751289969400e-02;
   rmatrixbdsvd(&d, &e, n, true, false, &mempty, 0, &mempty, 0, &mempty, 0, _state);

// zero matrix, several cases
   for (i = 0; i < maxn; i++) {
      d.xR[i] = 0.0;
   }
   for (i = 0; i < maxn - 1; i++) {
      e.xR[i] = 0.0;
   }
   for (n = 1; n <= maxn; n++) {
      testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
   }

// Dense matrix
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= 10; pass++) {
         for (i = 0; i < maxn; i++) {
            d.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         for (i = 0; i < maxn - 1; i++) {
            e.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
      }
   }

// Sparse matrices, very sparse matrices, incredible sparse matrices
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= 10; pass++) {
         testbdsvdunit_fillsparsede(&d, &e, n, 0.5, _state);
         testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
         testbdsvdunit_fillsparsede(&d, &e, n, 0.8, _state);
         testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
         testbdsvdunit_fillsparsede(&d, &e, n, 0.9, _state);
         testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
         testbdsvdunit_fillsparsede(&d, &e, n, 0.95, _state);
         testbdsvdunit_testbdsvdproblem(&d, &e, n, &materr, &orterr, &wsorted, &wfailed, &failcount, &succcount, _state);
      }
   }

// report
   failr = (double)failcount / (succcount + failcount);
   waserrors = ((wfailed || materr > threshold) || orterr > threshold) || !wsorted;
   if (!silent) {
      printf("TESTING BIDIAGONAL SVD DECOMPOSITION\n");
      printf("SVD decomposition error:                 %5.3e\n", materr);
      printf("SVD orthogonality error:                 %5.3e\n", orterr);
      printf("Singular values order:                   ");
      if (wsorted) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("Always converged:                        ");
      if (!wfailed) {
         printf("YES\n");
      } else {
         printf("NO\n");
         printf("Fail ratio:                              %5.3f\n", failr);
      }
      printf("Threshold:                               %5.3e\n", threshold);
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

static void testbdsvdunit_fillidentity(RMatrix *a, ae_int_t n, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;

   ae_matrix_set_length(a, n - 1 + 1, n - 1 + 1, _state);
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         if (i == j) {
            a->xyR[i][j] = 1.0;
         } else {
            a->xyR[i][j] = 0.0;
         }
      }
   }
}

static void testbdsvdunit_fillsparsede(RVector *d, RVector *e, ae_int_t n, double sparcity, ae_state *_state) {
   ae_int_t i;

   ae_vector_set_length(d, n - 1 + 1, _state);
   ae_vector_set_length(e, ae_maxint(0, n - 2, _state) + 1, _state);
   for (i = 0; i < n; i++) {
      if (ae_randomreal(_state) >= sparcity) {
         d->xR[i] = 2 * ae_randomreal(_state) - 1;
      } else {
         d->xR[i] = 0.0;
      }
   }
   for (i = 0; i < n - 1; i++) {
      if (ae_randomreal(_state) >= sparcity) {
         e->xR[i] = 2 * ae_randomreal(_state) - 1;
      } else {
         e->xR[i] = 0.0;
      }
   }
}

static void testbdsvdunit_getbdsvderror(RVector *d, RVector *e, ae_int_t n, bool isupper, RMatrix *u, RMatrix *c, RVector *w, RMatrix *vt, double *materr, double *orterr, bool *wsorted, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double locerr;
   double sm;

// decomposition error
   locerr = 0.0;
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         sm = 0.0;
         for (k = 0; k < n; k++) {
            sm = sm + w->xR[k] * u->xyR[i][k] * vt->xyR[k][j];
         }
         if (isupper) {
            if (i == j) {
               locerr = ae_maxreal(locerr, ae_fabs(d->xR[i] - sm, _state), _state);
            } else {
               if (i == j - 1) {
                  locerr = ae_maxreal(locerr, ae_fabs(e->xR[i] - sm, _state), _state);
               } else {
                  locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
               }
            }
         } else {
            if (i == j) {
               locerr = ae_maxreal(locerr, ae_fabs(d->xR[i] - sm, _state), _state);
            } else {
               if (i - 1 == j) {
                  locerr = ae_maxreal(locerr, ae_fabs(e->xR[j] - sm, _state), _state);
               } else {
                  locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
               }
            }
         }
      }
   }
   *materr = ae_maxreal(*materr, locerr, _state);

// check for C = U'
// we consider it as decomposition error
   locerr = 0.0;
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         locerr = ae_maxreal(locerr, ae_fabs(u->xyR[i][j] - c->xyR[j][i], _state), _state);
      }
   }
   *materr = ae_maxreal(*materr, locerr, _state);

// orthogonality error
   locerr = 0.0;
   for (i = 0; i < n; i++) {
      for (j = i; j < n; j++) {
         sm = ae_v_dotproduct(&u->xyR[0][i], u->stride, &u->xyR[0][j], u->stride, n);
         if (i != j) {
            locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
         } else {
            locerr = ae_maxreal(locerr, ae_fabs(sm - 1, _state), _state);
         }
         sm = ae_v_dotproduct(vt->xyR[i], 1, vt->xyR[j], 1, n);
         if (i != j) {
            locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
         } else {
            locerr = ae_maxreal(locerr, ae_fabs(sm - 1, _state), _state);
         }
      }
   }
   *orterr = ae_maxreal(*orterr, locerr, _state);

// values order error
   for (i = 1; i < n; i++) {
      if (w->xR[i] > w->xR[i - 1]) {
         *wsorted = false;
      }
   }
}

static void testbdsvdunit_checksvdmultiplication(RVector *d, RVector *e, ae_int_t n, bool isupper, RMatrix *u, RMatrix *c, RVector *w, RMatrix *vt, double *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t nru;
   ae_int_t ncc;
   ae_int_t ncvt;
   ae_int_t pass;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewVector(wt, 0, DT_REAL, _state);
   NewMatrix(u2, 0, 0, DT_REAL, _state);
   NewMatrix(c2, 0, 0, DT_REAL, _state);
   NewMatrix(vt2, 0, 0, DT_REAL, _state);
   NewMatrix(u1, 0, 0, DT_REAL, _state);
   NewMatrix(c1, 0, 0, DT_REAL, _state);
   NewMatrix(vt1, 0, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   ae_vector_set_length(&wt, n, _state);

// Perform nonsquare SVD
   for (pass = 1; pass <= 20; pass++) {

   // Problem size
      nru = hqrnduniformi(&rs, 2 * n, _state);
      ncc = hqrnduniformi(&rs, 2 * n, _state);
      ncvt = hqrnduniformi(&rs, 2 * n, _state);

   // Reference matrices (copy 1) and working matrices (copy 2)
      for (i = 0; i < n; i++) {
         wt.xR[i] = d->xR[i];
      }
      if (nru > 0) {

      // init U1/U2
         ae_matrix_set_length(&u1, nru, n, _state);
         ae_matrix_set_length(&u2, nru, n, _state);
         for (i = 0; i < u1.rows; i++) {
            for (j = 0; j < u1.cols; j++) {
               u1.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
               u2.xyR[i][j] = u1.xyR[i][j];
            }
         }
      } else {

      // Set U1/U2 to 1x1 matrices; working with 1x1 matrices allows
      // to test correctness of code which passes them to MKL.
         ae_matrix_set_length(&u1, 1, 1, _state);
         ae_matrix_set_length(&u2, 1, 1, _state);
      }
      if (ncc > 0) {
         ae_matrix_set_length(&c1, n, ncc, _state);
         ae_matrix_set_length(&c2, n, ncc, _state);
         for (i = 0; i < c1.rows; i++) {
            for (j = 0; j < c1.cols; j++) {
               c1.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
               c2.xyR[i][j] = c1.xyR[i][j];
            }
         }
      } else {

      // Set C1/C1 to 1x1 matrices; working with 1x1 matrices allows
      // to test correctness of code which passes them to MKL.
         ae_matrix_set_length(&c1, 1, 1, _state);
         ae_matrix_set_length(&c2, 1, 1, _state);
      }
      if (ncvt > 0) {
         ae_matrix_set_length(&vt1, n, ncvt, _state);
         ae_matrix_set_length(&vt2, n, ncvt, _state);
         for (i = 0; i < vt1.rows; i++) {
            for (j = 0; j < vt1.cols; j++) {
               vt1.xyR[i][j] = hqrnduniformr(&rs, _state) - 0.5;
               vt2.xyR[i][j] = vt1.xyR[i][j];
            }
         }
      } else {

      // Set VT1/VT1 to 1x1 matrices; working with 1x1 matrices allows
      // to test correctness of code which passes them to MKL.
         ae_matrix_set_length(&vt1, 1, 1, _state);
         ae_matrix_set_length(&vt2, 1, 1, _state);
      }

   // SVD with non-square U/C/VT
      if (!rmatrixbdsvd(&wt, e, n, isupper, hqrnduniformr(&rs, _state) > 0.0, &u2, nru, &c2, ncc, &vt2, ncvt, _state)) {
         *err = 1.0;
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < nru; i++) {
         for (j = 0; j < u2.cols; j++) {
            v = ae_v_dotproduct(u1.xyR[i], 1, &u->xyR[0][j], u->stride, n);
            *err = ae_maxreal(*err, ae_fabs(v - u2.xyR[i][j], _state), _state);
         }
      }
      for (i = 0; i < c2.rows; i++) {
         for (j = 0; j < ncc; j++) {
            v = ae_v_dotproduct(c->xyR[i], 1, &c1.xyR[0][j], c1.stride, n);
            *err = ae_maxreal(*err, ae_fabs(v - c2.xyR[i][j], _state), _state);
         }
      }
      for (i = 0; i < vt2.rows; i++) {
         for (j = 0; j < ncvt; j++) {
            v = ae_v_dotproduct(vt->xyR[i], 1, &vt1.xyR[0][j], vt1.stride, n);
            *err = ae_maxreal(*err, ae_fabs(v - vt2.xyR[i][j], _state), _state);
         }
      }
   }
   ae_frame_leave(_state);
}

static void testbdsvdunit_testbdsvdproblem(RVector *d, RVector *e, ae_int_t n, double *materr, double *orterr, bool *wsorted, bool *wfailed, ae_int_t *failcount, ae_int_t *succcount, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   double mx;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(u, 0, 0, DT_REAL, _state);
   NewMatrix(vt, 0, 0, DT_REAL, _state);
   NewMatrix(c, 0, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);

   mx = 0.0;
   for (i = 0; i < n; i++) {
      if (ae_fabs(d->xR[i], _state) > mx) {
         mx = ae_fabs(d->xR[i], _state);
      }
   }
   for (i = 0; i < n - 1; i++) {
      if (ae_fabs(e->xR[i], _state) > mx) {
         mx = ae_fabs(e->xR[i], _state);
      }
   }
   if (mx == 0.0) {
      mx = 1.0;
   }
// Upper BDSVD tests
   ae_vector_set_length(&w, n - 1 + 1, _state);
   testbdsvdunit_fillidentity(&u, n, _state);
   testbdsvdunit_fillidentity(&vt, n, _state);
   testbdsvdunit_fillidentity(&c, n, _state);
   for (i = 0; i < n; i++) {
      w.xR[i] = d->xR[i];
   }
   if (!rmatrixbdsvd(&w, e, n, true, false, &u, n, &c, n, &vt, n, _state)) {
      *failcount = *failcount + 1;
      *wfailed = true;
      ae_frame_leave(_state);
      return;
   }
   testbdsvdunit_getbdsvderror(d, e, n, true, &u, &c, &w, &vt, materr, orterr, wsorted, _state);
   testbdsvdunit_checksvdmultiplication(d, e, n, true, &u, &c, &w, &vt, materr, _state);
   testbdsvdunit_fillidentity(&u, n, _state);
   testbdsvdunit_fillidentity(&vt, n, _state);
   testbdsvdunit_fillidentity(&c, n, _state);
   for (i = 0; i < n; i++) {
      w.xR[i] = d->xR[i];
   }
   if (!rmatrixbdsvd(&w, e, n, true, true, &u, n, &c, n, &vt, n, _state)) {
      *failcount = *failcount + 1;
      *wfailed = true;
      ae_frame_leave(_state);
      return;
   }
   testbdsvdunit_getbdsvderror(d, e, n, true, &u, &c, &w, &vt, materr, orterr, wsorted, _state);
   testbdsvdunit_checksvdmultiplication(d, e, n, true, &u, &c, &w, &vt, materr, _state);

// Lower BDSVD tests
   ae_vector_set_length(&w, n - 1 + 1, _state);
   testbdsvdunit_fillidentity(&u, n, _state);
   testbdsvdunit_fillidentity(&vt, n, _state);
   testbdsvdunit_fillidentity(&c, n, _state);
   for (i = 0; i < n; i++) {
      w.xR[i] = d->xR[i];
   }
   if (!rmatrixbdsvd(&w, e, n, false, false, &u, n, &c, n, &vt, n, _state)) {
      *failcount = *failcount + 1;
      *wfailed = true;
      ae_frame_leave(_state);
      return;
   }
   testbdsvdunit_getbdsvderror(d, e, n, false, &u, &c, &w, &vt, materr, orterr, wsorted, _state);
   testbdsvdunit_checksvdmultiplication(d, e, n, false, &u, &c, &w, &vt, materr, _state);
   testbdsvdunit_fillidentity(&u, n, _state);
   testbdsvdunit_fillidentity(&vt, n, _state);
   testbdsvdunit_fillidentity(&c, n, _state);
   for (i = 0; i < n; i++) {
      w.xR[i] = d->xR[i];
   }
   if (!rmatrixbdsvd(&w, e, n, false, true, &u, n, &c, n, &vt, n, _state)) {
      *failcount = *failcount + 1;
      *wfailed = true;
      ae_frame_leave(_state);
      return;
   }
   testbdsvdunit_getbdsvderror(d, e, n, false, &u, &c, &w, &vt, materr, orterr, wsorted, _state);
   testbdsvdunit_checksvdmultiplication(d, e, n, false, &u, &c, &w, &vt, materr, _state);

// update counter
   *succcount = *succcount + 1;
   ae_frame_leave(_state);
}

// === svd testing unit ===
static void testsvdunit_fillsparsea(RMatrix *a, ae_int_t m, ae_int_t n, double sparcity, ae_state *_state);
static void testsvdunit_getsvderror(RMatrix *a, ae_int_t m, ae_int_t n, RMatrix *u, RVector *w, RMatrix *vt, double *materr, double *orterr, bool *wsorted, ae_state *_state);
static void testsvdunit_testsvdproblem(RMatrix *a, ae_int_t m, ae_int_t n, double *materr, double *orterr, double *othererr, bool *wsorted, bool *wfailed, ae_int_t *failcount, ae_int_t *succcount, ae_state *_state);

// Testing SVD decomposition subroutine
bool testsvd(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t m;
   ae_int_t n;
   ae_int_t maxmn;
   ae_int_t i;
   ae_int_t j;
   ae_int_t gpass;
   ae_int_t pass;
   bool waserrors;
   bool wsorted;
   bool wfailed;
   double materr;
   double orterr;
   double othererr;
   double threshold;
   double failr;
   ae_int_t failcount;
   ae_int_t succcount;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_REAL, _state);

   failcount = 0;
   succcount = 0;
   materr = 0.0;
   orterr = 0.0;
   othererr = 0.0;
   wsorted = true;
   wfailed = false;
   waserrors = false;
   maxmn = 30;
   threshold = 5 * 100 * ae_machineepsilon;
   ae_matrix_set_length(&a, maxmn - 1 + 1, maxmn - 1 + 1, _state);

// TODO: div by zero fail, convergence fail
   for (gpass = 1; gpass <= 1; gpass++) {

   // zero matrix, several cases
      for (i = 0; i < maxmn; i++) {
         for (j = 0; j < maxmn; j++) {
            a.xyR[i][j] = 0.0;
         }
      }
      for (i = 1; i <= ae_minint(5, maxmn, _state); i++) {
         for (j = 1; j <= ae_minint(5, maxmn, _state); j++) {
            testsvdunit_testsvdproblem(&a, i, j, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
         }
      }

   // Long dense matrix
      for (i = 0; i < maxmn; i++) {
         for (j = 0; j < ae_minint(5, maxmn, _state); j++) {
            a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         }
      }
      for (i = 1; i <= maxmn; i++) {
         for (j = 1; j <= ae_minint(5, maxmn, _state); j++) {
            testsvdunit_testsvdproblem(&a, i, j, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
         }
      }
      for (i = 0; i < ae_minint(5, maxmn, _state); i++) {
         for (j = 0; j < maxmn; j++) {
            a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         }
      }
      for (i = 1; i <= ae_minint(5, maxmn, _state); i++) {
         for (j = 1; j <= maxmn; j++) {
            testsvdunit_testsvdproblem(&a, i, j, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
         }
      }

   // Dense matrices
      for (m = 1; m <= ae_minint(10, maxmn, _state); m++) {
         for (n = 1; n <= ae_minint(10, maxmn, _state); n++) {
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
               }
            }
            testsvdunit_testsvdproblem(&a, m, n, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
         }
      }

   // Sparse matrices, very sparse matrices, incredible sparse matrices
      for (m = 1; m <= 10; m++) {
         for (n = 1; n <= 10; n++) {
            for (pass = 1; pass <= 2; pass++) {
               testsvdunit_fillsparsea(&a, m, n, 0.8, _state);
               testsvdunit_testsvdproblem(&a, m, n, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
               testsvdunit_fillsparsea(&a, m, n, 0.9, _state);
               testsvdunit_testsvdproblem(&a, m, n, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
               testsvdunit_fillsparsea(&a, m, n, 0.95, _state);
               testsvdunit_testsvdproblem(&a, m, n, &materr, &orterr, &othererr, &wsorted, &wfailed, &failcount, &succcount, _state);
            }
         }
      }
   }

// report
   failr = (double)failcount / (succcount + failcount);
   waserrors = (((wfailed || materr > threshold) || orterr > threshold) || othererr > threshold) || !wsorted;
   if (!silent) {
      printf("TESTING SVD DECOMPOSITION\n");
      printf("SVD decomposition error:                 %5.3e\n", materr);
      printf("SVD orthogonality error:                 %5.3e\n", orterr);
      printf("SVD with different parameters error:     %5.3e\n", othererr);
      printf("Singular values order:                   ");
      if (wsorted) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("Always converged:                        ");
      if (!wfailed) {
         printf("YES\n");
      } else {
         printf("NO\n");
         printf("Fail ratio:                              %5.3f\n", failr);
      }
      printf("Threshold:                               %5.3e\n", threshold);
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

static void testsvdunit_fillsparsea(RMatrix *a, ae_int_t m, ae_int_t n, double sparcity, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;

   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         if (ae_randomreal(_state) >= sparcity) {
            a->xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         } else {
            a->xyR[i][j] = 0.0;
         }
      }
   }
}

static void testsvdunit_getsvderror(RMatrix *a, ae_int_t m, ae_int_t n, RMatrix *u, RVector *w, RMatrix *vt, double *materr, double *orterr, bool *wsorted, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t minmn;
   double locerr;
   double sm;

   minmn = ae_minint(m, n, _state);

// decomposition error
   locerr = 0.0;
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         sm = 0.0;
         for (k = 0; k < minmn; k++) {
            sm = sm + w->xR[k] * u->xyR[i][k] * vt->xyR[k][j];
         }
         locerr = ae_maxreal(locerr, ae_fabs(a->xyR[i][j] - sm, _state), _state);
      }
   }
   *materr = ae_maxreal(*materr, locerr, _state);

// orthogonality error
   locerr = 0.0;
   for (i = 0; i < minmn; i++) {
      for (j = i; j < minmn; j++) {
         sm = ae_v_dotproduct(&u->xyR[0][i], u->stride, &u->xyR[0][j], u->stride, m);
         if (i != j) {
            locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
         } else {
            locerr = ae_maxreal(locerr, ae_fabs(sm - 1, _state), _state);
         }
         sm = ae_v_dotproduct(vt->xyR[i], 1, vt->xyR[j], 1, n);
         if (i != j) {
            locerr = ae_maxreal(locerr, ae_fabs(sm, _state), _state);
         } else {
            locerr = ae_maxreal(locerr, ae_fabs(sm - 1, _state), _state);
         }
      }
   }
   *orterr = ae_maxreal(*orterr, locerr, _state);

// values order error
   for (i = 1; i < minmn; i++) {
      if (w->xR[i] > w->xR[i - 1]) {
         *wsorted = false;
      }
   }
}

static void testsvdunit_testsvdproblem(RMatrix *a, ae_int_t m, ae_int_t n, double *materr, double *orterr, double *othererr, bool *wsorted, bool *wfailed, ae_int_t *failcount, ae_int_t *succcount, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t ujob;
   ae_int_t vtjob;
   ae_int_t memjob;
   ae_int_t ucheck;
   ae_int_t vtcheck;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(u, 0, 0, DT_REAL, _state);
   NewMatrix(vt, 0, 0, DT_REAL, _state);
   NewMatrix(u2, 0, 0, DT_REAL, _state);
   NewMatrix(vt2, 0, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);
   NewVector(w2, 0, DT_REAL, _state);

// Main SVD test
   if (!rmatrixsvd(a, m, n, 2, 2, 2, &w, &u, &vt, _state)) {
      *failcount = *failcount + 1;
      *wfailed = true;
      ae_frame_leave(_state);
      return;
   }
   testsvdunit_getsvderror(a, m, n, &u, &w, &vt, materr, orterr, wsorted, _state);

// Additional SVD tests
   for (ujob = 0; ujob <= 2; ujob++) {
      for (vtjob = 0; vtjob <= 2; vtjob++) {
         for (memjob = 0; memjob <= 2; memjob++) {
            if (!rmatrixsvd(a, m, n, ujob, vtjob, memjob, &w2, &u2, &vt2, _state)) {
               *failcount = *failcount + 1;
               *wfailed = true;
               ae_frame_leave(_state);
               return;
            }
            ucheck = 0;
            if (ujob == 1) {
               ucheck = ae_minint(m, n, _state);
            }
            if (ujob == 2) {
               ucheck = m;
            }
            vtcheck = 0;
            if (vtjob == 1) {
               vtcheck = ae_minint(m, n, _state);
            }
            if (vtjob == 2) {
               vtcheck = n;
            }
            for (i = 0; i < m; i++) {
               for (j = 0; j < ucheck; j++) {
                  *othererr = ae_maxreal(*othererr, ae_fabs(u.xyR[i][j] - u2.xyR[i][j], _state), _state);
               }
            }
            for (i = 0; i < vtcheck; i++) {
               for (j = 0; j < n; j++) {
                  *othererr = ae_maxreal(*othererr, ae_fabs(vt.xyR[i][j] - vt2.xyR[i][j], _state), _state);
               }
            }
            for (i = 0; i < ae_minint(m, n, _state); i++) {
               *othererr = ae_maxreal(*othererr, ae_fabs(w.xR[i] - w2.xR[i], _state), _state);
            }
         }
      }
   }

// update counter
   *succcount = *succcount + 1;
   ae_frame_leave(_state);
}

// === trlinsolve testing unit ===
// Main unittest subroutine
bool testtrlinsolve(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t maxmn;
   ae_int_t passcount;
   double threshold;
   ae_int_t n;
   ae_int_t pass;
   ae_int_t i;
   ae_int_t j;
   ae_int_t cnts;
   ae_int_t cntu;
   ae_int_t cntt;
   ae_int_t cntm;
   bool waserrors;
   bool isupper;
   bool istrans;
   bool isunit;
   double v;
   double s;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(aeffective, 0, 0, DT_REAL, _state);
   NewMatrix(aparam, 0, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);

   waserrors = false;
   maxmn = 15;
   passcount = 15;
   threshold = 1000 * ae_machineepsilon;

// Different problems
   for (n = 1; n <= maxmn; n++) {
      ae_matrix_set_length(&aeffective, n - 1 + 1, n - 1 + 1, _state);
      ae_matrix_set_length(&aparam, n - 1 + 1, n - 1 + 1, _state);
      ae_vector_set_length(&xe, n - 1 + 1, _state);
      ae_vector_set_length(&b, n - 1 + 1, _state);
      for (pass = 1; pass <= passcount; pass++) {
         for (cnts = 0; cnts <= 1; cnts++) {
            for (cntu = 0; cntu <= 1; cntu++) {
               for (cntt = 0; cntt <= 1; cntt++) {
                  for (cntm = 0; cntm <= 2; cntm++) {
                     isupper = cnts == 0;
                     isunit = cntu == 0;
                     istrans = cntt == 0;

                  // Skip meaningless combinations of parameters:
                  // (matrix is singular) AND (matrix is unit diagonal)
                     if (cntm == 2 && isunit) {
                        continue;
                     }
                  // Clear matrices
                     for (i = 0; i < n; i++) {
                        for (j = 0; j < n; j++) {
                           aeffective.xyR[i][j] = 0.0;
                           aparam.xyR[i][j] = 0.0;
                        }
                     }

                  // Prepare matrices
                     if (isupper) {
                        for (i = 0; i < n; i++) {
                           for (j = i; j < n; j++) {
                              aeffective.xyR[i][j] = 0.9 * (2 * ae_randomreal(_state) - 1);
                              aparam.xyR[i][j] = aeffective.xyR[i][j];
                           }
                           aeffective.xyR[i][i] = (2 * ae_randominteger(2, _state) - 1) * (0.8 + ae_randomreal(_state));
                           aparam.xyR[i][i] = aeffective.xyR[i][i];
                        }
                     } else {
                        for (i = 0; i < n; i++) {
                           for (j = 0; j <= i; j++) {
                              aeffective.xyR[i][j] = 0.9 * (2 * ae_randomreal(_state) - 1);
                              aparam.xyR[i][j] = aeffective.xyR[i][j];
                           }
                           aeffective.xyR[i][i] = (2 * ae_randominteger(2, _state) - 1) * (0.8 + ae_randomreal(_state));
                           aparam.xyR[i][i] = aeffective.xyR[i][i];
                        }
                     }
                     if (isunit) {
                        for (i = 0; i < n; i++) {
                           aeffective.xyR[i][i] = 1.0;
                           aparam.xyR[i][i] = 0.0;
                        }
                     }
                     if (istrans) {
                        if (isupper) {
                           for (i = 0; i < n; i++) {
                              for (j = i + 1; j < n; j++) {
                                 aeffective.xyR[j][i] = aeffective.xyR[i][j];
                                 aeffective.xyR[i][j] = 0.0;
                              }
                           }
                        } else {
                           for (i = 0; i < n; i++) {
                              for (j = i + 1; j < n; j++) {
                                 aeffective.xyR[i][j] = aeffective.xyR[j][i];
                                 aeffective.xyR[j][i] = 0.0;
                              }
                           }
                        }
                     }
                  // Prepare task, solve, compare
                     for (i = 0; i < n; i++) {
                        xe.xR[i] = 2 * ae_randomreal(_state) - 1;
                     }
                     for (i = 0; i < n; i++) {
                        v = ae_v_dotproduct(aeffective.xyR[i], 1, xe.xR, 1, n);
                        b.xR[i] = v;
                     }
                     rmatrixtrsafesolve(&aparam, n, &b, &s, isupper, istrans, isunit, _state);
                     ae_v_muld(xe.xR, 1, n, s);
                     ae_v_sub(xe.xR, 1, b.xR, 1, n);
                     v = ae_v_dotproduct(xe.xR, 1, xe.xR, 1, n);
                     v = ae_sqrt(v, _state);
                     waserrors = waserrors || v > threshold;
                  }
               }
            }
         }
      }
   }

// report
   if (!silent) {
      printf("TESTING RMatrixTRSafeSolve\n");
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// === safesolve testing unit ===
static void testsafesolveunit_rmatrixmakeacopy(RMatrix *a, ae_int_t m, ae_int_t n, RMatrix *b, ae_state *_state);
static void testsafesolveunit_cmatrixmakeacopy(CMatrix *a, ae_int_t m, ae_int_t n, CMatrix *b, ae_state *_state);

// Main unittest subroutine
bool testsafesolve(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t maxmn;
   double threshold;
   bool rerrors;
   bool cerrors;
   bool waserrors;
   bool isupper;
   ae_int_t trans;
   bool isunit;
   double scalea;
   double growth;
   ae_int_t i;
   ae_int_t j;
   ae_int_t n;
   ae_int_t j1;
   ae_int_t j2;
   ae_complex cv;
   double rv;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(ca, 0, 0, DT_COMPLEX, _state);
   NewMatrix(cea, 0, 0, DT_COMPLEX, _state);
   NewMatrix(ctmpa, 0, 0, DT_COMPLEX, _state);
   NewVector(cxs, 0, DT_COMPLEX, _state);
   NewVector(cxe, 0, DT_COMPLEX, _state);
   NewMatrix(ra, 0, 0, DT_REAL, _state);
   NewMatrix(rea, 0, 0, DT_REAL, _state);
   NewMatrix(rtmpa, 0, 0, DT_REAL, _state);
   NewVector(rxs, 0, DT_REAL, _state);
   NewVector(rxe, 0, DT_REAL, _state);

   maxmn = 30;
   threshold = 100000 * ae_machineepsilon;
   rerrors = false;
   cerrors = false;
   waserrors = false;

// Different problems: general tests
   for (n = 1; n <= maxmn; n++) {

   // test complex solver with well-conditioned matrix:
   // 1. generate A: fill off-diagonal elements with small values,
   //    diagonal elements are filled with larger values
   // 2. generate 'effective' A
   // 3. prepare task (exact X is stored in CXE, right part - in CXS),
   //    solve and compare CXS and CXE
      isupper = ae_randomreal(_state) > 0.5;
      trans = ae_randominteger(3, _state);
      isunit = ae_randomreal(_state) > 0.5;
      scalea = ae_randomreal(_state) + 0.5;
      ae_matrix_set_length(&ca, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (i == j) {
               ca.xyC[i][j].x = (2 * ae_randominteger(2, _state) - 1) * (5 + ae_randomreal(_state));
               ca.xyC[i][j].y = (2 * ae_randominteger(2, _state) - 1) * (5 + ae_randomreal(_state));
            } else {
               ca.xyC[i][j].x = 0.2 * ae_randomreal(_state) - 0.1;
               ca.xyC[i][j].y = 0.2 * ae_randomreal(_state) - 0.1;
            }
         }
      }
      testsafesolveunit_cmatrixmakeacopy(&ca, n, n, &ctmpa, _state);
      for (i = 0; i < n; i++) {
         if (isupper) {
            j1 = 0;
            j2 = i - 1;
         } else {
            j1 = i + 1;
            j2 = n - 1;
         }
         for (j = j1; j <= j2; j++) {
            ctmpa.xyC[i][j] = ae_complex_from_i(0);
         }
         if (isunit) {
            ctmpa.xyC[i][i] = ae_complex_from_i(1);
         }
      }
      ae_matrix_set_length(&cea, n, n, _state);
      for (i = 0; i < n; i++) {
         if (trans == 0) {
            ae_v_cmoved(cea.xyC[i], 1, ctmpa.xyC[i], 1, "N", n, scalea);
         }
         if (trans == 1) {
            ae_v_cmoved(&cea.xyC[0][i], cea.stride, ctmpa.xyC[i], 1, "N", n, scalea);
         }
         if (trans == 2) {
            ae_v_cmoved(&cea.xyC[0][i], cea.stride, ctmpa.xyC[i], 1, "Conj", n, scalea);
         }
      }
      ae_vector_set_length(&cxe, n, _state);
      for (i = 0; i < n; i++) {
         cxe.xC[i].x = 2 * ae_randomreal(_state) - 1;
         cxe.xC[i].y = 2 * ae_randomreal(_state) - 1;
      }
      ae_vector_set_length(&cxs, n, _state);
      for (i = 0; i < n; i++) {
         cv = ae_v_cdotproduct(cea.xyC[i], 1, "N", cxe.xC, 1, "N", n);
         cxs.xC[i] = cv;
      }
      if (cmatrixscaledtrsafesolve(&ca, scalea, n, &cxs, isupper, trans, isunit, ae_sqrt(ae_maxrealnumber, _state), _state)) {
         for (i = 0; i < n; i++) {
            cerrors = cerrors || ae_c_abs(ae_c_sub(cxs.xC[i], cxe.xC[i]), _state) > threshold;
         }
      } else {
         cerrors = true;
      }

   // same with real
      isupper = ae_randomreal(_state) > 0.5;
      trans = ae_randominteger(2, _state);
      isunit = ae_randomreal(_state) > 0.5;
      scalea = ae_randomreal(_state) + 0.5;
      ae_matrix_set_length(&ra, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (i == j) {
               ra.xyR[i][j] = (2 * ae_randominteger(2, _state) - 1) * (5 + ae_randomreal(_state));
            } else {
               ra.xyR[i][j] = 0.2 * ae_randomreal(_state) - 0.1;
            }
         }
      }
      testsafesolveunit_rmatrixmakeacopy(&ra, n, n, &rtmpa, _state);
      for (i = 0; i < n; i++) {
         if (isupper) {
            j1 = 0;
            j2 = i - 1;
         } else {
            j1 = i + 1;
            j2 = n - 1;
         }
         for (j = j1; j <= j2; j++) {
            rtmpa.xyR[i][j] = 0.0;
         }
         if (isunit) {
            rtmpa.xyR[i][i] = 1.0;
         }
      }
      ae_matrix_set_length(&rea, n, n, _state);
      for (i = 0; i < n; i++) {
         if (trans == 0) {
            ae_v_moved(rea.xyR[i], 1, rtmpa.xyR[i], 1, n, scalea);
         }
         if (trans == 1) {
            ae_v_moved(&rea.xyR[0][i], rea.stride, rtmpa.xyR[i], 1, n, scalea);
         }
      }
      ae_vector_set_length(&rxe, n, _state);
      for (i = 0; i < n; i++) {
         rxe.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      ae_vector_set_length(&rxs, n, _state);
      for (i = 0; i < n; i++) {
         rv = ae_v_dotproduct(rea.xyR[i], 1, rxe.xR, 1, n);
         rxs.xR[i] = rv;
      }
      if (rmatrixscaledtrsafesolve(&ra, scalea, n, &rxs, isupper, trans, isunit, ae_sqrt(ae_maxrealnumber, _state), _state)) {
         for (i = 0; i < n; i++) {
            rerrors = rerrors || ae_fabs(rxs.xR[i] - rxe.xR[i], _state) > threshold;
         }
      } else {
         rerrors = true;
      }
   }

// Special test with diagonal ill-conditioned matrix:
// * ability to solve it when resulting growth is less than threshold
// * ability to stop solve when resulting growth is greater than threshold
//
// A = diag(1, 1/growth)
// b = (1, 0.5)
   n = 2;
   growth = 10.0;
   ae_matrix_set_length(&ca, n, n, _state);
   ca.xyC[0][0] = ae_complex_from_i(1);
   ca.xyC[0][1] = ae_complex_from_i(0);
   ca.xyC[1][0] = ae_complex_from_i(0);
   ca.xyC[1][1] = ae_complex_from_d(1 / growth);
   ae_vector_set_length(&cxs, n, _state);
   cxs.xC[0] = ae_complex_from_d(1.0);
   cxs.xC[1] = ae_complex_from_d(0.5);
   cerrors = cerrors || !cmatrixscaledtrsafesolve(&ca, 1.0, n, &cxs, ae_randomreal(_state) > 0.5, ae_randominteger(3, _state), false, 1.05 * ae_maxreal(ae_c_abs(cxs.xC[1], _state) * growth, 1.0, _state), _state);
   cerrors = cerrors || !cmatrixscaledtrsafesolve(&ca, 1.0, n, &cxs, ae_randomreal(_state) > 0.5, ae_randominteger(3, _state), false, 0.95 * ae_maxreal(ae_c_abs(cxs.xC[1], _state) * growth, 1.0, _state), _state);
   ae_matrix_set_length(&ra, n, n, _state);
   ra.xyR[0][0] = 1.0;
   ra.xyR[0][1] = 0.0;
   ra.xyR[1][0] = 0.0;
   ra.xyR[1][1] = 1 / growth;
   ae_vector_set_length(&rxs, n, _state);
   rxs.xR[0] = 1.0;
   rxs.xR[1] = 0.5;
   rerrors = rerrors || !rmatrixscaledtrsafesolve(&ra, 1.0, n, &rxs, ae_randomreal(_state) > 0.5, ae_randominteger(2, _state), false, 1.05 * ae_maxreal(ae_fabs(rxs.xR[1], _state) * growth, 1.0, _state), _state);
   rerrors = rerrors || !rmatrixscaledtrsafesolve(&ra, 1.0, n, &rxs, ae_randomreal(_state) > 0.5, ae_randominteger(2, _state), false, 0.95 * ae_maxreal(ae_fabs(rxs.xR[1], _state) * growth, 1.0, _state), _state);

// Special test with diagonal degenerate matrix:
// * ability to solve it when resulting growth is less than threshold
// * ability to stop solve when resulting growth is greater than threshold
//
// A = diag(1, 0)
// b = (1, 0.5)
   n = 2;
   ae_matrix_set_length(&ca, n, n, _state);
   ca.xyC[0][0] = ae_complex_from_i(1);
   ca.xyC[0][1] = ae_complex_from_i(0);
   ca.xyC[1][0] = ae_complex_from_i(0);
   ca.xyC[1][1] = ae_complex_from_i(0);
   ae_vector_set_length(&cxs, n, _state);
   cxs.xC[0] = ae_complex_from_d(1.0);
   cxs.xC[1] = ae_complex_from_d(0.5);
   cerrors = cerrors || cmatrixscaledtrsafesolve(&ca, 1.0, n, &cxs, ae_randomreal(_state) > 0.5, ae_randominteger(3, _state), false, ae_sqrt(ae_maxrealnumber, _state), _state);
   ae_matrix_set_length(&ra, n, n, _state);
   ra.xyR[0][0] = 1.0;
   ra.xyR[0][1] = 0.0;
   ra.xyR[1][0] = 0.0;
   ra.xyR[1][1] = 0.0;
   ae_vector_set_length(&rxs, n, _state);
   rxs.xR[0] = 1.0;
   rxs.xR[1] = 0.5;
   rerrors = rerrors || rmatrixscaledtrsafesolve(&ra, 1.0, n, &rxs, ae_randomreal(_state) > 0.5, ae_randominteger(2, _state), false, ae_sqrt(ae_maxrealnumber, _state), _state);

// report
   waserrors = rerrors || cerrors;
   if (!silent) {
      printf("TESTING SAFE TR SOLVER\n");
      printf("REAL:                                    ");
      if (!rerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("COMPLEX:                                 ");
      if (!cerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

