// triangular inverse
static bool testrcondunit_rmatrixinvmattr(RMatrix *a, ae_int_t n, bool isupper, bool isunittriangular, ae_state *_state) {
   ae_frame _frame_block;
   bool nounit;
   ae_int_t i;
   ae_int_t j;
   double v;
   double ajj;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(t, 0, DT_REAL, _state);

   result = true;
   ae_vector_set_length(&t, n - 1 + 1, _state);

// Test the input parameters.
   nounit = !isunittriangular;
   if (isupper) {

   // Compute inverse of upper triangular matrix.
      for (j = 0; j < n; j++) {
         if (nounit) {
            if (a->xyR[j][j] == 0.0) {
               result = false;
               ae_frame_leave(_state);
               return result;
            }
            a->xyR[j][j] = 1 / a->xyR[j][j];
            ajj = -a->xyR[j][j];
         } else {
            ajj = -1.0;
         }

      // Compute elements 1:j-1 of j-th column.
         if (j > 0) {
            ae_v_move(t.xR, 1, &a->xyR[0][j], a->stride, j);
            for (i = 0; i < j; i++) {
               if (i < j - 1) {
                  v = ae_v_dotproduct(&a->xyR[i][i + 1], 1, &t.xR[i + 1], 1, j - i - 1);
               } else {
                  v = 0.0;
               }
               if (nounit) {
                  a->xyR[i][j] = v + a->xyR[i][i] * t.xR[i];
               } else {
                  a->xyR[i][j] = v + t.xR[i];
               }
            }
            ae_v_muld(&a->xyR[0][j], a->stride, j, ajj);
         }
      }
   } else {

   // Compute inverse of lower triangular matrix.
      for (j = n - 1; j >= 0; j--) {
         if (nounit) {
            if (a->xyR[j][j] == 0.0) {
               result = false;
               ae_frame_leave(_state);
               return result;
            }
            a->xyR[j][j] = 1 / a->xyR[j][j];
            ajj = -a->xyR[j][j];
         } else {
            ajj = -1.0;
         }
         if (j < n - 1) {

         // Compute elements j+1:n of j-th column.
            ae_v_move(&t.xR[j + 1], 1, &a->xyR[j + 1][j], a->stride, n - j - 1);
            for (i = j + 1; i < n; i++) {
               if (i > j + 1) {
                  v = ae_v_dotproduct(&a->xyR[i][j + 1], 1, &t.xR[j + 1], 1, i - j - 1);
               } else {
                  v = 0.0;
               }
               if (nounit) {
                  a->xyR[i][j] = v + a->xyR[i][i] * t.xR[i];
               } else {
                  a->xyR[i][j] = v + t.xR[i];
               }
            }
            ae_v_muld(&a->xyR[j + 1][j], a->stride, n - j - 1, ajj);
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

// LU inverse
static bool testrcondunit_rmatrixinvmatlu(RMatrix *a, ZVector *pivots, ae_int_t n, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t jp;
   double v;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(work, 0, DT_REAL, _state);

   result = true;

// Quick return if possible
   if (n == 0) {
      ae_frame_leave(_state);
      return result;
   }
   ae_vector_set_length(&work, n - 1 + 1, _state);

// Form inv(U)
   if (!testrcondunit_rmatrixinvmattr(a, n, true, false, _state)) {
      result = false;
      ae_frame_leave(_state);
      return result;
   }
// Solve the equation inv(A)*L = inv(U) for inv(A).
   for (j = n - 1; j >= 0; j--) {

   // Copy current column of L to WORK and replace with zeros.
      for (i = j + 1; i < n; i++) {
         work.xR[i] = a->xyR[i][j];
         a->xyR[i][j] = 0.0;
      }

   // Compute current column of inv(A).
      if (j < n - 1) {
         for (i = 0; i < n; i++) {
            v = ae_v_dotproduct(&a->xyR[i][j + 1], 1, &work.xR[j + 1], 1, n - j - 1);
            a->xyR[i][j] = a->xyR[i][j] - v;
         }
      }
   }

// Apply column interchanges.
   for (j = n - 2; j >= 0; j--) {
      jp = pivots->xZ[j];
      if (jp != j) {
         ae_v_move(work.xR, 1, &a->xyR[0][j], a->stride, n);
         ae_v_move(&a->xyR[0][j], a->stride, &a->xyR[0][jp], a->stride, n);
         ae_v_move(&a->xyR[0][jp], a->stride, work.xR, 1, n);
      }
   }
   ae_frame_leave(_state);
   return result;
}

