// Returns True for successful test, False - for failed test
static bool testrcondunit_testrmatrixtrrcond(ae_int_t maxn, ae_int_t passcount, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;
   ae_int_t j1;
   ae_int_t j2;
   ae_int_t pass;
   bool err50;
   bool err90;
   bool errspec;
   bool errless;
   double erc1;
   double ercinf;
   double v;
   bool isupper;
   bool isunit;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(ea, 0, 0, DT_REAL, _state);
   NewVector(p, 0, DT_INT, _state);
   NewVector(q50, 0, DT_REAL, _state);
   NewVector(q90, 0, DT_REAL, _state);

   err50 = false;
   err90 = false;
   errless = false;
   errspec = false;
   ae_vector_set_length(&q50, 2, _state);
   ae_vector_set_length(&q90, 2, _state);
   for (n = 1; n <= maxn; n++) {

   // special test for zero matrix
      testrcondunit_rmatrixgenzero(&a, n, _state);
      errspec = errspec || rmatrixtrrcond1(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;
      errspec = errspec || rmatrixtrrcondinf(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;

   // general test
      ae_matrix_set_length(&a, n, n, _state);
      for (i = 0; i <= 1; i++) {
         q50.xR[i] = 0.0;
         q90.xR[i] = 0.0;
      }
      for (pass = 1; pass <= passcount; pass++) {
         isupper = ae_randomreal(_state) > 0.5;
         isunit = ae_randomreal(_state) > 0.5;
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               a.xyR[i][j] = ae_randomreal(_state) - 0.5;
            }
         }
         for (i = 0; i < n; i++) {
            a.xyR[i][i] = 1 + ae_randomreal(_state);
         }
         testrcondunit_rmatrixmakeacopy(&a, n, n, &ea, _state);
         for (i = 0; i < n; i++) {
            if (isupper) {
               j1 = 0;
               j2 = i - 1;
            } else {
               j1 = i + 1;
               j2 = n - 1;
            }
            for (j = j1; j <= j2; j++) {
               ea.xyR[i][j] = 0.0;
            }
            if (isunit) {
               ea.xyR[i][i] = 1.0;
            }
         }
         testrcondunit_rmatrixrefrcond(&ea, n, &erc1, &ercinf, _state);

      // 1-norm
         v = 1 / rmatrixtrrcond1(&a, n, isupper, isunit, _state);
         if (v >= testrcondunit_threshold50 * erc1) {
            q50.xR[0] = q50.xR[0] + 1.0 / passcount;
         }
         if (v >= testrcondunit_threshold90 * erc1) {
            q90.xR[0] = q90.xR[0] + 1.0 / passcount;
         }
         errless = errless || v > erc1 * 1.001;

      // Inf-norm
         v = 1 / rmatrixtrrcondinf(&a, n, isupper, isunit, _state);
         if (v >= testrcondunit_threshold50 * ercinf) {
            q50.xR[1] = q50.xR[1] + 1.0 / passcount;
         }
         if (v >= testrcondunit_threshold90 * ercinf) {
            q90.xR[1] = q90.xR[1] + 1.0 / passcount;
         }
         errless = errless || v > ercinf * 1.001;
      }
      for (i = 0; i <= 1; i++) {
         err50 = err50 || q50.xR[i] < 0.50;
         err90 = err90 || q90.xR[i] < 0.90;
      }

   // degenerate matrix test
      if (n >= 3) {
         ae_matrix_set_length(&a, n, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               a.xyR[i][j] = 0.0;
            }
         }
         a.xyR[0][0] = 1.0;
         a.xyR[n - 1][n - 1] = 1.0;
         errspec = errspec || rmatrixtrrcond1(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;
         errspec = errspec || rmatrixtrrcondinf(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;
      }
   // near-degenerate matrix test
      if (n >= 2) {
         ae_matrix_set_length(&a, n, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               a.xyR[i][j] = 0.0;
            }
         }
         for (i = 0; i < n; i++) {
            a.xyR[i][i] = 1.0;
         }
         i = ae_randominteger(n, _state);
         a.xyR[i][i] = 0.1 * ae_maxrealnumber;
         errspec = errspec || rmatrixtrrcond1(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;
         errspec = errspec || rmatrixtrrcondinf(&a, n, ae_randomreal(_state) > 0.5, false, _state) != 0.0;
      }
   }

// report
   result = !(((err50 || err90) || errless) || errspec);
   ae_frame_leave(_state);
   return result;
}

