// === xblas testing unit ===
bool testxblas(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool approxerrors;
   bool exactnesserrors;
   bool waserrors;
   double approxthreshold;
   ae_int_t maxn;
   ae_int_t passcount;
   ae_int_t n;
   ae_int_t i;
   ae_int_t pass;
   double rv1;
   double rv2;
   double rv2err;
   ae_complex cv1;
   ae_complex cv2;
   double cv2err;
   double s;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(rx, 0, DT_REAL, _state);
   NewVector(ry, 0, DT_REAL, _state);
   NewVector(cx, 0, DT_COMPLEX, _state);
   NewVector(cy, 0, DT_COMPLEX, _state);
   NewVector(temp, 0, DT_REAL, _state);

   approxerrors = false;
   exactnesserrors = false;
   waserrors = false;
   approxthreshold = 1000 * ae_machineepsilon;
   maxn = 1000;
   passcount = 10;

// tests:
// 1. ability to calculate dot product
// 2. higher precision
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= passcount; pass++) {

      //  ability to approximately calculate real dot product
         ae_vector_set_length(&rx, n, _state);
         ae_vector_set_length(&ry, n, _state);
         ae_vector_set_length(&temp, n, _state);
         for (i = 0; i < n; i++) {
            if (ae_randomreal(_state) > 0.2) {
               rx.xR[i] = 2 * ae_randomreal(_state) - 1;
            } else {
               rx.xR[i] = 0.0;
            }
            if (ae_randomreal(_state) > 0.2) {
               ry.xR[i] = 2 * ae_randomreal(_state) - 1;
            } else {
               ry.xR[i] = 0.0;
            }
         }
         rv1 = ae_v_dotproduct(rx.xR, 1, ry.xR, 1, n);
         xdot(&rx, &ry, n, &temp, &rv2, &rv2err, _state);
         approxerrors = approxerrors || ae_fabs(rv1 - rv2, _state) > approxthreshold;

      //  ability to approximately calculate complex dot product
         ae_vector_set_length(&cx, n, _state);
         ae_vector_set_length(&cy, n, _state);
         ae_vector_set_length(&temp, 2 * n, _state);
         for (i = 0; i < n; i++) {
            if (ae_randomreal(_state) > 0.2) {
               cx.xC[i].x = 2 * ae_randomreal(_state) - 1;
               cx.xC[i].y = 2 * ae_randomreal(_state) - 1;
            } else {
               cx.xC[i] = ae_complex_from_i(0);
            }
            if (ae_randomreal(_state) > 0.2) {
               cy.xC[i].x = 2 * ae_randomreal(_state) - 1;
               cy.xC[i].y = 2 * ae_randomreal(_state) - 1;
            } else {
               cy.xC[i] = ae_complex_from_i(0);
            }
         }
         cv1 = ae_v_cdotproduct(cx.xC, 1, "N", cy.xC, 1, "N", n);
         xcdot(&cx, &cy, n, &temp, &cv2, &cv2err, _state);
         approxerrors = approxerrors || ae_c_abs(ae_c_sub(cv1, cv2), _state) > approxthreshold;
      }
   }

// test of precision: real
   n = 50000;
   ae_vector_set_length(&rx, n, _state);
   ae_vector_set_length(&ry, n, _state);
   ae_vector_set_length(&temp, n, _state);
   for (pass = 0; pass < passcount; pass++) {
      ae_assert(n % 2 == 0, "Assertion failed", _state);

   // First test: X + X + ... + X - X - X - ... - X = 1*X
      s = ae_exp((double)ae_maxint(pass, 50, _state), _state);
      if (pass == passcount - 1 && pass > 1) {
         s = ae_maxrealnumber;
      }
      ry.xR[0] = (2 * ae_randomreal(_state) - 1) * s * ae_sqrt(2 * ae_randomreal(_state), _state);
      for (i = 1; i < n; i++) {
         ry.xR[i] = ry.xR[0];
      }
      for (i = 0; i < n / 2; i++) {
         rx.xR[i] = 1.0;
      }
      for (i = n / 2; i < n - 1; i++) {
         rx.xR[i] = -1.0;
      }
      rx.xR[n - 1] = 0.0;
      xdot(&rx, &ry, n, &temp, &rv2, &rv2err, _state);
      exactnesserrors = exactnesserrors || rv2err < 0.0;
      exactnesserrors = exactnesserrors || rv2err > 4 * ae_machineepsilon * ae_fabs(ry.xR[0], _state);
      exactnesserrors = exactnesserrors || ae_fabs(rv2 - ry.xR[0], _state) > rv2err;

   // First test: X + X + ... + X = N*X
      s = ae_exp((double)ae_maxint(pass, 50, _state), _state);
      if (pass == passcount - 1 && pass > 1) {
         s = ae_maxrealnumber;
      }
      ry.xR[0] = (2 * ae_randomreal(_state) - 1) * s * ae_sqrt(2 * ae_randomreal(_state), _state);
      for (i = 1; i < n; i++) {
         ry.xR[i] = ry.xR[0];
      }
      for (i = 0; i < n; i++) {
         rx.xR[i] = 1.0;
      }
      xdot(&rx, &ry, n, &temp, &rv2, &rv2err, _state);
      exactnesserrors = exactnesserrors || rv2err < 0.0;
      exactnesserrors = exactnesserrors || rv2err > 4 * ae_machineepsilon * ae_fabs(ry.xR[0], _state) * n;
      exactnesserrors = exactnesserrors || ae_fabs(rv2 - n * ry.xR[0], _state) > rv2err;
   }

// test of precision: complex
   n = 50000;
   ae_vector_set_length(&cx, n, _state);
   ae_vector_set_length(&cy, n, _state);
   ae_vector_set_length(&temp, 2 * n, _state);
   for (pass = 0; pass < passcount; pass++) {
      ae_assert(n % 2 == 0, "Assertion failed", _state);

   // First test: X + X + ... + X - X - X - ... - X = 1*X
      s = ae_exp((double)ae_maxint(pass, 50, _state), _state);
      if (pass == passcount - 1 && pass > 1) {
         s = ae_maxrealnumber;
      }
      cy.xC[0].x = (2 * ae_randomreal(_state) - 1) * s * ae_sqrt(2 * ae_randomreal(_state), _state);
      cy.xC[0].y = (2 * ae_randomreal(_state) - 1) * s * ae_sqrt(2 * ae_randomreal(_state), _state);
      for (i = 1; i < n; i++) {
         cy.xC[i] = cy.xC[0];
      }
      for (i = 0; i < n / 2; i++) {
         cx.xC[i] = ae_complex_from_i(1);
      }
      for (i = n / 2; i < n - 1; i++) {
         cx.xC[i] = ae_complex_from_i(-1);
      }
      cx.xC[n - 1] = ae_complex_from_i(0);
      xcdot(&cx, &cy, n, &temp, &cv2, &cv2err, _state);
      exactnesserrors = exactnesserrors || cv2err < 0.0;
      exactnesserrors = exactnesserrors || cv2err > 4 * ae_machineepsilon * ae_c_abs(cy.xC[0], _state);
      exactnesserrors = exactnesserrors || ae_c_abs(ae_c_sub(cv2, cy.xC[0]), _state) > cv2err;

   // First test: X + X + ... + X = N*X
      s = ae_exp((double)ae_maxint(pass, 50, _state), _state);
      if (pass == passcount - 1 && pass > 1) {
         s = ae_maxrealnumber;
      }
      cy.xC[0] = ae_complex_from_d((2 * ae_randomreal(_state) - 1) * s * ae_sqrt(2 * ae_randomreal(_state), _state));
      for (i = 1; i < n; i++) {
         cy.xC[i] = cy.xC[0];
      }
      for (i = 0; i < n; i++) {
         cx.xC[i] = ae_complex_from_i(1);
      }
      xcdot(&cx, &cy, n, &temp, &cv2, &cv2err, _state);
      exactnesserrors = exactnesserrors || cv2err < 0.0;
      exactnesserrors = exactnesserrors || cv2err > 4 * ae_machineepsilon * ae_c_abs(cy.xC[0], _state) * n;
      exactnesserrors = exactnesserrors || ae_c_abs(ae_c_sub(cv2, ae_c_mul_d(cy.xC[0], 1.0 * n)), _state) > cv2err;
   }

// report
   waserrors = approxerrors || exactnesserrors;
   if (!silent) {
      printf("TESTING XBLAS\n");
      printf("APPROX.TESTS:                            ");
      if (approxerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("EXACT TESTS:                             ");
      if (exactnesserrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
// end
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

