// Checks whether solver results indicate singular matrix.
// Returns True on success.
static bool testdirectdensesolversunit_cmatrixchecksingularm(ae_int_t n, ae_int_t m, ae_int_t info, densesolverreport *rep, CMatrix *xs, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   bool result;

   result = true;
   if (info != -3 && info != 1) {
      result = false;
      return result;
   }
   result = result && !(rep->r1 < 0.0 || rep->r1 > 1000 * ae_machineepsilon);
   result = result && !(rep->rinf < 0.0 || rep->rinf > 1000 * ae_machineepsilon);
   if (info == -3) {
      for (i = 0; i < n; i++) {
         for (j = 0; j < m; j++) {
            result = result && ae_c_eq_d(xs->xyC[i][j], 0.0);
         }
      }
   }
   return result;
}

