// Drops upper or lower half of the matrix - fills it by special pattern
// which may be used later to ensure that this part wasn't changed
static void testdirectdensesolversunit_rmatrixdrophalf(RMatrix *a, ae_int_t n, bool droplower, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;

   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         if ((droplower && i > j) || (!droplower && i < j)) {
            a->xyR[i][j] = (double)(1 + 2 * i + 3 * j);
         }
      }
   }
}

// Drops upper or lower half of the matrix - fills it by special pattern
