                  rmatrixlusolvemfast(&lua, &p, n, &x, m, &info, _state);
                  ae_set_error_flag(rerrors, !testdirectdensesolversunit_rmatrixchecksingularmfast(n, m, info, &x, _state), __FILE__, __LINE__, "testdirectdensesolversunit.ap:808");
