// SPD SKS test
static void testdirectsparsesolversunit_testsks(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t maxn;
   double threshold;
   ae_int_t bw;
   bool isupper;
   ae_int_t n;
   ae_int_t i;
   ae_int_t j;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewVector(xs, 0, DT_REAL, _state);
   NewObj(sparsematrix, sa, _state);
   NewObj(sparsematrix, sa2, _state);
   NewObj(sparsesolverreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   passcount = 10;
   maxn = 30;
   threshold = 1.0E-6;
   hqrndrandomize(&rs, _state);

// Well conditioned SPD problems solved with SKS solver
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= passcount; pass++) {

      // 1. generate random well conditioned matrix A.
      // 2. generate random solution vector xe
      // 3. generate right part b=A*xe
         isupper = hqrndnormal(&rs, _state) > 0.5;
         bw = hqrnduniformi(&rs, n + 1, _state);
         ae_matrix_set_length(&a, n, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               a.xyR[i][j] = 0.0;
            }
         }
         sparsecreate(n, n, 0, &sa, _state);
         for (i = 0; i < n; i++) {
            a.xyR[i][i] = 1 + hqrnduniformr(&rs, _state);
            sparseset(&sa, i, i, a.xyR[i][i], _state);
            for (j = i + 1; j <= i + bw && j < n; j++) {
               a.xyR[i][j] = (hqrnduniformr(&rs, _state) - 0.5) / n;
               a.xyR[j][i] = a.xyR[i][j];
               if (isupper) {
                  sparseset(&sa, i, j, a.xyR[i][j], _state);
               } else {
                  sparseset(&sa, j, i, a.xyR[i][j], _state);
               }
            }
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               if ((j < i && isupper) || (j > i && !isupper)) {
                  if (hqrnduniformr(&rs, _state) < 0.25) {
                     sparseset(&sa, i, j, hqrndnormal(&rs, _state), _state);
                  }
               }
            }
         }
         sparseconvertto(&sa, hqrnduniformi(&rs, 3, _state), _state);
         ae_vector_set_length(&xe, n, _state);
         for (i = 0; i < n; i++) {
            xe.xR[i] = hqrndnormal(&rs, _state);
         }
         ae_vector_set_length(&b, n, _state);
         rmatrixgemv(n, n, 1.0, &a, 0, 0, 0, &xe, 0, 0.0, &b, 0, _state);

      // Test SKS solver
         ae_vector_set_length(&xs, 0, _state);
         sparsecopytosks(&sa, &sa2, _state);
         sparsespdsolvesks(&sa2, isupper, &b, &xs, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:88");
         ae_set_error_flag(errorflag, xs.cnt != n, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:89");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, ae_fabs(xe.xR[i] - xs.xR[i], _state) > threshold, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:93");
         }

      // Test solver #2
         sparsecopytosks(&sa, &sa2, _state);
         if (!sparsecholeskyskyline(&sa2, n, isupper, _state)) {
            ae_set_error_flag(errorflag, true, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:102");
            ae_frame_leave(_state);
            return;
         }
         ae_vector_set_length(&xs, 0, _state);
         sparsespdcholeskysolve(&sa2, isupper, &b, &xs, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:107");
         ae_set_error_flag(errorflag, xs.cnt != n, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:108");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, ae_fabs(xe.xR[i] - xs.xR[i], _state) > threshold, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:112");
         }
      }
   }
   ae_frame_leave(_state);
}

