// General linear test
static void testdirectsparsesolversunit_testgen(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t maxn;
   double threshold;
   ae_int_t n;
   ae_int_t noffdiag;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewVector(xs, 0, DT_REAL, _state);
   NewVector(xs2, 0, DT_REAL, _state);
   NewVector(pivp, 0, DT_INT, _state);
   NewVector(pivq, 0, DT_INT, _state);
   NewObj(sparsematrix, sa, _state);
   NewObj(sparsematrix, sa2, _state);
   NewObj(sparsesolverreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   passcount = 10;
   maxn = 30;
   threshold = 1.0E-6;
   hqrndrandomize(&rs, _state);

// Well conditioned general linear problems solved with LU solver
   for (n = 1; n <= maxn; n++) {
      for (pass = 1; pass <= passcount; pass++) {

      // Select number of off-diagonal entries, we want to try matrices
      // from dense to sparse
         noffdiag = n * (n - 1);
         while (true) {

         // 1. generate random well conditioned matrix A.
         // 2. apply row/col permutation
         // 3. generate random solution vector xe
         // 4. generate right part b=A*xe
            ae_matrix_set_length(&a, n, n, _state);
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  a.xyR[i][j] = 0.0;
               }
            }
            for (k = 0; k < noffdiag; k++) {
               i = hqrnduniformi(&rs, n, _state);
               j = hqrnduniformi(&rs, n, _state);
               a.xyR[i][j] = 0.01 * hqrndnormal(&rs, _state) / n;
            }
            for (i = 0; i < n; i++) {
               a.xyR[i][i] = 1.0 + ae_pow(2.0, hqrndnormal(&rs, _state), _state);
            }
            for (i = 0; i < n; i++) {
               swaprows(&a, i, i + hqrnduniformi(&rs, n - i, _state), n, _state);
            }
            for (i = 0; i < n; i++) {
               swapcols(&a, i, i + hqrnduniformi(&rs, n - i, _state), n, _state);
            }
            sparsecreate(n, n, 0, &sa, _state);
            for (i = 0; i < n; i++) {
               for (j = 0; j < n; j++) {
                  sparseset(&sa, i, j, a.xyR[i][j], _state);
               }
            }
            sparseconvertto(&sa, hqrnduniformi(&rs, 3, _state), _state);
            ae_vector_set_length(&xe, n, _state);
            for (i = 0; i < n; i++) {
               xe.xR[i] = hqrndnormal(&rs, _state);
            }
            ae_vector_set_length(&b, n, _state);
            rmatrixgemv(n, n, 1.0, &a, 0, 0, 0, &xe, 0, 0.0, &b, 0, _state);

         // Test solver #1
            ae_vector_set_length(&xs, 0, _state);
            sparsesolve(&sa, &b, &xs, &rep, _state);
            ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:285");
            ae_set_error_flag(errorflag, xs.cnt != n, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:286");
            if (*errorflag) {
               ae_frame_leave(_state);
               return;
            }
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, ae_fabs(xe.xR[i] - xs.xR[i], _state) > threshold, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:290");
            }

         // Test solver #2
            ae_vector_set_length(&xs, 0, _state);
            sparsecopytocrs(&sa, &sa2, _state);
            sparselu(&sa2, 0, &pivp, &pivq, _state);
            sparselusolve(&sa2, &pivp, &pivq, &b, &xs2, &rep, _state);
            ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:300");
            ae_set_error_flag(errorflag, xs2.cnt != n, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:301");
            if (*errorflag) {
               ae_frame_leave(_state);
               return;
            }
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, ae_fabs(xe.xR[i] - xs2.xR[i], _state) > threshold, __FILE__, __LINE__, "testdirectsparsesolversunit.ap:305");
            }

         // Update fill factor
            if (noffdiag == 0) {
               break;
            }
            noffdiag = noffdiag / 2;
         }
      }
   }
   ae_frame_leave(_state);
}

