// This function checks that XUpdated returns correct State.R2. It creates
// large badly conditioned problem (N=50), which should be large enough and
// ill-conditioned enough to cause periodic recalculation of R.
// ALGLIB: Copyright 14.11.2011 by Sergey Bochkanov
static bool testlincgunit_testrcorrectness(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   double c;
   ae_int_t i;
   ae_int_t j;
   double r2;
   double v;
   double rtol;
   ae_int_t maxits;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(lincgstate, s, _state);
   NewObj(lincgreport, rep, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);

   rtol = 1.0E6 * ae_machineepsilon;
   n = 50;
   maxits = n / 2;
   c = 10000.0;
   spdmatrixrndcond(n, c, &a, _state);
   ae_vector_set_length(&b, n, _state);
   for (i = 0; i < n; i++) {
      b.xR[i] = 2 * ae_randomreal(_state) - 1;
   }
   lincgcreate(n, &s, _state);
   lincgsetb(&s, &b, _state);
   lincgsetxrep(&s, true, _state);
   lincgsetcond(&s, 0.0, maxits, _state);
   while (lincgiteration(&s, _state)) {
      if (s.needmv) {
         for (i = 0; i < n; i++) {
            s.mv.xR[i] = 0.0;
            for (j = 0; j < n; j++) {
               s.mv.xR[i] = s.mv.xR[i] + a.xyR[i][j] * s.x.xR[j];
            }
         }
      }
      if (s.needvmv) {
         s.vmv = 0.0;
         for (i = 0; i < n; i++) {
            s.mv.xR[i] = 0.0;
            for (j = 0; j < n; j++) {
               s.mv.xR[i] = s.mv.xR[i] + a.xyR[i][j] * s.x.xR[j];
            }
            s.vmv = s.vmv + s.mv.xR[i] * s.x.xR[i];
         }
      }
      if (s.needprec) {
         for (i = 0; i < n; i++) {
            s.pv.xR[i] = s.x.xR[i];
         }
      }
      if (s.xupdated) {

      // calculate R2, compare with value returned in state.R2
         r2 = 0.0;
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + a.xyR[i][j] * s.x.xR[j];
            }
            r2 = r2 + ae_sqr(v - b.xR[i], _state);
         }
         if (ae_fabs(r2 - s.r2, _state) > rtol) {
            result = true;
            ae_frame_leave(_state);
            return result;
         }
      }
   }
   lincgresults(&s, &b, &rep, _state);
   if (rep.iterationscount != maxits) {
      result = true;
      ae_frame_leave(_state);
      return result;
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

