// The test does check, that algorithm correctly displays a progress report.
//
// INPUT:
//     Silent   -   if true then function output report
// ALGLIB: Copyright 30.11.2011 by Sergey Bochkanov
static bool testlinlsqrunit_reportcorrectnesstest(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   double rnorm;
   double tnorm;
   ae_int_t sz;
   ae_int_t n;
   ae_int_t m;
   ae_int_t lambdai;
   double mn;
   double mx;
   double c;
   ae_int_t i;
   ae_int_t j;
   ae_int_t its;
   double tmp;
   double eps;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(linlsqrstate, s, _state);
   NewObj(linlsqrreport, rep, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(u, 0, 0, DT_REAL, _state);
   NewMatrix(v, 0, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(firstx, 0, DT_REAL, _state);
   NewVector(lastx, 0, DT_REAL, _state);

   eps = 0.001;
   sz = 5;
   mn = -100.0;
   mx = 100.0;
   c = 100.0;
   result = false;
   for (m = 1; m <= sz; m++) {
      for (n = 1; n <= m; n++) {
         for (lambdai = 0; lambdai <= 1; lambdai++) {
            its = -1;

         // initialize matrix A
            spdmatrixrndcond(m + n, c, &a, _state);
            for (i = m; i < m + n; i++) {
               for (j = 0; j < n; j++) {
                  if (i - m == j) {
                     a.xyR[i][j] = (double)lambdai;
                  } else {
                     a.xyR[i][j] = 0.0;
                  }
               }
            }

         // initialize b
            ae_vector_set_length(&b, m + n, _state);
            rnorm = 0.0;
            for (i = 0; i < m + n; i++) {
               if (i < m) {
                  b.xR[i] = (mx - mn) * ae_randomreal(_state) + mn;
                  rnorm = rnorm + b.xR[i] * b.xR[i];
               } else {
                  b.xR[i] = 0.0;
               }
            }

         // initialize FirstX and LastX
            ae_vector_set_length(&firstx, n, _state);
            ae_vector_set_length(&lastx, n, _state);

         // calculating by LSQR method
            linlsqrcreate(m, n, &s, _state);
            linlsqrsetb(&s, &b, _state);
            linlsqrsetcond(&s, 0.0, 0.0, n, _state);
            linlsqrsetlambdai(&s, (double)lambdai, _state);
            linlsqrsetxrep(&s, true, _state);
            ae_set_error_flag(&result, linlsqrpeekiterationscount(&s, _state) != 0, __FILE__, __LINE__, "testlinlsqrunit.ap:811");
            while (linlsqriteration(&s, _state)) {
               if (s.needmv) {
                  for (i = 0; i < m; i++) {
                     s.mv.xR[i] = 0.0;
                     for (j = 0; j < n; j++) {
                        s.mv.xR[i] = s.mv.xR[i] + a.xyR[i][j] * s.x.xR[j];
                     }
                  }
               }
               if (s.needmtv) {
                  for (i = 0; i < n; i++) {
                     s.mtv.xR[i] = 0.0;
                     for (j = 0; j < m; j++) {
                        s.mtv.xR[i] = s.mtv.xR[i] + a.xyR[j][i] * s.x.xR[j];
                     }
                  }
               }
               if (s.xupdated) {
                  tnorm = 0.0;
                  for (i = 0; i < m + n; i++) {
                     tmp = 0.0;
                     for (j = 0; j < n; j++) {
                        tmp = tmp + a.xyR[i][j] * s.x.xR[j];
                     }
                     tnorm = tnorm + (b.xR[i] - tmp) * (b.xR[i] - tmp);
                  }

               // check, that RNorm is't more than S.R2
               // and difference between S.R2 and TNorm
               // is't more than 'eps'(here S.R2=||rk||,
               // calculated by the algorithm for LSQR, and
               // TNorm=||A*S.x-b||, calculated by test function).
                  if (s.r2 > rnorm + 1000 * ae_machineepsilon * ae_maxreal(rnorm, 1.0, _state)) {
                     ae_set_error_flag(&result, true, __FILE__, __LINE__, "testlinlsqrunit.ap:853");
                     ae_frame_leave(_state);
                     return result;
                  }
                  if (ae_fabs(s.r2 - tnorm, _state) > eps) {
                     ae_set_error_flag(&result, true, __FILE__, __LINE__, "testlinlsqrunit.ap:858");
                     ae_frame_leave(_state);
                     return result;
                  }
                  rnorm = s.r2;
                  its = its + 1;

               // get X value from first iteration
               // and from last iteration.
                  if (its == 0) {
                     for (i = 0; i < n; i++) {
                        firstx.xR[i] = s.x.xR[i];
                     }
                  }
                  if (its == n) {
                     for (i = 0; i < n; i++) {
                        lastx.xR[i] = s.x.xR[i];
                     }
                  }
               // check iterations counter
                  ae_set_error_flag(&result, linlsqrpeekiterationscount(&s, _state) != its, __FILE__, __LINE__, "testlinlsqrunit.ap:878");
               }
            }
            linlsqrresults(&s, &x0, &rep, _state);
            ae_set_error_flag(&result, linlsqrpeekiterationscount(&s, _state) != n, __FILE__, __LINE__, "testlinlsqrunit.ap:882");

         // check, that FirstX is equal to zero and LastX
         // is equal to x0.
            for (i = 0; i < n; i++) {
               if (firstx.xR[i] != 0.0 || lastx.xR[i] != x0.xR[i]) {
                  if (!silent) {
                     printf("ReportCorrectnessTest::Fail\n");
                     printf("IterationsCount=%0d\n", (int)rep.iterationscount);
                     printf("NMV=%0d\n", (int)rep.nmv);
                     printf("TerminationType=%0d\n", (int)rep.terminationtype);
                     printf("X and LastX...\n");
                     for (j = 0; j < n; j++) {
                        printf("x[%0d]=%0.10e; LastX[%0d]=%0.10e\n", (int)j, x0.xR[j], (int)j, lastx.xR[j]);
                     }
                  }
                  ae_set_error_flag(&result, true, __FILE__, __LINE__, "testlinlsqrunit.ap:902");
                  ae_frame_leave(_state);
                  return result;
               }
            }
         }
      }
   }
   if (!silent) {
      printf("ReportCorrectnessTest::Ok\n");
   }
   ae_frame_leave(_state);
   return result;
}

