// === nleq testing unit ===
static void testnlequnit_testfunchbm(nleqstate *state, ae_state *_state);
static void testnlequnit_testfunchb1(nleqstate *state, ae_state *_state);
static void testnlequnit_testfuncshbm(nleqstate *state, ae_state *_state);

bool testnleq(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   bool basicserrors;
   bool converror;
   bool othererrors;
   ae_int_t n;
   ae_int_t i;
   ae_int_t k;
   double v;
   double flast;
   bool firstrep;
   ae_int_t nfunc;
   ae_int_t njac;
   ae_int_t itcnt;
   ae_int_t pass;
   ae_int_t passcount;
   double epsf;
   double stpmax;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xlast, 0, DT_REAL, _state);
   NewObj(nleqstate, state, _state);
   NewObj(nleqreport, rep, _state);

   waserrors = false;
   basicserrors = false;
   converror = false;
   othererrors = false;

// Basic tests
//
// Test with Himmelblau's function (M):
// * ability to find correct result
// * ability to work after soft restart (restart after finish)
// * ability to work after hard restart (restart in the middle of optimization)
   passcount = 100;
   for (pass = 0; pass < passcount; pass++) {

   // Ability to find correct result
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqcreatelm(2, 2, &x, &state, _state);
      epsf = 1.0E-9;
      nleqsetcond(&state, epsf, 0, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfunchbm(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      if (rep.terminationtype > 0) {
         basicserrors = basicserrors || ae_sqr(x.xR[0] * x.xR[0] + x.xR[1] - 11, _state) + ae_sqr(x.xR[0] + x.xR[1] * x.xR[1] - 7, _state) > ae_sqr(epsf, _state);
      } else {
         basicserrors = true;
      }

   // Ability to work after soft restart
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqcreatelm(2, 2, &x, &state, _state);
      epsf = 1.0E-9;
      nleqsetcond(&state, epsf, 0, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfunchbm(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqrestartfrom(&state, &x, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfunchbm(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      if (rep.terminationtype > 0) {
         basicserrors = basicserrors || ae_sqr(x.xR[0] * x.xR[0] + x.xR[1] - 11, _state) + ae_sqr(x.xR[0] + x.xR[1] * x.xR[1] - 7, _state) > ae_sqr(epsf, _state);
      } else {
         basicserrors = true;
      }

   // Ability to work after hard restart:
   // * stopping condition: small F
   // * StpMax is so small that we need about 10000 iterations to
   //   find solution (steps are small)
   // * choose random K significantly less that 9999
   // * iterate for some time, then break, restart optimization
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 100.0;
      x.xR[1] = 100.0;
      nleqcreatelm(2, 2, &x, &state, _state);
      epsf = 1.0E-9;
      nleqsetcond(&state, epsf, 0, _state);
      nleqsetstpmax(&state, 0.01, _state);
      k = 1 + ae_randominteger(100, _state);
      for (i = 0; i < k; i++) {
         if (!nleqiteration(&state, _state)) {
            break;
         }
         testnlequnit_testfunchbm(&state, _state);
      }
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqrestartfrom(&state, &x, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfunchbm(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      if (rep.terminationtype > 0) {
         basicserrors = basicserrors || ae_sqr(x.xR[0] * x.xR[0] + x.xR[1] - 11, _state) + ae_sqr(x.xR[0] + x.xR[1] * x.xR[1] - 7, _state) > ae_sqr(epsf, _state);
      } else {
         basicserrors = true;
      }
   }

// Basic tests
//
// Test with Himmelblau's function (1):
// * ability to find correct result
   passcount = 100;
   for (pass = 0; pass < passcount; pass++) {

   // Ability to find correct result
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqcreatelm(2, 1, &x, &state, _state);
      epsf = 1.0E-9;
      nleqsetcond(&state, epsf, 0, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfunchb1(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      if (rep.terminationtype > 0) {
         basicserrors = basicserrors || ae_sqr(x.xR[0] * x.xR[0] + x.xR[1] - 11, _state) + ae_sqr(x.xR[0] + x.xR[1] * x.xR[1] - 7, _state) > epsf;
      } else {
         basicserrors = true;
      }
   }

// Basic tests
//
// Ability to detect situation when we can't find minimum
   passcount = 100;
   for (pass = 0; pass < passcount; pass++) {
      ae_vector_set_length(&x, 2, _state);
      x.xR[0] = 20 * ae_randomreal(_state) - 10;
      x.xR[1] = 20 * ae_randomreal(_state) - 10;
      nleqcreatelm(2, 3, &x, &state, _state);
      epsf = 1.0E-9;
      nleqsetcond(&state, epsf, 0, _state);
      while (nleqiteration(&state, _state)) {
         testnlequnit_testfuncshbm(&state, _state);
      }
      nleqresults(&state, &x, &rep, _state);
      basicserrors = basicserrors || rep.terminationtype != -4;
   }

// Test correctness of intermediate reports and final report:
// * first report is starting point
// * function value decreases on subsequent reports
// * function value is correctly reported
// * last report is final point
// * NFunc and NJac are compared with values counted directly
// * IterationsCount is compared with value counter directly
   n = 2;
   ae_vector_set_length(&x, n, _state);
   ae_vector_set_length(&xlast, n, _state);
   x.xR[0] = 20 * ae_randomreal(_state) - 10;
   x.xR[1] = 20 * ae_randomreal(_state) - 10;
   xlast.xR[0] = ae_maxrealnumber;
   xlast.xR[1] = ae_maxrealnumber;
   nleqcreatelm(n, 2, &x, &state, _state);
   nleqsetcond(&state, 1.0E-6, 0, _state);
   nleqsetxrep(&state, true, _state);
   firstrep = true;
   flast = ae_maxrealnumber;
   nfunc = 0;
   njac = 0;
   itcnt = 0;
   while (nleqiteration(&state, _state)) {
      if (state.xupdated) {

      // first report must be starting point
         if (firstrep) {
            for (i = 0; i < n; i++) {
               othererrors = othererrors || state.x.xR[i] != x.xR[i];
            }
            firstrep = false;
         }
      // function value must decrease
         othererrors = othererrors || state.f > flast;

      // check correctness of function value
         v = ae_sqr(state.x.xR[0] * state.x.xR[0] + state.x.xR[1] - 11, _state) + ae_sqr(state.x.xR[0] + state.x.xR[1] * state.x.xR[1] - 7, _state);
         othererrors = othererrors || ae_fabs(v - state.f, _state) / ae_maxreal(v, 1.0, _state) > 100 * ae_machineepsilon;

      // update info and continue
         ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
         flast = state.f;
         itcnt = itcnt + 1;
         continue;
      }
      if (state.needf) {
         nfunc = nfunc + 1;
      }
      if (state.needfij) {
         nfunc = nfunc + 1;
         njac = njac + 1;
      }
      testnlequnit_testfunchbm(&state, _state);
   }
   nleqresults(&state, &x, &rep, _state);
   if (rep.terminationtype > 0) {
      othererrors = (othererrors || xlast.xR[0] != x.xR[0]) || xlast.xR[1] != x.xR[1];
      v = ae_sqr(x.xR[0] * x.xR[0] + x.xR[1] - 11, _state) + ae_sqr(x.xR[0] + x.xR[1] * x.xR[1] - 7, _state);
      othererrors = othererrors || ae_fabs(flast - v, _state) / ae_maxreal(v, 1.0, _state) > 100 * ae_machineepsilon;
   } else {
      converror = true;
   }
   othererrors = othererrors || rep.nfunc != nfunc;
   othererrors = othererrors || rep.njac != njac;
   othererrors = othererrors || rep.iterationscount != itcnt - 1;

// Test ability to set limit on algorithm steps
   ae_vector_set_length(&x, 2, _state);
   ae_vector_set_length(&xlast, 2, _state);
   x.xR[0] = 20 * ae_randomreal(_state) + 20;
   x.xR[1] = 20 * ae_randomreal(_state) + 20;
   xlast.xR[0] = x.xR[0];
   xlast.xR[1] = x.xR[1];
   stpmax = 0.1 + 0.1 * ae_randomreal(_state);
   epsf = 1.0E-9;
   nleqcreatelm(2, 2, &x, &state, _state);
   nleqsetstpmax(&state, stpmax, _state);
   nleqsetcond(&state, epsf, 0, _state);
   nleqsetxrep(&state, true, _state);
   while (nleqiteration(&state, _state)) {
      if (state.needf || state.needfij) {
         testnlequnit_testfunchbm(&state, _state);
      }
      if ((state.needf || state.needfij) || state.xupdated) {
         othererrors = othererrors || ae_sqrt(ae_sqr(state.x.xR[0] - xlast.xR[0], _state) + ae_sqr(state.x.xR[1] - xlast.xR[1], _state), _state) > 1.00001 * stpmax;
      }
      if (state.xupdated) {
         xlast.xR[0] = state.x.xR[0];
         xlast.xR[1] = state.x.xR[1];
      }
   }

// end
   waserrors = (basicserrors || converror) || othererrors;
   if (!silent) {
      printf("TESTING NLEQ SOLVER\n");
      printf("BASIC FUNCTIONALITY:                      ");
      if (basicserrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("CONVERGENCE:                              ");
      if (converror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("OTHER PROPERTIES:                         ");
      if (othererrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Himmelblau's function
//
//     F = (x^2+y-11)^2 + (x+y^2-7)^2
//
// posed as system of M functions:
//
//     f0 = x^2+y-11
//     f1 = x+y^2-7
//
static void testnlequnit_testfunchbm(nleqstate *state, ae_state *_state) {
   double x;
   double y;

   ae_assert(state->needf || state->needfij, "TestNLEQUnit: internal error!", _state);
   x = state->x.xR[0];
   y = state->x.xR[1];
   if (state->needf) {
      state->f = ae_sqr(x * x + y - 11, _state) + ae_sqr(x + y * y - 7, _state);
      return;
   }
   if (state->needfij) {
      state->fi.xR[0] = x * x + y - 11;
      state->fi.xR[1] = x + y * y - 7;
      state->j.xyR[0][0] = 2 * x;
      state->j.xyR[0][1] = 1.0;
      state->j.xyR[1][0] = 1.0;
      state->j.xyR[1][1] = 2 * y;
      return;
   }
}

// Himmelblau's function
//
//     F = (x^2+y-11)^2 + (x+y^2-7)^2
//
// posed as system of 1 function
static void testnlequnit_testfunchb1(nleqstate *state, ae_state *_state) {
   double x;
   double y;

   ae_assert(state->needf || state->needfij, "TestNLEQUnit: internal error!", _state);
   x = state->x.xR[0];
   y = state->x.xR[1];
   if (state->needf) {
      state->f = ae_sqr(ae_sqr(x * x + y - 11, _state) + ae_sqr(x + y * y - 7, _state), _state);
      return;
   }
   if (state->needfij) {
      state->fi.xR[0] = ae_sqr(x * x + y - 11, _state) + ae_sqr(x + y * y - 7, _state);
      state->j.xyR[0][0] = 2 * (x * x + y - 11) * 2 * x + 2 * (x + y * y - 7);
      state->j.xyR[0][1] = 2 * (x * x + y - 11) + 2 * (x + y * y - 7) * 2 * y;
      return;
   }
}

// Shifted Himmelblau's function
//
//     F = (x^2+y-11)^2 + (x+y^2-7)^2 + 1
//
// posed as system of M functions:
//
//     f0 = x^2+y-11
//     f1 = x+y^2-7
//     f2 = 1
//
// This function is used to test algorithm on problem which has no solution.
static void testnlequnit_testfuncshbm(nleqstate *state, ae_state *_state) {
   double x;
   double y;

   ae_assert(state->needf || state->needfij, "TestNLEQUnit: internal error!", _state);
   x = state->x.xR[0];
   y = state->x.xR[1];
   if (state->needf) {
      state->f = ae_sqr(x * x + y - 11, _state) + ae_sqr(x + y * y - 7, _state) + 1;
      return;
   }
   if (state->needfij) {
      state->fi.xR[0] = x * x + y - 11;
      state->fi.xR[1] = x + y * y - 7;
      state->fi.xR[2] = 1.0;
      state->j.xyR[0][0] = 2 * x;
      state->j.xyR[0][1] = 1.0;
      state->j.xyR[1][0] = 1.0;
      state->j.xyR[1][1] = 2 * y;
      state->j.xyR[2][0] = 0.0;
      state->j.xyR[2][1] = 0.0;
      return;
   }
}

