// Checks whether inverse is correct
// Returns True on success.
static bool testmatinvunit_rmatrixcheckinverse(RMatrix *a, RMatrix *inva, ae_int_t n, double threshold, ae_int_t info, matinvreport *rep, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double v;
   bool result;

   result = true;
   if (info <= 0) {
      result = false;
   } else {
      result = result && !(rep->r1 < 100 * ae_machineepsilon || rep->r1 > 1 + 1000 * ae_machineepsilon);
      result = result && !(rep->rinf < 100 * ae_machineepsilon || rep->rinf > 1 + 1000 * ae_machineepsilon);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            v = ae_v_dotproduct(a->xyR[i], 1, &inva->xyR[0][j], inva->stride, n);
            if (i == j) {
               v = v - 1;
            }
            result = result && ae_fabs(v, _state) <= threshold;
         }
      }
   }
   return result;
}

