// This function checks preconditioning functions
//
// On failure sets error flag.
static void testoptservunit_testprec(bool *wereerrors, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t k;
   ae_int_t i;
   ae_int_t j;
   ae_int_t i0;
   ae_int_t j0;
   ae_int_t j1;
   double v;
   double rho;
   double theta;
   double tolg;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(va, 0, 0, DT_REAL, _state);
   NewVector(vc, 0, DT_REAL, _state);
   NewVector(vd, 0, DT_REAL, _state);
   NewVector(vb, 0, DT_REAL, _state);
   NewVector(s0, 0, DT_REAL, _state);
   NewVector(s1, 0, DT_REAL, _state);
   NewVector(s2, 0, DT_REAL, _state);
   NewVector(g, 0, DT_REAL, _state);
   NewObj(precbuflbfgs, buf, _state);
   NewObj(precbuflowrank, lowrankbuf, _state);
   NewVector(norms, 0, DT_REAL, _state);
   NewMatrix(sk, 0, 0, DT_REAL, _state);
   NewMatrix(yk, 0, 0, DT_REAL, _state);
   NewMatrix(bk, 0, 0, DT_REAL, _state);
   NewVector(bksk, 0, DT_REAL, _state);
   NewVector(tmp, 0, DT_REAL, _state);
   NewObj(matinvreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Test for inexact L-BFGS preconditioner.
//
// We generate QP problem 0.5*x'*H*x, with random H=D+V'*C*V.
// Different K's, from 0 to N, are tried. We test preconditioner
// code which uses compact L-BFGS update against reference implementation
// which uses non-compact BFGS scheme.
//
// For each K we perform two tests: first for KxN non-zero matrix V,
// second one for NxN matrix V with last N-K rows set to zero. Last test
// checks algorithm's ability to handle zero updates.
   tolg = 1.0E-9;
   for (n = 1; n <= 10; n++) {
      for (k = 0; k <= n; k++) {

      // Prepare problem:
      // * VD, VC, VA, with VC/VA reordered by ascending of VC[i]*norm(VA[i,...])^2
      // * trial vector S (copies are stored to S0,S1,S2)
         ae_vector_set_length(&vd, n, _state);
         ae_vector_set_length(&s0, n, _state);
         ae_vector_set_length(&s1, n, _state);
         ae_vector_set_length(&s2, n, _state);
         for (i = 0; i < n; i++) {
            vd.xR[i] = ae_exp(hqrndnormal(&rs, _state), _state);
            s0.xR[i] = hqrndnormal(&rs, _state);
            s1.xR[i] = s0.xR[i];
            s2.xR[i] = s0.xR[i];
         }
         rmatrixrndcond(n, 1.0E2, &va, _state);
         rvectorsetlengthatleast(&vc, n, _state);
         for (i = 0; i < k; i++) {
            vc.xR[i] = ae_exp(hqrndnormal(&rs, _state), _state);
         }
         for (i = k; i < n; i++) {
            vc.xR[i] = 0.0;
            for (j = 0; j < n; j++) {
               va.xyR[i][j] = 0.0;
            }
         }
         ae_vector_set_length(&norms, k, _state);
         for (i = 0; i < k; i++) {
            v = ae_v_dotproduct(va.xyR[i], 1, va.xyR[i], 1, n);
            norms.xR[i] = v * vc.xR[i];
         }
         for (i = 0; i < k; i++) {
            for (j = 0; j < k - 1; j++) {
               if (norms.xR[j] > norms.xR[j + 1]) {

               // Swap elements J and J+1
                  v = norms.xR[j];
                  norms.xR[j] = norms.xR[j + 1];
                  norms.xR[j + 1] = v;
                  v = vc.xR[j];
                  vc.xR[j] = vc.xR[j + 1];
                  vc.xR[j + 1] = v;
                  for (j0 = 0; j0 < n; j0++) {
                     v = va.xyR[j][j0];
                     va.xyR[j][j0] = va.xyR[j + 1][j0];
                     va.xyR[j + 1][j0] = v;
                  }
               }
            }
         }

      // Generate reference model and apply it to S2:
      // * generate approximate Hessian Bk
      // * calculate inv(Bk)
      // * calculate inv(Bk)*S2, store to S2
         rmatrixsetlengthatleast(&sk, k, n, _state);
         rmatrixsetlengthatleast(&yk, k, n, _state);
         ae_matrix_set_length(&bk, n, n, _state);
         ae_vector_set_length(&bksk, n, _state);
         ae_vector_set_length(&tmp, n, _state);
         for (i = 0; i < k; i++) {
            ae_v_move(sk.xyR[i], 1, va.xyR[i], 1, n);
            v = ae_v_dotproduct(va.xyR[i], 1, sk.xyR[i], 1, n);
            v = v * vc.xR[i];
            for (j = 0; j < n; j++) {
               yk.xyR[i][j] = vd.xR[j] * sk.xyR[i][j] + va.xyR[i][j] * v;
            }
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               if (i == j) {
                  bk.xyR[i][i] = vd.xR[i];
               } else {
                  bk.xyR[i][j] = 0.0;
               }
            }
         }
         for (i = 0; i < k; i++) {
            theta = 0.0;
            for (j0 = 0; j0 < n; j0++) {
               bksk.xR[j0] = 0.0;
               for (j1 = 0; j1 < n; j1++) {
                  theta = theta + sk.xyR[i][j0] * bk.xyR[j0][j1] * sk.xyR[i][j1];
                  bksk.xR[j0] = bksk.xR[j0] + bk.xyR[j0][j1] * sk.xyR[i][j1];
               }
            }
            theta = 1 / theta;
            rho = ae_v_dotproduct(sk.xyR[i], 1, yk.xyR[i], 1, n);
            rho = 1 / rho;
            for (j0 = 0; j0 < n; j0++) {
               for (j1 = 0; j1 < n; j1++) {
                  bk.xyR[j0][j1] = bk.xyR[j0][j1] + rho * yk.xyR[i][j0] * yk.xyR[i][j1];
               }
            }
            for (j0 = 0; j0 < n; j0++) {
               for (j1 = 0; j1 < n; j1++) {
                  bk.xyR[j0][j1] = bk.xyR[j0][j1] - theta * bksk.xR[j0] * bksk.xR[j1];
               }
            }
         }
         rmatrixinverse(&bk, n, &j0, &rep, _state);
         for (i = 0; i < n; i++) {
            v = ae_v_dotproduct(bk.xyR[i], 1, s2.xR, 1, n);
            tmp.xR[i] = v;
         }
         for (i = 0; i < n; i++) {
            s2.xR[i] = tmp.xR[i];
         }

      // First test for non-zero V:
      // * apply preconditioner to X0
      // * compare reference model against implementation being tested
         inexactlbfgspreconditioner(&s0, n, &vd, &vc, &va, k, &buf, _state);
         for (i = 0; i < n; i++) {
            ae_set_error_flag(wereerrors, ae_fabs(s2.xR[i] - s0.xR[i], _state) > tolg, __FILE__, __LINE__, "testoptservunit.ap:236");
         }

      // Second test - N-K zero rows appended to V and rows are
      // randomly reordered. Doing so should not change result,
      // algorithm must be able to order rows according to second derivative
      // and skip zero updates.
         for (i = 0; i < n; i++) {
            i0 = i + hqrnduniformi(&rs, n - i, _state);
            v = vc.xR[i];
            vc.xR[i] = vc.xR[i0];
            vc.xR[i0] = v;
            for (j = 0; j < n; j++) {
               v = va.xyR[i][j];
               va.xyR[i][j] = va.xyR[i0][j];
               va.xyR[i0][j] = v;
            }
         }
         inexactlbfgspreconditioner(&s1, n, &vd, &vc, &va, n, &buf, _state);
         for (i = 0; i < n; i++) {
            ae_set_error_flag(wereerrors, ae_fabs(s2.xR[i] - s1.xR[i], _state) > tolg, __FILE__, __LINE__, "testoptservunit.ap:259");
         }
      }
   }

// Test for exact low-rank preconditioner.
//
// We generate QP problem 0.5*x'*H*x, with random H=D+V'*C*V.
// Different K's, from 0 to N, are tried. We test preconditioner
// code which uses Woodbury update against reference implementation
// which performs straightforward matrix inversion.
//
// For each K we perform two tests: first for KxN non-zero matrix V,
// second one for NxN matrix V with randomly appended N-K zero rows.
// Last test checks algorithm's ability to handle zero updates.
   tolg = 1.0E-9;
   for (n = 1; n <= 10; n++) {
      for (k = 0; k <= n; k++) {

      // Prepare problem:
      // * VD, VC, VA
      // * trial vector S (copies are stored to S0,S1,S2)
         ae_vector_set_length(&vd, n, _state);
         ae_vector_set_length(&s0, n, _state);
         ae_vector_set_length(&s1, n, _state);
         ae_vector_set_length(&s2, n, _state);
         for (i = 0; i < n; i++) {
            vd.xR[i] = ae_exp(hqrndnormal(&rs, _state), _state);
            s0.xR[i] = hqrndnormal(&rs, _state);
            s1.xR[i] = s0.xR[i];
            s2.xR[i] = s0.xR[i];
         }
         rmatrixrndcond(n, 1.0E2, &va, _state);
         rvectorsetlengthatleast(&vc, n, _state);
         for (i = 0; i < k; i++) {
            vc.xR[i] = ae_exp(hqrndnormal(&rs, _state), _state);
         }
         for (i = k; i < n; i++) {
            vc.xR[i] = 0.0;
            for (j = 0; j < n; j++) {
               va.xyR[i][j] = 0.0;
            }
         }

      // Generate reference model and apply it to S2
         ae_matrix_set_length(&bk, n, n, _state);
         ae_vector_set_length(&tmp, n, _state);
         for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
               if (i == j) {
                  v = vd.xR[i];
               } else {
                  v = 0.0;
               }
               for (j1 = 0; j1 < k; j1++) {
                  v = v + va.xyR[j1][i] * vc.xR[j1] * va.xyR[j1][j];
               }
               bk.xyR[i][j] = v;
            }
         }
         rmatrixinverse(&bk, n, &j, &rep, _state);
         ae_assert(j > 0, "Assertion failed", _state);
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + bk.xyR[i][j] * s2.xR[j];
            }
            tmp.xR[i] = v;
         }
         for (i = 0; i < n; i++) {
            s2.xR[i] = tmp.xR[i];
         }

      // First test for non-zero V:
      // * apply preconditioner to X0
      // * compare reference model against implementation being tested
         preparelowrankpreconditioner(&vd, &vc, &va, n, k, &lowrankbuf, _state);
         applylowrankpreconditioner(&s0, &lowrankbuf, _state);
         for (i = 0; i < n; i++) {
            ae_set_error_flag(wereerrors, ae_fabs(s2.xR[i] - s0.xR[i], _state) > tolg, __FILE__, __LINE__, "testoptservunit.ap:341");
         }

      // Second test - N-K zero rows appended to V and rows are
      // randomly reordered. Doing so should not change result,
      // algorithm must be able to order rows according to second derivative
      // and skip zero updates.
         for (i = 0; i < n; i++) {
            i0 = i + hqrnduniformi(&rs, n - i, _state);
            v = vc.xR[i];
            vc.xR[i] = vc.xR[i0];
            vc.xR[i0] = v;
            for (j = 0; j < n; j++) {
               v = va.xyR[i][j];
               va.xyR[i][j] = va.xyR[i0][j];
               va.xyR[i0][j] = v;
            }
         }
         preparelowrankpreconditioner(&vd, &vc, &va, n, n, &lowrankbuf, _state);
         applylowrankpreconditioner(&s1, &lowrankbuf, _state);
         for (i = 0; i < n; i++) {
            ae_set_error_flag(wereerrors, ae_fabs(s2.xR[i] - s1.xR[i], _state) > tolg, __FILE__, __LINE__, "testoptservunit.ap:365");
         }
      }
   }
   ae_frame_leave(_state);
}

// === minlbfgs testing unit ===
static const ae_int_t testminlbfgsunit_maxoptguardlevel = 1;
static void testminlbfgsunit_testfunc2(minlbfgsstate *state, ae_state *_state);
static void testminlbfgsunit_testfunc3(minlbfgsstate *state, ae_state *_state);
static void testminlbfgsunit_calciip2(minlbfgsstate *state, ae_int_t n, ae_state *_state);
static void testminlbfgsunit_testpreconditioning(bool *err, ae_state *_state);
static void testminlbfgsunit_testother(bool *err, ae_state *_state);
static void testminlbfgsunit_testoptguard(bool *wereerrors, ae_state *_state);
static void testminlbfgsunit_testoptguardc1test0reportfortask0(bool *err, optguardnonc1test0report *rep, RMatrix *a, ae_int_t n, ae_state *_state);
static void testminlbfgsunit_testoptguardc1test1reportfortask0(bool *err, optguardnonc1test1report *rep, RMatrix *a, ae_int_t n, ae_state *_state);

bool testminlbfgs(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   bool referror;
   bool nonconverror;
   bool eqerror;
   bool converror;
   bool crashtest;
   bool othererrors;
   bool restartserror;
   bool precerror;
   bool optguarderr;
   ae_int_t n;
   ae_int_t m;
   ae_int_t i;
   ae_int_t j;
   double v;
   ae_int_t maxits;
   double diffstep;
   ae_int_t dkind;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewVector(xlast, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(diagh, 0, DT_REAL, _state);
   NewObj(minlbfgsstate, state, _state);
   NewObj(minlbfgsreport, rep, _state);

   waserrors = false;
   referror = false;
   precerror = false;
   nonconverror = false;
   restartserror = false;
   eqerror = false;
   converror = false;
   crashtest = false;
   othererrors = false;
   testminlbfgsunit_testpreconditioning(&precerror, _state);
   testminlbfgsunit_testother(&othererrors, _state);

// Reference problem
   diffstep = 1.0E-6;
   for (dkind = 0; dkind <= 1; dkind++) {
      ae_vector_set_length(&x, 3, _state);
      n = 3;
      m = 2;
      x.xR[0] = 100 * ae_randomreal(_state) - 50;
      x.xR[1] = 100 * ae_randomreal(_state) - 50;
      x.xR[2] = 100 * ae_randomreal(_state) - 50;
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.0, 0.0, 0.0, 0, _state);
      while (minlbfgsiteration(&state, _state)) {
         if (state.needf || state.needfg) {
            state.f = ae_sqr(state.x.xR[0] - 2, _state) + ae_sqr(state.x.xR[1], _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
         }
         if (state.needfg) {
            state.g.xR[0] = 2 * (state.x.xR[0] - 2) + 2 * (state.x.xR[0] - state.x.xR[2]);
            state.g.xR[1] = 2 * state.x.xR[1];
            state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
         }
      }
      minlbfgsresults(&state, &x, &rep, _state);
      referror = (((referror || rep.terminationtype <= 0) || ae_fabs(x.xR[0] - 2, _state) > 0.001) || ae_fabs(x.xR[1], _state) > 0.001) || ae_fabs(x.xR[2] - 2, _state) > 0.001;
   }

// nonconvex problems with complex surface: we start from point with very small
// gradient, but we need ever smaller gradient in the next step due to
// Wolfe conditions.
   diffstep = 1.0E-6;
   for (dkind = 0; dkind <= 1; dkind++) {
      ae_vector_set_length(&x, 1, _state);
      n = 1;
      m = 1;
      v = -100.0;
      while (v < 0.1) {
         x.xR[0] = v;
         if (dkind == 0) {
            minlbfgscreate(n, m, &x, &state, _state);
         }
         if (dkind == 1) {
            minlbfgscreatef(n, m, &x, diffstep, &state, _state);
         }
         minlbfgssetcond(&state, 1.0E-9, 0.0, 0.0, 0, _state);
         while (minlbfgsiteration(&state, _state)) {
            if (state.needf || state.needfg) {
               state.f = ae_sqr(state.x.xR[0], _state) / (1 + ae_sqr(state.x.xR[0], _state));
            }
            if (state.needfg) {
               state.g.xR[0] = (2 * state.x.xR[0] * (1 + ae_sqr(state.x.xR[0], _state)) - ae_sqr(state.x.xR[0], _state) * 2 * state.x.xR[0]) / ae_sqr(1 + ae_sqr(state.x.xR[0], _state), _state);
            }
         }
         minlbfgsresults(&state, &x, &rep, _state);
         nonconverror = (nonconverror || rep.terminationtype <= 0) || ae_fabs(x.xR[0], _state) > 0.001;
         v = v + 0.1;
      }
   }

// F2 problem with restarts:
// * make several iterations and restart BEFORE termination
// * iterate and restart AFTER termination
//
// NOTE: step is bounded from above to avoid premature convergence
   diffstep = 1.0E-6;
   for (dkind = 0; dkind <= 1; dkind++) {
      ae_vector_set_length(&x, 3, _state);
      n = 3;
      m = 2;
      x.xR[0] = 10 + 10 * ae_randomreal(_state);
      x.xR[1] = 10 + 10 * ae_randomreal(_state);
      x.xR[2] = 10 + 10 * ae_randomreal(_state);
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetstpmax(&state, 0.1, _state);
      minlbfgssetcond(&state, 0.0000001, 0.0, 0.0, 0, _state);
      for (i = 0; i <= 10; i++) {
         if (!minlbfgsiteration(&state, _state)) {
            break;
         }
         testminlbfgsunit_testfunc2(&state, _state);
      }
      x.xR[0] = 10 + 10 * ae_randomreal(_state);
      x.xR[1] = 10 + 10 * ae_randomreal(_state);
      x.xR[2] = 10 + 10 * ae_randomreal(_state);
      minlbfgsrestartfrom(&state, &x, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc2(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      restartserror = (((restartserror || rep.terminationtype <= 0) || ae_fabs(x.xR[0] - ae_log(2.0, _state), _state) > 0.01) || ae_fabs(x.xR[1], _state) > 0.01) || ae_fabs(x.xR[2] - ae_log(2.0, _state), _state) > 0.01;
      x.xR[0] = 10 + 10 * ae_randomreal(_state);
      x.xR[1] = 10 + 10 * ae_randomreal(_state);
      x.xR[2] = 10 + 10 * ae_randomreal(_state);
      minlbfgsrestartfrom(&state, &x, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc2(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      restartserror = (((restartserror || rep.terminationtype <= 0) || ae_fabs(x.xR[0] - ae_log(2.0, _state), _state) > 0.01) || ae_fabs(x.xR[1], _state) > 0.01) || ae_fabs(x.xR[2] - ae_log(2.0, _state), _state) > 0.01;
   }

// Linear equations
   diffstep = 1.0E-6;
   for (n = 1; n <= 10; n++) {

   // Prepare task
      ae_matrix_set_length(&a, n - 1 + 1, n - 1 + 1, _state);
      ae_vector_set_length(&x, n - 1 + 1, _state);
      ae_vector_set_length(&xe, n - 1 + 1, _state);
      ae_vector_set_length(&b, n - 1 + 1, _state);
      for (i = 0; i < n; i++) {
         xe.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
         }
         a.xyR[i][i] = a.xyR[i][i] + 3 * ae_sign(a.xyR[i][i], _state);
      }
      for (i = 0; i < n; i++) {
         v = ae_v_dotproduct(a.xyR[i], 1, xe.xR, 1, n);
         b.xR[i] = v;
      }

   // Test different M/DKind
      for (m = 1; m <= n; m++) {
         for (dkind = 0; dkind <= 1; dkind++) {

         // Solve task
            for (i = 0; i < n; i++) {
               x.xR[i] = 2 * ae_randomreal(_state) - 1;
            }
            if (dkind == 0) {
               minlbfgscreate(n, m, &x, &state, _state);
            }
            if (dkind == 1) {
               minlbfgscreatef(n, m, &x, diffstep, &state, _state);
            }
            minlbfgssetcond(&state, 0.0, 0.0, 0.0, 0, _state);
            while (minlbfgsiteration(&state, _state)) {
               if (state.needf || state.needfg) {
                  state.f = 0.0;
               }
               if (state.needfg) {
                  for (i = 0; i < n; i++) {
                     state.g.xR[i] = 0.0;
                  }
               }
               for (i = 0; i < n; i++) {
                  v = ae_v_dotproduct(a.xyR[i], 1, state.x.xR, 1, n);
                  if (state.needf || state.needfg) {
                     state.f = state.f + ae_sqr(v - b.xR[i], _state);
                  }
                  if (state.needfg) {
                     for (j = 0; j < n; j++) {
                        state.g.xR[j] = state.g.xR[j] + 2 * (v - b.xR[i]) * a.xyR[i][j];
                     }
                  }
               }
            }
            minlbfgsresults(&state, &x, &rep, _state);
            eqerror = eqerror || rep.terminationtype <= 0;
            for (i = 0; i < n; i++) {
               eqerror = eqerror || ae_fabs(x.xR[i] - xe.xR[i], _state) > 0.001;
            }
         }
      }
   }

// Testing convergence properties
   diffstep = 1.0E-6;
   for (dkind = 0; dkind <= 1; dkind++) {
      ae_vector_set_length(&x, 3, _state);
      n = 3;
      m = 2;
      for (i = 0; i <= 2; i++) {
         x.xR[i] = 6 * ae_randomreal(_state) - 3;
      }
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.001, 0.0, 0.0, 0, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc3(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      converror = converror || rep.terminationtype != 4;
      for (i = 0; i <= 2; i++) {
         x.xR[i] = 6 * ae_randomreal(_state) - 3;
      }
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.0, 0.001, 0.0, 0, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc3(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      converror = converror || rep.terminationtype != 1;
      for (i = 0; i <= 2; i++) {
         x.xR[i] = 6 * ae_randomreal(_state) - 3;
      }
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.0, 0.0, 0.001, 0, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc3(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      converror = converror || rep.terminationtype != 2;
      for (i = 0; i <= 2; i++) {
         x.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.0, 0.0, 0.0, 10, _state);
      while (minlbfgsiteration(&state, _state)) {
         testminlbfgsunit_testfunc3(&state, _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      converror = (converror || rep.terminationtype != 5) || rep.iterationscount != 10;
   }

// Crash test: too many iterations on a simple tasks
// May fail when encounter zero step, underflow or something like that
   ae_vector_set_length(&x, 2 + 1, _state);
   n = 3;
   m = 2;
   maxits = 10000;
   for (i = 0; i <= 2; i++) {
      x.xR[i] = 6 * ae_randomreal(_state) - 3;
   }
   minlbfgscreate(n, m, &x, &state, _state);
   minlbfgssetcond(&state, 0.0, 0.0, 0.0, maxits, _state);
   while (minlbfgsiteration(&state, _state)) {
      state.f = ae_sqr(ae_exp(state.x.xR[0], _state) - 2, _state) + ae_sqr(state.x.xR[1], _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
      state.g.xR[0] = 2 * (ae_exp(state.x.xR[0], _state) - 2) * ae_exp(state.x.xR[0], _state) + 2 * (state.x.xR[0] - state.x.xR[2]);
      state.g.xR[1] = 2 * state.x.xR[1];
      state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
   }
   minlbfgsresults(&state, &x, &rep, _state);
   crashtest = crashtest || rep.terminationtype <= 0;

//  Test OptGuard
   optguarderr = false;
   testminlbfgsunit_testoptguard(&optguarderr, _state);

// end
   waserrors = (((((((referror || nonconverror) || eqerror) || converror) || crashtest) || othererrors) || restartserror) || precerror) || optguarderr;
   if (!silent) {
      printf("TESTING L-BFGS OPTIMIZATION\n");
      printf("REFERENCE PROBLEM:                        ");
      if (referror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("NON-CONVEX PROBLEM:                       ");
      if (nonconverror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("LINEAR EQUATIONS:                         ");
      if (eqerror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("RESTARTS:                                 ");
      if (restartserror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("PRECONDITIONER:                           ");
      if (precerror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("CONVERGENCE PROPERTIES:                   ");
      if (converror) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("CRASH TEST:                               ");
      if (crashtest) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("OTHER PROPERTIES:                         ");
      if (othererrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("OPTGUARD:                                 ");
      if (optguarderr) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// Calculate test function #2
//
// Simple variation of #1, much more nonlinear, which makes unlikely premature
// convergence of algorithm .
static void testminlbfgsunit_testfunc2(minlbfgsstate *state, ae_state *_state) {

   if (state->x.xR[0] < 100.0) {
      if (state->needf || state->needfg) {
         state->f = ae_sqr(ae_exp(state->x.xR[0], _state) - 2, _state) + ae_sqr(ae_sqr(state->x.xR[1], _state), _state) + ae_sqr(state->x.xR[2] - state->x.xR[0], _state);
      }
      if (state->needfg) {
         state->g.xR[0] = 2 * (ae_exp(state->x.xR[0], _state) - 2) * ae_exp(state->x.xR[0], _state) + 2 * (state->x.xR[0] - state->x.xR[2]);
         state->g.xR[1] = 4 * state->x.xR[1] * ae_sqr(state->x.xR[1], _state);
         state->g.xR[2] = 2 * (state->x.xR[2] - state->x.xR[0]);
      }
   } else {
      if (state->needf || state->needfg) {
         state->f = ae_sqrt(ae_maxrealnumber, _state);
      }
      if (state->needfg) {
         state->g.xR[0] = ae_sqrt(ae_maxrealnumber, _state);
         state->g.xR[1] = 0.0;
         state->g.xR[2] = 0.0;
      }
   }
}

// Calculate test function #3
//
// Simple variation of #1, much more nonlinear, with non-zero value at minimum.
// It achieve two goals:
// * makes unlikely premature convergence of algorithm .
// * solves some issues with EpsF stopping condition which arise when
//   F(minimum) is zero
//
static void testminlbfgsunit_testfunc3(minlbfgsstate *state, ae_state *_state) {
   double s;

   s = 0.001;
   if (state->x.xR[0] < 100.0) {
      if (state->needf || state->needfg) {
         state->f = ae_sqr(ae_exp(state->x.xR[0], _state) - 2, _state) + ae_sqr(ae_sqr(state->x.xR[1], _state) + s, _state) + ae_sqr(state->x.xR[2] - state->x.xR[0], _state);
      }
      if (state->needfg) {
         state->g.xR[0] = 2 * (ae_exp(state->x.xR[0], _state) - 2) * ae_exp(state->x.xR[0], _state) + 2 * (state->x.xR[0] - state->x.xR[2]);
         state->g.xR[1] = 2 * (ae_sqr(state->x.xR[1], _state) + s) * 2 * state->x.xR[1];
         state->g.xR[2] = 2 * (state->x.xR[2] - state->x.xR[0]);
      }
   } else {
      if (state->needf || state->needfg) {
         state->f = ae_sqrt(ae_maxrealnumber, _state);
      }
      if (state->needfg) {
         state->g.xR[0] = ae_sqrt(ae_maxrealnumber, _state);
         state->g.xR[1] = 0.0;
         state->g.xR[2] = 0.0;
      }
   }
}

// Calculate test function IIP2
//
// f(x) = sum( ((i*i+1)*x[i])^2, i=0..N-1)
//
// It has high condition number which makes fast convergence unlikely without
// good preconditioner.
//
static void testminlbfgsunit_calciip2(minlbfgsstate *state, ae_int_t n, ae_state *_state) {
   ae_int_t i;

   if (state->needf || state->needfg) {
      state->f = 0.0;
   }
   for (i = 0; i < n; i++) {
      if (state->needf || state->needfg) {
         state->f = state->f + ae_sqr((double)(i * i + 1), _state) * ae_sqr(state->x.xR[i], _state);
      }
      if (state->needfg) {
         state->g.xR[i] = ae_sqr((double)(i * i + 1), _state) * 2 * state->x.xR[i];
      }
   }
}

// This function tests preconditioning
//
// On failure sets Err to True (leaves it unchanged otherwise)
static void testminlbfgsunit_testpreconditioning(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t n;
   ae_int_t m;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t cntb1;
   ae_int_t cntb2;
   ae_int_t cntg1;
   ae_int_t cntg2;
   ae_int_t pkind;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlbfgsstate, state, _state);
   NewObj(minlbfgsreport, rep, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(diagh, 0, DT_REAL, _state);

   m = 1;
   k = 50;

// Preconditioner test1.
//
// If
// * B1 is default preconditioner
// * B2 is Cholesky preconditioner with unit diagonal
// * G1 is Cholesky preconditioner based on exact Hessian with perturbations
// * G2 is diagonal precomditioner based on approximate diagonal of Hessian matrix
// then "bad" preconditioners (B1/B2/..) are worse than "good" ones (G1/G2/..).
// "Worse" means more iterations to converge.
//
// We test it using f(x) = sum( ((i*i+1)*x[i])^2, i=0..N-1) and L-BFGS
// optimizer with deliberately small M=1.
//
// N        - problem size
// PKind    - zero for upper triangular preconditioner, one for lower triangular.
// K        - number of repeated passes (should be large enough to average out random factors)
   for (n = 10; n <= 15; n++) {
      pkind = ae_randominteger(2, _state);
      ae_vector_set_length(&x, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 0.0;
      }
      minlbfgscreate(n, m, &x, &state, _state);

   // Test it with default preconditioner
      minlbfgssetprecdefault(&state, _state);
      cntb1 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntb1 = cntb1 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }

   // Test it with unit preconditioner
      ae_matrix_set_length(&a, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (i == j) {
               a.xyR[i][i] = 1.0;
            } else {
               a.xyR[i][j] = 0.0;
            }
         }
      }
      minlbfgssetpreccholesky(&state, &a, pkind == 0, _state);
      cntb2 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntb2 = cntb2 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }

   // Test it with perturbed Hessian preconditioner
      ae_matrix_set_length(&a, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            if (i == j) {
               a.xyR[i][i] = (i * i + 1) * (0.8 + 0.4 * ae_randomreal(_state));
            } else {
               if ((pkind == 0 && j > i) || (pkind == 1 && j < i)) {
                  a.xyR[i][j] = 0.1 * ae_randomreal(_state) - 0.05;
               } else {
                  a.xyR[i][j] = _state->v_nan;
               }
            }
         }
      }
      minlbfgssetpreccholesky(&state, &a, pkind == 0, _state);
      cntg1 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntg1 = cntg1 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }

   // Test it with perturbed diagonal preconditioner
      ae_vector_set_length(&diagh, n, _state);
      for (i = 0; i < n; i++) {
         diagh.xR[i] = 2 * ae_sqr((double)(i * i + 1), _state) * (0.8 + 0.4 * ae_randomreal(_state));
      }
      minlbfgssetprecdiag(&state, &diagh, _state);
      cntg2 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntg2 = cntg2 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }

   // Compare
      *err = *err || cntb1 < cntg1;
      *err = *err || cntb2 < cntg1;
      *err = *err || cntb1 < cntg2;
      *err = *err || cntb2 < cntg2;
   }

// Preconditioner test 2.
//
// If
// * B2 is default preconditioner with non-unit scale S[i]=1/sqrt(h[i])
// * G2 is scale-based preconditioner with non-unit scale S[i]=1/sqrt(h[i])
// then B2 is worse than G2.
// "Worse" means more iterations to converge.
   for (n = 10; n <= 15; n++) {
      ae_vector_set_length(&x, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 0.0;
      }
      minlbfgscreate(n, m, &x, &state, _state);
      ae_vector_set_length(&s, n, _state);
      for (i = 0; i < n; i++) {
         s.xR[i] = 1 / ae_sqrt(2 * ae_pow((double)(i * i + 1), 2.0, _state) * (0.8 + 0.4 * ae_randomreal(_state)), _state);
      }
      minlbfgssetprecdefault(&state, _state);
      minlbfgssetscale(&state, &s, _state);
      cntb2 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntb2 = cntb2 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }
      minlbfgssetprecscale(&state, _state);
      minlbfgssetscale(&state, &s, _state);
      cntg2 = 0;
      for (pass = 0; pass < k; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            testminlbfgsunit_calciip2(&state, n, _state);
         }
         minlbfgsresults(&state, &x, &rep, _state);
         cntg2 = cntg2 + rep.iterationscount;
         *err = *err || rep.terminationtype <= 0;
      }
      *err = *err || cntb2 < cntg2;
   }
   ae_frame_leave(_state);
}

// This function tests other properties
//
// On failure sets Err to True (leaves it unchanged otherwise)
static void testminlbfgsunit_testother(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   bool hasxlast;
   double lastscaledstep;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double fprev;
   double xprev;
   double v;
   double stpmax;
   double tmpeps;
   double epsg;
   ae_int_t pkind;
   ae_int_t ckind;
   ae_int_t mkind;
   double vc;
   double vm;
   double diffstep;
   ae_int_t dkind;
   bool wasf;
   bool wasfg;
   double r;
   ae_int_t spoiliteration;
   ae_int_t stopiteration;
   ae_int_t spoilvar;
   double spoilval;
   double ss;
   bool terminationrequested;
   ae_int_t pass;
   ae_int_t stopcallidx;
   ae_int_t callidx;
   ae_int_t maxits;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(a, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewVector(h, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(xlast, 0, DT_REAL, _state);
   NewMatrix(fulla, 0, 0, DT_REAL, _state);
   NewObj(minlbfgsstate, state, _state);
   NewObj(minlbfgsreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Test reports (F should form monotone sequence)
   n = 50;
   m = 2;
   ae_vector_set_length(&x, n, _state);
   ae_vector_set_length(&xlast, n, _state);
   for (i = 0; i < n; i++) {
      x.xR[i] = 1.0;
   }
   minlbfgscreate(n, m, &x, &state, _state);
   minlbfgssetcond(&state, 0.0, 0.0, 0.0, 100, _state);
   minlbfgssetxrep(&state, true, _state);
   fprev = ae_maxrealnumber;
   while (minlbfgsiteration(&state, _state)) {
      if (state.needfg) {
         state.f = 0.0;
         for (i = 0; i < n; i++) {
            state.f = state.f + ae_sqr((1 + i) * state.x.xR[i], _state);
            state.g.xR[i] = 2 * (1 + i) * state.x.xR[i];
         }
      }
      if (state.xupdated) {
         ae_set_error_flag(err, state.f > fprev, __FILE__, __LINE__, "testminlbfgsunit.ap:758");
         if (fprev == ae_maxrealnumber) {
            for (i = 0; i < n; i++) {
               ae_set_error_flag(err, state.x.xR[i] != x.xR[i], __FILE__, __LINE__, "testminlbfgsunit.ap:761");
            }
         }
         fprev = state.f;
         ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
      }
   }
   minlbfgsresults(&state, &x, &rep, _state);
   for (i = 0; i < n; i++) {
      ae_set_error_flag(err, x.xR[i] != xlast.xR[i], __FILE__, __LINE__, "testminlbfgsunit.ap:768");
   }

// Test differentiation vs. analytic gradient
// (first one issues NeedF requests, second one issues NeedFG requests)
   n = 50;
   m = 5;
   diffstep = 1.0E-6;
   for (dkind = 0; dkind <= 1; dkind++) {
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&xlast, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 1.0;
      }
      if (dkind == 0) {
         minlbfgscreate(n, m, &x, &state, _state);
      }
      if (dkind == 1) {
         minlbfgscreatef(n, m, &x, diffstep, &state, _state);
      }
      minlbfgssetcond(&state, 0.0, 0.0, 0.0, n / 2, _state);
      wasf = false;
      wasfg = false;
      while (minlbfgsiteration(&state, _state)) {
         if (state.needf || state.needfg) {
            state.f = 0.0;
         }
         for (i = 0; i < n; i++) {
            if (state.needf || state.needfg) {
               state.f = state.f + ae_sqr((1 + i) * state.x.xR[i], _state);
            }
            if (state.needfg) {
               state.g.xR[i] = 2 * (1 + i) * state.x.xR[i];
            }
         }
         wasf = wasf || state.needf;
         wasfg = wasfg || state.needfg;
      }
      minlbfgsresults(&state, &x, &rep, _state);
      if (dkind == 0) {
         ae_set_error_flag(err, wasf || !wasfg, __FILE__, __LINE__, "testminlbfgsunit.ap:806");
      }
      if (dkind == 1) {
         ae_set_error_flag(err, !wasf || wasfg, __FILE__, __LINE__, "testminlbfgsunit.ap:808");
      }
   }

// Test that numerical differentiation uses scaling.
//
// In order to test that we solve simple optimization
// problem: min(x^2) with initial x equal to 0.0.
//
// We choose random DiffStep and S, then we check that
// optimizer evaluates function at +-DiffStep*S only.
   ae_vector_set_length(&x, 1, _state);
   ae_vector_set_length(&s, 1, _state);
   diffstep = ae_randomreal(_state) * 1.0E-6;
   s.xR[0] = ae_exp(ae_randomreal(_state) * 4 - 2, _state);
   x.xR[0] = 0.0;
   minlbfgscreatef(1, 1, &x, diffstep, &state, _state);
   minlbfgssetcond(&state, 1.0E-6, 0.0, 0.0, 0, _state);
   minlbfgssetscale(&state, &s, _state);
   v = 0.0;
   while (minlbfgsiteration(&state, _state)) {
      state.f = ae_sqr(state.x.xR[0], _state);
      v = ae_maxreal(v, ae_fabs(state.x.xR[0], _state), _state);
   }
   minlbfgsresults(&state, &x, &rep, _state);
   r = v / (s.xR[0] * diffstep);
   ae_set_error_flag(err, ae_fabs(ae_log(r, _state), _state) > ae_log(1 + 1000 * ae_machineepsilon, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:836");

// test maximum step
   n = 1;
   m = 1;
   ae_vector_set_length(&x, n, _state);
   x.xR[0] = 100.0;
   stpmax = 0.05 + 0.05 * ae_randomreal(_state);
   minlbfgscreate(n, m, &x, &state, _state);
   minlbfgssetcond(&state, 1.0E-9, 0.0, 0.0, 0, _state);
   minlbfgssetstpmax(&state, stpmax, _state);
   minlbfgssetxrep(&state, true, _state);
   xprev = x.xR[0];
   while (minlbfgsiteration(&state, _state)) {
      if (state.needfg) {
         state.f = ae_exp(state.x.xR[0], _state) + ae_exp(-state.x.xR[0], _state);
         state.g.xR[0] = ae_exp(state.x.xR[0], _state) - ae_exp(-state.x.xR[0], _state);
         ae_set_error_flag(err, ae_fabs(state.x.xR[0] - xprev, _state) > (1 + ae_sqrt(ae_machineepsilon, _state)) * stpmax, __FILE__, __LINE__, "testminlbfgsunit.ap:858");
      }
      if (state.xupdated) {
         ae_set_error_flag(err, ae_fabs(state.x.xR[0] - xprev, _state) > (1 + ae_sqrt(ae_machineepsilon, _state)) * stpmax, __FILE__, __LINE__, "testminlbfgsunit.ap:862");
         xprev = state.x.xR[0];
      }
   }

// Test correctness of the scaling:
// * initial point is random point from [+1,+2]^N
// * f(x) = SUM(A[i]*x[i]^4), C[i] is random from [0.01,100]
// * we use random scaling matrix
// * we test different variants of the preconditioning:
//   0) unit preconditioner
//   1) random diagonal from [0.01,100]
//   2) scale preconditioner
// * we set stringent stopping conditions (we try EpsG and EpsX)
// * and we test that in the extremum stopping conditions are
//   satisfied subject to the current scaling coefficients.
   tmpeps = 1.0E-10;
   m = 1;
   for (n = 1; n <= 10; n++) {
      for (pkind = 0; pkind <= 2; pkind++) {
         ae_vector_set_length(&x, n, _state);
         ae_vector_set_length(&xlast, n, _state);
         ae_vector_set_length(&a, n, _state);
         ae_vector_set_length(&s, n, _state);
         ae_vector_set_length(&h, n, _state);
         for (i = 0; i < n; i++) {
            x.xR[i] = ae_randomreal(_state) + 1;
            a.xR[i] = ae_exp(ae_log(100.0, _state) * (2 * ae_randomreal(_state) - 1), _state);
            s.xR[i] = ae_exp(ae_log(100.0, _state) * (2 * ae_randomreal(_state) - 1), _state);
            h.xR[i] = ae_exp(ae_log(100.0, _state) * (2 * ae_randomreal(_state) - 1), _state);
         }
         minlbfgscreate(n, m, &x, &state, _state);
         minlbfgssetscale(&state, &s, _state);
         minlbfgssetxrep(&state, true, _state);
         if (pkind == 1) {
            minlbfgssetprecdiag(&state, &h, _state);
         }
         if (pkind == 2) {
            minlbfgssetprecscale(&state, _state);
         }
      // Test gradient-based stopping condition
         for (i = 0; i < n; i++) {
            x.xR[i] = ae_randomreal(_state) + 1;
         }
         minlbfgssetcond(&state, tmpeps, 0.0, 0.0, 0, _state);
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            if (state.needfg) {
               state.f = 0.0;
               for (i = 0; i < n; i++) {
                  state.f = state.f + a.xR[i] * ae_pow(state.x.xR[i], 4.0, _state);
                  state.g.xR[i] = 4 * a.xR[i] * ae_pow(state.x.xR[i], 3.0, _state);
               }
            }
         }
         minlbfgsresults(&state, &x, &rep, _state);
         if (rep.terminationtype <= 0) {
            *err = true;
            ae_frame_leave(_state);
            return;
         }
         v = 0.0;
         for (i = 0; i < n; i++) {
            v = v + ae_sqr(s.xR[i] * 4 * a.xR[i] * ae_pow(x.xR[i], 3.0, _state), _state);
         }
         v = ae_sqrt(v, _state);
         *err = *err || v > tmpeps;

      // Test step-based stopping condition
         for (i = 0; i < n; i++) {
            x.xR[i] = ae_randomreal(_state) + 1;
         }
         hasxlast = false;
         lastscaledstep = ae_maxrealnumber;
         minlbfgssetcond(&state, 0.0, 0.0, tmpeps, 0, _state);
         minlbfgsrestartfrom(&state, &x, _state);
         while (minlbfgsiteration(&state, _state)) {
            if (state.needfg) {
               state.f = 0.0;
               for (i = 0; i < n; i++) {
                  state.f = state.f + a.xR[i] * ae_pow(state.x.xR[i], 4.0, _state);
                  state.g.xR[i] = 4 * a.xR[i] * ae_pow(state.x.xR[i], 3.0, _state);
               }
            }
            if (state.xupdated) {
               if (hasxlast) {
                  lastscaledstep = 0.0;
                  for (i = 0; i < n; i++) {
                     lastscaledstep = lastscaledstep + ae_sqr(state.x.xR[i] - xlast.xR[i], _state) / ae_sqr(s.xR[i], _state);
                  }
                  lastscaledstep = ae_sqrt(lastscaledstep, _state);
               } else {
                  lastscaledstep = 0.0;
               }
               ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
               hasxlast = true;
            }
         }
         minlbfgsresults(&state, &x, &rep, _state);
         if (rep.terminationtype <= 0) {
            *err = true;
            ae_frame_leave(_state);
            return;
         }
         *err = *err || lastscaledstep > tmpeps;
      }
   }

// Check correctness of the "trimming".
//
// Trimming is a technique which is used to help algorithm
// cope with unbounded functions. In order to check this
// technique we will try to solve following optimization
// problem:
//
//     min f(x) subject to no constraints on X
//            { 1/(1-x) + 1/(1+x) + c*x, if -0.999999<x<0.999999
//     f(x) = {
//            { M, if x <= -0.999999 or x >= 0.999999
//
// where c is either 1.0 or 1.0E+6, M is either 1.0E8, 1.0E20 or +INF
// (we try different combinations)
   for (ckind = 0; ckind <= 1; ckind++) {
      for (mkind = 0; mkind <= 2; mkind++) {

      // Choose c and M
         vc = 1.0;
         vm = 1.0;
         if (ckind == 0) {
            vc = 1.0;
         }
         if (ckind == 1) {
            vc = 1.0E+6;
         }
         if (mkind == 0) {
            vm = 1.0E+8;
         }
         if (mkind == 1) {
            vm = 1.0E+20;
         }
         if (mkind == 2) {
            vm = _state->v_posinf;
         }
      // Create optimizer, solve optimization problem
         epsg = 1.0E-6 * vc;
         ae_vector_set_length(&x, 1, _state);
         x.xR[0] = 0.0;
         minlbfgscreate(1, 1, &x, &state, _state);
         minlbfgssetcond(&state, epsg, 0.0, 0.0, 0, _state);
         while (minlbfgsiteration(&state, _state)) {
            if (state.needfg) {
               if (-0.999999 < state.x.xR[0] && state.x.xR[0] < 0.999999) {
                  state.f = 1 / (1 - state.x.xR[0]) + 1 / (1 + state.x.xR[0]) + vc * state.x.xR[0];
                  state.g.xR[0] = 1 / ae_sqr(1 - state.x.xR[0], _state) - 1 / ae_sqr(1 + state.x.xR[0], _state) + vc;
               } else {
                  state.f = vm;
               }
            }
         }
         minlbfgsresults(&state, &x, &rep, _state);
         if (rep.terminationtype <= 0) {
            *err = true;
            ae_frame_leave(_state);
            return;
         }
         *err = *err || ae_fabs(1 / ae_sqr(1 - x.xR[0], _state) - 1 / ae_sqr(1 + x.xR[0], _state) + vc, _state) > epsg;
      }
   }

// Test integrity checks for NAN/INF:
// * algorithm solves optimization problem, which is normal for some time (quadratic)
// * after 5-th step we choose random component of gradient and consistently spoil
//   it by NAN or INF.
// * we check that correct termination code is returned (-8)
   n = 100;
   for (pass = 1; pass <= 10; pass++) {
      spoiliteration = 5;
      stopiteration = 8;
      if (hqrndnormal(&rs, _state) > 0.0) {

      // Gradient can be spoiled by +INF, -INF, NAN
         spoilvar = hqrnduniformi(&rs, n, _state);
         i = hqrnduniformi(&rs, 3, _state);
         spoilval = _state->v_nan;
         if (i == 0) {
            spoilval = _state->v_neginf;
         }
         if (i == 1) {
            spoilval = _state->v_posinf;
         }
      } else {

      // Function value can be spoiled only by NAN
      // (+INF can be recognized as legitimate value during optimization)
         spoilvar = -1;
         spoilval = _state->v_nan;
      }
      spdmatrixrndcond(n, 1.0E5, &fulla, _state);
      ae_vector_set_length(&b, n, _state);
      ae_vector_set_length(&x0, n, _state);
      for (i = 0; i < n; i++) {
         b.xR[i] = hqrndnormal(&rs, _state);
         x0.xR[i] = hqrndnormal(&rs, _state);
      }
      minlbfgscreate(n, 1, &x0, &state, _state);
      minlbfgssetcond(&state, 0.0, 0.0, 0.0, stopiteration, _state);
      minlbfgssetxrep(&state, true, _state);
      k = -1;
      while (minlbfgsiteration(&state, _state)) {
         if (state.needfg) {
            state.f = 0.0;
            for (i = 0; i < n; i++) {
               state.f = state.f + b.xR[i] * state.x.xR[i];
               state.g.xR[i] = b.xR[i];
               for (j = 0; j < n; j++) {
                  state.f = state.f + 0.5 * state.x.xR[i] * fulla.xyR[i][j] * state.x.xR[j];
                  state.g.xR[i] = state.g.xR[i] + fulla.xyR[i][j] * state.x.xR[j];
               }
            }
            if (k >= spoiliteration) {
               if (spoilvar < 0) {
                  state.f = spoilval;
               } else {
                  state.g.xR[spoilvar] = spoilval;
               }
            }
            continue;
         }
         if (state.xupdated) {
            inc(&k, _state);
            continue;
         }
         ae_assert(false, "Assertion failed", _state);
      }
      minlbfgsresults(&state, &x1, &rep, _state);
      ae_set_error_flag(err, rep.terminationtype != -8, __FILE__, __LINE__, "testminlbfgsunit.ap:1123");
   }

// Check algorithm ability to handle request for termination:
// * to terminate with correct return code = 8
// * to return point which was "current" at the moment of termination
   for (pass = 1; pass <= 50; pass++) {
      n = 3;
      ss = 100.0;
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&xlast, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 6 + ae_randomreal(_state);
      }
      stopcallidx = ae_randominteger(20, _state);
      maxits = 25;
      minlbfgscreate(n, 1, &x, &state, _state);
      minlbfgssetcond(&state, 0.0, 0.0, 0.0, maxits, _state);
      minlbfgssetxrep(&state, true, _state);
      callidx = 0;
      terminationrequested = false;
      ae_v_move(xlast.xR, 1, x.xR, 1, n);
      while (minlbfgsiteration(&state, _state)) {
         if (state.needfg) {
            state.f = ss * ae_sqr(ae_exp(state.x.xR[0], _state) - 2, _state) + ae_sqr(state.x.xR[1], _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
            state.g.xR[0] = 2 * ss * (ae_exp(state.x.xR[0], _state) - 2) * ae_exp(state.x.xR[0], _state) + 2 * (state.x.xR[2] - state.x.xR[0]) * (-1);
            state.g.xR[1] = 2 * state.x.xR[1];
            state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
            if (callidx == stopcallidx) {
               minlbfgsrequesttermination(&state, _state);
               terminationrequested = true;
            }
            inc(&callidx, _state);
            continue;
         }
         if (state.xupdated) {
            if (!terminationrequested) {
               ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
            }
            continue;
         }
         ae_assert(false, "Assertion failed", _state);
      }
      minlbfgsresults(&state, &x, &rep, _state);
      ae_set_error_flag(err, rep.terminationtype != 8, __FILE__, __LINE__, "testminlbfgsunit.ap:1173");
      for (i = 0; i < n; i++) {
         ae_set_error_flag(err, x.xR[i] != xlast.xR[i], __FILE__, __LINE__, "testminlbfgsunit.ap:1175");
      }
   }
   ae_frame_leave(_state);
}

// This function tests OptGuard
//
// On failure sets error flag.
static void testminlbfgsunit_testoptguard(bool *wereerrors, ae_state *_state) {
   ae_frame _frame_block;
   double v;
   ae_int_t i;
   ae_int_t j;
   ae_int_t n;
   ae_int_t m;
   double diffstep;
   ae_int_t pass;
   ae_int_t defecttype;
   bool failed;
   ae_int_t passcount;
   ae_int_t maxfails;
   ae_int_t failurecounter;
   ae_int_t maxc1test0fails;
   ae_int_t maxc1test1fails;
   ae_int_t c1test0fails;
   ae_int_t c1test1fails;
   double avgstr0len;
   double avglng0len;
   double avgstr1len;
   double avglng1len;
   ae_int_t varidx;
   ae_int_t skind;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewObj(minlbfgsstate, state, _state);
   NewObj(minlbfgsreport, rep, _state);
   NewObj(optguardreport, ogrep, _state);
   NewObj(optguardnonc1test0report, ognonc1test0strrep, _state);
   NewObj(optguardnonc1test0report, ognonc1test0lngrep, _state);
   NewObj(optguardnonc1test1report, ognonc1test1strrep, _state);
   NewObj(optguardnonc1test1report, ognonc1test1lngrep, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewMatrix(jactrue, 0, 0, DT_REAL, _state);
   NewMatrix(jacdefect, 0, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);

// Check that gradient verification is disabled by default:
// gradient checking for bad problem must return nothing
   n = 10;
   ae_vector_set_length(&x0, n, _state);
   for (i = 0; i < n; i++) {
      x0.xR[i] = 1.0 + 0.1 * i;
   }
   spdmatrixrndcond(n, 1.0E3, &a, _state);
   spdmatrixrndcond(n, 1.0E3, &a1, _state);
   minlbfgscreate(n, 3, &x0, &state, _state);
   minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 10, _state);
   while (minlbfgsiteration(&state, _state)) {
      if (state.needfg) {
         state.f = 0.0;
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + state.x.xR[j] * a.xyR[i][j];
            }
            state.f = state.f + 0.5 * state.x.xR[i] * v;
         }
         for (i = 0; i < n; i++) {
            state.g.xR[i] = 0.0;
         }
         continue;
      }
      ae_assert(false, "Assertion failed", _state);
   }
   minlbfgsresults(&state, &x1, &rep, _state);
   minlbfgsoptguardresults(&state, &ogrep, _state);
   ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1248");
   ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1249");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(wereerrors, ogrep.badgradxbase.cnt != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1252");
   ae_set_error_flag(wereerrors, ogrep.badgraduser.rows != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1253");
   ae_set_error_flag(wereerrors, ogrep.badgraduser.cols != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1254");
   ae_set_error_flag(wereerrors, ogrep.badgradnum.rows != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1255");
   ae_set_error_flag(wereerrors, ogrep.badgradnum.cols != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1256");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(wereerrors, ogrep.badgradsuspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1259");
   ae_set_error_flag(wereerrors, ogrep.badgradfidx != -1, __FILE__, __LINE__, "testminlbfgsunit.ap:1260");
   ae_set_error_flag(wereerrors, ogrep.badgradvidx != -1, __FILE__, __LINE__, "testminlbfgsunit.ap:1261");

// Test that C0/C1 continuity monitoring is disabled by default;
// we solve nonsmooth problem and test that nothing is returned
// by OptGuard.
   n = 10;
   ae_vector_set_length(&x0, n, _state);
   for (i = 0; i < n; i++) {
      x0.xR[i] = hqrndnormal(&rs, _state);
   }
   ae_matrix_set_length(&a, n, n, _state);
   for (i = 0; i < n; i++) {
      for (j = 0; j < n; j++) {
         a.xyR[i][j] = hqrndnormal(&rs, _state);
      }
   }
   minlbfgscreate(n, 2, &x0, &state, _state);
   minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 50, _state);
   while (minlbfgsiteration(&state, _state)) {
      if (state.needfg) {
         state.f = 0.0;
         for (i = 0; i < n; i++) {
            state.g.xR[i] = 0.0;
         }
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + state.x.xR[j] * a.xyR[i][j];
            }
            state.f = state.f + ae_fabs(v, _state);
            v = (double)ae_sign(v, _state);
            for (j = 0; j < n; j++) {
               state.g.xR[j] = state.g.xR[j] + v * a.xyR[i][j];
            }
         }
         continue;
      }
      ae_assert(false, "Assertion failed", _state);
   }
   minlbfgsresults(&state, &x1, &rep, _state);
   minlbfgsoptguardresults(&state, &ogrep, _state);
   ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1301");
   ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1302");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(wereerrors, ogrep.nonc0suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1305");
   ae_set_error_flag(wereerrors, ogrep.nonc1suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1306");
   ae_set_error_flag(wereerrors, !optguardallclear(&ogrep, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1307");
   ae_set_error_flag(wereerrors, ogrep.nonc0fidx >= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1308");
   ae_set_error_flag(wereerrors, ogrep.nonc1fidx >= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1309");
   ae_set_error_flag(wereerrors, ogrep.nonc1test0positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1310");
   ae_set_error_flag(wereerrors, ogrep.nonc1test1positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1311");

// Test gradient checking functionality, try various
// defect types:
// * accidental zeroing of some gradient component
// * accidental addition of 1.0 to some component
// * accidental multiplication by 2.0
// Try distorting both target and constraints.
   diffstep = 0.001;
   n = 10;
   m = 2;
   for (skind = 0; skind <= 1; skind++) {
      for (defecttype = -1; defecttype <= 2; defecttype++) {
         varidx = hqrnduniformi(&rs, n, _state);
         ae_vector_set_length(&x0, n, _state);
         ae_vector_set_length(&s, n, _state);
         for (i = 0; i < n; i++) {
            s.xR[i] = ae_pow(10.0, skind * (30 * hqrnduniformr(&rs, _state) - 15), _state);
            x0.xR[i] = (1.0 + 0.1 * i) * s.xR[i];
         }
         spdmatrixrndcond(n, 1.0E3, &a, _state);
         spdmatrixrndcond(n, 1.0E3, &a1, _state);
         minlbfgscreate(n, m, &x0, &state, _state);
         minlbfgsoptguardgradient(&state, diffstep, _state);
         minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 10, _state);
         minlbfgssetscale(&state, &s, _state);
         while (minlbfgsiteration(&state, _state)) {
            if (state.needfg) {
               state.f = 0.0;
               for (i = 0; i < n; i++) {
                  v = 0.0;
                  for (j = 0; j < n; j++) {
                     v = v + state.x.xR[j] / s.xR[j] * a.xyR[i][j];
                  }
                  state.f = state.f + 0.5 * (state.x.xR[i] / s.xR[i]) * v;
                  state.g.xR[i] = v;
               }
               if (defecttype == 0) {
                  state.g.xR[varidx] = 0.0;
               }
               if (defecttype == 1) {
                  state.g.xR[varidx] = state.g.xR[varidx] + 1;
               }
               if (defecttype == 2) {
                  state.g.xR[varidx] = state.g.xR[varidx] * 2;
               }
               for (i = 0; i < n; i++) {
                  state.g.xR[i] = state.g.xR[i] / s.xR[i];
               }
               continue;
            }
            ae_assert(false, "Assertion failed", _state);
         }
         minlbfgsresults(&state, &x1, &rep, _state);
         minlbfgsoptguardresults(&state, &ogrep, _state);

      // Check that something is returned
         ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1377");
         ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1378");
         if (*wereerrors) {
            ae_frame_leave(_state);
            return;
         }
      // Compute reference values for true and spoiled Jacobian at X0
         ae_set_error_flag(wereerrors, !isfinitevector(&ogrep.badgradxbase, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1385");
         if (*wereerrors) {
            ae_frame_leave(_state);
            return;
         }
         ae_matrix_set_length(&jactrue, 1, n, _state);
         ae_matrix_set_length(&jacdefect, 1, n, _state);
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + ogrep.badgradxbase.xR[j] / s.xR[j] * a.xyR[i][j];
            }
            jactrue.xyR[0][i] = v;
            jacdefect.xyR[0][i] = v;
         }
         if (defecttype == 0) {
            jacdefect.xyR[0][varidx] = 0.0;
         }
         if (defecttype == 1) {
            jacdefect.xyR[0][varidx] = jacdefect.xyR[0][varidx] + 1;
         }
         if (defecttype == 2) {
            jacdefect.xyR[0][varidx] = jacdefect.xyR[0][varidx] * 2;
         }
         for (i = 0; i < n; i++) {
            jactrue.xyR[0][i] = jactrue.xyR[0][i] / s.xR[i];
            jacdefect.xyR[0][i] = jacdefect.xyR[0][i] / s.xR[i];
         }

      // Check OptGuard report
         ae_set_error_flag(wereerrors, !apservisfinitematrix(&ogrep.badgraduser, 1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1413");
         ae_set_error_flag(wereerrors, !apservisfinitematrix(&ogrep.badgradnum, 1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1414");
         if (*wereerrors) {
            ae_frame_leave(_state);
            return;
         }
         if (defecttype >= 0) {
            ae_set_error_flag(wereerrors, !ogrep.badgradsuspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1419");
            ae_set_error_flag(wereerrors, ogrep.badgradfidx != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1420");
            ae_set_error_flag(wereerrors, ogrep.badgradvidx != varidx, __FILE__, __LINE__, "testminlbfgsunit.ap:1421");
         } else {
            ae_set_error_flag(wereerrors, ogrep.badgradsuspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1425");
            ae_set_error_flag(wereerrors, ogrep.badgradfidx != -1, __FILE__, __LINE__, "testminlbfgsunit.ap:1426");
            ae_set_error_flag(wereerrors, ogrep.badgradvidx != -1, __FILE__, __LINE__, "testminlbfgsunit.ap:1427");
         }
         for (j = 0; j < n; j++) {
            ae_set_error_flag(wereerrors, ae_fabs(jactrue.xyR[0][j] - ogrep.badgradnum.xyR[0][j], _state) > 0.01 / s.xR[j], __FILE__, __LINE__, "testminlbfgsunit.ap:1431");
            ae_set_error_flag(wereerrors, ae_fabs(jacdefect.xyR[0][j] - ogrep.badgraduser.xyR[0][j], _state) > 0.01 / s.xR[j], __FILE__, __LINE__, "testminlbfgsunit.ap:1432");
         }
      }
   }

// A test for detection of C1 continuity violations in the target.
//
// Target function is a sum of |(x,c_i)| for i=1..N.
// No constraints is present.
// Analytic gradient is provided.
//
// OptGuard should be able to detect violations in more than
// 85% of runs; it means that 100 runs should have no more than 15
// failures in all cases.
//
// We select some N and perform exhaustive search for this N.
   passcount = 100;
   maxfails = 15;
   maxc1test0fails = 15;
   maxc1test1fails = 15;
   n = 1 + hqrnduniformi(&rs, 10, _state);
   m = 1 + hqrnduniformi(&rs, n, _state);
   failurecounter = 0;
   c1test0fails = 0;
   c1test1fails = 0;
   avgstr0len = 0.0;
   avglng0len = 0.0;
   avgstr1len = 0.0;
   avglng1len = 0.0;
   for (pass = 1; pass <= passcount; pass++) {

   // Formulate problem
      ae_vector_set_length(&x0, n, _state);
      for (i = 0; i < n; i++) {
         x0.xR[i] = hqrndnormal(&rs, _state);
      }
      ae_vector_set_length(&s, n, _state);
      for (i = 0; i < n; i++) {
         s.xR[i] = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
      }
      ae_matrix_set_length(&a, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = hqrndnormal(&rs, _state);
         }
      }

   // Create and try to solve
      minlbfgscreate(n, m, &x0, &state, _state);
      minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 50, _state);
      minlbfgssetscale(&state, &s, _state);
      minlbfgsoptguardsmoothness(&state, 1 + hqrnduniformi(&rs, testminlbfgsunit_maxoptguardlevel, _state), _state);
      while (minlbfgsiteration(&state, _state)) {
         if (state.needfg) {
            state.f = 0.0;
            for (i = 0; i < n; i++) {
               state.g.xR[i] = 0.0;
            }
            for (i = 0; i < n; i++) {
               v = 0.0;
               for (j = 0; j < n; j++) {
                  v = v + state.x.xR[j] * a.xyR[i][j];
               }
               state.f = state.f + ae_fabs(v, _state);
               v = (double)ae_sign(v, _state);
               for (j = 0; j < n; j++) {
                  state.g.xR[j] = state.g.xR[j] + v * a.xyR[i][j];
               }
            }
            continue;
         }
         ae_assert(false, "Assertion failed", _state);
      }
      minlbfgsresults(&state, &x1, &rep, _state);
      minlbfgsoptguardresults(&state, &ogrep, _state);

   // Check basic properties of the solution
      ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1513");
      ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1514");
      if (*wereerrors) {
         ae_frame_leave(_state);
         return;
      }
   // Check generic OptGuard report: distinguish between "hard"
   // failures which result in immediate termination
   // (C0 violation being reported) and "soft" ones
   // (C1 violation is NOT reported) which accumulate
   // until we exhaust limit.
      ae_set_error_flag(wereerrors, ogrep.nonc0suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1525");
      ae_set_error_flag(wereerrors, ogrep.nonc0fidx >= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1526");
      failed = false;
      failed = failed || optguardallclear(&ogrep, _state);
      failed = failed || !ogrep.nonc1suspected;
      failed = failed || ogrep.nonc1fidx != 0;
      if (failed) {
         inc(&failurecounter, _state);
      }
   // Check C1 continuity test #0
      minlbfgsoptguardnonc1test0results(&state, &ognonc1test0strrep, &ognonc1test0lngrep, _state);
      minlbfgsoptguardnonc1test1results(&state, &ognonc1test1strrep, &ognonc1test1lngrep, _state);
      if (ogrep.nonc1test0positive) {
         ae_set_error_flag(wereerrors, !ogrep.nonc1suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1541");
         ae_set_error_flag(wereerrors, !ognonc1test0strrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1542");
         ae_set_error_flag(wereerrors, !ognonc1test0lngrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1543");
         ae_set_error_flag(wereerrors, ogrep.nonc1fidx != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1544");
         testminlbfgsunit_testoptguardc1test0reportfortask0(wereerrors, &ognonc1test0strrep, &a, n, _state);
         testminlbfgsunit_testoptguardc1test0reportfortask0(wereerrors, &ognonc1test0lngrep, &a, n, _state);
         avgstr0len = avgstr0len + (double)ognonc1test0strrep.cnt / passcount;
         avglng0len = avglng0len + (double)ognonc1test0lngrep.cnt / passcount;
      } else {
         ae_set_error_flag(wereerrors, ognonc1test0strrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1552");
         ae_set_error_flag(wereerrors, ognonc1test0lngrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1553");
         testminlbfgsunit_testoptguardc1test0reportfortask0(wereerrors, &ognonc1test0strrep, &a, n, _state);
         testminlbfgsunit_testoptguardc1test0reportfortask0(wereerrors, &ognonc1test0lngrep, &a, n, _state);
         inc(&c1test0fails, _state);
      }
      if (ogrep.nonc1test1positive) {
         ae_set_error_flag(wereerrors, !ogrep.nonc1suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1560");
         ae_set_error_flag(wereerrors, !ognonc1test1strrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1561");
         ae_set_error_flag(wereerrors, !ognonc1test1lngrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1562");
         ae_set_error_flag(wereerrors, ogrep.nonc1fidx != 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1563");
         testminlbfgsunit_testoptguardc1test1reportfortask0(wereerrors, &ognonc1test1strrep, &a, n, _state);
         testminlbfgsunit_testoptguardc1test1reportfortask0(wereerrors, &ognonc1test1lngrep, &a, n, _state);
         avgstr1len = avgstr1len + (double)ognonc1test1strrep.cnt / passcount;
         avglng1len = avglng1len + (double)ognonc1test1lngrep.cnt / passcount;
      } else {
         ae_set_error_flag(wereerrors, ognonc1test1strrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1571");
         ae_set_error_flag(wereerrors, ognonc1test1lngrep.positive, __FILE__, __LINE__, "testminlbfgsunit.ap:1572");
         testminlbfgsunit_testoptguardc1test1reportfortask0(wereerrors, &ognonc1test1strrep, &a, n, _state);
         testminlbfgsunit_testoptguardc1test1reportfortask0(wereerrors, &ognonc1test1lngrep, &a, n, _state);
         inc(&c1test1fails, _state);
      }
   }
   ae_set_error_flag(wereerrors, failurecounter > maxfails, __FILE__, __LINE__, "testminlbfgsunit.ap:1579");
   ae_set_error_flag(wereerrors, c1test0fails > maxc1test0fails, __FILE__, __LINE__, "testminlbfgsunit.ap:1580");
   ae_set_error_flag(wereerrors, c1test1fails > maxc1test1fails, __FILE__, __LINE__, "testminlbfgsunit.ap:1581");
   ae_set_error_flag(wereerrors, avglng0len <= avgstr0len, __FILE__, __LINE__, "testminlbfgsunit.ap:1582");
   ae_set_error_flag(wereerrors, avglng1len <= avgstr1len, __FILE__, __LINE__, "testminlbfgsunit.ap:1583");

// Detection of C1 continuity violations in the target under numerical differentiation:
// * target function is a sum of |(x,c_i)| for i=1..N.
// * no constraints is present.
// * analytic gradient is provided.
//
// OptGuard should always be able to detect violations in more than
// 85% of runs.
//
// We select some N and perform exhaustive search for this N.
   diffstep = 0.0001;
   passcount = 100;
   maxfails = 15;
   n = 1 + hqrnduniformi(&rs, 10, _state);
   m = 1 + hqrnduniformi(&rs, n, _state);
   failurecounter = 0;
   for (pass = 1; pass <= passcount; pass++) {

   // Formulate problem
      ae_vector_set_length(&x0, n, _state);
      for (i = 0; i < n; i++) {
         x0.xR[i] = hqrndnormal(&rs, _state);
      }
      ae_vector_set_length(&s, n, _state);
      for (i = 0; i < n; i++) {
         s.xR[i] = 0.01 * ae_pow(2.0, 0.33 * hqrndnormal(&rs, _state), _state);
      }
      ae_matrix_set_length(&a, n, n, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = hqrndnormal(&rs, _state);
         }
      }

   // Create and try to solve
      minlbfgscreatef(n, m, &x0, diffstep, &state, _state);
      minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 50, _state);
      minlbfgsoptguardsmoothness(&state, 1 + hqrnduniformi(&rs, testminlbfgsunit_maxoptguardlevel, _state), _state);
      while (minlbfgsiteration(&state, _state)) {
         if (state.needf) {
            state.f = 0.0;
            for (i = 0; i < n; i++) {
               v = 0.0;
               for (j = 0; j < n; j++) {
                  v = v + state.x.xR[j] * a.xyR[i][j];
               }
               state.f = state.f + ae_fabs(v, _state);
            }
            continue;
         }
         ae_assert(false, "Assertion failed", _state);
      }
      minlbfgsresults(&state, &x1, &rep, _state);
      minlbfgsoptguardresults(&state, &ogrep, _state);

   // Check basic properties of the solution
      ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1646");
      ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1647");
      if (*wereerrors) {
         ae_frame_leave(_state);
         return;
      }
   // Check OptGuard report: distinguish between "hard"
   // failures which result in immediate termination
   // (C0 violation being reported) and "soft" ones
   // (C1 violation is NOT reported) which accumulate
   // until we exhaust limit.
      ae_set_error_flag(wereerrors, ogrep.nonc0suspected, __FILE__, __LINE__, "testminlbfgsunit.ap:1658");
      ae_set_error_flag(wereerrors, ogrep.nonc0fidx >= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1659");
      failed = false;
      failed = failed || optguardallclear(&ogrep, _state);
      failed = failed || !ogrep.nonc1suspected;
      failed = failed || ogrep.nonc1fidx != 0;
      if (failed) {
         inc(&failurecounter, _state);
      }
   }
   ae_set_error_flag(wereerrors, failurecounter > maxfails, __FILE__, __LINE__, "testminlbfgsunit.ap:1667");

// Make sure than no false positives are reported for larger
// problems where numerical noise can be an issue:
// * N=100 dimensions
// * positive-definite quadratic programming problem
// * upper limit on iterations count, MaxIts=25
// We simply test that OptGuard does not return error code.
   n = 100;
   m = 5;
   spdmatrixrndcond(n, 1.0E2, &a, _state);
   ae_vector_set_length(&b, n, _state);
   ae_vector_set_length(&x0, n, _state);
   for (i = 0; i < n; i++) {
      b.xR[i] = hqrndnormal(&rs, _state);
      x0.xR[i] = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
   }
   minlbfgscreate(n, m, &x0, &state, _state);
   minlbfgsoptguardsmoothness(&state, 1 + hqrnduniformi(&rs, testminlbfgsunit_maxoptguardlevel, _state), _state);
   minlbfgssetcond(&state, 0.0, 0.0, 1.0E-9, 25, _state);
   while (minlbfgsiteration(&state, _state)) {
      if (state.needfg) {
         state.f = 0.0;
         for (i = 0; i < n; i++) {
            state.f = state.f + b.xR[i] * state.x.xR[i];
            state.g.xR[i] = b.xR[i];
            for (j = 0; j < n; j++) {
               state.f = state.f + 0.5 * state.x.xR[i] * a.xyR[i][j] * state.x.xR[j];
               state.g.xR[i] = state.g.xR[i] + a.xyR[i][j] * state.x.xR[j];
            }
         }
         continue;
      }
      ae_assert(false, "Assertion failed", _state);
   }
   minlbfgsresults(&state, &x1, &rep, _state);
   ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1711");
   ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlbfgsunit.ap:1712");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   minlbfgsoptguardresults(&state, &ogrep, _state);
   ae_set_error_flag(wereerrors, !optguardallclear(&ogrep, _state), __FILE__, __LINE__, "testminlbfgsunit.ap:1716");
   ae_frame_leave(_state);
}

