// This function performs tests specific for QuickQP solver
//
// Returns True on failure.
static bool quickqptests(ae_state *_state);

// This function performs tests specific for BLEIC solver
//
// Returns True on error, False on success.
static bool bleictests(ae_state *_state);

bool testminqp(bool silent, ae_state *_state) {
   bool simpleerrors;
   bool func1errors;
   bool func2errors;
   bool bcqperrors;
   bool ecqperrors;
   bool icqperrors;
   bool lcqperrors;
   bool quickqperrors;
   bool bleicerrors;
   bool denseaulerrors;
   bool ipmerrors;
   bool specerrors;
   bool waserrors;
   bool result;

// The VERY basic tests for Cholesky and BLEIC
   simpleerrors = simpletest(_state);
   func1errors = functest1(_state);
   func2errors = functest2(_state);

// Solver-specific tests
   denseaulerrors = false;
   ipmerrors = false;
   quickqperrors = quickqptests(_state);
   bleicerrors = bleictests(_state);
   testminqpunit_denseaultests(&denseaulerrors, _state);
   testminqpunit_ipmtests(&ipmerrors, _state);

//
   icqperrors = false;
   lcqperrors = false;
   bcqperrors = false;
   testminqpunit_bcqptest(&bcqperrors, _state);
   ecqperrors = testminqpunit_ecqptest(_state);
   testminqpunit_icqptest(&icqperrors, _state);
   icqperrors = icqperrors || testminqpunit_specialicqptests(_state);
   testminqpunit_generallcqptest(&lcqperrors, _state);
   specerrors = false;
   testminqpunit_spectests(&specerrors, _state);

// report
   waserrors = ((((((((((simpleerrors || func1errors) || func2errors) || bcqperrors) || ecqperrors) || icqperrors) || lcqperrors) || quickqperrors) || bleicerrors) || denseaulerrors) || ipmerrors) || specerrors;
   if (!silent) {
      printf("TESTING MinQP\n");
      printf("BASIC TESTS:\n");
      printf("* SimpleTest:                             ");
      if (simpleerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* Func1Test:                              ");
      if (func1errors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* Func2Test:                              ");
      if (func2errors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("GENERIC QP TESTS:\n");
      printf("* box constrained:                        ");
      if (bcqperrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* linearly constrained:                   ");
      if ((ecqperrors || icqperrors) || lcqperrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("SOLVER-SPECIFIC TESTS:\n");
      printf("* QuickQP solver tests:                   ");
      if (quickqperrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* BLEIC solver tests:                     ");
      if (bleicerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* DENSE-AUL solver tests:                 ");
      if (denseaulerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("* IPM solver tests:                       ");
      if (ipmerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("SPECIAL PROPERTIES:                       ");
      if (specerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   return result;
}

// Function to test: 'MinQPCreate', 'MinQPSetQuadraticTerm', 'MinQPSetBC',
// 'MinQPSetOrigin', 'MinQPSetStartingPoint', 'MinQPOptimize', 'MinQPResults'.
//
// Test problem:
//     A = diag(aii), aii>0 (random)
//     b = 0
//     random bounds (either no bounds, one bound, two bounds a<b, two bounds a=b)
//     random start point
//     dimension - from 1 to 5.
//
// Returns True on success, False on failure.
static bool simpletest(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nexp;
   ae_int_t msn;
   ae_int_t sn;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double maxstb;
   ae_int_t infd;
   double maxn;
   double maxnb;
   double minnb;
   double eps;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(minqpstate, state, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(ub, 0, DT_REAL, _state);
   NewVector(db, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(tx, 0, DT_REAL, _state);
   NewVector(stx, 0, DT_REAL, _state);
   NewVector(xori, 0, DT_REAL, _state);
   NewObj(minqpreport, rep, _state);

   eps = 0.001;
   msn = 5;
   maxstb = 10.0;
   nexp = 1000;
   maxn = 10.0;
   maxnb = 1000.0;
   minnb = -1000.0;
   for (sn = 1; sn <= msn; sn++) {
      ae_vector_set_length(&tx, sn, _state);
      ae_vector_set_length(&xori, sn, _state);
      ae_vector_set_length(&stx, sn, _state);
      ae_vector_set_length(&db, sn, _state);
      ae_vector_set_length(&ub, sn, _state);
      ae_matrix_set_length(&a, sn, sn, _state);
      for (i = 0; i <= nexp; i++) {

      // create diagonal matrix
         for (k = 0; k < sn; k++) {
            for (j = 0; j <= k; j++) {
               if (j != k) {
                  a.xyR[k][j] = 0.0;
               } else {
                  a.xyR[k][j] = maxn * ae_randomreal(_state) + 1;
               }
            }
         }
         minqpcreate(sn, &state, _state);
         testminqpunit_setrandomalgobc(&state, _state);
         minqpsetquadraticterm(&state, &a, false, _state);
         for (j = 0; j < sn; j++) {
            infd = ae_randominteger(5, _state);
            if (infd == 0) {
               db.xR[j] = _state->v_neginf;
               ub.xR[j] = _state->v_posinf;
            } else {
               if (infd == 1) {
                  db.xR[j] = _state->v_neginf;
                  ub.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
               } else {
                  if (infd == 2) {
                     db.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
                     ub.xR[j] = _state->v_posinf;
                  } else {
                     if (infd == 3) {
                        db.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
                        ub.xR[j] = db.xR[j] + maxstb * ae_randomreal(_state) + 0.01;
                     } else {
                        db.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
                        ub.xR[j] = db.xR[j];
                     }
                  }
               }
            }
         }
         minqpsetbc(&state, &db, &ub, _state);

      // initialization for shifting
      // initial value for 'XORi'
      // and searching true results
         for (j = 0; j < sn; j++) {
            xori.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
            tx.xR[j] = boundval(xori.xR[j], db.xR[j], ub.xR[j], _state);
         }
         minqpsetorigin(&state, &xori, _state);

      // initialization for starting point
         for (j = 0; j < sn; j++) {
            stx.xR[j] = (maxnb - minnb) * ae_randomreal(_state) + minnb;
         }
         minqpsetstartingpoint(&state, &stx, _state);

      // optimize and get result
         minqpoptimize(&state, _state);
         minqpresults(&state, &x, &rep, _state);
         for (j = 0; j < sn; j++) {
            if (ae_fabs(tx.xR[j] - x.xR[j], _state) > eps || (x.xR[j] < db.xR[j] || x.xR[j] > ub.xR[j])) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

// Function to test: 'MinQPCreate', 'MinQPSetLinearTerm', 'MinQPSetQuadraticTerm',
// 'MinQPSetOrigin', 'MinQPSetStartingPoint', 'MinQPOptimize', 'MinQPResults'.
//
// Test problem:
//     A = positive-definite matrix, obtained by 'SPDMatrixRndCond' function
//     b != 0
//     without bounds
//     random start point
//     dimension - from 1 to 5.
static bool functest1(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nexp;
   ae_int_t msn;
   ae_int_t sn;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double eps;
   ae_int_t c2;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(minqpstate, state, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewVector(ub, 0, DT_REAL, _state);
   NewVector(db, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(tx, 0, DT_REAL, _state);
   NewVector(stx, 0, DT_REAL, _state);
   NewVector(xori, 0, DT_REAL, _state);
   NewVector(xoric, 0, DT_REAL, _state);
   NewObj(minqpreport, rep, _state);
   NewVector(b, 0, DT_REAL, _state);

   eps = 0.001;
   msn = 5;
   c2 = 1000;
   nexp = 1000;
   for (sn = 1; sn <= msn; sn++) {
      ae_vector_set_length(&b, sn, _state);
      ae_vector_set_length(&tx, sn, _state);
      ae_vector_set_length(&xori, sn, _state);
      ae_vector_set_length(&xoric, sn, _state);
      ae_vector_set_length(&stx, sn, _state);
      for (i = 0; i <= nexp; i++) {

      // create simmetric matrix 'A'
         spdmatrixrndcond(sn, ae_exp(ae_randomreal(_state) * ae_log((double)c2, _state), _state), &a, _state);
         minqpcreate(sn, &state, _state);
         testminqpunit_setrandomalgobc(&state, _state);
         minqpsetquadraticterm(&state, &a, false, _state);
         for (j = 0; j < sn; j++) {
            xoric.xR[j] = 2 * ae_randomreal(_state) - 1;
         }

      // create linear part
         for (j = 0; j < sn; j++) {
            b.xR[j] = 0.0;
            for (k = 0; k < sn; k++) {
               b.xR[j] = b.xR[j] - xoric.xR[k] * a.xyR[k][j];
            }
         }
         minqpsetlinearterm(&state, &b, _state);

      // initialization for shifting
      // initial value for 'XORi'
      // and searching true results
         for (j = 0; j < sn; j++) {
            xori.xR[j] = 2 * ae_randomreal(_state) - 1;
            tx.xR[j] = xori.xR[j] + xoric.xR[j];
         }
         minqpsetorigin(&state, &xori, _state);

      // initialization for starting point
         for (j = 0; j < sn; j++) {
            stx.xR[j] = 2 * ae_randomreal(_state) - 1;
         }
         minqpsetstartingpoint(&state, &stx, _state);

      // optimize and get result
         minqpoptimize(&state, _state);
         minqpresults(&state, &x, &rep, _state);
         for (j = 0; j < sn; j++) {
            if (ae_fabs(tx.xR[j] - x.xR[j], _state) > eps) {
               result = true;
               ae_frame_leave(_state);
               return result;
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

