// Test for unconstrained problems.
// On failure sets error flag, leaves it unchanged on success.
static void testminlmunit_testu(bool *errorflag, bool *statefieldsconsistencyflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   ae_int_t i;
   double h;
   ae_int_t rkind;
   double v;
   double s;
   double eps;
   double epsx;
   ae_int_t maxits;
   ae_int_t ckind;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(b, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewObj(minlmstate, state, _state);
   NewObj(minlmreport, rep, _state);

// Reference problem.
// See comments for RKindVsStateCheck() for more info about RKind.
//
// NOTES: we also test negative RKind's corresponding to "inexact" schemes
// which use approximate finite difference Jacobian.
   ae_vector_set_length(&x, 3, _state);
   n = 3;
   m = 3;
   h = 0.0001;
   for (rkind = -2; rkind <= 5; rkind++) {
      x.xR[0] = 100 * ae_randomreal(_state) - 50;
      x.xR[1] = 100 * ae_randomreal(_state) - 50;
      x.xR[2] = 100 * ae_randomreal(_state) - 50;
      if (rkind == -2) {
         minlmcreatev(n, m, &x, h, &state, _state);
         minlmsetacctype(&state, 1, _state);
      }
      if (rkind == -1) {
         minlmcreatev(n, m, &x, h, &state, _state);
         minlmsetacctype(&state, 0, _state);
      }
      if (rkind == 0) {
         minlmcreatefj(n, m, &x, &state, _state);
      }
      if (rkind == 1) {
         minlmcreatefgj(n, m, &x, &state, _state);
      }
      if (rkind == 2) {
         minlmcreatefgh(n, &x, &state, _state);
      }
      if (rkind == 3) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 0, _state);
      }
      if (rkind == 4) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 1, _state);
      }
      if (rkind == 5) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 2, _state);
      }
      while (minlmiteration(&state, _state)) {

      // (x-2)^2 + y^2 + (z-x)^2
         if (state.needfi) {
            state.fi.xR[0] = state.x.xR[0] - 2;
            state.fi.xR[1] = state.x.xR[1];
            state.fi.xR[2] = state.x.xR[2] - state.x.xR[0];
         }
         if (state.needfij) {
            state.fi.xR[0] = state.x.xR[0] - 2;
            state.fi.xR[1] = state.x.xR[1];
            state.fi.xR[2] = state.x.xR[2] - state.x.xR[0];
            state.j.xyR[0][0] = 1.0;
            state.j.xyR[0][1] = 0.0;
            state.j.xyR[0][2] = 0.0;
            state.j.xyR[1][0] = 0.0;
            state.j.xyR[1][1] = 1.0;
            state.j.xyR[1][2] = 0.0;
            state.j.xyR[2][0] = -1.0;
            state.j.xyR[2][1] = 0.0;
            state.j.xyR[2][2] = 1.0;
         }
         if ((state.needf || state.needfg) || state.needfgh) {
            state.f = ae_sqr(state.x.xR[0] - 2, _state) + ae_sqr(state.x.xR[1], _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
         }
         if (state.needfg || state.needfgh) {
            state.g.xR[0] = 2 * (state.x.xR[0] - 2) + 2 * (state.x.xR[0] - state.x.xR[2]);
            state.g.xR[1] = 2 * state.x.xR[1];
            state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
         }
         if (state.needfgh) {
            state.h.xyR[0][0] = 4.0;
            state.h.xyR[0][1] = 0.0;
            state.h.xyR[0][2] = -2.0;
            state.h.xyR[1][0] = 0.0;
            state.h.xyR[1][1] = 2.0;
            state.h.xyR[1][2] = 0.0;
            state.h.xyR[2][0] = -2.0;
            state.h.xyR[2][1] = 0.0;
            state.h.xyR[2][2] = 2.0;
         }
         ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:212");
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:215");
      ae_set_error_flag(errorflag, ae_fabs(x.xR[0] - 2, _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:216");
      ae_set_error_flag(errorflag, ae_fabs(x.xR[1], _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:217");
      ae_set_error_flag(errorflag, ae_fabs(x.xR[2] - 2, _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:218");
   }

// 1D problem #1
//
// NOTES: we also test negative RKind's corresponding to "inexact" schemes
// which use approximate finite difference Jacobian.
   for (rkind = -2; rkind <= 5; rkind++) {
      ae_vector_set_length(&x, 1, _state);
      n = 1;
      m = 1;
      h = 0.00001;
      x.xR[0] = 100 * ae_randomreal(_state) - 50;
      if (rkind == -2) {
         minlmcreatev(n, m, &x, h, &state, _state);
         minlmsetacctype(&state, 1, _state);
      }
      if (rkind == -1) {
         minlmcreatev(n, m, &x, h, &state, _state);
         minlmsetacctype(&state, 0, _state);
      }
      if (rkind == 0) {
         minlmcreatefj(n, m, &x, &state, _state);
      }
      if (rkind == 1) {
         minlmcreatefgj(n, m, &x, &state, _state);
      }
      if (rkind == 2) {
         minlmcreatefgh(n, &x, &state, _state);
      }
      if (rkind == 3) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 0, _state);
      }
      if (rkind == 4) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 1, _state);
      }
      if (rkind == 5) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 2, _state);
      }
      while (minlmiteration(&state, _state)) {
         if (state.needfi) {
            state.fi.xR[0] = ae_sin(state.x.xR[0], _state);
         }
         if (state.needfij) {
            state.fi.xR[0] = ae_sin(state.x.xR[0], _state);
            state.j.xyR[0][0] = ae_cos(state.x.xR[0], _state);
         }
         if ((state.needf || state.needfg) || state.needfgh) {
            state.f = ae_sqr(ae_sin(state.x.xR[0], _state), _state);
         }
         if (state.needfg || state.needfgh) {
            state.g.xR[0] = 2 * ae_sin(state.x.xR[0], _state) * ae_cos(state.x.xR[0], _state);
         }
         if (state.needfgh) {
            state.h.xyR[0][0] = 2 * (ae_cos(state.x.xR[0], _state) * ae_cos(state.x.xR[0], _state) - ae_sin(state.x.xR[0], _state) * ae_sin(state.x.xR[0], _state));
         }
         ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:281");
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:284");
      ae_set_error_flag(errorflag, ae_fabs(x.xR[0] / ae_pi - ae_round(x.xR[0] / ae_pi, _state), _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:285");
   }

// Linear equations: test normal optimization and optimization with restarts
   for (n = 1; n <= 10; n++) {

   // Prepare task
      h = 0.00001;
      rmatrixrndcond(n, 100.0, &a, _state);
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&xe, n, _state);
      ae_vector_set_length(&b, n, _state);
      for (i = 0; i < n; i++) {
         xe.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      for (i = 0; i < n; i++) {
         v = ae_v_dotproduct(a.xyR[i], 1, xe.xR, 1, n);
         b.xR[i] = v;
      }

   // Test different RKind
   //
   // NOTES: we also test negative RKind's corresponding to "inexact" schemes
   // which use approximate finite difference Jacobian.
      for (rkind = -2; rkind <= 5; rkind++) {

      // Solve task (first attempt)
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         if (rkind == -2) {
            minlmcreatev(n, n, &x, h, &state, _state);
            minlmsetacctype(&state, 1, _state);
         }
         if (rkind == -1) {
            minlmcreatev(n, n, &x, h, &state, _state);
            minlmsetacctype(&state, 0, _state);
         }
         if (rkind == 0) {
            minlmcreatefj(n, n, &x, &state, _state);
         }
         if (rkind == 1) {
            minlmcreatefgj(n, n, &x, &state, _state);
         }
         if (rkind == 2) {
            minlmcreatefgh(n, &x, &state, _state);
         }
         if (rkind == 3) {
            minlmcreatevj(n, n, &x, &state, _state);
            minlmsetacctype(&state, 0, _state);
         }
         if (rkind == 4) {
            minlmcreatevj(n, n, &x, &state, _state);
            minlmsetacctype(&state, 1, _state);
         }
         if (rkind == 5) {
            minlmcreatevj(n, n, &x, &state, _state);
            minlmsetacctype(&state, 2, _state);
         }
         while (minlmiteration(&state, _state)) {
            testminlmunit_axmb(&state, &a, &b, n, _state);
            ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:357");
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:360");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, ae_fabs(x.xR[i] - xe.xR[i], _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:364");
         }

      // Now we try to restart algorithm from new point
         for (i = 0; i < n; i++) {
            x.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         minlmrestartfrom(&state, &x, _state);
         while (minlmiteration(&state, _state)) {
            testminlmunit_axmb(&state, &a, &b, n, _state);
            ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:375");
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:378");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, ae_fabs(x.xR[i] - xe.xR[i], _state) > 0.001, __FILE__, __LINE__, "testminlmunit.ap:382");
         }
      }
   }

// Testing convergence properties using
// different optimizer types and different conditions.
//
// Only limited subset of optimizers is tested because some
// optimizers converge too quickly.
   s = 100.0;
   for (rkind = 0; rkind <= 5; rkind++) {

   // Skip FGH optimizer - it converges too quickly
      if (rkind == 2) {
         continue;
      }
   // Test
      for (ckind = 0; ckind <= 1; ckind++) {
         eps = 0.0;
         epsx = 0.0;
         maxits = 0;
         if (ckind == 0) {
            epsx = 1.0E-6;
            eps = 1.0E-4;
         }
         if (ckind == 1) {
            maxits = 2;
            eps = 0.05;
         }
         ae_vector_set_length(&x, 3, _state);
         n = 3;
         m = 3;
         for (i = 0; i <= 2; i++) {
            x.xR[i] = 6.0;
         }
         if (rkind == 0) {
            minlmcreatefj(n, m, &x, &state, _state);
         }
         if (rkind == 1) {
            minlmcreatefgj(n, m, &x, &state, _state);
         }
         ae_assert(rkind != 2, "Assertion failed", _state);
         if (rkind == 3) {
            minlmcreatevj(n, m, &x, &state, _state);
            minlmsetacctype(&state, 0, _state);
         }
         if (rkind == 4) {
            minlmcreatevj(n, m, &x, &state, _state);
            minlmsetacctype(&state, 1, _state);
         }
         if (rkind == 5) {
            minlmcreatevj(n, m, &x, &state, _state);
            minlmsetacctype(&state, 2, _state);
         }
         minlmsetcond(&state, epsx, maxits, _state);
         while (minlmiteration(&state, _state)) {
            if (state.needfi || state.needfij) {
               state.fi.xR[0] = s * (ae_exp(state.x.xR[0], _state) - 2);
               state.fi.xR[1] = ae_sqr(state.x.xR[1], _state) + 1;
               state.fi.xR[2] = state.x.xR[2] - state.x.xR[0];
            }
            if (state.needfij) {
               state.j.xyR[0][0] = s * ae_exp(state.x.xR[0], _state);
               state.j.xyR[0][1] = 0.0;
               state.j.xyR[0][2] = 0.0;
               state.j.xyR[1][0] = 0.0;
               state.j.xyR[1][1] = 2 * state.x.xR[1];
               state.j.xyR[1][2] = 0.0;
               state.j.xyR[2][0] = -1.0;
               state.j.xyR[2][1] = 0.0;
               state.j.xyR[2][2] = 1.0;
            }
            if ((state.needf || state.needfg) || state.needfgh) {
               state.f = s * ae_sqr(ae_exp(state.x.xR[0], _state) - 2, _state) + ae_sqr(ae_sqr(state.x.xR[1], _state) + 1, _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
            }
            if (state.needfg || state.needfgh) {
               state.g.xR[0] = s * 2 * (ae_exp(state.x.xR[0], _state) - 2) * ae_exp(state.x.xR[0], _state) + 2 * (state.x.xR[0] - state.x.xR[2]);
               state.g.xR[1] = 2 * (ae_sqr(state.x.xR[1], _state) + 1) * 2 * state.x.xR[1];
               state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
            }
            if (state.needfgh) {
               state.h.xyR[0][0] = s * (4 * ae_sqr(ae_exp(state.x.xR[0], _state), _state) - 4 * ae_exp(state.x.xR[0], _state)) + 2;
               state.h.xyR[0][1] = 0.0;
               state.h.xyR[0][2] = -2.0;
               state.h.xyR[1][0] = 0.0;
               state.h.xyR[1][1] = 12 * ae_sqr(state.x.xR[1], _state) + 4;
               state.h.xyR[1][2] = 0.0;
               state.h.xyR[2][0] = -2.0;
               state.h.xyR[2][1] = 0.0;
               state.h.xyR[2][2] = 2.0;
            }
            ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:486");
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:489");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         if (ckind == 0) {
            ae_set_error_flag(errorflag, ae_fabs(x.xR[0] - ae_log(2.0, _state), _state) > eps, __FILE__, __LINE__, "testminlmunit.ap:494");
            ae_set_error_flag(errorflag, ae_fabs(x.xR[1], _state) > eps, __FILE__, __LINE__, "testminlmunit.ap:495");
            ae_set_error_flag(errorflag, ae_fabs(x.xR[2] - ae_log(2.0, _state), _state) > eps, __FILE__, __LINE__, "testminlmunit.ap:496");
            ae_set_error_flag(errorflag, rep.terminationtype != 2, __FILE__, __LINE__, "testminlmunit.ap:497");
         }
         if (ckind == 1) {
            ae_set_error_flag(errorflag, rep.terminationtype != 5, __FILE__, __LINE__, "testminlmunit.ap:501");
            ae_set_error_flag(errorflag, rep.iterationscount != maxits, __FILE__, __LINE__, "testminlmunit.ap:502");
         }
      }
   }
   ae_frame_leave(_state);
}

// Test for box constrained problems.
// On failure sets error flag, leaves it unchanged on success.
static void testminlmunit_testbc(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   ae_int_t i;
   ae_int_t j;
   double v;
   double h;
   ae_int_t tmpkind;
   double epsx;
   double tolf;
   double f0;
   double f1;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlmstate, state, _state);
   NewObj(minlmreport, rep, _state);
   NewVector(bl, 0, DT_REAL, _state);
   NewVector(bu, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(d, 0, DT_REAL, _state);
   NewMatrix(c, 0, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Reference box constrained problem:
//
//     min sum((x[i]-xe[i])^4) subject to 0 <= x[i] <= 1
//
// NOTES:
// 1. we test only two optimization modes - V and FGH,
//    because from algorithm internals we can assume that actual
//    mode being used doesn't matter for bound constrained optimization
//    process.
   for (tmpkind = 0; tmpkind <= 1; tmpkind++) {
      for (n = 1; n <= 5; n++) {
         ae_vector_set_length(&bl, n, _state);
         ae_vector_set_length(&bu, n, _state);
         ae_vector_set_length(&xe, n, _state);
         ae_vector_set_length(&x, n, _state);
         for (i = 0; i < n; i++) {
            bl.xR[i] = 0.0;
            bu.xR[i] = 1.0;
            xe.xR[i] = 3 * ae_randomreal(_state) - 1;
            x.xR[i] = ae_randomreal(_state);
         }
         if (tmpkind == 0) {
            minlmcreatefgh(n, &x, &state, _state);
         }
         if (tmpkind == 1) {
            minlmcreatev(n, n, &x, 1.0E-5, &state, _state);
         }
         minlmsetcond(&state, 1.0E-6, 0, _state);
         minlmsetbc(&state, &bl, &bu, _state);
         while (minlmiteration(&state, _state)) {
            if (state.needfi) {
               for (i = 0; i < n; i++) {
                  state.fi.xR[i] = ae_pow(state.x.xR[i] - xe.xR[i], 2.0, _state);
               }
            }
            if ((state.needf || state.needfg) || state.needfgh) {
               state.f = 0.0;
               for (i = 0; i < n; i++) {
                  state.f = state.f + ae_pow(state.x.xR[i] - xe.xR[i], 4.0, _state);
               }
            }
            if (state.needfg || state.needfgh) {
               for (i = 0; i < n; i++) {
                  state.g.xR[i] = 4 * ae_pow(state.x.xR[i] - xe.xR[i], 3.0, _state);
               }
            }
            if (state.needfgh) {
               for (i = 0; i < n; i++) {
                  for (j = 0; j < n; j++) {
                     state.h.xyR[i][j] = 0.0;
                  }
               }
               for (i = 0; i < n; i++) {
                  state.h.xyR[i][i] = 12 * ae_pow(state.x.xR[i] - xe.xR[i], 2.0, _state);
               }
            }
         }
         minlmresults(&state, &x, &rep, _state);
         if (rep.terminationtype > 0) {
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, ae_fabs(x.xR[i] - boundval(xe.xR[i], bl.xR[i], bu.xR[i], _state), _state) > 5.0E-2, __FILE__, __LINE__, "testminlmunit.ap:594");
            }
         } else {
            ae_set_error_flag(errorflag, true, __FILE__, __LINE__, "testminlmunit.ap:597");
         }
      }
   }

// Minimize
//
//          [      [                   ]2  ]
//     SUM_i[ SUM_j[ power(x_j,3)*c_ij ]   ]
//          [      [                   ]   ]
//
// subject to non-negativity constraints on x_j
   epsx = 1.0E-9;
   tolf = 1.0E-10;
   for (tmpkind = 0; tmpkind <= 1; tmpkind++) {
      for (n = 1; n <= 20; n++) {
         m = n + hqrnduniformi(&rs, n, _state);
         ae_vector_set_length(&bl, n, _state);
         ae_vector_set_length(&bu, n, _state);
         ae_vector_set_length(&x0, n, _state);
         for (i = 0; i < n; i++) {
            bl.xR[i] = 0.0;
            bu.xR[i] = _state->v_posinf;
            x0.xR[i] = 1 + hqrnduniformr(&rs, _state);
         }
         ae_matrix_set_length(&c, m, n + 1, _state);
         for (i = 0; i < m; i++) {
            for (j = 0; j <= n; j++) {
               c.xyR[i][j] = hqrndnormal(&rs, _state);
            }
         }
         if (tmpkind == 0) {
            minlmcreatev(n, m, &x0, 10 * epsx, &state, _state);
         }
         if (tmpkind == 1) {
            minlmcreatevj(n, m, &x0, &state, _state);
         }
         minlmsetcond(&state, epsx, 0, _state);
         minlmsetbc(&state, &bl, &bu, _state);
         while (minlmiteration(&state, _state)) {
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, state.x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:639");
               ae_set_error_flag(errorflag, state.x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:640");
            }
            if (state.needfi) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
               continue;
            }
            if (state.needfij) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
               continue;
            }
            ae_assert(false, "minlm test: integrity check failed", _state);
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:655");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         testminlmunit_testfunc1(n, m, &c, &x, &f0, true, &state.fi, false, &state.j, false, _state);
         ae_vector_set_length(&x1, n, _state);
         h = 0.001;
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:664");
            ae_set_error_flag(errorflag, x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:665");
            if (x.xR[i] + h >= bl.xR[i]) {
               for (j = 0; j < n; j++) {
                  x1.xR[j] = x.xR[j];
               }
               x1.xR[i] = x.xR[i] + h;
               testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
               ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:674");
            }
            if (x.xR[i] - h >= bl.xR[i]) {
               for (j = 0; j < n; j++) {
                  x1.xR[j] = x.xR[j];
               }
               x1.xR[i] = x.xR[i] - h;
               testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
               ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:684");
            }
         }
      }
   }

// Minimize
//
//          [      [                   ]2  ]
//     SUM_i[ SUM_j[ power(x_j,3)*c_ij ]   ]
//          [      [                   ]   ]
//
// subject to random box constraints on x_j
   epsx = 1.0E-9;
   tolf = 1.0E-10;
   for (tmpkind = 0; tmpkind <= 1; tmpkind++) {
      for (n = 1; n <= 20; n++) {
         m = n + hqrnduniformi(&rs, n, _state);
         ae_vector_set_length(&bl, n, _state);
         ae_vector_set_length(&bu, n, _state);
         ae_vector_set_length(&x0, n, _state);
         for (i = 0; i < n; i++) {
            bl.xR[i] = hqrndnormal(&rs, _state);
            bu.xR[i] = bl.xR[i] + hqrnduniformr(&rs, _state);
            x0.xR[i] = 1 + hqrnduniformr(&rs, _state);
         }
         ae_matrix_set_length(&c, m, n + 1, _state);
         for (i = 0; i < m; i++) {
            for (j = 0; j <= n; j++) {
               c.xyR[i][j] = hqrndnormal(&rs, _state);
            }
         }
         if (tmpkind == 0) {
            minlmcreatev(n, m, &x0, 10 * epsx, &state, _state);
         }
         if (tmpkind == 1) {
            minlmcreatevj(n, m, &x0, &state, _state);
         }
         minlmsetcond(&state, epsx, 0, _state);
         minlmsetbc(&state, &bl, &bu, _state);
         while (minlmiteration(&state, _state)) {
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, state.x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:728");
               ae_set_error_flag(errorflag, state.x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:729");
            }
            if (state.needfi) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
               continue;
            }
            if (state.needfij) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
               continue;
            }
            ae_assert(false, "minlm test: integrity check failed", _state);
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:744");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
         testminlmunit_testfunc1(n, m, &c, &x, &f0, true, &state.fi, false, &state.j, false, _state);
         ae_vector_set_length(&x1, n, _state);
         h = 0.001;
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:753");
            ae_set_error_flag(errorflag, x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:754");
            if (x.xR[i] + h >= bl.xR[i] && x.xR[i] + h <= bu.xR[i]) {
               for (j = 0; j < n; j++) {
                  x1.xR[j] = x.xR[j];
               }
               x1.xR[i] = x.xR[i] + h;
               testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
               ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:763");
            }
            if (x.xR[i] - h >= bl.xR[i] && x.xR[i] - h <= bu.xR[i]) {
               for (j = 0; j < n; j++) {
                  x1.xR[j] = x.xR[j];
               }
               x1.xR[i] = x.xR[i] - h;
               testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
               ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:773");
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Test for linearly constrained problems.
// On failure sets error flag, leaves it unchanged on success.
static void testminlmunit_testlc(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   ae_int_t m1;
   ae_int_t m2;
   ae_int_t rawccnt;
   ae_int_t trialidx;
   ae_int_t i;
   ae_int_t j;
   double v;
   double h;
   ae_int_t optkind;
   double epsx;
   double xtol;
   double tolf;
   double f0;
   double f1;
   bool bflag;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlmstate, state, _state);
   NewObj(minlmreport, rep, _state);
   NewVector(bl, 0, DT_REAL, _state);
   NewVector(bu, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xe, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(x12, 0, DT_REAL, _state);
   NewVector(d, 0, DT_REAL, _state);
   NewMatrix(rawc, 0, 0, DT_REAL, _state);
   NewVector(rawct, 0, DT_INT, _state);
   NewMatrix(c, 0, 0, DT_REAL, _state);
   NewMatrix(c12, 0, 0, DT_REAL, _state);
   NewMatrix(z, 0, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Minimize
//
//          [      [                             ]2  ]
//     SUM_i[ SUM_j[ (0.1*x_j+power(x_j,3))*c_ij ]   ]
//          [      [                             ]   ]
//
// subject to mix of box and linear inequality constraints on x_j
//
// We check correctness of solution by sampling a few random points
// around one returned by optimizer, and comparing function value
// with target. Sampling is performed with respect to inequality
// constraints.
   epsx = 1.0E-12;
   xtol = 1.0E-8;
   tolf = 1.0E-10;
   for (optkind = 0; optkind <= 1; optkind++) {
      for (n = 5; n <= 20; n++) {

      // Generate problem
         m = n + hqrnduniformi(&rs, n, _state);
         ae_vector_set_length(&bl, n, _state);
         ae_vector_set_length(&bu, n, _state);
         ae_vector_set_length(&x0, n, _state);
         for (i = 0; i < n; i++) {
            bl.xR[i] = hqrndnormal(&rs, _state);
            bu.xR[i] = bl.xR[i] + hqrnduniformr(&rs, _state);
            x0.xR[i] = bl.xR[i] + (bu.xR[i] - bl.xR[i]) * hqrnduniformr(&rs, _state);
         }
         ae_matrix_set_length(&c, m, n + 1, _state);
         for (i = 0; i < m; i++) {
            for (j = 0; j <= n; j++) {
               c.xyR[i][j] = hqrndnormal(&rs, _state);
            }
         }
         ae_assert(n >= 5, "Assertion failed", _state);
         rawccnt = 3;
         ae_matrix_set_length(&rawc, rawccnt, n + 1, _state);
         ae_vector_set_length(&rawct, rawccnt, _state);
         for (i = 0; i < rawccnt; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               rawc.xyR[i][j] = hqrndnormal(&rs, _state);
               v = v + x0.xR[j] * rawc.xyR[i][j];
            }
            rawc.xyR[i][n] = v;
            rawct.xZ[i] = 2 * hqrnduniformi(&rs, 2, _state) - 1;
         }

      // Solve
         if (optkind == 0) {
            minlmcreatev(n, m, &x0, 10 * epsx, &state, _state);
         }
         if (optkind == 1) {
            minlmcreatevj(n, m, &x0, &state, _state);
         }
         minlmsetcond(&state, epsx, 0, _state);
         minlmsetbc(&state, &bl, &bu, _state);
         minlmsetlc(&state, &rawc, &rawct, rawccnt, _state);
         while (minlmiteration(&state, _state)) {
            for (i = 0; i < n; i++) {
               ae_set_error_flag(errorflag, state.x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:878");
               ae_set_error_flag(errorflag, state.x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:879");
            }
            if (state.needfi) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
               continue;
            }
            if (state.needfij) {
               testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
               continue;
            }
            ae_assert(false, "minlm test: integrity check failed", _state);
         }
         minlmresults(&state, &x, &rep, _state);
         ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:894");
         if (*errorflag) {
            ae_frame_leave(_state);
            return;
         }
      // Test feasibility w.r.t. box and linear inequality constraints
         for (i = 0; i < n; i++) {
            ae_set_error_flag(errorflag, x.xR[i] < bl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:903");
            ae_set_error_flag(errorflag, x.xR[i] > bu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:904");
         }
         for (i = 0; i < rawccnt; i++) {
            v = ae_v_dotproduct(rawc.xyR[i], 1, x.xR, 1, n);
            v = v - rawc.xyR[i][n];
            if (rawct.xZ[i] > 0) {
               ae_set_error_flag(errorflag, v < -xtol, __FILE__, __LINE__, "testminlmunit.ap:911");
            }
            if (rawct.xZ[i] < 0) {
               ae_set_error_flag(errorflag, v > xtol, __FILE__, __LINE__, "testminlmunit.ap:913");
            }
         }

      // Make several random trial steps and:
      // 0) generate small random trial step
      // 1) if step is infeasible, skip to next trial
      // 2) compare function value in the trial point against one in other points
         testminlmunit_testfunc1(n, m, &c, &x, &f0, true, &state.fi, false, &state.j, false, _state);
         ae_vector_set_length(&x1, n, _state);
         for (trialidx = 0; trialidx <= 10 * n; trialidx++) {
            h = 0.001;
            for (i = 0; i < n; i++) {
               do {
                  x1.xR[i] = x.xR[i] + (hqrnduniformr(&rs, _state) * 2 - 1) * h;
               } while (!(x1.xR[i] >= bl.xR[i] && x1.xR[i] <= bu.xR[i]));
            }
            bflag = false;
            for (i = 0; i < rawccnt; i++) {
               ae_assert(rawct.xZ[i] != 0, "Assertion failed", _state);
               v = ae_v_dotproduct(rawc.xyR[i], 1, x1.xR, 1, n);
               v = v - rawc.xyR[i][n];
               bflag = bflag || (rawct.xZ[i] > 0 && v < 0.0);
               bflag = bflag || (rawct.xZ[i] < 0 && v > 0.0);
            }
            if (bflag) {
               continue;
            }
            testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
            ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:950");
         }
      }
   }

// Minimize
//
//          [      [                             ]2  ]
//     SUM_i[ SUM_j[ (0.1*x_j+power(x_j,3))*c_ij ]   ]
//          [      [                             ]   ]
//
// subject to linear EQUALITY constraints on x_j.
//
// We check correctness of solution by sampling a few random points
// around one returned by optimizer, and comparing function value
// with target. Sampling is performed with respect to equality
// constraints. In order to simplify algorithm we use orthogonal
// equality constraints.
//
// NOTE: we solve problem using VJ mode (analytic Jacobian) because
//       roundoff errors from numerical differentiation sometimes
//       prevent us from converging with good precision.
   epsx = 1.0E-12;
   xtol = 1.0E-8;
   tolf = 1.0E-10;
   optkind = 1;
   for (n = 10; n <= 20; n++) {

   // Generate problem
      m = n + hqrnduniformi(&rs, n, _state);
      ae_vector_set_length(&x0, n, _state);
      for (i = 0; i < n; i++) {
         x0.xR[i] = hqrndnormal(&rs, _state);
      }
      ae_matrix_set_length(&c, m, n + 1, _state);
      for (i = 0; i < m; i++) {
         for (j = 0; j <= n; j++) {
            c.xyR[i][j] = hqrndnormal(&rs, _state);
         }
      }
      ae_assert(n >= 5, "Assertion failed", _state);
      rawccnt = 1 + hqrnduniformi(&rs, 5, _state);
      rmatrixrndorthogonal(n, &z, _state);
      ae_matrix_set_length(&rawc, rawccnt, n + 1, _state);
      ae_vector_set_length(&rawct, rawccnt, _state);
      for (i = 0; i < rawccnt; i++) {
         v = 0.0;
         for (j = 0; j < n; j++) {
            rawc.xyR[i][j] = z.xyR[i][j];
            v = v + x0.xR[j] * rawc.xyR[i][j];
         }
         rawc.xyR[i][n] = v;
         rawct.xZ[i] = 0;
      }

   // Solve
      if (optkind == 0) {
         minlmcreatev(n, m, &x0, 1.0E-12, &state, _state);
      }
      if (optkind == 1) {
         minlmcreatevj(n, m, &x0, &state, _state);
      }
      minlmsetcond(&state, epsx, 0, _state);
      minlmsetlc(&state, &rawc, &rawct, rawccnt, _state);
      while (minlmiteration(&state, _state)) {
         if (state.needfi) {
            testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
            continue;
         }
         if (state.needfij) {
            testminlmunit_testfunc1(n, m, &c, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
            continue;
         }
         ae_assert(false, "minlm test: integrity check failed", _state);
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1031");
      if (*errorflag) {
         ae_frame_leave(_state);
         return;
      }
   // Test feasibility w.r.t. linear equality constraints
      for (i = 0; i < rawccnt; i++) {
         v = ae_v_dotproduct(rawc.xyR[i], 1, x.xR, 1, n);
         v = v - rawc.xyR[i][n];
         ae_set_error_flag(errorflag, ae_fabs(v, _state) > xtol, __FILE__, __LINE__, "testminlmunit.ap:1042");
      }

   // Make several random trial steps and:
   // 0) generate small random trial step
   // 1) project it onto equality constrained subspace
   // 2) compare function value in the trial point against one in other points
      testminlmunit_testfunc1(n, m, &c, &x, &f0, true, &state.fi, false, &state.j, false, _state);
      ae_vector_set_length(&x1, n, _state);
      for (trialidx = 0; trialidx <= 10 * n; trialidx++) {
         h = 0.001;
         for (i = 0; i < n; i++) {
            x1.xR[i] = hqrndnormal(&rs, _state);
         }
         for (i = 0; i < rawccnt; i++) {
            v = ae_v_dotproduct(rawc.xyR[i], 1, x1.xR, 1, n);
            ae_v_subd(x1.xR, 1, rawc.xyR[i], 1, n, v);
         }
         v = ae_v_dotproduct(x1.xR, 1, x1.xR, 1, n);
         ae_assert(v > 0.0, "Assertion failed", _state);
         v = h / ae_sqrt(v, _state);
         ae_v_muld(x1.xR, 1, n, v);
         ae_v_add(x1.xR, 1, x.xR, 1, n);
         testminlmunit_testfunc1(n, m, &c, &x1, &f1, true, &state.fi, false, &state.j, false, _state);
         ae_set_error_flag(errorflag, f1 < f0 * (1 - tolf), __FILE__, __LINE__, "testminlmunit.ap:1072");
      }
   }

// Minimize
//
//          [      [                               ]2  ]        [      [                               ]2  ]
//     SUM_i[ SUM_j[ (0.1*x_j+power(x0_j,3))*c0_ij ]   ] + SUM_i[ SUM_j[ (0.1*x_j+power(x1_j,3))*c1_ij ]   ]
//          [      [                               ]   ]        [      [                               ]   ]
//
// for two sets of unknowns (x0_j and x1_j) and two sets of
// coefficients (c0_ij and c1_ij, M1*N and M2*N matrices) subject
// to equality constraint
//
//     x0_j=x1_j for all j
//
// Such optimization problem arises when we fit same model to
// two distinct datasets and want to share SOME of coefficients
// between fits. If we share ALL coefficients, it is equal to
// fitting one model to combination of two datasets.
//
// Our test checks that such "combined" 2N-dimensional problem
// solved with general linear constraints which "glue" two datasets
// together returns same answer as N-dimensional problem on (M1+M2)-point
// dataset.
//
// NOTE: we solve problem using VJ mode (analytic Jacobian) because
//       roundoff errors from numerical differentiation prevent us
//       from converging with good precision.
   epsx = 1.0E-12;
   for (n = 5; n <= 20; n++) {

   // Generate problems
      m1 = n + hqrnduniformi(&rs, n, _state);
      m2 = n + hqrnduniformi(&rs, n, _state);
      ae_vector_set_length(&x0, n, _state);
      ae_vector_set_length(&x12, 2 * n, _state);
      for (i = 0; i < n; i++) {
         x0.xR[i] = hqrndnormal(&rs, _state);
         x12.xR[2 * i + 0] = hqrndnormal(&rs, _state);
         x12.xR[2 * i + 1] = hqrndnormal(&rs, _state);
      }
      ae_matrix_set_length(&c, m1 + m2, n + 1, _state);
      ae_matrix_set_length(&c12, m1 + m2, 2 * n + 1, _state);
      for (i = 0; i < m1 + m2; i++) {
         for (j = 0; j <= 2 * n; j++) {
            c12.xyR[i][j] = 0.0;
         }
      }
      for (i = 0; i < m1 + m2; i++) {
         for (j = 0; j < n; j++) {
            c.xyR[i][j] = hqrndnormal(&rs, _state);
            if (i < m1) {
               c12.xyR[i][j] = c.xyR[i][j];
            } else {
               c12.xyR[i][n + j] = c.xyR[i][j];
            }
         }
         c.xyR[i][n] = hqrndnormal(&rs, _state);
         c12.xyR[i][2 * n] = c.xyR[i][n];
      }
      rawccnt = n;
      ae_matrix_set_length(&rawc, rawccnt, 2 * n + 1, _state);
      ae_vector_set_length(&rawct, rawccnt, _state);
      for (i = 0; i < rawccnt; i++) {
         for (j = 0; j <= 2 * n; j++) {
            rawc.xyR[i][j] = 0.0;
         }
         rawc.xyR[i][i] = 1.0;
         rawc.xyR[i][n + i] = -1.0;
         rawct.xZ[i] = 0;
      }

   // Solve N-dimensional "combined" problem, store result to X1
      minlmcreatevj(n, m1 + m2, &x0, &state, _state);
      minlmsetcond(&state, epsx, 0, _state);
      while (minlmiteration(&state, _state)) {
         if (state.needfi) {
            testminlmunit_testfunc1(n, m1 + m2, &c, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
            continue;
         }
         if (state.needfij) {
            testminlmunit_testfunc1(n, m1 + m2, &c, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
            continue;
         }
         ae_assert(false, "minlm test: integrity check failed", _state);
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1170");
      if (*errorflag) {
         ae_frame_leave(_state);
         return;
      }
   // Solve N-dimensional "glued" problem, store result to X12
      minlmcreatevj(2 * n, m1 + m2, &x12, &state, _state);
      minlmsetcond(&state, epsx, 0, _state);
      minlmsetlc(&state, &rawc, &rawct, rawccnt, _state);
      while (minlmiteration(&state, _state)) {
         if (state.needfi) {
            testminlmunit_testfunc1(2 * n, m1 + m2, &c12, &state.x, &v, false, &state.fi, true, &state.j, false, _state);
            continue;
         }
         if (state.needfij) {
            testminlmunit_testfunc1(2 * n, m1 + m2, &c12, &state.x, &v, false, &state.fi, true, &state.j, true, _state);
            continue;
         }
         ae_assert(false, "minlm test: integrity check failed", _state);
      }
      minlmresults(&state, &x12, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1195");
      if (*errorflag) {
         ae_frame_leave(_state);
         return;
      }
   // Compare solutions
      for (i = 0; i < n; i++) {
         ae_set_error_flag(errorflag, ae_fabs(x.xR[i] - x12.xR[i], _state) > 1.0E-3, __FILE__, __LINE__, "testminlmunit.ap:1204");
         ae_set_error_flag(errorflag, ae_fabs(x.xR[i] - x12.xR[n + i], _state) > 1.0E-3, __FILE__, __LINE__, "testminlmunit.ap:1205");
         ae_set_error_flag(errorflag, ae_fabs(x12.xR[i] - x12.xR[n + i], _state) > 1.0E-6, __FILE__, __LINE__, "testminlmunit.ap:1206");
      }
   }
   ae_frame_leave(_state);
}

// Test other properties
// On failure sets error flag, leaves it unchanged on success.
static void testminlmunit_testother(bool *errorflag, bool *statefieldsconsistencyflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   double v;
   double s;
   ae_int_t i;
   ae_int_t j;
   ae_int_t rkind;
   double fprev;
   double xprev;
   double stpmax;
   ae_int_t stopcallidx;
   ae_int_t callidx;
   ae_int_t maxits;
   bool terminationrequested;
   ae_int_t pass;
   ae_int_t spoilcnt;
   double mx;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlmstate, state, _state);
   NewObj(minlmreport, rep, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xlast, 0, DT_REAL, _state);

// Other properties:
// 1. test reports (F should form monotone sequence)
// 2. test maximum step
   for (rkind = 0; rkind <= 5; rkind++) {

   // reports:
   // * check that first report is initial point
   // * check that F is monotone decreasing
   // * check that last report is final result
      n = 3;
      m = 3;
      s = 100.0;
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&xlast, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 6 + ae_randomreal(_state);
      }
      if (rkind == 0) {
         minlmcreatefj(n, m, &x, &state, _state);
      }
      if (rkind == 1) {
         minlmcreatefgj(n, m, &x, &state, _state);
      }
      if (rkind == 2) {
         minlmcreatefgh(n, &x, &state, _state);
      }
      if (rkind == 3) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 0, _state);
      }
      if (rkind == 4) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 1, _state);
      }
      if (rkind == 5) {
         minlmcreatevj(n, m, &x, &state, _state);
         minlmsetacctype(&state, 2, _state);
      }
      minlmsetcond(&state, 0.0, 4, _state);
      minlmsetxrep(&state, true, _state);
      fprev = ae_maxrealnumber;
      while (minlmiteration(&state, _state)) {
         if (state.needfi || state.needfij) {
            state.fi.xR[0] = ae_sqrt(s, _state) * (ae_exp(state.x.xR[0], _state) - 2);
            state.fi.xR[1] = state.x.xR[1];
            state.fi.xR[2] = state.x.xR[2] - state.x.xR[0];
         }
         if (state.needfij) {
            state.j.xyR[0][0] = ae_sqrt(s, _state) * ae_exp(state.x.xR[0], _state);
            state.j.xyR[0][1] = 0.0;
            state.j.xyR[0][2] = 0.0;
            state.j.xyR[1][0] = 0.0;
            state.j.xyR[1][1] = 1.0;
            state.j.xyR[1][2] = 0.0;
            state.j.xyR[2][0] = -1.0;
            state.j.xyR[2][1] = 0.0;
            state.j.xyR[2][2] = 1.0;
         }
         if ((state.needf || state.needfg) || state.needfgh) {
            state.f = s * ae_sqr(ae_exp(state.x.xR[0], _state) - 2, _state) + ae_sqr(state.x.xR[1], _state) + ae_sqr(state.x.xR[2] - state.x.xR[0], _state);
         }
         if (state.needfg || state.needfgh) {
            state.g.xR[0] = s * 2 * (ae_exp(state.x.xR[0], _state) - 2) * ae_exp(state.x.xR[0], _state) + 2 * (state.x.xR[0] - state.x.xR[2]);
            state.g.xR[1] = 2 * state.x.xR[1];
            state.g.xR[2] = 2 * (state.x.xR[2] - state.x.xR[0]);
         }
         if (state.needfgh) {
            state.h.xyR[0][0] = s * (4 * ae_sqr(ae_exp(state.x.xR[0], _state), _state) - 4 * ae_exp(state.x.xR[0], _state)) + 2;
            state.h.xyR[0][1] = 0.0;
            state.h.xyR[0][2] = -2.0;
            state.h.xyR[1][0] = 0.0;
            state.h.xyR[1][1] = 2.0;
            state.h.xyR[1][2] = 0.0;
            state.h.xyR[2][0] = -2.0;
            state.h.xyR[2][1] = 0.0;
            state.h.xyR[2][2] = 2.0;
         }
         ae_set_error_flag(statefieldsconsistencyflag, !testminlmunit_rkindvsstatecheck(rkind, &state, _state), __FILE__, __LINE__, "testminlmunit.ap:1320");
         if (state.xupdated) {
            ae_set_error_flag(errorflag, state.f > fprev, __FILE__, __LINE__, "testminlmunit.ap:1323");
            if (fprev == ae_maxrealnumber) {
               for (i = 0; i < n; i++) {
                  ae_set_error_flag(errorflag, state.x.xR[i] != x.xR[i], __FILE__, __LINE__, "testminlmunit.ap:1326");
               }
            }
            fprev = state.f;
            ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
         }
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1332");
      if (*errorflag) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(errorflag, x.xR[i] != xlast.xR[i], __FILE__, __LINE__, "testminlmunit.ap:1336");
      }
   }
   n = 1;
   ae_vector_set_length(&x, n, _state);
   x.xR[0] = 100.0;
   stpmax = 0.05 + 0.05 * ae_randomreal(_state);
   minlmcreatefgh(n, &x, &state, _state);
   minlmsetcond(&state, 1.0E-12, 0, _state);
   minlmsetstpmax(&state, stpmax, _state);
   minlmsetxrep(&state, true, _state);
   xprev = x.xR[0];
   while (minlmiteration(&state, _state)) {
      if ((state.needf || state.needfg) || state.needfgh) {
         state.f = ae_exp(state.x.xR[0], _state) + ae_exp(-state.x.xR[0], _state);
      }
      if (state.needfg || state.needfgh) {
         state.g.xR[0] = ae_exp(state.x.xR[0], _state) - ae_exp(-state.x.xR[0], _state);
      }
      if (state.needfgh) {
         state.h.xyR[0][0] = ae_exp(state.x.xR[0], _state) + ae_exp(-state.x.xR[0], _state);
      }
      ae_set_error_flag(errorflag, ae_fabs(state.x.xR[0] - xprev, _state) > (1 + ae_sqrt(ae_machineepsilon, _state)) * stpmax, __FILE__, __LINE__, "testminlmunit.ap:1355");
      if (state.xupdated) {
         xprev = state.x.xR[0];
      }
   }

// Check algorithm ability to handle request for termination:
// * to terminate with correct return code = 8
// * to return point which was "current" at the moment of termination
   for (pass = 1; pass <= 50; pass++) {
      n = 3;
      m = 3;
      s = 100.0;
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&xlast, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = 6 + ae_randomreal(_state);
      }
      stopcallidx = ae_randominteger(20, _state);
      maxits = 25;
      minlmcreatevj(n, m, &x, &state, _state);
      minlmsetcond(&state, 0.0, maxits, _state);
      minlmsetxrep(&state, true, _state);
      callidx = 0;
      terminationrequested = false;
      ae_v_move(xlast.xR, 1, x.xR, 1, n);
      while (minlmiteration(&state, _state)) {
         if (state.needfi || state.needfij) {
            state.fi.xR[0] = ae_sqrt(s, _state) * (ae_exp(state.x.xR[0], _state) - 2);
            state.fi.xR[1] = state.x.xR[1];
            state.fi.xR[2] = state.x.xR[2] - state.x.xR[0];
            if (state.needfij) {
               state.j.xyR[0][0] = ae_sqrt(s, _state) * ae_exp(state.x.xR[0], _state);
               state.j.xyR[0][1] = 0.0;
               state.j.xyR[0][2] = 0.0;
               state.j.xyR[1][0] = 0.0;
               state.j.xyR[1][1] = 1.0;
               state.j.xyR[1][2] = 0.0;
               state.j.xyR[2][0] = -1.0;
               state.j.xyR[2][1] = 0.0;
               state.j.xyR[2][2] = 1.0;
            }
            if (callidx == stopcallidx) {
               minlmrequesttermination(&state, _state);
               terminationrequested = true;
            }
            inc(&callidx, _state);
            continue;
         }
         if (state.xupdated) {
            if (!terminationrequested) {
               ae_v_move(xlast.xR, 1, state.x.xR, 1, n);
            }
            continue;
         }
         ae_assert(false, "Assertion failed", _state);
      }
      minlmresults(&state, &x, &rep, _state);
      ae_set_error_flag(errorflag, rep.terminationtype != 8, __FILE__, __LINE__, "testminlmunit.ap:1419");
      for (i = 0; i < n; i++) {
         ae_set_error_flag(errorflag, x.xR[i] != xlast.xR[i], __FILE__, __LINE__, "testminlmunit.ap:1421");
      }
   }

// Test ability to detect NAN/INF values.
//
// We use Rosenbrock's function which is modified to return
// NAN/INF (randomly) when near solution. Algorithm should set
// appropriate error code (-8) on exit.
   n = 10;
   m = 2 * (n - 1);
   s = 10.0;
   ae_vector_set_length(&x, n, _state);
   for (i = 0; i < n; i++) {
      x.xR[i] = 0.0;
   }
   minlmcreatevj(n, m, &x, &state, _state);
   minlmsetcond(&state, 1.0E-12, 0, _state);
   spoilcnt = 0;
   while (minlmiteration(&state, _state)) {
      if (state.needfij) {
         for (i = 0; i < m; i++) {
            for (j = 0; j < n; j++) {
               state.j.xyR[i][j] = 0.0;
            }
         }
         for (i = 0; i < n - 1; i++) {
            state.fi.xR[2 * i + 0] = s * (state.x.xR[i + 1] - ae_sqr(state.x.xR[i], _state));
            state.j.xyR[2 * i + 0][i] = -s * 2 * state.x.xR[i];
            state.j.xyR[2 * i + 0][i + 1] = s;
            state.fi.xR[2 * i + 1] = 1 - state.x.xR[i];
            state.j.xyR[2 * i + 1][i] = -1.0;
         }
         mx = 0.0;
         for (i = 0; i < n; i++) {
            mx = ae_maxreal(mx, ae_fabs(state.x.xR[i] - 1, _state), _state);
         }
         if (mx < 1.0E-2) {
            i = ae_randominteger(3, _state);
            v = _state->v_nan;
            if (i == 1) {
               v = _state->v_posinf;
            }
            if (i == 2) {
               v = _state->v_neginf;
            }
            if (ae_randomreal(_state) > 0.5) {
               state.fi.xR[ae_randominteger(m, _state)] = v;
            } else {
               state.j.xyR[ae_randominteger(m, _state)][ae_randominteger(n, _state)] = v;
            }
            inc(&spoilcnt, _state);
         }
         continue;
      }
      if (state.needfi) {
         for (i = 0; i < n - 1; i++) {
            state.fi.xR[2 * i + 0] = s * (state.x.xR[i + 1] - ae_sqr(state.x.xR[i], _state));
            state.fi.xR[2 * i + 1] = 1 - state.x.xR[i];
         }
         mx = 0.0;
         for (i = 0; i < n; i++) {
            mx = ae_maxreal(mx, ae_fabs(state.x.xR[i] - 1, _state), _state);
         }
         if (mx < 1.0E-2) {
            i = ae_randominteger(3, _state);
            v = _state->v_nan;
            if (i == 1) {
               v = _state->v_posinf;
            }
            if (i == 2) {
               v = _state->v_neginf;
            }
            state.fi.xR[ae_randominteger(m, _state)] = v;
            inc(&spoilcnt, _state);
         }
         continue;
      }
      ae_assert(false, "Assertion failed", _state);
   }
   minlmresults(&state, &x, &rep, _state);
   ae_set_error_flag(errorflag, rep.terminationtype != -8, __FILE__, __LINE__, "testminlmunit.ap:1513");
   ae_set_error_flag(errorflag, spoilcnt != 1, __FILE__, __LINE__, "testminlmunit.ap:1514");
   ae_frame_leave(_state);
}

// This function tests OptGuard
//
// On failure sets error flag.
static void testminlmunit_testoptguard(bool *wereerrors, ae_state *_state) {
   ae_frame _frame_block;
   double v;
   ae_int_t i;
   ae_int_t j;
   ae_int_t n;
   double diffstep;
   ae_int_t defecttype;
   ae_int_t funcidx;
   ae_int_t varidx;
   ae_int_t skind;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewObj(minlmstate, state, _state);
   NewObj(minlmreport, rep, _state);
   NewObj(optguardreport, ogrep, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(bndl, 0, DT_REAL, _state);
   NewVector(bndu, 0, DT_REAL, _state);
   NewMatrix(jactrue, 0, 0, DT_REAL, _state);
   NewMatrix(jacdefect, 0, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);

// Check that gradient verification is disabled by default:
// gradient checking for bad problem must return nothing
   n = 10;
   ae_vector_set_length(&x0, n, _state);
   for (i = 0; i < n; i++) {
      x0.xR[i] = 1.0 + 0.1 * i;
   }
   spdmatrixrndcond(n, 1.0E3, &a, _state);
   spdmatrixrndcond(n, 1.0E3, &a1, _state);
   minlmcreatevj(n, 1, &x0, &state, _state);
   minlmsetcond(&state, 1.0E-9, 10, _state);
   while (minlmiteration(&state, _state)) {
      if (state.needfij) {
         state.fi.xR[0] = 0.0;
         for (i = 0; i < n; i++) {
            v = 0.0;
            for (j = 0; j < n; j++) {
               v = v + state.x.xR[j] * a.xyR[i][j];
            }
            state.fi.xR[0] = state.fi.xR[0] + 0.5 * state.x.xR[i] * v;
         }
         for (i = 0; i < n; i++) {
            state.j.xyR[0][i] = 0.0;
         }
         continue;
      }
      ae_assert(false, "Assertion failed", _state);
   }
   minlmresults(&state, &x1, &rep, _state);
   minlmoptguardresults(&state, &ogrep, _state);
   ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1575");
   ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlmunit.ap:1576");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(wereerrors, ogrep.badgradxbase.cnt != 0, __FILE__, __LINE__, "testminlmunit.ap:1579");
   ae_set_error_flag(wereerrors, ogrep.badgraduser.rows != 0, __FILE__, __LINE__, "testminlmunit.ap:1580");
   ae_set_error_flag(wereerrors, ogrep.badgraduser.cols != 0, __FILE__, __LINE__, "testminlmunit.ap:1581");
   ae_set_error_flag(wereerrors, ogrep.badgradnum.rows != 0, __FILE__, __LINE__, "testminlmunit.ap:1582");
   ae_set_error_flag(wereerrors, ogrep.badgradnum.cols != 0, __FILE__, __LINE__, "testminlmunit.ap:1583");
   if (*wereerrors) {
      ae_frame_leave(_state);
      return;
   }
   ae_set_error_flag(wereerrors, ogrep.badgradsuspected, __FILE__, __LINE__, "testminlmunit.ap:1586");
   ae_set_error_flag(wereerrors, ogrep.badgradfidx != -1, __FILE__, __LINE__, "testminlmunit.ap:1587");
   ae_set_error_flag(wereerrors, ogrep.badgradvidx != -1, __FILE__, __LINE__, "testminlmunit.ap:1588");

// Test gradient checking functionality, try various
// defect types:
// * accidental zeroing of some gradient component
// * accidental addition of 1.0 to some component
// * accidental multiplication by 2.0
// Try distorting both target and constraints.
   diffstep = 0.001;
   n = 10;
   for (skind = 0; skind <= 1; skind++) {
      for (funcidx = 0; funcidx <= 1; funcidx++) {
         for (defecttype = -1; defecttype <= 2; defecttype++) {
            varidx = hqrnduniformi(&rs, n, _state);
            ae_vector_set_length(&x0, n, _state);
            ae_vector_set_length(&s, n, _state);
            ae_vector_set_length(&bndl, n, _state);
            ae_vector_set_length(&bndu, n, _state);
            for (i = 0; i < n; i++) {
               s.xR[i] = ae_pow(10.0, skind * (30 * hqrnduniformr(&rs, _state) - 15), _state);
               x0.xR[i] = (1.0 + 0.1 * i) * s.xR[i];
               j = hqrnduniformi(&rs, 3, _state);
               bndl.xR[i] = -100 * s.xR[i];
               bndu.xR[i] = 100 * s.xR[i];
               if (j == 1) {
                  bndl.xR[i] = x0.xR[i];
               }
               if (j == 2) {
                  bndu.xR[i] = x0.xR[i];
               }
            }
            spdmatrixrndcond(n, 1.0E3, &a, _state);
            spdmatrixrndcond(n, 1.0E3, &a1, _state);
            minlmcreatevj(n, 2, &x0, &state, _state);
            minlmoptguardgradient(&state, diffstep, _state);
            minlmsetcond(&state, 1.0E-9, 10, _state);
            minlmsetscale(&state, &s, _state);
            minlmsetbc(&state, &bndl, &bndu, _state);
            while (minlmiteration(&state, _state)) {
               if (state.needfi || state.needfij) {
                  for (i = 0; i < n; i++) {
                     ae_set_error_flag(wereerrors, state.x.xR[i] < bndl.xR[i], __FILE__, __LINE__, "testminlmunit.ap:1637");
                     ae_set_error_flag(wereerrors, state.x.xR[i] > bndu.xR[i], __FILE__, __LINE__, "testminlmunit.ap:1638");
                  }
                  state.fi.xR[0] = 0.0;
                  for (i = 0; i < n; i++) {
                     v = 0.0;
                     for (j = 0; j < n; j++) {
                        v = v + state.x.xR[j] / s.xR[j] * a.xyR[i][j];
                     }
                     state.fi.xR[0] = state.fi.xR[0] + 0.5 * (state.x.xR[i] / s.xR[i]) * v;
                     if (state.needfij) {
                        state.j.xyR[0][i] = v;
                     }
                  }
                  state.fi.xR[1] = 0.0;
                  for (i = 0; i < n; i++) {
                     v = 0.0;
                     for (j = 0; j < n; j++) {
                        v = v + state.x.xR[j] / s.xR[j] * a1.xyR[i][j];
                     }
                     state.fi.xR[1] = state.fi.xR[1] + 0.5 * (state.x.xR[i] / s.xR[i]) * v;
                     if (state.needfij) {
                        state.j.xyR[1][i] = v;
                     }
                  }
                  if (state.needfij) {
                     if (defecttype == 0) {
                        state.j.xyR[funcidx][varidx] = 0.0;
                     }
                     if (defecttype == 1) {
                        state.j.xyR[funcidx][varidx] = state.j.xyR[funcidx][varidx] + 1;
                     }
                     if (defecttype == 2) {
                        state.j.xyR[funcidx][varidx] = state.j.xyR[funcidx][varidx] * 2;
                     }
                  }
                  if (state.needfij) {
                     for (i = 0; i < n; i++) {
                        state.j.xyR[0][i] = state.j.xyR[0][i] / s.xR[i];
                        state.j.xyR[1][i] = state.j.xyR[1][i] / s.xR[i];
                     }
                  }
                  continue;
               }
               ae_assert(false, "Assertion failed", _state);
            }
            minlmresults(&state, &x1, &rep, _state);
            minlmoptguardresults(&state, &ogrep, _state);

         // Check that something is returned
            ae_set_error_flag(wereerrors, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlmunit.ap:1695");
            ae_set_error_flag(wereerrors, !isfinitevector(&x1, n, _state), __FILE__, __LINE__, "testminlmunit.ap:1696");
            if (*wereerrors) {
               ae_frame_leave(_state);
               return;
            }
         // Compute reference values for true and spoiled Jacobian at X0
            ae_set_error_flag(wereerrors, !isfinitevector(&ogrep.badgradxbase, n, _state), __FILE__, __LINE__, "testminlmunit.ap:1703");
            if (*wereerrors) {
               ae_frame_leave(_state);
               return;
            }
            ae_matrix_set_length(&jactrue, 2, n, _state);
            ae_matrix_set_length(&jacdefect, 2, n, _state);
            for (i = 0; i < n; i++) {
               v = 0.0;
               for (j = 0; j < n; j++) {
                  v = v + ogrep.badgradxbase.xR[j] / s.xR[j] * a.xyR[i][j];
               }
               jactrue.xyR[0][i] = v;
               jacdefect.xyR[0][i] = v;
            }
            for (i = 0; i < n; i++) {
               v = 0.0;
               for (j = 0; j < n; j++) {
                  v = v + ogrep.badgradxbase.xR[j] / s.xR[j] * a1.xyR[i][j];
               }
               jactrue.xyR[1][i] = v;
               jacdefect.xyR[1][i] = v;
            }
            if (defecttype == 0) {
               jacdefect.xyR[funcidx][varidx] = 0.0;
            }
            if (defecttype == 1) {
               jacdefect.xyR[funcidx][varidx] = jacdefect.xyR[funcidx][varidx] + 1;
            }
            if (defecttype == 2) {
               jacdefect.xyR[funcidx][varidx] = jacdefect.xyR[funcidx][varidx] * 2;
            }
            for (i = 0; i < n; i++) {
               jactrue.xyR[0][i] = jactrue.xyR[0][i] / s.xR[i];
               jactrue.xyR[1][i] = jactrue.xyR[1][i] / s.xR[i];
               jacdefect.xyR[0][i] = jacdefect.xyR[0][i] / s.xR[i];
               jacdefect.xyR[1][i] = jacdefect.xyR[1][i] / s.xR[i];
            }

         // Check OptGuard report
            ae_set_error_flag(wereerrors, !apservisfinitematrix(&ogrep.badgraduser, 2, n, _state), __FILE__, __LINE__, "testminlmunit.ap:1741");
            ae_set_error_flag(wereerrors, !apservisfinitematrix(&ogrep.badgradnum, 2, n, _state), __FILE__, __LINE__, "testminlmunit.ap:1742");
            if (*wereerrors) {
               ae_frame_leave(_state);
               return;
            }
            if (defecttype >= 0) {
               ae_set_error_flag(wereerrors, !ogrep.badgradsuspected, __FILE__, __LINE__, "testminlmunit.ap:1747");
               ae_set_error_flag(wereerrors, ogrep.badgradfidx != funcidx, __FILE__, __LINE__, "testminlmunit.ap:1748");
               ae_set_error_flag(wereerrors, ogrep.badgradvidx != varidx, __FILE__, __LINE__, "testminlmunit.ap:1749");
            } else {
               ae_set_error_flag(wereerrors, ogrep.badgradsuspected, __FILE__, __LINE__, "testminlmunit.ap:1753");
               ae_set_error_flag(wereerrors, ogrep.badgradfidx != -1, __FILE__, __LINE__, "testminlmunit.ap:1754");
               ae_set_error_flag(wereerrors, ogrep.badgradvidx != -1, __FILE__, __LINE__, "testminlmunit.ap:1755");
            }
            for (i = 0; i <= 1; i++) {
               for (j = 0; j < n; j++) {
                  ae_set_error_flag(wereerrors, ae_fabs(jactrue.xyR[i][j] - ogrep.badgradnum.xyR[i][j], _state) > 0.01 / s.xR[j], __FILE__, __LINE__, "testminlmunit.ap:1760");
                  ae_set_error_flag(wereerrors, ae_fabs(jacdefect.xyR[i][j] - ogrep.badgraduser.xyR[i][j], _state) > 0.01 / s.xR[j], __FILE__, __LINE__, "testminlmunit.ap:1761");
               }
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Asserts that State fields are consistent with RKind.
// Returns False otherwise.
//
// RKind is an algorithm selector:
// * -2 = V, AccType=1
// * -1 = V, AccType=0
// *  0 = FJ
// *  1 = FGJ
// *  2 = FGH
// *  3 = VJ, AccType=0
// *  4 = VJ, AccType=1
// *  5 = VJ, AccType=2
//
static bool testminlmunit_rkindvsstatecheck(ae_int_t rkind, minlmstate *state, ae_state *_state) {
   ae_int_t nset;
   bool result;

   nset = 0;
   if (state->needfi) {
      nset = nset + 1;
   }
   if (state->needf) {
      nset = nset + 1;
   }
   if (state->needfg) {
      nset = nset + 1;
   }
   if (state->needfij) {
      nset = nset + 1;
   }
   if (state->needfgh) {
      nset = nset + 1;
   }
   if (state->xupdated) {
      nset = nset + 1;
   }
   if (nset != 1) {
      result = false;
      return result;
   }
   if (rkind == -2) {
      result = state->needfi || state->xupdated;
      return result;
   }
   if (rkind == -1) {
      result = state->needfi || state->xupdated;
      return result;
   }
   if (rkind == 0) {
      result = (state->needf || state->needfij) || state->xupdated;
      return result;
   }
   if (rkind == 1) {
      result = ((state->needf || state->needfij) || state->needfg) || state->xupdated;
      return result;
   }
   if (rkind == 2) {
      result = ((state->needf || state->needfg) || state->needfgh) || state->xupdated;
      return result;
   }
   if (rkind == 3) {
      result = (state->needfi || state->needfij) || state->xupdated;
      return result;
   }
   if (rkind == 4) {
      result = (state->needfi || state->needfij) || state->xupdated;
      return result;
   }
   if (rkind == 5) {
      result = (state->needfi || state->needfij) || state->xupdated;
      return result;
   }
   result = false;
   return result;
}

// Calculates FI/F/G/H for problem min(||Ax-b||)
static void testminlmunit_axmb(minlmstate *state, RMatrix *a, RVector *b, ae_int_t n, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v;

   if ((state->needf || state->needfg) || state->needfgh) {
      state->f = 0.0;
   }
   if (state->needfg || state->needfgh) {
      for (i = 0; i < n; i++) {
         state->g.xR[i] = 0.0;
      }
   }
   if (state->needfgh) {
      for (i = 0; i < n; i++) {
         for (j = 0; j < n; j++) {
            state->h.xyR[i][j] = 0.0;
         }
      }
   }
   for (i = 0; i < n; i++) {
      v = ae_v_dotproduct(a->xyR[i], 1, state->x.xR, 1, n);
      if ((state->needf || state->needfg) || state->needfgh) {
         state->f = state->f + ae_sqr(v - b->xR[i], _state);
      }
      if (state->needfg || state->needfgh) {
         for (j = 0; j < n; j++) {
            state->g.xR[j] = state->g.xR[j] + 2 * (v - b->xR[i]) * a->xyR[i][j];
         }
      }
      if (state->needfgh) {
         for (j = 0; j < n; j++) {
            for (k = 0; k < n; k++) {
               state->h.xyR[j][k] = state->h.xyR[j][k] + 2 * a->xyR[i][j] * a->xyR[i][k];
            }
         }
      }
      if (state->needfi) {
         state->fi.xR[i] = v - b->xR[i];
      }
      if (state->needfij) {
         state->fi.xR[i] = v - b->xR[i];
         ae_v_move(state->j.xyR[i], 1, a->xyR[i], 1, n);
      }
   }
}

// This function tries to reproduce previously fixed bugs; in case of bug
// being present sets Err to True; leaves it unchanged otherwise.
static void testminlmunit_tryreproducefixedbugs(bool *err, ae_state *_state) {
   ae_frame _frame_block;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlmstate, s, _state);
   NewObj(minlmreport, rep, _state);
   NewVector(bl, 0, DT_REAL, _state);
   NewVector(bu, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);

// Reproduce bug reported by ISS:
// when solving bound constrained problem with numerical differentiation
// and starting from infeasible point, we won't stop at the feasible point
   ae_vector_set_length(&x, 2, _state);
   ae_vector_set_length(&bl, 2, _state);
   ae_vector_set_length(&bu, 2, _state);
   x.xR[0] = 2.0;
   bl.xR[0] = -1.0;
   bu.xR[0] = 1.0;
   x.xR[1] = 2.0;
   bl.xR[1] = -1.0;
   bu.xR[1] = 1.0;
   minlmcreatev(2, 2, &x, 0.001, &s, _state);
   minlmsetbc(&s, &bl, &bu, _state);
   while (minlmiteration(&s, _state)) {
      if (s.needfi) {
         s.fi.xR[0] = ae_sqr(s.x.xR[0], _state);
         s.fi.xR[1] = ae_sqr(s.x.xR[1], _state);
      }
   }
   minlmresults(&s, &x, &rep, _state);
   ae_set_error_flag(err, ((x.xR[0] < bl.xR[0] || x.xR[0] > bu.xR[0]) || x.xR[1] < bl.xR[1]) || x.xR[1] > bu.xR[1], __FILE__, __LINE__, "testminlmunit.ap:1923");
   ae_frame_leave(_state);
}

// Test function 1:
//
//     F(N, M, C, X) = SUM( f_i^2 )
//     f_i = SUM( (power(x_j,3)+alpha*x_j)*c_ij )
static void testminlmunit_testfunc1(ae_int_t n, ae_int_t m, RMatrix *c, RVector *x, double *f, bool needf, RVector *fi, bool needfi, RMatrix *jac, bool needjac, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double v;
   double alpha;

   alpha = 0.01;
   if (needf) {
      *f = 0.0;
   }
   for (i = 0; i < m; i++) {
      v = c->xyR[i][n];
      for (j = 0; j < n; j++) {
         v = v + (alpha * x->xR[j] + ae_pow(x->xR[j], 3.0, _state)) * c->xyR[i][j];
         if (needjac) {
            jac->xyR[i][j] = (alpha + 3 * ae_pow(x->xR[j], 2.0, _state)) * c->xyR[i][j];
         }
      }
      if (needfi) {
         fi->xR[i] = v;
      }
      if (needf) {
         *f = *f + v * v;
      }
   }
}

