         tooclose = (tooclose || ae_fabs(va, _state) < 1.0E-8) || ae_fabs(vb, _state) < 1.0E-8;
         hasc1discontinuities = hasc1discontinuities || ae_sign(va, _state) != ae_sign(vb, _state);
