bool testminlp(bool silent, ae_state *_state) {
   bool basicerrors;
   bool singlecall;
   bool wereerrors;
   bool result;

   basicerrors = false;
   singlecall = false;
   result = true;
   testminlpunit_basictests(&basicerrors, _state);
   testminlpunit_singlecalltests(&singlecall, _state);

// report
   wereerrors = basicerrors || singlecall;
   if (!silent) {
      printf("TESTING MinLP\n");
      printf("BASIC TESTS                               ");
      if (basicerrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("COMMON TESTS:\n");
      printf("* single call usage                       ");
      if (singlecall) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (wereerrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !wereerrors;
   return result;
}

// Basic tests; sets error flag on failure, does not touch it on success.
static void testminlpunit_basictests(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t n;
   ae_int_t ccnt;
   ae_int_t pass;
   double v0;
   double v1;
   double errslack;
   double primtol;
   double dualtol;
   double slacktol;
   ae_int_t solvertype;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlpstate, state, _state);
   NewObj(minlpreport, rep, _state);
   NewVector(c, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x2, 0, DT_REAL, _state);
   NewVector(bndl, 0, DT_REAL, _state);
   NewVector(bndu, 0, DT_REAL, _state);
   NewVector(al, 0, DT_REAL, _state);
   NewVector(au, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   primtol = 1.0E-4;
   dualtol = 1.0E-4;
   slacktol = 1.0E-4;
   hqrndrandomize(&rs, _state);

// Test default state of the solver
   for (pass = 1; pass <= 5; pass++) {
      n = 1 + hqrnduniformi(&rs, 10, _state);
      minlpcreate(n, &state, _state);
      testminlpunit_selectrandomsolver(&state, _state);
      minlpoptimize(&state, _state);
      minlpresults(&state, &x, &rep, _state);
      ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:100");
      ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:101");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(err, x.xR[i] != 0.0, __FILE__, __LINE__, "testminlpunit.ap:105");
      }
   }

// Test box constrained problems without linear constraints
   for (n = 1; n <= 10; n++) {
      for (pass = 1; pass <= 20; pass++) {
         solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);
         ae_vector_set_length(&c, n, _state);
         ae_vector_set_length(&bndl, n, _state);
         ae_vector_set_length(&bndu, n, _state);

      // Feasible bounded problems
         for (i = 0; i < n; i++) {
            c.xR[i] = hqrndnormal(&rs, _state);
            bndl.xR[i] = hqrndnormal(&rs, _state);
            bndu.xR[i] = bndl.xR[i] + hqrnduniformi(&rs, 3, _state);
         }
         testminlpunit_shiftfromzero(&c, n, 0.01, _state);
         minlpcreate(n, &state, _state);
         testminlpunit_selectsolver(&state, solvertype, _state);
         minlpsetcost(&state, &c, _state);
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:135");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:136");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:141");
            ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:142");
            ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:143");
            ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:144");
         }
         for (i = 0; i < n; i++) {
            c.xR[i] = hqrndnormal(&rs, _state);
            v0 = hqrndnormal(&rs, _state);
            v1 = hqrndnormal(&rs, _state);
            bndl.xR[i] = ae_minreal(v0, v1, _state);
            bndu.xR[i] = ae_maxreal(v0, v1, _state);
            if (c.xR[i] > 0.0) {
               bndu.xR[i] = _state->v_posinf;
            }
            if (c.xR[i] < 0.0) {
               bndl.xR[i] = _state->v_neginf;
            }
         }
         testminlpunit_shiftfromzero(&c, n, 0.01, _state);
         minlpcreate(n, &state, _state);
         testminlpunit_selectsolver(&state, solvertype, _state);
         minlpsetcost(&state, &c, _state);
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:166");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:167");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:172");
            ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:173");
            ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:174");
            ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:175");
         }

      // Feasible bounded problems with zeros in cost vector
         for (i = 0; i < n; i++) {
            c.xR[i] = hqrndnormal(&rs, _state);
            bndl.xR[i] = hqrndnormal(&rs, _state);
            bndu.xR[i] = bndl.xR[i] + hqrnduniformi(&rs, 3, _state);
         }
         testminlpunit_shiftfromzero(&c, n, 0.01, _state);
         k = hqrnduniformi(&rs, n, _state);
         c.xR[k] = 0.0;
         bndl.xR[k] = _state->v_neginf;
         bndu.xR[k] = _state->v_posinf;
         minlpcreate(n, &state, _state);
         testminlpunit_selectsolver(&state, solvertype, _state);
         minlpsetcost(&state, &c, _state);
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:199");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:200");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:205");
            ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:206");
            ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:207");
            ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:208");
         }
         ae_set_error_flag(err, x.xR[k] != 0.0, __FILE__, __LINE__, "testminlpunit.ap:210");
         bndl.xR[k] = hqrndnormal(&rs, _state);
         bndu.xR[k] = _state->v_posinf;
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:217");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:218");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:223");
            ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:224");
            ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:225");
            ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:226");
         }
         ae_set_error_flag(err, x.xR[k] < bndl.xR[k], __FILE__, __LINE__, "testminlpunit.ap:228");
         ae_set_error_flag(err, solvertype == 0 && x.xR[k] != bndl.xR[k], __FILE__, __LINE__, "testminlpunit.ap:229");
         bndl.xR[k] = _state->v_neginf;
         bndu.xR[k] = hqrndnormal(&rs, _state);
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:236");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:237");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:242");
            ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:243");
            ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:244");
            ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:245");
         }
         ae_set_error_flag(err, x.xR[k] > bndu.xR[k], __FILE__, __LINE__, "testminlpunit.ap:247");
         ae_set_error_flag(err, solvertype == 0 && x.xR[k] != bndu.xR[k], __FILE__, __LINE__, "testminlpunit.ap:248");

      // Infeasible problems
         ae_vector_set_length(&x, 0, _state);
         for (i = 0; i < n; i++) {
            c.xR[i] = hqrndnormal(&rs, _state);
            bndl.xR[i] = hqrndnormal(&rs, _state);
            bndu.xR[i] = bndl.xR[i] + hqrnduniformi(&rs, 3, _state);
         }
         k = hqrnduniformi(&rs, n, _state);
         bndu.xR[k] = bndl.xR[k] - 1;
         minlpcreate(n, &state, _state);
         testminlpunit_selectsolver(&state, solvertype, _state);
         minlpsetcost(&state, &c, _state);
         minlpsetbc(&state, &bndl, &bndu, _state);
         minlpoptimize(&state, _state);
         minlpresults(&state, &x, &rep, _state);
         ae_set_error_flag(err, rep.terminationtype != -3, __FILE__, __LINE__, "testminlpunit.ap:268");
         ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:269");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
      // Unbounded problems
      //
      // NOTE: we solve it without regularization, because with regularizer any LP problem is bounded
         if (solvertype != 1) {
            ae_vector_set_length(&x, 0, _state);
            for (i = 0; i < n; i++) {
               do {
                  c.xR[i] = hqrndnormal(&rs, _state);
               } while (c.xR[i] == 0.0);
               bndl.xR[i] = hqrndnormal(&rs, _state);
               bndu.xR[i] = bndl.xR[i] + hqrnduniformi(&rs, 3, _state);
            }
            k = hqrnduniformi(&rs, n, _state);
            if (c.xR[k] > 0.0) {
               bndl.xR[k] = _state->v_neginf;
            } else {
               bndu.xR[k] = _state->v_posinf;
            }
            minlpcreate(n, &state, _state);
            testminlpunit_selectsolver(&state, solvertype, _state);
            minlpsetcost(&state, &c, _state);
            minlpsetbc(&state, &bndl, &bndu, _state);
            minlpoptimize(&state, _state);
            minlpresults(&state, &x, &rep, _state);
            ae_set_error_flag(err, rep.terminationtype != -4, __FILE__, __LINE__, "testminlpunit.ap:300");
            ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:301");
            if (*err) {
               ae_frame_leave(_state);
               return;
            }
         }
      }
   }

// Test linearly constrained problems with all variables being boxed ones
// and with all linear constraints being inequality ones satisfied in the
// box internals.
   for (n = 1; n <= 10; n++) {
      solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);
      ccnt = 1 + hqrnduniformi(&rs, 10, _state);
      ae_vector_set_length(&c, n, _state);
      ae_vector_set_length(&bndl, n, _state);
      ae_vector_set_length(&bndu, n, _state);
      ae_matrix_set_length(&a, ccnt, n, _state);
      ae_vector_set_length(&al, ccnt, _state);
      ae_vector_set_length(&au, ccnt, _state);
      for (i = 0; i < n; i++) {
         c.xR[i] = hqrndnormal(&rs, _state);
         bndl.xR[i] = -0.5 - 0.5 * hqrnduniformr(&rs, _state);
         bndu.xR[i] = 0.5 + 0.5 * hqrnduniformr(&rs, _state);
      }
      testminlpunit_shiftfromzero(&c, n, 0.01, _state);
      for (i = 0; i < ccnt; i++) {
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = 2 * hqrnduniformr(&rs, _state) - 1;
         }
         al.xR[i] = (double)(-2 * n);
         au.xR[i] = (double)(2 * n);
      }
      minlpcreate(n, &state, _state);
      testminlpunit_selectsolver(&state, solvertype, _state);
      minlpsetcost(&state, &c, _state);
      minlpsetbc(&state, &bndl, &bndu, _state);
      minlpsetlc2dense(&state, &a, &al, &au, ccnt, _state);
      minlpoptimize(&state, _state);
      minlpresults(&state, &x, &rep, _state);
      ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:343");
      ae_set_error_flag(err, x.cnt < n, __FILE__, __LINE__, "testminlpunit.ap:344");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(err, c.xR[i] > 0.0 && (x.xR[i] < bndl.xR[i] || x.xR[i] > bndl.xR[i] + primtol), __FILE__, __LINE__, "testminlpunit.ap:349");
         ae_set_error_flag(err, c.xR[i] < 0.0 && (x.xR[i] > bndu.xR[i] || x.xR[i] < bndu.xR[i] - primtol), __FILE__, __LINE__, "testminlpunit.ap:350");
         ae_set_error_flag(err, (c.xR[i] > 0.0 && solvertype == 0) && x.xR[i] != bndl.xR[i], __FILE__, __LINE__, "testminlpunit.ap:351");
         ae_set_error_flag(err, (c.xR[i] < 0.0 && solvertype == 0) && x.xR[i] != bndu.xR[i], __FILE__, __LINE__, "testminlpunit.ap:352");
      }
   }

// Test linearly constrained problems with all variables being boxed ones
// and with random linear constraints, with at least one feasible point
// in the box internals.
   for (n = 1; n <= 10; n++) {
      solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);
      ccnt = 1 + hqrnduniformi(&rs, 10, _state);
      ae_vector_set_length(&c, n, _state);
      ae_vector_set_length(&x0, n, _state);
      ae_vector_set_length(&bndl, n, _state);
      ae_vector_set_length(&bndu, n, _state);
      ae_matrix_set_length(&a, ccnt, n, _state);
      ae_vector_set_length(&al, ccnt, _state);
      ae_vector_set_length(&au, ccnt, _state);
      for (i = 0; i < n; i++) {
         c.xR[i] = hqrndnormal(&rs, _state);
         bndl.xR[i] = -0.5 - 0.5 * hqrnduniformr(&rs, _state);
         bndu.xR[i] = 0.5 + 0.5 * hqrnduniformr(&rs, _state);
         x0.xR[i] = 0.5 * (bndl.xR[i] + bndu.xR[i]);
      }
      for (i = 0; i < ccnt; i++) {
         v0 = 0.0;
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = hqrndnormal(&rs, _state);
            v0 = v0 + a.xyR[i][j] * x0.xR[j];
         }
         al.xR[i] = v0 - 0.25 * hqrnduniformr(&rs, _state);
         au.xR[i] = v0 + 0.25 * hqrnduniformr(&rs, _state);
      }
      minlpcreate(n, &state, _state);
      testminlpunit_selectsolver(&state, solvertype, _state);
      minlpsetcost(&state, &c, _state);
      minlpsetbc(&state, &bndl, &bndu, _state);
      minlpsetlc2dense(&state, &a, &al, &au, ccnt, _state);
      minlpoptimize(&state, _state);
      minlpresults(&state, &x, &rep, _state);
      ae_set_error_flag(err, rep.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:397");
      ae_set_error_flag(err, x.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:398");
      ae_set_error_flag(err, rep.y.cnt != ccnt, __FILE__, __LINE__, "testminlpunit.ap:399");
      ae_set_error_flag(err, rep.stats.cnt != n + ccnt, __FILE__, __LINE__, "testminlpunit.ap:400");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      testminlpunit_validatesolution(&c, &bndl, &bndu, n, &a, &al, &au, ccnt, &x, &rep, solvertype, &v0, &v1, &errslack, _state);
      ae_set_error_flag(err, ae_fabs(v0, _state) > primtol, __FILE__, __LINE__, "testminlpunit.ap:404");
      ae_set_error_flag(err, ae_fabs(v1, _state) > dualtol, __FILE__, __LINE__, "testminlpunit.ap:405");
      ae_set_error_flag(err, ae_fabs(errslack, _state) > slacktol, __FILE__, __LINE__, "testminlpunit.ap:406");
   }
   ae_frame_leave(_state);
}

// Single-call tests; sets error flag on failure, does not touch it on success.
static void testminlpunit_singlecalltests(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t pass;
   ae_int_t i;
   ae_int_t j;
   ae_int_t m;
   ae_int_t n0;
   double v0;
   double v1;
   double primtol;
   double dualtol;
   double slacktol;
   double etol;
   double ftol;
   double f;
   double f1;
   double errp;
   double errd;
   double errs;
   double alpha;
   ae_int_t solvertype;

   ae_frame_make(_state, &_frame_block);
   NewObj(minlpstate, state0, _state);
   NewObj(minlpreport, rep0, _state);
   NewObj(minlpstate, state1, _state);
   NewObj(minlpreport, rep1, _state);
   NewVector(c, 0, DT_REAL, _state);
   NewVector(bndl, 0, DT_REAL, _state);
   NewVector(bndu, 0, DT_REAL, _state);
   NewVector(al, 0, DT_REAL, _state);
   NewVector(au, 0, DT_REAL, _state);
   NewVector(s, 0, DT_REAL, _state);
   NewVector(bndls, 0, DT_REAL, _state);
   NewVector(bndus, 0, DT_REAL, _state);
   NewVector(cs, 0, DT_REAL, _state);
   NewMatrix(a, 0, 0, DT_REAL, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   primtol = 1.0E-3;
   dualtol = 1.0E-3;
   slacktol = 1.0E-3;
   etol = 1.0E-8;
   ftol = 1.0E-3;
   hqrndrandomize(&rs, _state);

// Try different feasible problems
   for (n = 1; n <= 50; n++) {
      for (pass = 1; pass <= 30; pass++) {
         solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);

      // Generate random feasible problem and solve it using basic MinLPSetLC2Dense() API
         testminlpunit_generatelpproblem(&rs, n, &c, &bndl, &bndu, &a, &al, &au, &m, _state);
         minlpcreate(n, &state0, _state);
         testminlpunit_selectsolver(&state0, solvertype, _state);
         minlpsetcost(&state0, &c, _state);
         minlpsetbc(&state0, &bndl, &bndu, _state);
         minlpsetlc2dense(&state0, &a, &al, &au, m, _state);
         minlpoptimize(&state0, _state);
         minlpresults(&state0, &x0, &rep0, _state);
         ae_set_error_flag(err, rep0.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:470");
         ae_set_error_flag(err, x0.cnt != n || !isfinitevector(&x0, n, _state), __FILE__, __LINE__, "testminlpunit.ap:471");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         testminlpunit_validatesolution(&c, &bndl, &bndu, n, &a, &al, &au, m, &x0, &rep0, solvertype, &errp, &errd, &errs, _state);
         ae_set_error_flag(err, ae_fabs(errp, _state) > primtol, __FILE__, __LINE__, "testminlpunit.ap:475");
         ae_set_error_flag(err, ae_fabs(errd, _state) > dualtol, __FILE__, __LINE__, "testminlpunit.ap:476");
         ae_set_error_flag(err, ae_fabs(errs, _state) > slacktol, __FILE__, __LINE__, "testminlpunit.ap:477");
         ae_set_error_flag(err, ae_fabs(errp - rep0.primalerror, _state) > etol * ae_maxreal(errp, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:478");
         ae_set_error_flag(err, ae_fabs(errd - rep0.dualerror, _state) > etol * ae_maxreal(errd, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:479");
         ae_set_error_flag(err, ae_fabs(errs - rep0.slackerror, _state) > etol * ae_maxreal(errs, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:480");

      // Apply random modification to the problem (and to the way
      // we pass constraints to the solver), try solving one more time
      // and compare with the original solution.
      //
      // NOTE: because our test suite has degenerate problems, we do
      //       not compare points returned - only target values are
      //       compared.
         ae_vector_set_length(&x1, 0, _state);
         minlpcreate(n, &state1, _state);
         testminlpunit_selectsolver(&state1, solvertype, _state);
         minlpsetcost(&state1, &c, _state);
         minlpsetbc(&state1, &bndl, &bndu, _state);
         testminlpunit_modifyandsendconstraintsto(n, &a, &al, &au, m, &rs, &state1, _state);
         minlpoptimize(&state1, _state);
         minlpresults(&state1, &x1, &rep1, _state);
         ae_set_error_flag(err, rep1.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:500");
         ae_set_error_flag(err, x1.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:501");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         f = 0.0;
         f1 = 0.0;
         for (i = 0; i < n; i++) {
            f = f + x0.xR[i] * c.xR[i];
            f1 = f1 + x1.xR[i] * c.xR[i];
         }
         ae_set_error_flag(err, ae_fabs(f1 - f, _state) > ftol, __FILE__, __LINE__, "testminlpunit.ap:511");

      // Test scaling (random scale is applied, target values for
      // original and scaled problems are compared).
      //
      // NOTE: because our test suite has degenerate problems, we do
      //       not compare points returned - only target values.
         ae_vector_set_length(&x1, 0, _state);
         ae_vector_set_length(&s, n, _state);
         ae_vector_set_length(&cs, n, _state);
         ae_vector_set_length(&bndls, n, _state);
         ae_vector_set_length(&bndus, n, _state);
         for (i = 0; i < n; i++) {
            s.xR[i] = ae_pow(2.0, (double)(hqrnduniformi(&rs, 21, _state) - 10), _state);
            cs.xR[i] = c.xR[i] / s.xR[i];
            bndls.xR[i] = bndl.xR[i] * s.xR[i];
            bndus.xR[i] = bndu.xR[i] * s.xR[i];
         }
         ae_matrix_set_length(&a1, m, n, _state);
         for (i = 0; i < m; i++) {
            for (j = 0; j < n; j++) {
               a1.xyR[i][j] = a.xyR[i][j] / s.xR[j];
            }
         }
         minlpcreate(n, &state1, _state);
         testminlpunit_selectsolver(&state1, solvertype, _state);
         minlpsetscale(&state1, &s, _state);
         minlpsetcost(&state1, &cs, _state);
         minlpsetbc(&state1, &bndls, &bndus, _state);
         minlpsetlc2dense(&state1, &a1, &al, &au, m, _state);
         minlpoptimize(&state1, _state);
         minlpresults(&state1, &x1, &rep1, _state);
         ae_set_error_flag(err, rep1.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:544");
         ae_set_error_flag(err, x1.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:545");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n; i++) {
            ae_set_error_flag(err, ae_fabs(x0.xR[i] - x1.xR[i] / s.xR[i], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testminlpunit.ap:557");
         }
      }
   }

// Test infeasible/unbounded problems
   for (n = 1; n <= 50; n++) {
      for (pass = 1; pass <= 30; pass++) {
         solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);

      // Generate random primal unbounded
      //
      // NOTE: because we use constraint validation code, we can not
      //       use ModifyAndSendConstraintsTo() function - it permutes
      //       constraints order and prevents correct validation.
         ae_vector_set_length(&x0, 0, _state);
         testminlpunit_generateunboundedlpproblem(&rs, n, &c, &bndl, &bndu, &a, &al, &au, &m, _state);
         minlpcreate(n, &state0, _state);
         testminlpunit_selectsolver(&state0, solvertype, _state);
         minlpsetcost(&state0, &c, _state);
         minlpsetbc(&state0, &bndl, &bndu, _state);
         minlpsetlc2dense(&state0, &a, &al, &au, m, _state);
         minlpoptimize(&state0, _state);
         minlpresults(&state0, &x0, &rep0, _state);
         ae_set_error_flag(err, rep0.terminationtype != -4 && rep0.terminationtype != -2, __FILE__, __LINE__, "testminlpunit.ap:584");
         ae_set_error_flag(err, x0.cnt != n || !isfinitevector(&x0, n, _state), __FILE__, __LINE__, "testminlpunit.ap:585");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         testminlpunit_validatesolution(&c, &bndl, &bndu, n, &a, &al, &au, m, &x0, &rep0, solvertype, &errp, &errd, &errs, _state);
         ae_set_error_flag(err, ae_fabs(errp - rep0.primalerror, _state) > etol * rmax3(errp, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:589");
         ae_set_error_flag(err, ae_fabs(errd - rep0.dualerror, _state) > etol * rmax3(errd, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:590");
         ae_set_error_flag(err, ae_fabs(errs - rep0.slackerror, _state) > etol * rmax3(errs, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:591");

      // Generate random primal infeasible
      //
      // NOTE: because we use constraint validation code, we can not
      //       use ModifyAndSendConstraintsTo() function - it permutes
      //       constraints order and prevents correct validation.
         ae_vector_set_length(&x0, 0, _state);
         testminlpunit_generateinfeasiblelpproblem(&rs, n, &c, &bndl, &bndu, &a, &al, &au, &m, _state);
         minlpcreate(n, &state0, _state);
         testminlpunit_selectsolver(&state0, solvertype, _state);
         minlpsetcost(&state0, &c, _state);
         minlpsetbc(&state0, &bndl, &bndu, _state);
         minlpsetlc2dense(&state0, &a, &al, &au, m, _state);
         minlpoptimize(&state0, _state);
         minlpresults(&state0, &x0, &rep0, _state);
         ae_set_error_flag(err, rep0.terminationtype != -3 && rep0.terminationtype != -2, __FILE__, __LINE__, "testminlpunit.ap:610");
         ae_set_error_flag(err, x0.cnt != n || !isfinitevector(&x0, n, _state), __FILE__, __LINE__, "testminlpunit.ap:611");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         testminlpunit_validatesolution(&c, &bndl, &bndu, n, &a, &al, &au, m, &x0, &rep0, solvertype, &errp, &errd, &errs, _state);
         ae_set_error_flag(err, ae_fabs(errp - rep0.primalerror, _state) > etol * rmax3(errp, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:615");
         ae_set_error_flag(err, ae_fabs(errd - rep0.dualerror, _state) > etol * rmax3(errd, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:616");
         ae_set_error_flag(err, ae_fabs(errs - rep0.slackerror, _state) > etol * rmax3(errs, rmaxabsv(n, &x0, _state), 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:617");
      }
   }
   for (n = 2; n <= 50; n++) {
      for (pass = 1; pass <= 30; pass++) {

      // Special test for simplex solver.
      //
      // Generate carefully crafted primal infeasible - the problem is ALMOST feasible.
      // So, we expect simplex solver to stop at the best point possible. We also test
      // that X and Y are correctly unscaled on return.
         ae_assert(n >= 2, "LPTEST: integrity check failed", _state);
         n0 = n / 2;
         ae_assert(n0 < n, "LPTEST: integrity check failed", _state);
         alpha = 0.0001;
         ae_vector_set_length(&x0, 0, _state);
         ae_vector_set_length(&c, n, _state);
         ae_vector_set_length(&s, n, _state);
         ae_vector_set_length(&bndl, n, _state);
         ae_vector_set_length(&bndu, n, _state);
         for (i = 0; i < n; i++) {
            if (i < n0) {
               c.xR[i] = -ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            } else {
               c.xR[i] = hqrnduniformr(&rs, _state) - 0.5;
            }
            bndl.xR[i] = -ae_pow(2.0, hqrndnormal(&rs, _state), _state);
            bndu.xR[i] = 1.0;
            s.xR[i] = ae_pow(2.0, 2 * hqrnduniformr(&rs, _state) - 1, _state);
         }
         m = 1 + hqrnduniformi(&rs, n, _state);
         ae_matrix_set_length(&a, m, n, _state);
         ae_vector_set_length(&al, m, _state);
         ae_vector_set_length(&au, m, _state);
         for (i = 0; i < m - 1; i++) {
            for (j = 0; j < n0; j++) {
               a.xyR[i][j] = 0.0;
            }
            v0 = 0.0;
            for (j = n0; j < n; j++) {
               a.xyR[i][j] = hqrndnormal(&rs, _state);
               v0 = v0 + 0.5 * a.xyR[i][j];
            }
            al.xR[i] = v0 - ae_pow(2.0, hqrndnormal(&rs, _state), _state);
            au.xR[i] = v0 + ae_pow(2.0, hqrndnormal(&rs, _state), _state);
         }
         v0 = 0.0;
         for (i = 0; i < n; i++) {
            if (i < n0) {
               a.xyR[m - 1][i] = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
            } else {
               a.xyR[m - 1][i] = 0.0;
            }
            v0 = v0 + a.xyR[m - 1][i];
         }
         al.xR[m - 1] = v0 + alpha;
         au.xR[m - 1] = al.xR[m - 1];
         minlpcreate(n, &state0, _state);
         minlpsetalgodss(&state0, 0.0, _state);
         minlpsetcost(&state0, &c, _state);
         if (hqrndnormal(&rs, _state) > 0.0) {
            minlpsetscale(&state0, &s, _state);
         }
         minlpsetbc(&state0, &bndl, &bndu, _state);
         minlpsetlc2dense(&state0, &a, &al, &au, m, _state);
         minlpoptimize(&state0, _state);
         minlpresults(&state0, &x0, &rep0, _state);
         ae_set_error_flag(err, rep0.terminationtype != -3 && rep0.terminationtype != -2, __FILE__, __LINE__, "testminlpunit.ap:686");
         ae_set_error_flag(err, x0.cnt != n || !isfinitevector(&x0, n, _state), __FILE__, __LINE__, "testminlpunit.ap:687");
         if (*err) {
            ae_frame_leave(_state);
            return;
         }
         for (i = 0; i < n0; i++) {
            ae_set_error_flag(err, x0.xR[i] < 1.0, __FILE__, __LINE__, "testminlpunit.ap:691");
         }
         testminlpunit_validatesolution(&c, &bndl, &bndu, n, &a, &al, &au, m, &x0, &rep0, solvertype, &errp, &errd, &errs, _state);
         ae_set_error_flag(err, ae_fabs(errd, _state) > 0.001, __FILE__, __LINE__, "testminlpunit.ap:693");
         ae_set_error_flag(err, ae_fabs(errp - rep0.primalerror, _state) > etol * ae_maxreal(errp, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:694");
         ae_set_error_flag(err, ae_fabs(errd - rep0.dualerror, _state) > etol * ae_maxreal(errd, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:695");
         ae_set_error_flag(err, ae_fabs(errs - rep0.slackerror, _state) > etol * ae_maxreal(errs, 1.0, _state), __FILE__, __LINE__, "testminlpunit.ap:696");
      }
   }

// Check SetBCAll() and SetBCi()
//
// We generate random problem with box constraints
// L <= x[i] <= U, with L<0<U, and random linear constraints
// feasible at x=0.
//
// In order to test SetBCAll() we solve it two times,
// first one with box constraints specified via setbc(),
// second one with setbcall(). Both solutions are compared.
//
// After that we rewrite box constraints to be different
// for each variable, and send them to the first solver
// via setbc() and to the second one via sequential setbci().
// Both solutions are compared.
   for (n = 1; n <= 30; n++) {
      solvertype = hqrnduniformi(&rs, testminlpunit_solverscount, _state);
      v0 = -ae_pow(2.0, hqrndnormal(&rs, _state), _state);
      v1 = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
      m = hqrnduniformi(&rs, 2 * n, _state);
      ae_vector_set_length(&c, n, _state);
      ae_vector_set_length(&bndl, n, _state);
      ae_vector_set_length(&bndu, n, _state);
      ae_matrix_set_length(&a, m, n, _state);
      ae_vector_set_length(&al, m, _state);
      ae_vector_set_length(&au, m, _state);
      for (i = 0; i < n; i++) {
         c.xR[i] = hqrndnormal(&rs, _state);
         bndl.xR[i] = v0;
         bndu.xR[i] = v1;
      }
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            a.xyR[i][j] = hqrnduniformi(&rs, 2, _state) * hqrndnormal(&rs, _state);
         }
         al.xR[i] = -ae_pow(2.0, hqrndnormal(&rs, _state), _state);
         au.xR[i] = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
      }

   // SetBCAll() vs SetBC()
      ae_vector_set_length(&x0, hqrnduniformi(&rs, 2 * n, _state), _state);
      ae_vector_set_length(&x1, hqrnduniformi(&rs, 2 * n, _state), _state);
      minlpcreate(n, &state0, _state);
      testminlpunit_selectsolver(&state0, solvertype, _state);
      minlpsetcost(&state0, &c, _state);
      minlpsetbc(&state0, &bndl, &bndu, _state);
      minlpsetlc2dense(&state0, &a, &al, &au, m, _state);
      minlpoptimize(&state0, _state);
      minlpresults(&state0, &x0, &rep0, _state);
      ae_set_error_flag(err, rep0.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:754");
      ae_set_error_flag(err, x0.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:755");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      minlpcreate(n, &state1, _state);
      testminlpunit_selectsolver(&state1, solvertype, _state);
      minlpsetcost(&state1, &c, _state);
      minlpsetbcall(&state1, v0, v1, _state);
      minlpsetlc2dense(&state1, &a, &al, &au, m, _state);
      minlpoptimize(&state1, _state);
      minlpresults(&state1, &x1, &rep1, _state);
      ae_set_error_flag(err, rep1.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:765");
      ae_set_error_flag(err, x1.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:766");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(err, x0.xR[i] != x1.xR[i], __FILE__, __LINE__, "testminlpunit.ap:770");
      }

   // SetBCi() vs SetBC()
      ae_vector_set_length(&x0, hqrnduniformi(&rs, 2 * n, _state), _state);
      ae_vector_set_length(&x1, hqrnduniformi(&rs, 2 * n, _state), _state);
      for (i = 0; i < n; i++) {
         bndl.xR[i] = -ae_pow(2.0, hqrndnormal(&rs, _state), _state);
         bndu.xR[i] = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
         minlpsetbci(&state1, i, bndl.xR[i], bndu.xR[i], _state);
      }
      minlpsetbc(&state0, &bndl, &bndu, _state);
      minlpoptimize(&state0, _state);
      minlpresults(&state0, &x0, &rep0, _state);
      ae_set_error_flag(err, rep0.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:786");
      ae_set_error_flag(err, x0.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:787");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      minlpoptimize(&state1, _state);
      minlpresults(&state1, &x1, &rep1, _state);
      ae_set_error_flag(err, rep1.terminationtype <= 0, __FILE__, __LINE__, "testminlpunit.ap:792");
      ae_set_error_flag(err, x1.cnt != n, __FILE__, __LINE__, "testminlpunit.ap:793");
      if (*err) {
         ae_frame_leave(_state);
         return;
      }
      for (i = 0; i < n; i++) {
         ae_set_error_flag(err, x0.xR[i] != x1.xR[i], __FILE__, __LINE__, "testminlpunit.ap:797");
      }
   }
   ae_frame_leave(_state);
}

// This function validates solution returned by LP solver. It returns  primal
// and dual feasibility errors for this solution.
//
// SolverType:
// * 0 for dual simplex solver; additional test relying on explicit information
//   about constraint statuses is performed
// * 1 for interior point method; only straightforward dual  feasibility is
//   tested
//
static void testminlpunit_validatesolution(RVector *c, RVector *bndl, RVector *bndu, ae_int_t n, RMatrix *a, RVector *al, RVector *au, ae_int_t m, RVector *xcand, minlpreport *rep, ae_int_t solvertype, double *errprim, double *errdual, double *errslack, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double v;
   bool checkstats;

   ae_frame_make(_state, &_frame_block);
   *errprim = 0;
   *errdual = 0;
   *errslack = 0;
   NewVector(d, 0, DT_REAL, _state);

   checkstats = solvertype == 0;
   *errprim = 0.0;
   *errdual = 0.0;
   *errslack = 0.0;

// Test primal feasibility
   for (i = 0; i < n; i++) {
      if (ae_isfinite(bndl->xR[i], _state)) {
         *errprim = ae_maxreal(*errprim, bndl->xR[i] - xcand->xR[i], _state);
      }
      if (ae_isfinite(bndu->xR[i], _state)) {
         *errprim = ae_maxreal(*errprim, xcand->xR[i] - bndu->xR[i], _state);
      }
   }
   for (i = 0; i < m; i++) {
      v = rdotvr(n, xcand, a, i, _state);
      if (ae_isfinite(al->xR[i], _state)) {
         *errprim = ae_maxreal(*errprim, al->xR[i] - v, _state);
      }
      if (ae_isfinite(au->xR[i], _state)) {
         *errprim = ae_maxreal(*errprim, v - au->xR[i], _state);
      }
   }

// Test dual feasibility using Lagrange multipliers
   ae_vector_set_length(&d, n, _state);
   for (i = 0; i < n; i++) {
      d.xR[i] = c->xR[i];
   }
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         d.xR[j] = d.xR[j] + rep->laglc.xR[i] * a->xyR[i][j];
      }
   }
   for (i = 0; i < n; i++) {
      d.xR[i] = d.xR[i] + rep->lagbc.xR[i];
   }
   *errdual = ae_maxreal(*errdual, rmaxabsv(n, &d, _state), _state);

// Test complementary slackness
   for (i = 0; i < n; i++) {
      if (ae_isfinite(bndl->xR[i], _state)) {
         *errslack = ae_maxreal(*errslack, ae_maxreal(xcand->xR[i] - bndl->xR[i], 0.0, _state) * ae_maxreal(-rep->lagbc.xR[i], 0.0, _state), _state);
      }
      if (ae_isfinite(bndu->xR[i], _state)) {
         *errslack = ae_maxreal(*errslack, ae_maxreal(bndu->xR[i] - xcand->xR[i], 0.0, _state) * ae_maxreal(rep->lagbc.xR[i], 0.0, _state), _state);
      }
   }
   for (i = 0; i < m; i++) {
      v = rdotvr(n, xcand, a, i, _state);
      if (ae_isfinite(al->xR[i], _state)) {
         *errslack = ae_maxreal(*errslack, ae_maxreal(v - al->xR[i], 0.0, _state) * ae_maxreal(-rep->laglc.xR[i], 0.0, _state), _state);
      }
      if (ae_isfinite(au->xR[i], _state)) {
         *errslack = ae_maxreal(*errslack, ae_maxreal(au->xR[i] - v, 0.0, _state) * ae_maxreal(rep->laglc.xR[i], 0.0, _state), _state);
      }
   }

// Test dual feasibility using Stats[] array
   if (checkstats) {
   }
   ae_frame_leave(_state);
}

// This function generates random LP problem with  user-specified  number  of
// variables, with several problem types being randomly chosen internally.
//
// Constraint counts and types are generated randomly; it  is  possible  that
// zero amount of constraints is generated.
static void testminlpunit_generatelpproblem(hqrndstate *rs, ae_int_t n, RVector *c, RVector *bndl, RVector *bndu, RMatrix *a, RVector *al, RVector *au, ae_int_t *m, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t ptype;
   ae_int_t pcount;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t kk;
   ae_int_t kt;
   double v;
   double q;
   double big;
   double mincoeff;
   ae_int_t nprimal;
   ae_int_t ndual;

   ae_frame_make(_state, &_frame_block);
   SetVector(c);
   SetVector(bndl);
   SetVector(bndu);
   SetMatrix(a);
   SetVector(al);
   SetVector(au);
   *m = 0;
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(xx, 0, DT_REAL, _state);
   NewVector(d, 0, DT_REAL, _state);
   NewVector(basicnonbasic, 0, DT_INT, _state);

   ae_vector_set_length(&x0, n, _state);
   ae_vector_set_length(c, n, _state);
   ae_vector_set_length(bndl, n, _state);
   ae_vector_set_length(bndu, n, _state);

// Choose problem type
   pcount = 4;
   ptype = hqrnduniformi(rs, pcount, _state);
   mincoeff = 0.001;

// A linearly constrained LP problem with all variables/linear constraints
// being boxed (easy start).
   if (ptype == 0) {
      q = 5.0;
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      ae_matrix_set_length(a, *m, n, _state);
      ae_vector_set_length(al, *m, _state);
      ae_vector_set_length(au, *m, _state);
      for (i = 0; i < n; i++) {
         c->xR[i] = hqrndnormal(rs, _state);
         bndl->xR[i] = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
         bndu->xR[i] = bndl->xR[i] + ae_pow(q, hqrndnormal(rs, _state), _state);
         v = 0.01 + 0.98 * hqrnduniformr(rs, _state);
         x0.xR[i] = (1 - v) * bndl->xR[i] + v * bndu->xR[i];
      }
      for (i = 0; i < *m; i++) {
         v = 0.0;
         for (j = 0; j < n; j++) {
            a->xyR[i][j] = hqrndnormal(rs, _state) * hqrnduniformi(rs, 2, _state);
            a->xyR[i][j] = a->xyR[i][j] + ae_sign(a->xyR[i][j], _state) * mincoeff;
            v = v + a->xyR[i][j] * x0.xR[j];
         }
         al->xR[i] = v - ae_pow(q, hqrndnormal(rs, _state), _state);
         au->xR[i] = v + ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      ae_frame_leave(_state);
      return;
   }
// A linearly constrained LP problem with up to M variables (linear
// constraints) being non-boxed (free or having just one constraint).
// A bit harder than all-boxed version.
//
// NOTE: components of C[] corresponding to these non-boxed variables
//       are chosen in a way which guarantees dual feasibility of the
//       problem.
   if (ptype == 1) {
      q = 2.0;
      big = 100.00;
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      ae_matrix_set_length(a, *m, n, _state);
      ae_vector_set_length(al, *m, _state);
      ae_vector_set_length(au, *m, _state);
      for (i = 0; i < n; i++) {
         c->xR[i] = hqrndnormal(rs, _state);
         bndl->xR[i] = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
         bndu->xR[i] = bndl->xR[i] + ae_pow(q, hqrndnormal(rs, _state), _state);
         v = 0.01 + 0.98 * hqrnduniformr(rs, _state);
         x0.xR[i] = (1 - v) * bndl->xR[i] + v * bndu->xR[i];
      }
      for (i = 0; i < *m; i++) {
         v = 0.0;
         for (j = 0; j < n; j++) {
            a->xyR[i][j] = hqrndnormal(rs, _state);
            a->xyR[i][j] = a->xyR[i][j] + ae_sign(a->xyR[i][j], _state) * mincoeff;
            v = v + a->xyR[i][j] * x0.xR[j];
         }
         al->xR[i] = v - ae_pow(q, hqrndnormal(rs, _state), _state);
         au->xR[i] = v + ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      kk = 1 + hqrnduniformi(rs, *m, _state);
      for (k = 0; k < kk; k++) {
         i = hqrnduniformi(rs, n + (*m), _state);
         if (i < n) {
            kt = hqrnduniformi(rs, 3, _state);
            if (kt == 0) {
               bndl->xR[i] = _state->v_neginf;
               c->xR[i] = -big * (1 + hqrnduniformr(rs, _state));
            }
            if (kt == 1) {
               bndu->xR[i] = _state->v_posinf;
               c->xR[i] = big * (1 + hqrnduniformr(rs, _state));
            }
            if (kt == 2) {
               bndl->xR[i] = _state->v_neginf;
               bndu->xR[i] = _state->v_posinf;
            }
         } else {
            kt = hqrnduniformi(rs, 3, _state);
            if (kt == 0) {
               au->xR[i - n] = _state->v_posinf;
            }
            if (kt == 1) {
               al->xR[i - n] = _state->v_neginf;
            }
            if (kt == 2) {
               al->xR[i - n] = _state->v_neginf;
               au->xR[i - n] = _state->v_posinf;
            }
         }
      }
      ae_frame_leave(_state);
      return;
   }
// A randomly generated non-degenerate LP problem. A mix of fixed,
// range, upper/lower bounds and free variables.
   if (ptype == 2) {
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      testminlpunit_generatecabxd(rs, n, *m, c, a, &xx, &d, &basicnonbasic, _state);
      testminlpunit_generatebounds(rs, n, *m, &xx, &d, &basicnonbasic, bndl, bndu, al, au, _state);
      ae_frame_leave(_state);
      return;
   }
// A randomly generated primal/dual degenerate LP problem. A mix of
// fixed, range, upper/lower bounds and free variables.
   if (ptype == 3) {
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      testminlpunit_generatecabxd(rs, n, *m, c, a, &xx, &d, &basicnonbasic, _state);
      testminlpunit_generatebounds(rs, n, *m, &xx, &d, &basicnonbasic, bndl, bndu, al, au, _state);
      ndual = 1 + hqrnduniformi(rs, *m, _state);
      for (i = 0; i < ndual; i++) {
         j = basicnonbasic.xZ[*m + hqrnduniformi(rs, n, _state)];
         if (j < n) {
            c->xR[j] = c->xR[j] - d.xR[j];
            d.xR[j] = 0.0;
         }
      }
      nprimal = 1 + hqrnduniformi(rs, *m, _state);
      for (i = 0; i < nprimal; i++) {
         j = basicnonbasic.xZ[hqrnduniformi(rs, *m, _state)];
         if (j < n) {
            if (hqrndnormal(rs, _state) > 0.0) {
               bndl->xR[j] = xx.xR[j];
            } else {
               bndu->xR[j] = xx.xR[j];
            }
         } else {
            if (hqrndnormal(rs, _state) > 0.0) {
               al->xR[j - n] = xx.xR[j];
            } else {
               au->xR[j - n] = xx.xR[j];
            }
         }
      }
      ae_frame_leave(_state);
      return;
   }
   ae_assert(false, "GenerateLPProblem failed", _state);
   ae_frame_leave(_state);
}

// This function generates random primal unbounded LP problem.
static void testminlpunit_generateunboundedlpproblem(hqrndstate *rs, ae_int_t n, RVector *c, RVector *bndl, RVector *bndu, RMatrix *a, RVector *al, RVector *au, ae_int_t *m, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t ptype;
   ae_int_t pcount;
   double v;
   double vv;
   double q;

   ae_frame_make(_state, &_frame_block);
   SetVector(c);
   SetVector(bndl);
   SetVector(bndu);
   SetMatrix(a);
   SetVector(al);
   SetVector(au);
   *m = 0;
   NewVector(x0, 0, DT_REAL, _state);

   ae_vector_set_length(c, n, _state);
   ae_vector_set_length(bndl, n, _state);
   ae_vector_set_length(bndu, n, _state);

// Choose problem type
   pcount = 2;
   ptype = hqrnduniformi(rs, pcount, _state);

// Boundary-only constraints, can be easily made primal unbounded
   if (ptype == 0) {
      q = 5.0;
      *m = 0;
      ae_matrix_set_length(a, 0, 0, _state);
      ae_vector_set_length(al, 0, _state);
      ae_vector_set_length(au, 0, _state);
      for (i = 0; i < n; i++) {
         c->xR[i] = hqrndnormal(rs, _state);
         v = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
         if (c->xR[i] > 0.0) {
            bndl->xR[i] = v;
            bndu->xR[i] = _state->v_posinf;
         } else {
            bndl->xR[i] = _state->v_neginf;
            bndu->xR[i] = v;
         }
      }
      i = hqrnduniformi(rs, n, _state);
      do {
         c->xR[i] = hqrndnormal(rs, _state);
      } while (c->xR[i] == 0.0);
      c->xR[i] = c->xR[i] + 0.1 * ae_sign(c->xR[i], _state);
      v = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
      if (c->xR[i] < 0.0) {
         bndl->xR[i] = v;
         bndu->xR[i] = _state->v_posinf;
      } else {
         bndl->xR[i] = _state->v_neginf;
         bndu->xR[i] = v;
      }
      ae_frame_leave(_state);
      return;
   }
// Boundary and single-sided linear constraints; a bit more tricky to make it primal unbounded
   if (ptype == 1) {
      q = 5.0;
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      ae_matrix_set_length(a, *m, n, _state);
      ae_vector_set_length(al, *m, _state);
      ae_vector_set_length(au, *m, _state);
      ae_vector_set_length(&x0, n, _state);
      for (i = 0; i < n; i++) {
         c->xR[i] = hqrndnormal(rs, _state);
         x0.xR[i] = hqrndnormal(rs, _state);
         if (c->xR[i] >= 0.0) {
            bndl->xR[i] = _state->v_neginf;
            bndu->xR[i] = x0.xR[i];
         } else {
            bndl->xR[i] = x0.xR[i];
            bndu->xR[i] = _state->v_posinf;
         }
      }
      for (i = 0; i < *m; i++) {
         v = 0.0;
         vv = 0.0;
         for (j = 0; j < n; j++) {
            a->xyR[i][j] = hqrndnormal(rs, _state);
            v = v + a->xyR[i][j] * x0.xR[j];
            vv = vv + a->xyR[i][j] * c->xR[j];
         }
         if (vv >= 0.0) {
            al->xR[i] = _state->v_neginf;
            au->xR[i] = v;
         } else {
            al->xR[i] = v;
            au->xR[i] = _state->v_posinf;
         }
      }
      ae_frame_leave(_state);
      return;
   }
   ae_assert(false, "GenerateUnboundedLPProblem failed", _state);
   ae_frame_leave(_state);
}

// This function generates random primal infeasible LP problem
static void testminlpunit_generateinfeasiblelpproblem(hqrndstate *rs, ae_int_t n, RVector *c, RVector *bndl, RVector *bndu, RMatrix *a, RVector *al, RVector *au, ae_int_t *m, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t ptype;
   ae_int_t pcount;
   double v;
   double q;
   double minerr;

   ae_frame_make(_state, &_frame_block);
   SetVector(c);
   SetVector(bndl);
   SetVector(bndu);
   SetMatrix(a);
   SetVector(al);
   SetVector(au);
   *m = 0;
   NewVector(xx, 0, DT_REAL, _state);
   NewVector(d, 0, DT_REAL, _state);
   NewVector(x0, 0, DT_REAL, _state);
   NewVector(tmpi, 0, DT_INT, _state);

   ae_vector_set_length(c, n, _state);
   ae_vector_set_length(bndl, n, _state);
   ae_vector_set_length(bndu, n, _state);

// Choose problem type
   pcount = 3;
   ptype = hqrnduniformi(rs, pcount, _state);

// Boundary-only constraints, can be easily made primal infeasible
   if (ptype == 0) {
      q = 5.0;
      *m = 0;
      ae_matrix_set_length(a, 0, 0, _state);
      ae_vector_set_length(al, 0, _state);
      ae_vector_set_length(au, 0, _state);
      for (i = 0; i < n; i++) {
         c->xR[i] = hqrndnormal(rs, _state);
         v = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
         bndl->xR[i] = v - ae_pow(q, hqrndnormal(rs, _state), _state);
         bndu->xR[i] = v + ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      i = hqrnduniformi(rs, n, _state);
      v = bndl->xR[i];
      bndl->xR[i] = bndu->xR[i];
      bndu->xR[i] = v;
      ae_frame_leave(_state);
      return;
   }
// Linearly constrained problem with infeasible box constraints
   if (ptype == 1) {
      q = 5.0;
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      testminlpunit_generatecabxd(rs, n, *m, c, a, &xx, &d, &tmpi, _state);
      testminlpunit_generatebounds(rs, n, *m, &xx, &d, &tmpi, bndl, bndu, al, au, _state);
      i = hqrnduniformi(rs, n, _state);
      v = ae_pow(q, hqrndnormal(rs, _state), _state) - ae_pow(q, hqrndnormal(rs, _state), _state);
      bndl->xR[i] = v + ae_pow(q, hqrndnormal(rs, _state), _state);
      bndu->xR[i] = v - ae_pow(q, hqrndnormal(rs, _state), _state);
      ae_frame_leave(_state);
      return;
   }
// Linearly constrained problem with infeasible linear constraints
   if (ptype == 2) {
      q = 5.0;
      minerr = 0.01;
      *m = 1 + hqrnduniformi(rs, 2 * n, _state);
      testminlpunit_generatecabxd(rs, n, *m, c, a, &xx, &d, &tmpi, _state);
      testminlpunit_generatebounds(rs, n, *m, &xx, &d, &tmpi, bndl, bndu, al, au, _state);
      rmatrixresize(a, a->rows + 1, a->cols, _state);
      rvectorresize(al, al->cnt + 1, _state);
      rvectorresize(au, au->cnt + 1, _state);
      for (i = 0; i < n; i++) {
         a->xyR[*m][i] = a->xyR[*m - 1][i];
      }
      v = minerr + ae_pow(q, hqrndnormal(rs, _state), _state);
      v = v * ae_maxreal(1.0, ae_sqrt(rdotrr(n, a, *m, a, *m, _state), _state), _state);
      if (ae_isfinite(al->xR[*m - 1], _state) && ae_isfinite(au->xR[*m - 1], _state)) {
         al->xR[*m] = au->xR[*m - 1] + v;
         au->xR[*m] = al->xR[*m] + ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      if (ae_isfinite(al->xR[*m - 1], _state) && !ae_isfinite(au->xR[*m - 1], _state)) {
         au->xR[*m] = al->xR[*m - 1] - v;
         al->xR[*m] = au->xR[*m] - ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      if (!ae_isfinite(al->xR[*m - 1], _state) && ae_isfinite(au->xR[*m - 1], _state)) {
         al->xR[*m] = au->xR[*m - 1] + v;
         au->xR[*m] = al->xR[*m] + ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      if (!ae_isfinite(al->xR[*m - 1], _state) && !ae_isfinite(au->xR[*m - 1], _state)) {
         al->xR[*m] = v;
         au->xR[*m] = v - ae_pow(q, hqrndnormal(rs, _state), _state);
      }
      *m = *m + 1;
      ae_frame_leave(_state);
      return;
   }
   ae_assert(false, "GenerateUnboundedLPProblem failed", _state);
   ae_frame_leave(_state);
}

// This function generates random C[] and A[] as well as feasible point XX[N+M],
// with XX[0..N-1] being structural variables and XX[N..N+M-1] being values
// of logical variables.
//
// It also splits structural/logical variables into basic/nonbasic  ones  and
// generates BasicNonbasic[] array, with indexes of basic variables being stored in
// first M positions, and indexes of non-basic variables being stored in the
// next N positions.
static void testminlpunit_generatecabxd(hqrndstate *rs, ae_int_t n, ae_int_t m, RVector *c, RMatrix *a, RVector *xx, RVector *d, ZVector *basicnonbasic, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t kk;
   double v;
   double mincoeff;

   ae_frame_make(_state, &_frame_block);
   SetVector(c);
   SetMatrix(a);
   SetVector(xx);
   SetVector(d);
   SetVector(basicnonbasic);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(cx, 0, DT_REAL, _state);
   NewMatrix(ax, 0, 0, DT_REAL, _state);
   NewVector(pivots, 0, DT_INT, _state);

   mincoeff = 0.001;

// Randomly partition columns into basic and nonbasic ones
   ae_vector_set_length(basicnonbasic, n + m, _state);
   for (i = 0; i < n + m; i++) {
      basicnonbasic->xZ[i] = i;
   }
   for (i = 0; i < m; i++) {
      k = i + hqrnduniformi(rs, n + m - i, _state);
      kk = basicnonbasic->xZ[i];
      basicnonbasic->xZ[i] = basicnonbasic->xZ[k];
      basicnonbasic->xZ[k] = kk;
   }

// Generate constraint matrix A
   ae_matrix_set_length(a, m, n, _state);
   ae_matrix_set_length(&ax, m, n + m, _state);
   for (i = 0; i < m; i++) {
      for (j = 0; j < n; j++) {
         ax.xyR[i][j] = hqrndnormal(rs, _state);
         ax.xyR[i][j] = ax.xyR[i][j] + ae_sign(ax.xyR[i][j], _state) * mincoeff;
         a->xyR[i][j] = ax.xyR[i][j];
      }
      for (j = 0; j < m; j++) {
         ax.xyR[i][n + j] = 0.0;
      }
      ax.xyR[i][n + i] = -1.0;
   }

// Generate feasible point
   ae_vector_set_length(xx, n + m, _state);
   for (i = 0; i < n; i++) {
      xx->xR[i] = hqrndnormal(rs, _state);
   }
   for (i = n; i < n + m; i++) {
      xx->xR[i] = 0.0;
   }
   for (i = 0; i < m; i++) {
      v = 0.0;
      for (j = 0; j < n; j++) {
         v = v + xx->xR[j] * ax.xyR[i][j];
      }
      xx->xR[n + i] = v;
   }

// Generate random Y.
// Entries corresponding to basic constraints must be zero.
   ae_vector_set_length(&y, m, _state);
   for (i = 0; i < m; i++) {
      y.xR[i] = hqrndnormal(rs, _state);
   }
   for (i = 0; i < m; i++) {
      if (basicnonbasic->xZ[i] >= n) {
         y.xR[basicnonbasic->xZ[i] - n] = 0.0;
      }
   }

// Generate D and C
   ae_vector_set_length(d, n + m, _state);
   rmatrixgemv(n + m, m, -1.0, &ax, 0, 0, 1, &y, 0, 0.0, d, 0, _state);
   ae_vector_set_length(&cx, n + m, _state);
   for (i = 0; i < n; i++) {
      cx.xR[i] = hqrndnormal(rs, _state);
   }
   for (i = n; i < n + m; i++) {
      cx.xR[i] = 0.0;
   }
   for (i = 0; i < m; i++) {
      cx.xR[basicnonbasic->xZ[i]] = -d->xR[basicnonbasic->xZ[i]];
   }
   raddv(n + m, 1.0, &cx, d, _state);
   ae_vector_set_length(c, n, _state);
   rcopyv(n, &cx, c, _state);
   ae_frame_leave(_state);
}

// This function generates random non-degenerate bounds for given XX and D.
static void testminlpunit_generatebounds(hqrndstate *rs, ae_int_t n, ae_int_t m, RVector *xx, RVector *d, ZVector *basicnonbasic, RVector *bndl, RVector *bndu, RVector *al, RVector *au, ae_state *_state) {
   double v0;
   double v1;
   double q;
   ae_int_t i;
   ae_int_t kk;

   SetVector(bndl);
   SetVector(bndu);
   SetVector(al);
   SetVector(au);

   ae_vector_set_length(bndl, n, _state);
   ae_vector_set_length(bndu, n, _state);
   ae_vector_set_length(al, m, _state);
   ae_vector_set_length(au, m, _state);

// Choose bounds in such a way that non-basic variables are
// strictly at the bounds and dual feasible, and all basic
// variables are strictly within bounds.
   q = 5.0;
   for (kk = 0; kk < n + m; kk++) {
      i = basicnonbasic->xZ[kk];
      v0 = xx->xR[i] - ae_pow(q, hqrndnormal(rs, _state), _state);
      v1 = xx->xR[i] + ae_pow(q, hqrndnormal(rs, _state), _state);
      if (hqrndnormal(rs, _state) > 0.0) {
         v0 = _state->v_neginf;
      }
      if (hqrndnormal(rs, _state) > 0.0) {
         v1 = _state->v_posinf;
      }
      if (kk >= m) {
         if (d->xR[i] >= 0.0) {
            v0 = xx->xR[i];
            if (hqrnduniformr(rs, _state) < 0.05) {
               v1 = v0;
            }
         } else {
            v1 = xx->xR[i];
            if (hqrnduniformr(rs, _state) < 0.05) {
               v0 = v1;
            }
         }
      }
      if (i < n) {
         bndl->xR[i] = v0;
         bndu->xR[i] = v1;
      } else {
         al->xR[i - n] = v0;
         au->xR[i - n] = v1;
      }
   }
}

// This function sets linear constraints using randomly  chosen  sequence  of
// SetLC/AddLC calls.
//
// It may also modify constraints:
// * split two-sided constraint into two one-sided constraints
// * create duplicates
// * insert zero dummies
//
// Thus, you should not use this function when you want to test  things  like
// correctness of Lagrange multipliers and so on.
static void testminlpunit_modifyandsendconstraintsto(ae_int_t n, RMatrix *a, RVector *al, RVector *au, ae_int_t m, hqrndstate *rs, minlpstate *state, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t stype;
   ae_int_t nz;
   ae_int_t nzmod;
   ae_int_t ccnt;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t t;
   ae_int_t minit;
   ae_int_t nadd;
   ae_int_t ndup;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewVector(ai, 0, DT_REAL, _state);
   NewVector(idxi, 0, DT_INT, _state);
   NewMatrix(a1, 0, 0, DT_REAL, _state);
   NewObj(sparsematrix, sa, _state);
   NewVector(ct, 0, DT_INT, _state);

// Choose sequence type
   stype = hqrnduniformi(rs, 4, _state);

// Straightforward SetLC2() call
   if (stype == 0) {
      minlpsetlc2dense(state, a, al, au, m, _state);
      ae_frame_leave(_state);
      return;
   }
// SetLC1() call (conversion to other format)
   if (stype == 1) {
      ae_matrix_set_length(&a1, 2 * m, n + 1, _state);
      ae_vector_set_length(&ct, 2 * m, _state);
      ccnt = 0;
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            a1.xyR[ccnt + 0][j] = a->xyR[i][j];
            a1.xyR[ccnt + 1][j] = a->xyR[i][j];
         }
         if ((ae_isfinite(al->xR[i], _state) && ae_isfinite(au->xR[i], _state)) && al->xR[i] == au->xR[i]) {
            a1.xyR[ccnt][n] = al->xR[i];
            ct.xZ[ccnt] = 0;
            ccnt = ccnt + 1;
         } else {
            if (ae_isfinite(al->xR[i], _state)) {
               a1.xyR[ccnt][n] = al->xR[i];
               ct.xZ[ccnt] = 1;
               ccnt = ccnt + 1;
            }
            if (ae_isfinite(au->xR[i], _state)) {
               a1.xyR[ccnt][n] = au->xR[i];
               ct.xZ[ccnt] = -1;
               ccnt = ccnt + 1;
            }
         }
      }
      minlpsetlc(state, &a1, &ct, ccnt, _state);
      ae_frame_leave(_state);
      return;
   }
// Straightforward SetLC2Sparse() call
   if (stype == 2) {
      if (m == 0) {
         minlpsetlc2(state, &sa, al, au, 0, _state);
         ae_frame_leave(_state);
         return;
      }
      sparsecreate(m, n, 0, &sa, _state);
      for (i = 0; i < m; i++) {
         for (j = 0; j < n; j++) {
            if (a->xyR[i][j] != 0.0) {
               sparseset(&sa, i, j, a->xyR[i][j], _state);
            }
         }
      }
      if (hqrndnormal(rs, _state) > 0.0) {
         sparseconverttocrs(&sa, _state);
      }
      minlpsetlc2(state, &sa, al, au, m, _state);
      ae_frame_leave(_state);
      return;
   }
// A few rows are added with SetLC2Dense() call, the rest
// is processed with random mix of AddLC2Dense()/AddLC2()
   if (stype == 3) {
      if (m == 0) {
         minlpsetlc2(state, &sa, al, au, 0, _state);
         ae_frame_leave(_state);
         return;
      }
      minit = hqrnduniformi(rs, m, _state);
      minlpsetlc2dense(state, a, al, au, minit, _state);
      for (i = minit; i < m; i++) {
         if (hqrnduniformi(rs, 2, _state) == 0) {

         // Add as dense row
            ae_vector_set_length(&ai, n, _state);
            for (j = 0; j < n; j++) {
               ai.xR[j] = a->xyR[i][j];
            }
            minlpaddlc2dense(state, &ai, al->xR[i], au->xR[i], _state);
         } else {

         // Add as sparse row with shuffle and possible duplicates
            ae_vector_set_length(&ai, n, _state);
            ae_vector_set_length(&idxi, n, _state);
            nz = 0;
            for (j = 0; j < n; j++) {
               if (a->xyR[i][j] != 0.0) {
                  ai.xR[nz] = a->xyR[i][j];
                  idxi.xZ[nz] = j;
                  nz = nz + 1;
               }
            }
            rvectorresize(&ai, nz, _state);
            ivectorresize(&idxi, nz, _state);

         // Already existing elements are split in two
            nadd = 0;
            if (nz != 0) {
               nadd = hqrnduniformi(rs, 2, _state) * hqrnduniformi(rs, 4, _state);
               rvectorresize(&ai, nz + nadd, _state);
               ivectorresize(&idxi, nz + nadd, _state);
               for (j = 0; j < nadd; j++) {
                  k = hqrnduniformi(rs, nz, _state);
                  v = hqrndnormal(rs, _state);
                  idxi.xZ[nz + j] = idxi.xZ[k];
                  ai.xR[nz + j] = v;
                  ai.xR[k] = ai.xR[k] - v;
               }
            }
         // Possibly nonexistent elements are added as +V and -V
         // Do not performed for NZ=0 rows because it may introduce slightly nonzero coefficients
         // to exactly zero row (constraint normalization goes crazy).
            ndup = hqrnduniformi(rs, 2, _state) * hqrnduniformi(rs, 4, _state);
            if (nz == 0) {
               ndup = 0;
            }
            rvectorresize(&ai, nz + nadd + 2 * ndup, _state);
            ivectorresize(&idxi, nz + nadd + 2 * ndup, _state);
            for (j = 0; j < ndup; j++) {
               k = hqrnduniformi(rs, n, _state);
               v = hqrndnormal(rs, _state);
               idxi.xZ[nz + nadd + 2 * j + 0] = k;
               idxi.xZ[nz + nadd + 2 * j + 1] = k;
               ai.xR[nz + nadd + 2 * j + 0] = v;
               ai.xR[nz + nadd + 2 * j + 1] = -v;
            }
            nzmod = nz + nadd + 2 * ndup;
            for (j = 0; j < nzmod; j++) {
               k = j + hqrnduniformi(rs, nzmod - j, _state);
               t = idxi.xZ[j];
               idxi.xZ[j] = idxi.xZ[k];
               idxi.xZ[k] = t;
               v = ai.xR[j];
               ai.xR[j] = ai.xR[k];
               ai.xR[k] = v;
            }
            minlpaddlc2(state, &idxi, &ai, nzmod, al->xR[i], au->xR[i], _state);
         }
      }
      ae_frame_leave(_state);
      return;
   }
   ae_assert(false, "MINLPTest: integrity check failed", _state);
   ae_frame_leave(_state);
}

// This function selects random LP solver
static void testminlpunit_selectrandomsolver(minlpstate *state, ae_state *_state) {
   ae_int_t k;

   k = ae_randominteger(2, _state);
   if (k == 0) {
      minlpsetalgodss(state, 0.0, _state);
   }
   if (k == 1) {
      minlpsetalgoipm(state, 0.0, _state);
   }
}

// This function selects specific LP solver.
//
// Reg parameter is used to regularize IPM algo, ignore for DSS
static void testminlpunit_selectsolver(minlpstate *state, ae_int_t st, ae_state *_state) {

   if (st == 0) {
      minlpsetalgodss(state, 0.0, _state);
      return;
   }
   if (st == 1) {
      minlpsetalgoipm(state, 0.0, _state);
      return;
   }
   ae_assert(false, "SelectSolver: unexpected solver", _state);
}

// Shifts nonzero elements of C[] away from zero
static void testminlpunit_shiftfromzero(RVector *c, ae_int_t n, double s, ae_state *_state) {
   ae_int_t i;

   for (i = 0; i < n; i++) {
      c->xR[i] = c->xR[i] + ae_sign(c->xR[i], _state) * s;
   }
}

