                     ae_set_error_flag(wereerrors, ae_fabs(jacdefect.xyR[i][j] - ogrep.badgraduser.xyR[i][j], _state) > 0.01 / s.xR[j], __FILE__, __LINE__, "testminnlcunit.ap:4157");
