// === wsr testing unit ===
bool testwsr(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   ae_int_t n;
   ae_int_t i;
   double taill;
   double tailr;
   double tailb;
   double taillprev;
   double tailrprev;
   double ebase;
   double eshift;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(xa, 0, DT_REAL, _state);

   waserrors = false;

// Test monotonicity of tail values for moving value of E
   for (n = 5; n <= 50; n++) {

   // Generate uniform and sorted X spanning [0,1]
      ae_vector_set_length(&xa, n, _state);
      for (i = 0; i < n; i++) {
         xa.xR[i] = (double)i / (n - 1);
      }

   // Test N+1 values of E
      ebase = -0.5 / (n - 1);
      eshift = 1.0 / (n - 1);
      tailrprev = 0.0;
      taillprev = 1.0;
      for (i = 0; i <= n; i++) {
         wilcoxonsignedranktest(&xa, n, ebase + eshift * i, &tailb, &taill, &tailr, _state);
         ae_set_error_flag(&waserrors, tailb != 2 * ae_minreal(taill, tailr, _state), __FILE__, __LINE__, "testwsrunit.ap:42");
         ae_set_error_flag(&waserrors, tailrprev > tailr, __FILE__, __LINE__, "testwsrunit.ap:43");
         ae_set_error_flag(&waserrors, taillprev < taill, __FILE__, __LINE__, "testwsrunit.ap:44");
         tailrprev = tailr;
         taillprev = taill;
      }
   }

// Test for integer overflow in the function: if one crucial
// calculation step is performed in 32-bit integer arithmetics,
// it will return incorrect results.
//
// We use special handcrafted N, such that in 32-bit integer
// arithmetics int32(N*N)<0. Such negative N leads to domain
// error in the sqrt() function.
   n = 50000;
   ae_vector_set_length(&xa, n, _state);
   for (i = 0; i < n; i++) {
      xa.xR[i] = ae_sin((double)(10 * i), _state);
   }
   wilcoxonsignedranktest(&xa, n, 0.0, &tailb, &taill, &tailr, _state);
   ae_set_error_flag(&waserrors, !ae_isfinite(tailb, _state), __FILE__, __LINE__, "testwsrunit.ap:64");
   ae_set_error_flag(&waserrors, !ae_isfinite(taill, _state), __FILE__, __LINE__, "testwsrunit.ap:65");
   ae_set_error_flag(&waserrors, !ae_isfinite(tailr, _state), __FILE__, __LINE__, "testwsrunit.ap:66");

//
   if (!silent) {
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

// === mannwhitneyu testing unit ===
bool testmannwhitneyu(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   ae_int_t testmin;
   ae_int_t testmax;
   ae_int_t testcnt;
   ae_int_t pass;
   ae_int_t n;
   ae_int_t m;
   ae_int_t i;
   ae_int_t k;
   double taill;
   double tailr;
   double tailb;
   double taill1;
   double tailr1;
   double tailb1;
   double taillprev;
   double tailrprev;
   double ebase;
   double eshift;
   ae_int_t ecnt;
   double worsterr;
   double v;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   waserrors = false;
   hqrndrandomize(&rs, _state);

// Test monotonicity of tail values for monotinically moving distributions.
   for (n = 5; n <= 20; n++) {
      for (m = 5; m <= 20; m++) {

      // Generate uniform and sorted X/Y spanning [0,1]
         ae_vector_set_length(&x, n, _state);
         for (i = 0; i < n; i++) {
            x.xR[i] = (double)i / (n - 1) + 100 * ae_machineepsilon * hqrndnormal(&rs, _state);
         }
         ae_vector_set_length(&y, m, _state);
         for (i = 0; i < m; i++) {
            y.xR[i] = (double)i / (m - 1) + 100 * ae_machineepsilon * hqrndnormal(&rs, _state);
         }

      // Test 100 values of E
         ecnt = 100;
         ebase = -1.1;
         eshift = -2 * ebase / (ecnt - 1);
         tailrprev = 0.0;
         taillprev = 1.0;
         for (i = 0; i < m; i++) {
            y.xR[i] = y.xR[i] + ebase;
         }
         for (k = 0; k < ecnt; k++) {
            mannwhitneyutest(&x, n, &y, m, &tailb, &taill, &tailr, _state);
            ae_set_error_flag(&waserrors, tailb != 2 * ae_minreal(taill, tailr, _state), __FILE__, __LINE__, "testmannwhitneyuunit.ap:57");
            ae_set_error_flag(&waserrors, tailrprev > tailr, __FILE__, __LINE__, "testmannwhitneyuunit.ap:58");
            ae_set_error_flag(&waserrors, taillprev < taill, __FILE__, __LINE__, "testmannwhitneyuunit.ap:59");
            tailrprev = tailr;
            taillprev = taill;
            for (i = 0; i < m; i++) {
               y.xR[i] = y.xR[i] + eshift;
            }
         }
      }
   }

// Test frequency of p-value 0.05
   testmin = 5;
   testmax = 50;
   testcnt = 10000;
   worsterr = 0.0;
   for (n = testmin; n <= testmax; n++) {
      m = n + hqrnduniformi(&rs, testmax - n + 1, _state);
      ae_vector_set_length(&x, n, _state);
      ae_vector_set_length(&y, m, _state);

   // Generate two uniformly distributed values, calculate p-value for both-tails, repeat
      k = 0;
      for (pass = 0; pass < testcnt; pass++) {
         for (i = 0; i < n; i++) {
            x.xR[i] = hqrnduniformr(&rs, _state);
         }
         for (i = 0; i < m; i++) {
            y.xR[i] = hqrnduniformr(&rs, _state);
         }
         mannwhitneyutest(&x, n, &y, m, &tailb, &taill, &tailr, _state);
         if (tailb < 0.05) {
            k = k + 1;
         }
      }
      v = ae_fabs((double)k / testcnt - 0.05, _state);
      worsterr = ae_maxreal(worsterr, v, _state);

   // Test error in quantile; for different N's we have different tolerances
      if (n < 10) {
         ae_set_error_flag(&waserrors, v > 0.035, __FILE__, __LINE__, "testmannwhitneyuunit.ap:101");
      } else {
         if (n < 15) {
            ae_set_error_flag(&waserrors, v > 0.025, __FILE__, __LINE__, "testmannwhitneyuunit.ap:103");
         } else {
            if (n < 30) {
               ae_set_error_flag(&waserrors, v > 0.020, __FILE__, __LINE__, "testmannwhitneyuunit.ap:105");
            } else {
               ae_set_error_flag(&waserrors, v > 0.015, __FILE__, __LINE__, "testmannwhitneyuunit.ap:107");
            }
         }
      }
   }

// Test symmetry properties
   for (n = 5; n <= 50; n++) {
      for (m = 5; m <= 50; m++) {
         ae_vector_set_length(&x, n, _state);
         ae_vector_set_length(&y, m, _state);
         for (i = 0; i < n; i++) {
            x.xR[i] = hqrnduniformr(&rs, _state);
         }
         for (i = 0; i < m; i++) {
            y.xR[i] = hqrnduniformr(&rs, _state);
         }
         mannwhitneyutest(&x, n, &y, m, &tailb, &taill, &tailr, _state);
         mannwhitneyutest(&y, m, &x, n, &tailb1, &taill1, &tailr1, _state);
         ae_set_error_flag(&waserrors, ae_fabs(tailb - tailb1, _state) > 1.0E-12, __FILE__, __LINE__, "testmannwhitneyuunit.ap:126");
         ae_set_error_flag(&waserrors, ae_fabs(taill - tailr1, _state) > 1.0E-12, __FILE__, __LINE__, "testmannwhitneyuunit.ap:127");
         ae_set_error_flag(&waserrors, ae_fabs(tailr - taill1, _state) > 1.0E-12, __FILE__, __LINE__, "testmannwhitneyuunit.ap:128");
      }
   }

// Test for integer overflow in the function: if one crucial
// calculation step is performed in 32-bit integer arithmetics,
// it will return incorrect results.
//
// We use special handcrafted N, such that in 32-bit integer
// arithmetics int32(N*N)<0. Such negative N leads to domain
// error in the sqrt() function.
   n = 50000;
   ae_vector_set_length(&x, n, _state);
   ae_vector_set_length(&y, n, _state);
   for (i = 0; i < n; i++) {
      x.xR[i] = ae_sin((double)(10 * i), _state);
      y.xR[i] = ae_sin((double)(13 * i), _state);
   }
   mannwhitneyutest(&x, n, &y, n, &tailb, &taill, &tailr, _state);
   ae_set_error_flag(&waserrors, !ae_isfinite(tailb, _state), __FILE__, __LINE__, "testmannwhitneyuunit.ap:149");
   ae_set_error_flag(&waserrors, !ae_isfinite(taill, _state), __FILE__, __LINE__, "testmannwhitneyuunit.ap:150");
   ae_set_error_flag(&waserrors, !ae_isfinite(tailr, _state), __FILE__, __LINE__, "testmannwhitneyuunit.ap:151");

//
   if (!silent) {
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
   }
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

