static void testidwunit_testcontinuity(idwmodel *model, ae_int_t nx, ae_int_t ny, RVector *x0, RVector *x1, ae_int_t nsteps, ae_int_t d, bool *err, ae_state *_state);
static void testidwunit_testcommon(bool *err, ae_state *_state);
static void testidwunit_testmstab(bool *err, ae_state *_state);

// Testing IDW interpolation
bool testidw(bool silent, ae_state *_state) {
   bool waserrors;
   bool commonerrors;
   bool mstaberrors;
   bool result;

   commonerrors = false;
   mstaberrors = false;
   testidwunit_testcommon(&commonerrors, _state);
   testidwunit_testmstab(&mstaberrors, _state);
   waserrors = commonerrors || mstaberrors;
   if (!silent) {
      printf("TESTING INVERSE DISTANCE WEIGHTING\n");
      printf("* common properties                      ");
      if (!commonerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* MSTAB-specific tests                   ");
      if (!mstaberrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   return result;
}

// Testing continuity properties: C0 (D=0) or C1 (D=1) continuity.
//
// Error flag is modified on failure, unchanged on success.
static void testidwunit_testcontinuity(idwmodel *model, ae_int_t nx, ae_int_t ny, RVector *x0, RVector *x1, ae_int_t nsteps, ae_int_t d, bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   ae_int_t cidx;
   double t;
   double lc1;
   double lc2;

   ae_frame_make(_state, &_frame_block);
   NewVector(xc, 0, DT_REAL, _state);
   NewVector(yc, 0, DT_REAL, _state);
   NewMatrix(yy, 0, 0, DT_REAL, _state);

   ae_assert(nsteps >= 10, "TestContinuity: NSteps is too small", _state);
   ae_assert(d == 0 || d == 1, "TestContinuity: incorrect D", _state);

// Compute sequence of function values
   ae_vector_set_length(&xc, nx, _state);
   ae_matrix_set_length(&yy, nsteps, ny, _state);
   for (i = 0; i < nsteps; i++) {
      t = (double)i / (nsteps - 1);
      for (j = 0; j < nx; j++) {
         xc.xR[j] = x0->xR[j] * t + x1->xR[j] * (1 - t);
      }
      idwcalcbuf(model, &xc, &yc, _state);
      for (j = 0; j < ny; j++) {
         yy.xyR[i][j] = yc.xR[j];
      }
   }

// Evaluate all differentiability levels (C0, C1) requested by user
   for (cidx = 0; cidx <= d; cidx++) {

   // Compute Lipschitz constant for original and increased steps
      lc1 = 0.0;
      lc2 = 0.0;
      for (i = 0; i < nsteps - 2; i++) {
         for (j = 0; j < ny; j++) {
            lc1 = ae_maxreal(lc1, ae_fabs(yy.xyR[i][j] - yy.xyR[i + 1][j], _state), _state);
            lc2 = ae_maxreal(lc2, ae_fabs(yy.xyR[i][j] - yy.xyR[i + 2][j], _state) / 2, _state);
         }
      }
      ae_set_error_flag(err, lc2 > 1.0E-4 && lc1 > 1.750 * lc2, __FILE__, __LINE__, "testidwunit.ap:67");

   // Differentiate function, repeat one more time
      for (i = 0; i < nsteps - 1; i++) {
         for (j = 0; j < ny; j++) {
            yy.xyR[i][j] = yy.xyR[i + 1][j] - yy.xyR[i][j];
         }
      }
      nsteps = nsteps - 1;
   }
   ae_frame_leave(_state);
}

// Test MSTAB; Err is set to True on failure, unchanged otherwise.
static void testidwunit_testcommon(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t algotype;
   ae_int_t i;
   ae_int_t i0;
   ae_int_t i1;
   ae_int_t j;
   ae_int_t k;
   ae_int_t n;
   double v;
   double vv;
   bool initdone;
   double shepardp;
   double rbase;
   double tol;
   double mindistinf;
   double refrms;
   double refavg;
   double refmax;
   double refr2;
   double refrss;
   double reftss;
   ae_int_t nx;
   ae_int_t ny;
   double x0;
   double x1;
   double x2;
   ae_int_t continuitytesting;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(idwmodel, model, _state);
   NewObj(idwmodel, model1, _state);
   NewObj(idwbuilder, builder, _state);
   NewObj(idwreport, rep, _state);
   NewObj(idwcalcbuffer, buffer, _state);
   NewObj(hqrndstate, rs, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(xx, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(meany, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);
   tol = 1.0E-10;
   mindistinf = 0.05;

// Try all algorithms
   for (algotype = 0; algotype <= 2; algotype++) {

   // Test empty dataset
      for (nx = 1; nx <= 5; nx++) {
         for (ny = 1; ny <= 5; ny++) {
            initdone = false;
            idwbuildercreate(nx, ny, &builder, _state);
            if (algotype == 0) {
               initdone = true;
               shepardp = 1 + (nx + 1) * hqrnduniformr(&rs, _state);
               idwbuildersetalgotextbookshepard(&builder, shepardp, _state);
            }
            if (algotype == 1) {
               initdone = true;
               rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
               idwbuildersetalgotextbookmodshepard(&builder, rbase, _state);
            }
            if (algotype == 2) {
               initdone = true;
               rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
               idwbuildersetalgomstab(&builder, rbase, _state);
            }
            ae_assert(initdone, "TestCommon: unexpected AlgoType", _state);
            if (hqrndnormal(&rs, _state) > 0.0) {

            // Fit and store result directly into the variable
               idwfit(&builder, &model, &rep, _state);
            } else {

            // Fit, store result to temporary, pass through the serializer
               idwfit(&builder, &model1, &rep, _state);
               {
               // This code passes data structure through serializers
               // (serializes it to string and loads back)
                  ae_int_t _local_ssize;
                  ae_frame _local_frame_block;

                  ae_frame_make(_state, &_local_frame_block);

                  NewSerializer(_local_serializer);
                  ae_serializer_alloc_start(&_local_serializer);
                  idwalloc(&_local_serializer, &model1, _state);
                  _local_ssize = ae_serializer_get_alloc_size(&_local_serializer);
                  NewBlock(_local_dynamic_block, _local_ssize + 1, _state);
                  ae_serializer_sstart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
                  idwserialize(&_local_serializer, &model1, _state);
                  ae_serializer_stop(&_local_serializer, _state);

                  ae_serializer_init(&_local_serializer);
                  ae_serializer_ustart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
                  idwunserialize(&_local_serializer, &model, _state);
                  ae_serializer_stop(&_local_serializer, _state);

                  ae_frame_leave(_state);
               }
            }
            idwcreatecalcbuffer(&model, &buffer, _state);

         // Test report
            ae_set_error_flag(err, rep.rmserror != 0.0, __FILE__, __LINE__, "testidwunit.ap:169");
            ae_set_error_flag(err, rep.avgerror != 0.0, __FILE__, __LINE__, "testidwunit.ap:170");
            ae_set_error_flag(err, rep.maxerror != 0.0, __FILE__, __LINE__, "testidwunit.ap:171");
            ae_set_error_flag(err, rep.r2 != 1.0, __FILE__, __LINE__, "testidwunit.ap:172");

         // Test simplified evaluation
            x0 = hqrndnormal(&rs, _state);
            x1 = hqrndnormal(&rs, _state);
            x2 = hqrndnormal(&rs, _state);
            if (nx == 1 && ny == 1) {
               ae_set_error_flag(err, idwcalc1(&model, x0, _state) != 0.0, __FILE__, __LINE__, "testidwunit.ap:181");
            }
            if (nx == 2 && ny == 1) {
               ae_set_error_flag(err, idwcalc2(&model, x0, x1, _state) != 0.0, __FILE__, __LINE__, "testidwunit.ap:183");
            }
            if (nx == 3 && ny == 1) {
               ae_set_error_flag(err, idwcalc3(&model, x0, x1, x2, _state) != 0.0, __FILE__, __LINE__, "testidwunit.ap:185");
            }
         // Test generic evaluation
            ae_vector_set_length(&x, nx, _state);
            for (i = 0; i < nx; i++) {
               x.xR[i] = hqrndnormal(&rs, _state);
            }
            ae_vector_set_length(&y, 0, _state);
            idwcalc(&model, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ny, __FILE__, __LINE__, "testidwunit.ap:195");
            if (*err) {
               ae_frame_leave(_state);
               return;
            }
            for (i = 0; i < ny; i++) {
               ae_set_error_flag(err, y.xR[i] != 0.0, __FILE__, __LINE__, "testidwunit.ap:199");
            }
            ae_vector_set_length(&y, 0, _state);
            idwcalcbuf(&model, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ny, __FILE__, __LINE__, "testidwunit.ap:202");
            if (*err) {
               ae_frame_leave(_state);
               return;
            }
            for (i = 0; i < ny; i++) {
               ae_set_error_flag(err, y.xR[i] != 0.0, __FILE__, __LINE__, "testidwunit.ap:206");
            }
            ae_vector_set_length(&y, 0, _state);
            idwtscalcbuf(&model, &buffer, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ny, __FILE__, __LINE__, "testidwunit.ap:209");
            if (*err) {
               ae_frame_leave(_state);
               return;
            }
            for (i = 0; i < ny; i++) {
               ae_set_error_flag(err, y.xR[i] != 0.0, __FILE__, __LINE__, "testidwunit.ap:213");
            }
         }
      }

   // Generate random dataset with distinct points, test interpolation
   // properties (target function is reproduced almost exactly, the model
   // is continuous)
      for (pass = 1; pass <= 20; pass++) {
         n = 1 + hqrnduniformi(&rs, 25, _state);
         nx = 1 + hqrnduniformi(&rs, 4, _state);
         ny = 1 + hqrnduniformi(&rs, 4, _state);
         ae_vector_set_length(&x, nx, _state);
         ae_vector_set_length(&xx, nx, _state);
         ae_vector_set_length(&y, ny, _state);

      // Generate dataset with distinct points
         ae_matrix_set_length(&xy, n, nx + ny, _state);
         ae_vector_set_length(&meany, ny, _state);
         for (j = 0; j < ny; j++) {
            meany.xR[j] = 0.0;
         }
         i = 0;
         while (i < n) {

         // Generate random point
            for (j = 0; j < nx; j++) {
               xy.xyR[i][j] = hqrndnormal(&rs, _state);
            }

         // Test distance between newly generated point and other ones.
         // Repeat point generation if it is too close to some other point.
            v = ae_maxrealnumber;
            for (i0 = 0; i0 < i; i0++) {
               vv = 0.0;
               for (j = 0; j < nx; j++) {
                  vv = ae_maxreal(vv, ae_fabs(xy.xyR[i][j] - xy.xyR[i0][j], _state), _state);
               }
               v = ae_minreal(v, vv, _state);
            }
            if (v < mindistinf) {
               continue;
            }
         // Point is accepted
            for (j = 0; j < ny; j++) {
               xy.xyR[i][nx + j] = hqrndnormal(&rs, _state);
               meany.xR[j] = meany.xR[j] + xy.xyR[i][nx + j] / n;
            }
            i = i + 1;
         }

      // Build IDW model
         initdone = false;
         idwbuildercreate(nx, ny, &builder, _state);
         if (algotype == 0) {
            initdone = true;
            shepardp = 1 + (nx + 1) * hqrnduniformr(&rs, _state);
            idwbuildersetalgotextbookshepard(&builder, shepardp, _state);
         }
         if (algotype == 1) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgotextbookmodshepard(&builder, rbase, _state);
         }
         if (algotype == 2) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgomstab(&builder, rbase, _state);
         }
         ae_assert(initdone, "TestCommon: unexpected AlgoType", _state);
         idwbuildersetpoints(&builder, &xy, n, _state);
         if (hqrndnormal(&rs, _state) > 0.0) {

         // Fit and store result directly into the variable
            idwfit(&builder, &model, &rep, _state);
         } else {

         // Fit, store result to temporary, pass through the serializer
            idwfit(&builder, &model1, &rep, _state);
            {
            // This code passes data structure through serializers
            // (serializes it to string and loads back)
               ae_int_t _local_ssize;
               ae_frame _local_frame_block;

               ae_frame_make(_state, &_local_frame_block);

               NewSerializer(_local_serializer);
               ae_serializer_alloc_start(&_local_serializer);
               idwalloc(&_local_serializer, &model1, _state);
               _local_ssize = ae_serializer_get_alloc_size(&_local_serializer);
               NewBlock(_local_dynamic_block, _local_ssize + 1, _state);
               ae_serializer_sstart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
               idwserialize(&_local_serializer, &model1, _state);
               ae_serializer_stop(&_local_serializer, _state);

               ae_serializer_init(&_local_serializer);
               ae_serializer_ustart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
               idwunserialize(&_local_serializer, &model, _state);
               ae_serializer_stop(&_local_serializer, _state);

               ae_frame_leave(_state);
            }
         }
         idwcreatecalcbuffer(&model, &buffer, _state);

      // Test error metrics
      //
      // NOTE: we expect that dataset is reproduced exactly
         ae_set_error_flag(err, ae_fabs(rep.rmserror, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:320");
         ae_set_error_flag(err, ae_fabs(rep.avgerror, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:321");
         ae_set_error_flag(err, ae_fabs(rep.maxerror, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:322");
         ae_set_error_flag(err, ae_fabs(rep.r2, _state) < 1 - tol, __FILE__, __LINE__, "testidwunit.ap:323");

      // Test that dataset is actually exactly reproduced
         for (i = 0; i < n; i++) {

         // Test generic evaluation
            for (j = 0; j < nx; j++) {
               x.xR[j] = xy.xyR[i][j];
            }
            ae_vector_set_length(&y, 0, _state);
            idwcalc(&model, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ny, __FILE__, __LINE__, "testidwunit.ap:337");
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j] - xy.xyR[i][nx + j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:339");
            }
            k = hqrnduniformi(&rs, 2 * ny + 1, _state);
            ae_vector_set_length(&y, k, _state);
            for (j = 0; j < y.cnt; j++) {
               y.xR[j] = 0.0;
            }
            idwcalcbuf(&model, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ae_maxint(ny, k, _state), __FILE__, __LINE__, "testidwunit.ap:346");
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j] - xy.xyR[i][nx + j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:348");
            }
            k = hqrnduniformi(&rs, 2 * ny + 1, _state);
            ae_vector_set_length(&y, k, _state);
            for (j = 0; j < y.cnt; j++) {
               y.xR[j] = 0.0;
            }
            idwtscalcbuf(&model, &buffer, &x, &y, _state);
            ae_set_error_flag(err, y.cnt != ae_maxint(ny, k, _state), __FILE__, __LINE__, "testidwunit.ap:355");
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j] - xy.xyR[i][nx + j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:357");
            }

         // Specialized 1, 2, 3-dimensional cases
            if (ny == 1) {
               if (nx == 1) {
                  ae_set_error_flag(err, ae_fabs(idwcalc1(&model, x.xR[0], _state) - xy.xyR[i][nx], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:366");
               }
               if (nx == 2) {
                  ae_set_error_flag(err, ae_fabs(idwcalc2(&model, x.xR[0], x.xR[1], _state) - xy.xyR[i][nx], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:368");
               }
               if (nx == 3) {
                  ae_set_error_flag(err, ae_fabs(idwcalc3(&model, x.xR[0], x.xR[1], x.xR[2], _state) - xy.xyR[i][nx], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:370");
               }
            }
         }

      // Test continuity properties:
      // * continuity is guaranteed for original Shepard's method, MSTAB and MSMOOTH
      // * modified Shepard method does not guarantee continuity of the model, but
      //   we can be sure that model is continuous along line connecting two nearest
      //   points
         for (k = 0; k <= 1; k++) {
            i0 = hqrnduniformi(&rs, n, _state);
            for (j = 0; j < nx; j++) {
               x.xR[j] = xy.xyR[i0][j];
            }
            i1 = -1;
            v = ae_maxrealnumber;
            for (i = 0; i < n; i++) {
               vv = 0.0;
               for (j = 0; j < nx; j++) {
                  vv = vv + ae_sqr(x.xR[j] - xy.xyR[i][j], _state);
               }
               if (vv < v && vv > 0.0) {
                  i1 = i;
                  for (j = 0; j < nx; j++) {
                     xx.xR[j] = xy.xyR[i][j];
                  }
                  v = vv;
               }
            }
            if (i1 < 0) {
               i1 = hqrnduniformi(&rs, n, _state);
               for (j = 0; j < nx; j++) {
                  xx.xR[j] = xy.xyR[i1][j];
               }
            }
            continuitytesting = 1;
            if (algotype == 0) {
               continuitytesting = 0;
            }
            if (algotype == 1) {
               continuitytesting = -1;
            }
            if (continuitytesting >= 0) {
               testidwunit_testcontinuity(&model, nx, ny, &x, &xx, 10000, continuitytesting, err, _state);
            }
         }

      // Test evaluation at remote points
         ae_vector_set_length(&x, nx, _state);
         for (j = 0; j < nx; j++) {
            x.xR[j] = 1.0E20 * (2 * hqrnduniformi(&rs, 2, _state) - 1);
         }
         idwcalc(&model, &x, &y, _state);
         for (j = 0; j < ny; j++) {
            ae_set_error_flag(err, ae_fabs(y.xR[j] - meany.xR[j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:424");
         }
      }

   // Generate random dataset with NONDISTINCT points, test approximation
   // properties and error reports.
      for (pass = 1; pass <= 20; pass++) {
         n = 2 * (1 + hqrnduniformi(&rs, 10, _state));
         nx = 1 + hqrnduniformi(&rs, 4, _state);
         ny = 1 + hqrnduniformi(&rs, 4, _state);
         ae_vector_set_length(&x, nx, _state);
         ae_vector_set_length(&xx, nx, _state);
         ae_vector_set_length(&y, ny, _state);
         ae_vector_set_length(&meany, ny, _state);

      // Generate dataset with nondistinct points, each point is repeated;
      // compute reference values of the error metrics
         ae_matrix_set_length(&xy, n, nx + ny, _state);
         refrms = 0.0;
         refavg = 0.0;
         refmax = 0.0;
         refrss = 0.0;
         reftss = 0.0;
         for (j = 0; j < ny; j++) {
            meany.xR[j] = 0.0;
         }
         i = 0;
         while (i < n / 2) {

         // Generate two copies of the same point
            for (j = 0; j < nx + ny; j++) {
               v = hqrndnormal(&rs, _state);
               xy.xyR[2 * i + 0][j] = v;
               xy.xyR[2 * i + 1][j] = v;
            }

         // Test distance between newly generated point and other ones.
         // Repeat point generation if it is too close to some other point.
            v = ae_maxrealnumber;
            for (i0 = 0; i0 < 2 * i; i0++) {
               vv = 0.0;
               for (j = 0; j < nx; j++) {
                  vv = ae_maxreal(vv, ae_fabs(xy.xyR[2 * i + 0][j] - xy.xyR[i0][j], _state), _state);
               }
               v = ae_minreal(v, vv, _state);
            }
            if (v < mindistinf) {
               continue;
            }
         // Update meanY
            for (j = 0; j < ny; j++) {
               meany.xR[j] = meany.xR[j] + (xy.xyR[2 * i + 0][nx + j] + xy.xyR[2 * i + 1][nx + j]) / n;
            }

         // Apply perturbation to the target value
            for (j = 0; j < ny; j++) {
               v = ae_pow(2.0, hqrndnormal(&rs, _state), _state);
               xy.xyR[2 * i + 0][nx + j] = xy.xyR[2 * i + 0][nx + j] + v;
               xy.xyR[2 * i + 1][nx + j] = xy.xyR[2 * i + 1][nx + j] - v;
               v = ae_fabs(v, _state);
               refrms = refrms + 2 * v * v;
               refavg = refavg + 2 * v;
               refmax = ae_maxreal(refmax, v, _state);
               refrss = refrss + 2 * v * v;
            }

         // Next I
            i = i + 1;
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < ny; j++) {
               reftss = reftss + ae_sqr(xy.xyR[i][nx + j] - meany.xR[j], _state);
            }
         }
         refrms = ae_sqrt(refrms / (n * ny), _state);
         refavg = refavg / (n * ny);
         refr2 = 1.0 - refrss / coalesce(reftss, 1.0, _state);

      // Build IDW model
         initdone = false;
         idwbuildercreate(nx, ny, &builder, _state);
         if (algotype == 0) {
            initdone = true;
            shepardp = nx * (1 + hqrnduniformr(&rs, _state));
            idwbuildersetalgotextbookshepard(&builder, shepardp, _state);
         }
         if (algotype == 1) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgotextbookmodshepard(&builder, rbase, _state);
         }
         if (algotype == 2) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgomstab(&builder, rbase, _state);
         }
         ae_assert(initdone, "TestCommon: unexpected AlgoType", _state);
         idwbuildersetpoints(&builder, &xy, n, _state);
         if (hqrndnormal(&rs, _state) > 0.0) {

         // Fit and store result directly into the variable
            idwfit(&builder, &model, &rep, _state);
         } else {

         // Fit, store result to temporary, pass through the serializer
            idwfit(&builder, &model1, &rep, _state);
            {
            // This code passes data structure through serializers
            // (serializes it to string and loads back)
               ae_int_t _local_ssize;
               ae_frame _local_frame_block;

               ae_frame_make(_state, &_local_frame_block);

               NewSerializer(_local_serializer);
               ae_serializer_alloc_start(&_local_serializer);
               idwalloc(&_local_serializer, &model1, _state);
               _local_ssize = ae_serializer_get_alloc_size(&_local_serializer);
               NewBlock(_local_dynamic_block, _local_ssize + 1, _state);
               ae_serializer_sstart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
               idwserialize(&_local_serializer, &model1, _state);
               ae_serializer_stop(&_local_serializer, _state);

               ae_serializer_init(&_local_serializer);
               ae_serializer_ustart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
               idwunserialize(&_local_serializer, &model, _state);
               ae_serializer_stop(&_local_serializer, _state);

               ae_frame_leave(_state);
            }
         }
         idwcreatecalcbuffer(&model, &buffer, _state);

      // Test error metrics
         ae_set_error_flag(err, ae_fabs(rep.rmserror - refrms, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:562");
         ae_set_error_flag(err, ae_fabs(rep.avgerror - refavg, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:563");
         ae_set_error_flag(err, ae_fabs(rep.maxerror - refmax, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:564");
         ae_set_error_flag(err, ae_fabs(rep.r2 - refr2, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:565");

      // Test ability to reproduce mean over non-distinct points
      //
      // NOTE: we do not test all evaluation functions, just IDWCalc()
         for (i = 0; i < n / 2; i++) {
            for (j = 0; j < nx; j++) {
               x.xR[j] = xy.xyR[2 * i + 0][j];
            }
            idwcalc(&model, &x, &y, _state);
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j] - 0.5 * (xy.xyR[2 * i + 0][nx + j] + xy.xyR[2 * i + 1][nx + j]), _state) > tol, __FILE__, __LINE__, "testidwunit.ap:578");
            }
         }

      // Test continuity properties:
      // * continuity is guaranteed for original Shepard's method, MSTAB and MSMOOTH
      // * modified Shepard method does not guarantee continuity of the model, but
      //   we can be sure that model is continuous along line connecting two nearest
      //   points
         for (k = 0; k <= 1; k++) {
            i0 = hqrnduniformi(&rs, n, _state);
            for (j = 0; j < nx; j++) {
               x.xR[j] = xy.xyR[i0][j];
            }
            i1 = -1;
            v = ae_maxrealnumber;
            for (i = 0; i < n; i++) {
               vv = 0.0;
               for (j = 0; j < nx; j++) {
                  vv = vv + ae_sqr(x.xR[j] - xy.xyR[i][j], _state);
               }
               if (vv < v && vv > 0.0) {
                  i1 = i;
                  for (j = 0; j < nx; j++) {
                     xx.xR[j] = xy.xyR[i][j];
                  }
                  v = vv;
               }
            }
            if (i1 < 0) {
               i1 = hqrnduniformi(&rs, n, _state);
               for (j = 0; j < nx; j++) {
                  xx.xR[j] = xy.xyR[i1][j];
               }
            }
            continuitytesting = 1;
            if (algotype == 0) {
               continuitytesting = 0;
            }
            if (algotype == 1) {
               continuitytesting = -1;
            }
            if (continuitytesting >= 0) {
               testidwunit_testcontinuity(&model, nx, ny, &x, &xx, 10000, continuitytesting, err, _state);
            }
         }
      }

   // Test correct handling of the prior term
      n = 10;
      for (pass = 1; pass <= 20; pass++) {
         nx = 1 + hqrnduniformi(&rs, 4, _state);
         ny = 1 + hqrnduniformi(&rs, 4, _state);
         ae_vector_set_length(&x, nx, _state);
         ae_vector_set_length(&y, ny, _state);
         ae_matrix_set_length(&xy, n, nx + ny, _state);
         ae_vector_set_length(&meany, ny, _state);
         for (j = 0; j < ny; j++) {
            meany.xR[j] = 0.0;
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < nx; j++) {
               xy.xyR[i][j] = hqrndnormal(&rs, _state);
            }
            for (j = 0; j < ny; j++) {
               xy.xyR[i][nx + j] = hqrndnormal(&rs, _state);
               meany.xR[j] = meany.xR[j] + xy.xyR[i][nx + j] / n;
            }
         }
         initdone = false;
         idwbuildercreate(nx, ny, &builder, _state);
         if (algotype == 0) {
            initdone = true;
            shepardp = 1 + (nx + 1) * hqrnduniformr(&rs, _state);
            idwbuildersetalgotextbookshepard(&builder, shepardp, _state);
         }
         if (algotype == 1) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgotextbookmodshepard(&builder, rbase, _state);
         }
         if (algotype == 2) {
            initdone = true;
            rbase = ae_pow(2.0, 4 * hqrnduniformr(&rs, _state) - 2, _state);
            idwbuildersetalgomstab(&builder, rbase, _state);
         }
         ae_assert(initdone, "TestCommon: unexpected AlgoType (prior test)", _state);
         idwbuildersetpoints(&builder, &xy, n, _state);

      // Zero prior (not tested with textbook Shepard method)
         if (algotype != 0) {
            idwbuildersetzeroterm(&builder, _state);
            idwfit(&builder, &model, &rep, _state);
            for (j = 0; j < nx; j++) {
               x.xR[j] = 1.0E20 * (2 * hqrnduniformi(&rs, 2, _state) - 1);
            }
            idwcalc(&model, &x, &y, _state);
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:684");
            }
         }
      // Mean prior
         idwbuildersetconstterm(&builder, _state);
         idwfit(&builder, &model, &rep, _state);
         for (j = 0; j < nx; j++) {
            x.xR[j] = 1.0E20 * (2 * hqrnduniformi(&rs, 2, _state) - 1);
         }
         idwcalc(&model, &x, &y, _state);
         for (j = 0; j < ny; j++) {
            ae_set_error_flag(err, ae_fabs(y.xR[j] - meany.xR[j], _state) > tol, __FILE__, __LINE__, "testidwunit.ap:696");
         }

      // User-specified prior (not tested with textbook Shepard method)
         if (algotype != 0) {
            v = hqrndnormal(&rs, _state);
            idwbuildersetuserterm(&builder, v, _state);
            idwfit(&builder, &model, &rep, _state);
            for (j = 0; j < nx; j++) {
               x.xR[j] = 1.0E20 * (2 * hqrnduniformi(&rs, 2, _state) - 1);
            }
            idwcalc(&model, &x, &y, _state);
            for (j = 0; j < ny; j++) {
               ae_set_error_flag(err, ae_fabs(y.xR[j] - v, _state) > tol, __FILE__, __LINE__, "testidwunit.ap:710");
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Test MSTAB; Err is set to True on failure, unchanged otherwise.
static void testidwunit_testmstab(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t n;
   double v;
   double vv;
   double x0;
   double x1;
   double rbase;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(idwmodel, model, _state);
   NewObj(idwbuilder, builder, _state);
   NewObj(idwreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);

// Basic test #1: nonzero derivative
// * XY = [[-1,-1],[0,0,],[1,1]]
// * RBase >= 2
// * derivative at x=0 must be positive and bigger than 0.1
   ae_matrix_set_length(&xy, 3, 2, _state);
   for (i = 0; i <= 2; i++) {
      xy.xyR[i][0] = (double)(i - 1);
      xy.xyR[i][1] = (double)(i - 1);
   }
   idwbuildercreate(1, 1, &builder, _state);
   rbase = ae_pow(2.0, 1.0 + hqrnduniformr(&rs, _state), _state);
   idwbuildersetalgomstab(&builder, rbase, _state);
   idwbuildersetpoints(&builder, &xy, 3, _state);
   idwfit(&builder, &model, &rep, _state);
   v = 0.01;
   ae_set_error_flag(err, (idwcalc1(&model, v, _state) - idwcalc1(&model, -v, _state)) / (2 * v) < 0.1, __FILE__, __LINE__, "testidwunit.ap:758");

// Basic test #2: good smoothness
// * 2D task, dataset is composed from 3 parallel lines
//   along y=-0.1, y=0, y=+0.1, with outer lines having
//   constant zero target value, inner line having constant
//   target equal to 1
// * RBase=1 is used
// * we test that function value does not change significantly
//   along the line
   n = 100;
   ae_matrix_set_length(&xy, 3 * n, 3, _state);
   for (i = 0; i < n; i++) {
      xy.xyR[3 * i + 0][0] = (double)i / (n - 1);
      xy.xyR[3 * i + 0][1] = -0.1;
      xy.xyR[3 * i + 0][2] = 0.0;
      xy.xyR[3 * i + 1][0] = (double)i / (n - 1);
      xy.xyR[3 * i + 1][1] = 0.0;
      xy.xyR[3 * i + 1][2] = 1.0;
      xy.xyR[3 * i + 2][0] = (double)i / (n - 1);
      xy.xyR[3 * i + 2][1] = 0.1;
      xy.xyR[3 * i + 2][2] = 0.0;
   }
   rbase = 1.0;
   idwbuildercreate(2, 1, &builder, _state);
   idwbuildersetalgomstab(&builder, rbase, _state);
   idwbuildersetpoints(&builder, &xy, 3 * n, _state);
   idwfit(&builder, &model, &rep, _state);
   v = 0.0;
   for (i = 0; i <= 1000; i++) {
      v = ae_maxreal(v, ae_fabs(idwcalc2(&model, hqrnduniformr(&rs, _state), -0.1, _state), _state), _state);
      v = ae_maxreal(v, ae_fabs(idwcalc2(&model, hqrnduniformr(&rs, _state), 0.1, _state), _state), _state);
      v = ae_maxreal(v, ae_fabs(idwcalc2(&model, hqrnduniformr(&rs, _state), 0.0, _state) - 1, _state), _state);
   }
   ae_set_error_flag(err, v > 0.001, __FILE__, __LINE__, "testidwunit.ap:796");

// Continuity when moving away from the dataset
   ae_matrix_set_length(&xy, 1, 2, _state);
   xy.xyR[0][0] = 0.0;
   xy.xyR[0][1] = 1.0;
   rbase = 1.0;
   idwbuildercreate(1, 1, &builder, _state);
   idwbuildersetalgomstab(&builder, rbase, _state);
   idwbuildersetpoints(&builder, &xy, 1, _state);
   idwbuildersetzeroterm(&builder, _state);
   idwfit(&builder, &model, &rep, _state);
   ae_set_error_flag(err, idwcalc1(&model, 100000.0, _state) != 0.0, __FILE__, __LINE__, "testidwunit.ap:810");
   v = 0.0;
   for (i = 0; i <= 500; i++) {
      x0 = 1.2 * rbase * ((double)i / 500.0);
      x1 = 1.2 * rbase * ((double)(i + 1) / 500.0);
      v = ae_maxreal(v, ae_fabs((idwcalc1(&model, x1, _state) - idwcalc1(&model, x0, _state)) / (x1 - x0), _state), _state);
   }
   vv = 0.0;
   for (i = 0; i <= 1000; i++) {
      x0 = 1.2 * rbase * ((double)i / 1000.0);
      x1 = 1.2 * rbase * ((double)(i + 1) / 1000.0);
      vv = ae_maxreal(vv, ae_fabs((idwcalc1(&model, x1, _state) - idwcalc1(&model, x0, _state)) / (x1 - x0), _state), _state);
   }
   ae_set_error_flag(err, vv / v > 1.333, __FILE__, __LINE__, "testidwunit.ap:825");
   ae_frame_leave(_state);
}

// === polint testing unit ===
static double testpolintunit_internalpolint(RVector *x, RVector *f, ae_int_t n, double t, ae_state *_state);
static void testpolintunit_brcunset(barycentricinterpolant *b, ae_state *_state);

// Unit test
bool testpolint(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   bool waserrors;
   bool interrors;
   double threshold;
   double a;
   double b;
   double t;
   ae_int_t i;
   ae_int_t k;
   double v;
   double v0;
   double v1;
   double v2;
   double v3;
   double v4;
   double pscale;
   double poffset;
   double eps;
   ae_int_t n;
   ae_int_t maxn;
   ae_int_t pass;
   ae_int_t passcount;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);
   NewVector(c, 0, DT_REAL, _state);
   NewVector(c0, 0, DT_REAL, _state);
   NewVector(c1, 0, DT_REAL, _state);
   NewVector(c2, 0, DT_REAL, _state);
   NewVector(x2, 0, DT_REAL, _state);
   NewVector(y2, 0, DT_REAL, _state);
   NewVector(w2, 0, DT_REAL, _state);
   NewVector(xfull, 0, DT_REAL, _state);
   NewVector(yfull, 0, DT_REAL, _state);
   NewVector(xc, 0, DT_REAL, _state);
   NewVector(yc, 0, DT_REAL, _state);
   NewVector(dc, 0, DT_INT, _state);
   NewObj(barycentricinterpolant, p, _state);
   NewObj(barycentricinterpolant, p1, _state);
   NewObj(barycentricinterpolant, p2, _state);

   waserrors = false;
   interrors = false;
   maxn = 5;
   passcount = 20;
   threshold = 1.0E8 * ae_machineepsilon;

// Test equidistant interpolation
   for (pass = 1; pass <= passcount; pass++) {
      for (n = 1; n <= maxn; n++) {

      // prepare task:
      // * equidistant points
      // * random Y
      // * T in [A,B] or near (within 10% of its width)
         do {
            a = 2 * ae_randomreal(_state) - 1;
            b = 2 * ae_randomreal(_state) - 1;
         } while (ae_fabs(a - b, _state) <= 0.2);
         t = a + (1.2 * ae_randomreal(_state) - 0.1) * (b - a);
         taskgenint1dequidist(a, b, n, &x, &y, _state);

      // test "fast" equidistant interpolation (no barycentric model)
         interrors = interrors || ae_fabs(polynomialcalceqdist(a, b, &y, n, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "slow" equidistant interpolation (create barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuild(&x, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "fast" interpolation (create "fast" barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuildeqdist(a, b, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;
      }
   }

// Test Chebyshev-1 interpolation
   for (pass = 1; pass <= passcount; pass++) {
      for (n = 1; n <= maxn; n++) {

      // prepare task:
      // * equidistant points
      // * random Y
      // * T in [A,B] or near (within 10% of its width)
         do {
            a = 2 * ae_randomreal(_state) - 1;
            b = 2 * ae_randomreal(_state) - 1;
         } while (ae_fabs(a - b, _state) <= 0.2);
         t = a + (1.2 * ae_randomreal(_state) - 0.1) * (b - a);
         taskgenint1dcheb1(a, b, n, &x, &y, _state);

      // test "fast" interpolation (no barycentric model)
         interrors = interrors || ae_fabs(polynomialcalccheb1(a, b, &y, n, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "slow" interpolation (create barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuild(&x, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "fast" interpolation (create "fast" barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuildcheb1(a, b, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;
      }
   }

// Test Chebyshev-2 interpolation
   for (pass = 1; pass <= passcount; pass++) {
      for (n = 1; n <= maxn; n++) {

      // prepare task:
      // * equidistant points
      // * random Y
      // * T in [A,B] or near (within 10% of its width)
         do {
            a = 2 * ae_randomreal(_state) - 1;
            b = 2 * ae_randomreal(_state) - 1;
         } while (ae_fabs(a - b, _state) <= 0.2);
         t = a + (1.2 * ae_randomreal(_state) - 0.1) * (b - a);
         taskgenint1dcheb2(a, b, n, &x, &y, _state);

      // test "fast" interpolation (no barycentric model)
         interrors = interrors || ae_fabs(polynomialcalccheb2(a, b, &y, n, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "slow" interpolation (create barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuild(&x, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;

      // test "fast" interpolation (create "fast" barycentric model)
         testpolintunit_brcunset(&p, _state);
         polynomialbuildcheb2(a, b, &y, n, &p, _state);
         interrors = interrors || ae_fabs(barycentriccalc(&p, t, _state) - testpolintunit_internalpolint(&x, &y, n, t, _state), _state) > threshold;
      }
   }

// Testing conversion Barycentric<->Chebyshev
   for (pass = 1; pass <= passcount; pass++) {
      for (k = 1; k <= 3; k++) {

      // Allocate
         ae_vector_set_length(&x, k, _state);
         ae_vector_set_length(&y, k, _state);

      // Generate problem
         a = 2 * ae_randomreal(_state) - 1;
         b = a + (0.1 + ae_randomreal(_state)) * (2 * ae_randominteger(2, _state) - 1);
         v0 = 2 * ae_randomreal(_state) - 1;
         v1 = 2 * ae_randomreal(_state) - 1;
         v2 = 2 * ae_randomreal(_state) - 1;
         if (k == 1) {
            x.xR[0] = 0.5 * (a + b);
            y.xR[0] = v0;
         }
         if (k == 2) {
            x.xR[0] = a;
            y.xR[0] = v0 - v1;
            x.xR[1] = b;
            y.xR[1] = v0 + v1;
         }
         if (k == 3) {
            x.xR[0] = a;
            y.xR[0] = v0 - v1 + v2;
            x.xR[1] = 0.5 * (a + b);
            y.xR[1] = v0 - v2;
            x.xR[2] = b;
            y.xR[2] = v0 + v1 + v2;
         }
      // Test forward conversion
         polynomialbuild(&x, &y, k, &p, _state);
         ae_vector_set_length(&c, 1, _state);
         polynomialbar2cheb(&p, a, b, &c, _state);
         interrors = interrors || c.cnt != k;
         if (k >= 1) {
            interrors = interrors || ae_fabs(c.xR[0] - v0, _state) > threshold;
         }
         if (k >= 2) {
            interrors = interrors || ae_fabs(c.xR[1] - v1, _state) > threshold;
         }
         if (k >= 3) {
            interrors = interrors || ae_fabs(c.xR[2] - v2, _state) > threshold;
         }
      // Test backward conversion
         polynomialcheb2bar(&c, k, a, b, &p2, _state);
         v = a + ae_randomreal(_state) * (b - a);
         interrors = interrors || ae_fabs(barycentriccalc(&p, v, _state) - barycentriccalc(&p2, v, _state), _state) > threshold;
      }
   }

// Testing conversion Barycentric<->Power
   for (pass = 1; pass <= passcount; pass++) {
      for (k = 1; k <= 5; k++) {

      // Allocate
         ae_vector_set_length(&x, k, _state);
         ae_vector_set_length(&y, k, _state);

      // Generate problem
         poffset = 2 * ae_randomreal(_state) - 1;
         pscale = (0.1 + ae_randomreal(_state)) * (2 * ae_randominteger(2, _state) - 1);
         v0 = 2 * ae_randomreal(_state) - 1;
         v1 = 2 * ae_randomreal(_state) - 1;
         v2 = 2 * ae_randomreal(_state) - 1;
         v3 = 2 * ae_randomreal(_state) - 1;
         v4 = 2 * ae_randomreal(_state) - 1;
         if (k == 1) {
            x.xR[0] = poffset;
            y.xR[0] = v0;
         }
         if (k == 2) {
            x.xR[0] = poffset - pscale;
            y.xR[0] = v0 - v1;
            x.xR[1] = poffset + pscale;
            y.xR[1] = v0 + v1;
         }
         if (k == 3) {
            x.xR[0] = poffset - pscale;
            y.xR[0] = v0 - v1 + v2;
            x.xR[1] = poffset;
            y.xR[1] = v0;
            x.xR[2] = poffset + pscale;
            y.xR[2] = v0 + v1 + v2;
         }
         if (k == 4) {
            x.xR[0] = poffset - pscale;
            y.xR[0] = v0 - v1 + v2 - v3;
            x.xR[1] = poffset - 0.5 * pscale;
            y.xR[1] = v0 - 0.5 * v1 + 0.25 * v2 - 0.125 * v3;
            x.xR[2] = poffset + 0.5 * pscale;
            y.xR[2] = v0 + 0.5 * v1 + 0.25 * v2 + 0.125 * v3;
            x.xR[3] = poffset + pscale;
            y.xR[3] = v0 + v1 + v2 + v3;
         }
         if (k == 5) {
            x.xR[0] = poffset - pscale;
            y.xR[0] = v0 - v1 + v2 - v3 + v4;
            x.xR[1] = poffset - 0.5 * pscale;
            y.xR[1] = v0 - 0.5 * v1 + 0.25 * v2 - 0.125 * v3 + 0.0625 * v4;
            x.xR[2] = poffset;
            y.xR[2] = v0;
            x.xR[3] = poffset + 0.5 * pscale;
            y.xR[3] = v0 + 0.5 * v1 + 0.25 * v2 + 0.125 * v3 + 0.0625 * v4;
            x.xR[4] = poffset + pscale;
            y.xR[4] = v0 + v1 + v2 + v3 + v4;
         }
      // Test forward conversion
         polynomialbuild(&x, &y, k, &p, _state);
         ae_vector_set_length(&c, 1, _state);
         polynomialbar2pow(&p, poffset, pscale, &c, _state);
         interrors = interrors || c.cnt != k;
         if (k >= 1) {
            interrors = interrors || ae_fabs(c.xR[0] - v0, _state) > threshold;
         }
         if (k >= 2) {
            interrors = interrors || ae_fabs(c.xR[1] - v1, _state) > threshold;
         }
         if (k >= 3) {
            interrors = interrors || ae_fabs(c.xR[2] - v2, _state) > threshold;
         }
         if (k >= 4) {
            interrors = interrors || ae_fabs(c.xR[3] - v3, _state) > threshold;
         }
         if (k >= 5) {
            interrors = interrors || ae_fabs(c.xR[4] - v4, _state) > threshold;
         }
      // Test backward conversion
         polynomialpow2bar(&c, k, poffset, pscale, &p2, _state);
         v = poffset + (2 * ae_randomreal(_state) - 1) * pscale;
         interrors = interrors || ae_fabs(barycentriccalc(&p, v, _state) - barycentriccalc(&p2, v, _state), _state) > threshold;
      }
   }

// crash-test: ability to solve tasks which will overflow/underflow
// weights with straightforward implementation
   for (n = 1; n <= 20; n++) {
      a = -0.1 * ae_maxrealnumber;
      b = 0.1 * ae_maxrealnumber;
      taskgenint1dequidist(a, b, n, &x, &y, _state);
      polynomialbuild(&x, &y, n, &p, _state);
      for (i = 0; i < n; i++) {
         interrors = interrors || p.w.xR[i] == 0.0;
      }
   }

// Test issue #634: instability in PolynomialBar2Pow().
//
// Function returns incorrect coefficients when called with
// approximately-unit scale for data which have significantly
// non-unit scale.
   n = 7;
   eps = 1.0E-8;
   ae_vector_set_length(&x, n, _state);
   ae_vector_set_length(&x2, n, _state);
   ae_vector_set_length(&y, n, _state);
   x.xR[0] = ae_randomreal(_state) - 0.5;
   y.xR[0] = ae_randomreal(_state) - 0.5;
   for (i = 1; i < n; i++) {
      x.xR[i] = x.xR[i - 1] + ae_randomreal(_state) + 0.1;
      y.xR[i] = ae_randomreal(_state) - 0.5;
   }
   polynomialbuild(&x, &y, n, &p, _state);
   polynomialbar2pow(&p, 0.0, 1.0, &c0, _state);
   pscale = 1.0E-10;
   for (i = 0; i < n; i++) {
      x2.xR[i] = x.xR[i] * pscale;
   }
   polynomialbuild(&x2, &y, n, &p, _state);
   polynomialbar2pow(&p, 0.0, 1.0, &c1, _state);
   for (i = 0; i < n; i++) {
      ae_set_error_flag(&interrors, ae_fabs(c0.xR[i] - c1.xR[i] * ae_pow(pscale, (double)i, _state), _state) > eps, __FILE__, __LINE__, "testpolintunit.ap:395");
   }
   pscale = 1.0E10;
   for (i = 0; i < n; i++) {
      x2.xR[i] = x.xR[i] * pscale;
   }
   polynomialbuild(&x2, &y, n, &p, _state);
   polynomialbar2pow(&p, 0.0, 1.0, &c2, _state);
   for (i = 0; i < n; i++) {
      ae_set_error_flag(&interrors, ae_fabs(c0.xR[i] - c2.xR[i] * ae_pow(pscale, (double)i, _state), _state) > eps, __FILE__, __LINE__, "testpolintunit.ap:402");
   }

// report
   waserrors = interrors;
   if (!silent) {
      printf("TESTING POLYNOMIAL INTERPOLATION\n");

   // Normal tests
      printf("INTERPOLATION TEST:                      ");
      if (interrors) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
// end
   result = !waserrors;
   ae_frame_leave(_state);
   return result;
}

static double testpolintunit_internalpolint(RVector *x, RVector *f, ae_int_t n, double t, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t i;
   ae_int_t j;
   double result;

   ae_frame_make(_state, &_frame_block);
   DupVector(f, _state);

   n = n - 1;
   for (j = 0; j < n; j++) {
      for (i = j + 1; i <= n; i++) {
         f->xR[i] = ((t - x->xR[j]) * f->xR[i] - (t - x->xR[i]) * f->xR[j]) / (x->xR[i] - x->xR[j]);
      }
   }
   result = f->xR[n];
   ae_frame_leave(_state);
   return result;
}

static void testpolintunit_brcunset(barycentricinterpolant *b, ae_state *_state) {
   ae_frame _frame_block;

   ae_frame_make(_state, &_frame_block);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(w, 0, DT_REAL, _state);

   ae_vector_set_length(&x, 1, _state);
   ae_vector_set_length(&y, 1, _state);
   ae_vector_set_length(&w, 1, _state);
   x.xR[0] = 0.0;
   y.xR[0] = 0.0;
   w.xR[0] = 1.0;
   barycentricbuildxyw(&x, &y, &w, 1, b, _state);
   ae_frame_leave(_state);
}

