// This function tests least squares (LS) sphere fitting using generic
// synthetic datasets
//
// On failure sets Err to True (leaves it unchanged otherwise)
static void testfitsphereunit_testspherefittingls(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   double xtol;
   ae_int_t npoints;
   ae_int_t nx;
   double rlo;
   double rhi;
   double ftol;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v;
   double vv;
   double v0;
   double v1;
   ae_int_t problemtype;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewVector(cx, 0, DT_REAL, _state);
   NewVector(cy, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);
   xtol = 1.0E-5;

// Generate random problem
   for (nx = 1; nx <= 4; nx++) {

   // Generate synthetic dataset, at least 5 points
      npoints = 5 + 2 * nx + hqrnduniformi(&rs, 50 + ae_round(ae_pow(4.0, (double)nx, _state), _state), _state);
      ae_matrix_set_length(&xy, npoints, nx, _state);
      for (i = 0; i < npoints; i++) {
         v = 0.0;
         for (j = 0; j < nx; j++) {
            vv = hqrndnormal(&rs, _state);
            v = v + ae_sqr(vv, _state);
            xy.xyR[i][j] = vv;
         }
         ae_assert(v > 0.0, "Assertion failed", _state);
         v = (1 + 0.1 * hqrnduniformr(&rs, _state)) / ae_sqrt(v, _state);
         for (j = 0; j < nx; j++) {
            xy.xyR[i][j] = xy.xyR[i][j] * v;
         }
      }

   // Solve with generic solver, check
      unsetrealarray(&cx, _state);
      rlo = 0.0;
      rhi = 0.0;
      problemtype = 0;
      fitspherex(&xy, npoints, nx, problemtype, 0.0, 0, 0.0, &cx, &rlo, &rhi, _state);
      ae_set_error_flag(err, rlo != rhi, __FILE__, __LINE__, "testfitsphereunit.ap:109");
      vv = 0.0;
      for (i = 0; i < npoints; i++) {
         v = 0.0;
         for (j = 0; j < nx; j++) {
            v = v + ae_sqr(cx.xR[j] - xy.xyR[i][j], _state);
         }
         vv = vv + ae_sqrt(v, _state) / npoints;
      }
      ae_set_error_flag(err, ae_fabs(vv - rlo, _state) > xtol, __FILE__, __LINE__, "testfitsphereunit.ap:118");
      testfitsphereunit_calclserror(&xy, npoints, nx, &cx, &v0, _state);

   // Check that small perturbations to center position increase target function
   //
   // NOTE: in fact, we do allow small increase in target function - but no more
   //       than FTol=1E-6*XTol. It helps to avoid spurious error reports in
   //       degenerate cases.
      ftol = 1.0E-6 * xtol;
      ae_vector_set_length(&cy, nx, _state);
      for (j = 0; j < nx; j++) {
         for (k = 0; k < nx; k++) {
            cy.xR[k] = cx.xR[k];
         }
         cy.xR[j] = cx.xR[j] + xtol;
         testfitsphereunit_calclserror(&xy, npoints, nx, &cy, &v1, _state);
         ae_set_error_flag(err, v1 < v0 - ftol, __FILE__, __LINE__, "testfitsphereunit.ap:137");
         for (k = 0; k < nx; k++) {
            cy.xR[k] = cx.xR[k];
         }
         cy.xR[j] = cx.xR[j] - xtol;
         testfitsphereunit_calclserror(&xy, npoints, nx, &cy, &v1, _state);
         ae_set_error_flag(err, v1 < v0 - ftol, __FILE__, __LINE__, "testfitsphereunit.ap:144");
      }

   // Compare against results returned by specific solver
      fitspherels(&xy, npoints, nx, &cy, &v, _state);
      ae_set_error_flag(err, v != rlo, __FILE__, __LINE__, "testfitsphereunit.ap:151");
      for (j = 0; j < nx; j++) {
         ae_set_error_flag(err, cy.xR[j] != cx.xR[j], __FILE__, __LINE__, "testfitsphereunit.ap:153");
      }
   }
   ae_frame_leave(_state);
}

// This function tests sphere fitting using generic synthetic datasets and
// non-smooth target functions (MC, MI, MZ fitting)
//
// On failure sets Err to True (leaves it unchanged otherwise)
static void testfitsphereunit_testspherefittingns(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t npoints;
   ae_int_t nx;
   double rlo;
   double rhi;
   double rlo2;
   double rhi2;
   double xtol;
   double ftol;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v;
   double vv;
   ae_int_t problemtype;
   double vlo;
   double vhi;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewVector(cx, 0, DT_REAL, _state);
   NewVector(cy, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);
   xtol = 1.0E-5;

// Generate random problem
   for (nx = 1; nx <= 4; nx++) {

   // Generate synthetic dataset
      npoints = 50 + ae_round(ae_pow(4.0, (double)nx, _state), _state);
      ae_matrix_set_length(&xy, npoints, nx, _state);
      for (i = 0; i < npoints; i++) {
         v = 0.0;
         for (j = 0; j < nx; j++) {
            vv = hqrndnormal(&rs, _state);
            v = v + ae_sqr(vv, _state);
            xy.xyR[i][j] = vv;
         }
         ae_assert(v > 0.0, "Assertion failed", _state);
         v = (1 + 0.1 * hqrnduniformr(&rs, _state)) / ae_sqrt(v, _state);
         for (j = 0; j < nx; j++) {
            xy.xyR[i][j] = xy.xyR[i][j] * v;
         }
      }

   // Perform various kinds of fit, NLC solver is used
      for (problemtype = 1; problemtype <= 3; problemtype++) {

      // Solve with generic solver
         unsetrealarray(&cx, _state);
         rlo = 0.0;
         rhi = 0.0;
         fitspherex(&xy, npoints, nx, problemtype, 0.0, 0, 0.0, &cx, &rlo, &rhi, _state);

      // Check that small perturbations to center position increase target function
      //
      // NOTE: in fact, we do allow small increase in target function - but no more
      //       than FTol=1E-6*XTol. It helps to avoid spurious error reports in
      //       degenerate cases.
         ftol = 1.0E-6 * xtol;
         ae_vector_set_length(&cy, nx, _state);
         if (problemtype == 2 || problemtype == 3) {
            vlo = 1.0;
         } else {
            vlo = 0.0;
         }
         if (problemtype == 1 || problemtype == 3) {
            vhi = 1.0;
         } else {
            vhi = 0.0;
         }
         for (j = 0; j < nx; j++) {
            for (k = 0; k < nx; k++) {
               cy.xR[k] = cx.xR[k];
            }
            cy.xR[j] = cx.xR[j] + xtol;
            testfitsphereunit_calcradii(&xy, npoints, nx, &cy, &rlo2, &rhi2, _state);
            ae_set_error_flag(err, rhi2 * vhi - rlo2 * vlo < rhi * vhi - rlo * vlo - ftol, __FILE__, __LINE__, "testfitsphereunit.ap:245");
            for (k = 0; k < nx; k++) {
               cy.xR[k] = cx.xR[k];
            }
            cy.xR[j] = cx.xR[j] - xtol;
            testfitsphereunit_calcradii(&xy, npoints, nx, &cy, &rlo2, &rhi2, _state);
            ae_set_error_flag(err, rhi2 * vhi - rlo2 * vlo < rhi * vhi - rlo * vlo - ftol, __FILE__, __LINE__, "testfitsphereunit.ap:252");
         }

      // Compare against results returned by specific solver
         if (problemtype == 1) {
            fitspheremc(&xy, npoints, nx, &cy, &rhi2, _state);
            ae_set_error_flag(err, rhi2 != rhi, __FILE__, __LINE__, "testfitsphereunit.ap:261");
            for (j = 0; j < nx; j++) {
               ae_set_error_flag(err, cy.xR[j] != cx.xR[j], __FILE__, __LINE__, "testfitsphereunit.ap:263");
            }
         }
         if (problemtype == 2) {
            fitspheremi(&xy, npoints, nx, &cy, &rlo2, _state);
            ae_set_error_flag(err, rlo2 != rlo, __FILE__, __LINE__, "testfitsphereunit.ap:268");
            for (j = 0; j < nx; j++) {
               ae_set_error_flag(err, cy.xR[j] != cx.xR[j], __FILE__, __LINE__, "testfitsphereunit.ap:270");
            }
         }
         if (problemtype == 3) {
            fitspheremz(&xy, npoints, nx, &cy, &rlo2, &rhi2, _state);
            ae_set_error_flag(err, rlo2 != rlo, __FILE__, __LINE__, "testfitsphereunit.ap:275");
            ae_set_error_flag(err, rhi2 != rhi, __FILE__, __LINE__, "testfitsphereunit.ap:276");
            for (j = 0; j < nx; j++) {
               ae_set_error_flag(err, cy.xR[j] != cx.xR[j], __FILE__, __LINE__, "testfitsphereunit.ap:278");
            }
         }
      }
   }
   ae_frame_leave(_state);
}

