// Used to calculate RLo/Rhi given XY and center position
static void testfitsphereunit_calcradii(RMatrix *xy, ae_int_t npoints, ae_int_t nx, RVector *cx, double *rlo, double *rhi, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double v;

   *rlo = 0;
   *rhi = 0;

   *rlo = ae_maxrealnumber;
   *rhi = 0.0;
   for (i = 0; i < npoints; i++) {
      v = 0.0;
      for (j = 0; j < nx; j++) {
         v = v + ae_sqr(xy->xyR[i][j] - cx->xR[j], _state);
      }
      v = ae_sqrt(v, _state);
      *rhi = ae_maxreal(*rhi, v, _state);
      *rlo = ae_minreal(*rlo, v, _state);
   }
}

// Used to calculate least squares error given XY and center position
static void testfitsphereunit_calclserror(RMatrix *xy, ae_int_t npoints, ae_int_t nx, RVector *cx, double *err, ae_state *_state) {
   ae_int_t i;
   ae_int_t j;
   double v;
   double rad;

   *err = 0;

   rad = 0.0;
   for (i = 0; i < npoints; i++) {
      v = 0.0;
      for (j = 0; j < nx; j++) {
         v = v + ae_sqr(cx->xR[j] - xy->xyR[i][j], _state);
      }
      rad = rad + ae_sqrt(v, _state) / npoints;
   }
   *err = 0.0;
   for (i = 0; i < npoints; i++) {
      v = 0.0;
      for (j = 0; j < nx; j++) {
         v = v + ae_sqr(cx->xR[j] - xy->xyR[i][j], _state);
      }
      *err = *err + ae_sqr(rad - ae_sqrt(v, _state), _state);
   }
}

// Used to initialize dynamic array with constant values
static void testfitsphereunit_addvalue(RMatrix *xy, ae_int_t *cnt, double v, ae_state *_state) {

   xy->xyR[*cnt / xy->cols][*cnt % xy->cols] = v;
   *cnt = *cnt + 1;
}

