// Unset spline, i.e. initialize it with random garbage
static void testparametricunit_unsetp2(pspline2interpolant *p, ae_state *_state) {
   ae_frame _frame_block;

   ae_frame_make(_state, &_frame_block);
   SetObj(pspline2interpolant, p);
   NewMatrix(xy, 0, 0, DT_REAL, _state);

   ae_matrix_set_length(&xy, 2, 2, _state);
   xy.xyR[0][0] = -1.0;
   xy.xyR[0][1] = -1.0;
   xy.xyR[1][0] = 1.0;
   xy.xyR[1][1] = 1.0;
   pspline2build(&xy, 2, 1, 0, p, _state);
   ae_frame_leave(_state);
}

