// The function check, that follow functions works correctly:
//     Spline2DBilinearV, Spline2DBicubicV, Spline2DCalcV, Spline2DCalcVI,
//     Spline2DDiffVI and Spline2DUnpackV.
static bool testspline2dunit_testspline2dvf(bool silent, ae_state *_state) {
   ae_frame _frame_block;
   double range;
   double rndx;
   double rndy;
   double resef;
   ae_int_t m;
   ae_int_t n;
   ae_int_t d;
   ae_int_t tstn;
   ae_int_t tstm;
   ae_int_t tstd;
   double eps;
   double st;
   ae_int_t p0;
   ae_int_t p1;
   ae_int_t variant;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t i0;
   ae_int_t j0;
   double xmin;
   double xmax;
   double ymin;
   double ymax;
   double vi;
   double vxi;
   double vyi;
   double vxyi;
   double si;
   double sxi;
   double syi;
   double sxyi;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(spline2dinterpolant, vc, _state);
   NewObj(spline2dinterpolant, sc, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(f, 0, DT_REAL, _state);
   NewVector(resf, 0, DT_REAL, _state);
   NewMatrix(ef, 0, 0, DT_REAL, _state);
   NewMatrix(tsttbl0, 0, 0, DT_REAL, _state);
   NewMatrix(tsttbl1, 0, 0, DT_REAL, _state);

   eps = 10000.0 * ae_machineepsilon;
   st = 0.1;
   passcount = 5;
   result = false;
   for (pass = 1; pass <= passcount; pass++) {
      for (variant = 1; variant <= 2; variant++) {
         range = (double)(ae_randominteger(71, _state) + 30);
         m = ae_randominteger(4, _state) + 2;
         n = ae_randominteger(4, _state) + 2;
         d = ae_randominteger(3, _state) + 1;
         rvectorsetlengthatleast(&x, n, _state);
         rvectorsetlengthatleast(&y, m, _state);
         rvectorsetlengthatleast(&f, n * m * d, _state);
         rmatrixsetlengthatleast(&ef, m, n, _state);

      // Build a grid for spline
         x.xR[0] = range * (2 * ae_randomreal(_state) - 1);
         y.xR[0] = range * (2 * ae_randomreal(_state) - 1);
         for (i = 1; i < n; i++) {
            x.xR[i] = x.xR[i - 1] + st + ae_randomreal(_state);
         }
         for (i = 1; i < m; i++) {
            y.xR[i] = y.xR[i - 1] + st + ae_randomreal(_state);
         }
         for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
               for (k = 0; k < d; k++) {
                  f.xR[d * (n * j + i) + k] = range * (2 * ae_randomreal(_state) - 1);
               }
            }
         }
         xmin = x.xR[0];
         xmax = x.xR[n - 1];
         ymin = y.xR[0];
         ymax = y.xR[m - 1];

      // Build a spline
         if (variant == 1) {
            spline2dbuildbilinearv(&x, n, &y, m, &f, d, &vc, _state);
         }
         if (variant == 2) {
            spline2dbuildbicubicv(&x, n, &y, m, &f, d, &vc, _state);
         }
      // Part of test, which shows that Spline2DBuildBilinearV function
      // works correctly.
      // And there is test for Spline2DUnpackV.
         spline2dunpackv(&vc, &tstm, &tstn, &tstd, &tsttbl1, _state);
         if ((tstm != m || tstn != n) || tstd != d) {
            if (!silent) {
               printf("TestSpline2DVF fail Spline2DUnpack:\n");
               printf("    TstM=%0d; M=%0d;\n    TstN=%0d; N=%0d;\n    TstD=%0d; D=%0d.\n", (int)tstm, (int)m, (int)tstn, (int)n, (int)tstd, (int)d);
            }
            result = true;
            ae_frame_leave(_state);
            return result;
         }
         for (k = 0; k < d; k++) {
            for (i = 0; i < m; i++) {
               for (j = 0; j < n; j++) {
                  ef.xyR[i][j] = f.xR[d * (i * n + j) + k];
               }
            }
            if (variant == 1) {
               spline2dbuildbilinear(&x, &y, &ef, m, n, &sc, _state);
            }
            if (variant == 2) {
               spline2dbuildbicubic(&x, &y, &ef, m, n, &sc, _state);
            }
            spline2dunpack(&sc, &tstm, &tstn, &tsttbl0, _state);
            if (tstm != m || tstn != n) {
               if (!silent) {
                  printf("TestSpline2DVF fail Spline2DUnpack:\n");
                  printf("    TstM=%0d; M=%0d;\n    TstN=%0d; N=%0d.\n", (int)tstm, (int)m, (int)tstn, (int)n);
               }
               result = true;
               ae_frame_leave(_state);
               return result;
            }
            for (i = 0; i < m - 1; i++) {
               for (j = 0; j < n - 1; j++) {
                  p0 = i * (n - 1) + j;
                  p1 = d * p0;
                  for (i0 = 0; i0 <= 19; i0++) {
                     if (tsttbl1.xyR[p1 + k][i0] != tsttbl0.xyR[p0][i0]) {
                        if (!silent) {
                           printf("TestSpline2DVF: Tbl error\n");
                        }
                        result = true;
                        ae_frame_leave(_state);
                        return result;
                     }
                  }
               }
            }
         }

      // Part of test, which shows that functions Spline2DCalcVBuf, Spline2DDiffVI,
      // Spline2DCalcVI and Spline2DCalcV work correctly
         for (k = 0; k < d; k++) {

         // Build 1D spline corresponding to selected dimension (already tested)
            for (i0 = 0; i0 < m; i0++) {
               for (j0 = 0; j0 < n; j0++) {
                  ef.xyR[i0][j0] = f.xR[d * (i0 * n + j0) + k];
               }
            }
            if (variant == 1) {
               spline2dbuildbilinear(&x, &y, &ef, m, n, &sc, _state);
            }
            if (variant == 2) {
               spline2dbuildbicubic(&x, &y, &ef, m, n, &sc, _state);
            }
         // test at grid points
            for (i = 0; i < n; i++) {
               for (j = 0; j < m; j++) {

               // Test calculation at grid points
                  spline2dcalcv(&vc, x.xR[i], y.xR[j], &resf, _state);
                  resef = spline2dcalc(&sc, x.xR[i], y.xR[j], _state);
                  ae_set_error_flag(&result, ae_fabs(resf.xR[k] - resef, _state) > eps, __FILE__, __LINE__, "testspline2dunit.ap:914");
                  ae_set_error_flag(&result, ae_fabs(spline2dcalcvi(&vc, x.xR[i], y.xR[j], k, _state) - resef, _state) > eps, __FILE__, __LINE__, "testspline2dunit.ap:915");

               // Test differentiation at grid points
                  spline2ddiffvi(&vc, x.xR[i], y.xR[j], k, &vi, &vxi, &vyi, &vxyi, _state);
                  spline2ddiff(&sc, x.xR[i], y.xR[j], &si, &sxi, &syi, &sxyi, _state);
                  ae_set_error_flag(&result, ae_fabs(vi - si, _state) > eps * (ae_fabs(si, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:922");
                  ae_set_error_flag(&result, ae_fabs(vxi - sxi, _state) > eps * (ae_fabs(sxi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:923");
                  ae_set_error_flag(&result, ae_fabs(vyi - syi, _state) > eps * (ae_fabs(syi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:924");
                  ae_set_error_flag(&result, ae_fabs(vxyi - sxyi, _state) > eps * (ae_fabs(sxyi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:925");
               }
            }

         // Test at random points
            for (i = 0; i < n * m; i++) {
               rndx = xmin + (xmax - xmin) * ae_randomreal(_state);
               rndy = ymin + (ymax - ymin) * ae_randomreal(_state);

            // Test calculation at random points
               spline2dcalcv(&vc, rndx, rndy, &resf, _state);
               resef = spline2dcalc(&sc, rndx, rndy, _state);
               ae_set_error_flag(&result, ae_fabs(resf.xR[k] - resef, _state) > eps, __FILE__, __LINE__, "testspline2dunit.ap:941");
               ae_set_error_flag(&result, ae_fabs(spline2dcalcvi(&vc, rndx, rndy, k, _state) - resef, _state) > eps, __FILE__, __LINE__, "testspline2dunit.ap:942");

            // Test differentiation at random points
               spline2ddiffvi(&vc, rndx, rndy, k, &vi, &vxi, &vyi, &vxyi, _state);
               spline2ddiff(&sc, rndx, rndy, &si, &sxi, &syi, &sxyi, _state);
               ae_set_error_flag(&result, ae_fabs(vi - si, _state) > eps * (ae_fabs(si, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:949");
               ae_set_error_flag(&result, ae_fabs(vxi - sxi, _state) > eps * (ae_fabs(sxi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:950");
               ae_set_error_flag(&result, ae_fabs(vyi - syi, _state) > eps * (ae_fabs(syi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:951");
               ae_set_error_flag(&result, ae_fabs(vxyi - sxyi, _state) > eps * (ae_fabs(sxyi, _state) + 1), __FILE__, __LINE__, "testspline2dunit.ap:952");
            }
         }
      }
   }
   ae_frame_leave(_state);
   return result;
}

// Fitting, properties of linear prior term
static void testspline2dunit_testfittingprior(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t d;
   ae_int_t kidx;
   ae_int_t areatype;
   ae_int_t solvertype;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double tol;
   ae_int_t kx;
   ae_int_t ky;
   ae_int_t npoints;
   double vprior;
   double v;
   bool bad;

   ae_frame_make(_state, &_frame_block);
   NewVector(kv, 0, DT_INT, _state);
   NewObj(hqrndstate, rs, _state);
   NewObj(spline2dbuilder, state, _state);
   NewObj(spline2dinterpolant, s, _state);
   NewObj(spline2dinterpolant, s2, _state);
   NewObj(spline2dinterpolant, s3, _state);
   NewObj(spline2dfitreport, rep, _state);
   NewObj(spline2dfitreport, rep2, _state);
   NewObj(spline2dfitreport, rep3, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewMatrix(xy2, 0, 0, DT_REAL, _state);
   NewMatrix(xy3, 0, 0, DT_REAL, _state);
   NewMatrix(vterm, 0, 0, DT_REAL, _state);
   NewVector(meany, 0, DT_REAL, _state);
   NewVector(tmp0, 0, DT_REAL, _state);
   NewVector(tmp1, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);
   tol = 1.0E-5;

// Check properties of underlying linear prior.
   ae_vector_set_length(&kv, 3, _state);
   kv.xZ[0] = 5;
   kv.xZ[1] = 10;
   kv.xZ[2] = 16;
   for (kidx = 0; kidx < kv.cnt; kidx++) {
      for (d = 1; d <= 3; d++) {
         for (areatype = 0; areatype <= 0; areatype++) {
            for (solvertype = 1; solvertype <= 2; solvertype++) {

            // Select grid size and create builder
               kx = 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state);
               ky = 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state);
               if (hqrndnormal(&rs, _state) > 0.0) {
                  kx = kv.xZ[kidx];
               } else {
                  ky = kv.xZ[kidx];
               }
               spline2dbuildercreate(d, &state, _state);
               spline2dbuildersetgrid(&state, kx, ky, _state);
               if (solvertype == 1) {
                  spline2dbuildersetalgoblocklls(&state, 0.0, _state);
               } else {
                  if (solvertype == 2) {
                     spline2dbuildersetalgonaivells(&state, 0.0, _state);
                  } else {
                     ae_assert(false, "Assertion failed", _state);
                  }
               }

            // Generate perfectly linear dataset, check against linear prior.
            // At least 8 points is required to get stable results.
            //
            // NOTE: we also check that constant term does NOT work.
               ae_matrix_set_length(&vterm, d, 3, _state);
               for (i = 0; i < d; i++) {
                  for (j = 0; j <= 2; j++) {
                     vterm.xyR[i][j] = hqrndnormal(&rs, _state);
                  }
               }
               npoints = 8 + ae_round(10 * hqrnduniformr(&rs, _state), _state);
               ae_vector_set_length(&meany, d, _state);
               for (j = 0; j < d; j++) {
                  meany.xR[j] = 0.0;
               }
               ae_matrix_set_length(&xy, npoints, 2 + d, _state);
               for (i = 0; i < npoints; i++) {
                  xy.xyR[i][0] = hqrnduniformr(&rs, _state);
                  xy.xyR[i][1] = hqrnduniformr(&rs, _state);
                  for (j = 0; j < d; j++) {
                     xy.xyR[i][2 + j] = vterm.xyR[j][0] * xy.xyR[i][0] + vterm.xyR[j][1] * xy.xyR[i][1] + vterm.xyR[j][2];
                     meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
                  }
               }
               spline2dbuildersetpoints(&state, &xy, npoints, _state);
               spline2dbuildersetlinterm(&state, _state);
               spline2dfit(&state, &s, &rep, _state);
               ae_vector_set_length(&tmp0, 3, _state);
               for (k = 0; k <= 10; k++) {
                  tmp0.xR[0] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[1] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[2] = 1.0;
                  spline2dcalcv(&s, tmp0.xR[0], tmp0.xR[1], &tmp1, _state);
                  for (j = 0; j < d; j++) {
                     v = vterm.xyR[j][0] * tmp0.xR[0] + vterm.xyR[j][1] * tmp0.xR[1] + vterm.xyR[j][2];
                     ae_set_error_flag(errorflag, ae_fabs(tmp1.xR[j] - v, _state) / ae_maxreal(ae_fabs(v, _state), 1.0, _state) > tol, __FILE__, __LINE__, "testspline2dunit.ap:1094");
                  }
               }
               ae_set_error_flag(errorflag, rep.rmserror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1097");
               ae_set_error_flag(errorflag, rep.avgerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1098");
               ae_set_error_flag(errorflag, rep.maxerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1099");
               spline2dbuildersetconstterm(&state, _state);
               spline2dfit(&state, &s, &rep, _state);
               ae_vector_set_length(&tmp0, 3, _state);
               bad = false;
               for (k = 0; k <= 10; k++) {
                  tmp0.xR[0] = 2 + hqrnduniformr(&rs, _state);
                  tmp0.xR[1] = 2 + hqrnduniformr(&rs, _state);
                  tmp0.xR[2] = 1.0;
                  spline2dcalcv(&s, tmp0.xR[0], tmp0.xR[1], &tmp1, _state);
                  for (j = 0; j < d; j++) {
                     v = vterm.xyR[j][0] * tmp0.xR[0] + vterm.xyR[j][1] * tmp0.xR[1] + vterm.xyR[j][2];
                     bad = bad || ae_fabs(tmp1.xR[j] - v, _state) > tol;
                  }
               }
               ae_set_error_flag(errorflag, !bad, __FILE__, __LINE__, "testspline2dunit.ap:1118");

            // Generate perfectly constant dataset, check against constant prior.
            // At least 8 points is required to get stable results.
               ae_matrix_set_length(&vterm, d, 3, _state);
               for (i = 0; i < d; i++) {
                  for (j = 0; j <= 1; j++) {
                     vterm.xyR[i][j] = 0.0;
                  }
                  vterm.xyR[i][2] = hqrndnormal(&rs, _state);
               }
               npoints = 8 + ae_round(10 * hqrnduniformr(&rs, _state), _state);
               ae_vector_set_length(&meany, d, _state);
               for (j = 0; j < d; j++) {
                  meany.xR[j] = 0.0;
               }
               ae_matrix_set_length(&xy, npoints, 2 + d, _state);
               for (i = 0; i < npoints; i++) {
                  xy.xyR[i][0] = hqrnduniformr(&rs, _state);
                  xy.xyR[i][1] = hqrnduniformr(&rs, _state);
                  for (j = 0; j < d; j++) {
                     xy.xyR[i][2 + j] = vterm.xyR[j][0] * xy.xyR[i][0] + vterm.xyR[j][1] * xy.xyR[i][1] + vterm.xyR[j][2];
                     meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
                  }
               }
               spline2dbuildersetpoints(&state, &xy, npoints, _state);
               spline2dbuildersetconstterm(&state, _state);
               spline2dfit(&state, &s, &rep, _state);
               ae_vector_set_length(&tmp0, 3, _state);
               for (k = 0; k <= 10; k++) {
                  tmp0.xR[0] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[1] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[2] = 1.0;
                  spline2dcalcv(&s, tmp0.xR[0], tmp0.xR[1], &tmp1, _state);
                  for (j = 0; j < d; j++) {
                     v = vterm.xyR[j][0] * tmp0.xR[0] + vterm.xyR[j][1] * tmp0.xR[1] + vterm.xyR[j][2];
                     ae_set_error_flag(errorflag, ae_fabs(tmp1.xR[j] - v, _state) / ae_maxreal(ae_fabs(v, _state), 1.0, _state) > tol, __FILE__, __LINE__, "testspline2dunit.ap:1159");
                  }
               }
               ae_set_error_flag(errorflag, rep.rmserror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1162");
               ae_set_error_flag(errorflag, rep.avgerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1163");
               ae_set_error_flag(errorflag, rep.maxerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1164");

            // Generate perfectly constant dataset, check against user-defined constant prior.
            // At least 8 points is required to get stable results.
               vprior = hqrndnormal(&rs, _state);
               npoints = 8 + ae_round(10 * hqrnduniformr(&rs, _state), _state);
               ae_vector_set_length(&meany, d, _state);
               for (j = 0; j < d; j++) {
                  meany.xR[j] = 0.0;
               }
               ae_matrix_set_length(&xy, npoints, 2 + d, _state);
               for (i = 0; i < npoints; i++) {
                  xy.xyR[i][0] = hqrnduniformr(&rs, _state);
                  xy.xyR[i][1] = hqrnduniformr(&rs, _state);
                  for (j = 0; j < d; j++) {
                     xy.xyR[i][2 + j] = vprior;
                     meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
                  }
               }
               spline2dbuildersetpoints(&state, &xy, npoints, _state);
               spline2dbuildersetuserterm(&state, vprior, _state);
               spline2dfit(&state, &s, &rep, _state);
               ae_vector_set_length(&tmp0, 3, _state);
               for (k = 0; k <= 10; k++) {
                  tmp0.xR[0] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[1] = 3 * hqrnduniformr(&rs, _state) - 1;
                  tmp0.xR[2] = 1.0;
                  spline2dcalcv(&s, tmp0.xR[0], tmp0.xR[1], &tmp1, _state);
                  for (j = 0; j < d; j++) {
                     ae_set_error_flag(errorflag, ae_fabs(tmp1.xR[j] - vprior, _state) > tol, __FILE__, __LINE__, "testspline2dunit.ap:1199");
                  }
               }
               ae_set_error_flag(errorflag, rep.rmserror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1201");
               ae_set_error_flag(errorflag, rep.avgerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1202");
               ae_set_error_flag(errorflag, rep.maxerror > tol, __FILE__, __LINE__, "testspline2dunit.ap:1203");
               spline2dbuildersetuserterm(&state, vprior + (2 * hqrnduniformi(&rs, 2, _state) - 1), _state);
               spline2dfit(&state, &s, &rep, _state);
               ae_vector_set_length(&tmp0, 3, _state);
               for (k = 0; k <= 10; k++) {
                  tmp0.xR[0] = 2 + hqrnduniformr(&rs, _state);
                  tmp0.xR[1] = 2 + hqrnduniformr(&rs, _state);
                  tmp0.xR[2] = 1.0;
                  spline2dcalcv(&s, tmp0.xR[0], tmp0.xR[1], &tmp1, _state);
                  for (j = 0; j < d; j++) {
                     ae_set_error_flag(errorflag, ae_fabs(tmp1.xR[j] - vprior, _state) <= tol, __FILE__, __LINE__, "testspline2dunit.ap:1216");
                  }
               }
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Nonlinearity penalty test
static void testspline2dunit_testfittingpenalty(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t kidx;
   ae_int_t pidx;
   ae_int_t solvertype;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t d;
   ae_int_t kx;
   ae_int_t ky;
   ae_int_t npoints;
   double rmserror;
   double v;
   double v0;
   double v1;
   double f0;
   double f1;
   double f2;
   double f3;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(spline2dinterpolant, s, _state);
   NewObj(spline2dinterpolant, s2, _state);
   NewObj(spline2dfitreport, rep, _state);
   NewObj(spline2dfitreport, rep2, _state);
   NewObj(hqrndstate, rs, _state);
   NewObj(spline2dbuilder, state, _state);
   NewVector(penalty, 0, DT_REAL, _state);
   NewVector(penalty2, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);

// Penalized fitting problem:
// * square KxK grid, K >= 8 (points do not "touch" each other by their far fields)
// * four points in the corners, perfectly linear value
// * zero prior term
//
// Check that:
// * with significant nonlinearity penalty we have nearly-linear fit,
//   central value is an average of corner values
// * without nonlinearity penalty, central value is zero
   for (kidx = 3; kidx <= 4; kidx++) {
      for (solvertype = 1; solvertype <= 2; solvertype++) {

      // Generate dataset
         kx = ae_round(ae_pow(2.0, (double)kidx, _state), _state);
         ky = kx;
         npoints = 4;
         ae_matrix_set_length(&xy, npoints, 3, _state);
         xy.xyR[0][0] = -1.0;
         xy.xyR[0][1] = -1.0;
         xy.xyR[0][2] = hqrndnormal(&rs, _state);
         xy.xyR[1][0] = 1.0;
         xy.xyR[1][1] = -1.0;
         xy.xyR[1][2] = hqrndnormal(&rs, _state);
         xy.xyR[2][0] = -1.0;
         xy.xyR[2][1] = 1.0;
         xy.xyR[2][2] = hqrndnormal(&rs, _state);
         xy.xyR[3][0] = 1.0;
         xy.xyR[3][1] = 1.0;
         xy.xyR[3][2] = xy.xyR[0][2] + (xy.xyR[1][2] - xy.xyR[0][2]) + (xy.xyR[2][2] - xy.xyR[0][2]);

      // Create solver, set grid, area and other properties
      // according to chosen test mode.
         spline2dbuildercreate(1, &state, _state);
         spline2dbuildersetgrid(&state, kx, ky, _state);
         spline2dbuildersetarea(&state, -1.0, 1.0, -1.0, 1.0, _state);
         spline2dbuildersetpoints(&state, &xy, npoints, _state);
         spline2dbuildersetzeroterm(&state, _state);

      // Check fitting with penalty
         if (solvertype == 1) {
            spline2dbuildersetalgoblocklls(&state, 1.0, _state);
         } else {
            if (solvertype == 2) {
               spline2dbuildersetalgonaivells(&state, 1.0, _state);
            } else {
               ae_assert(false, "Assertion failed", _state);
            }
         }
         spline2dfit(&state, &s, &rep, _state);
         ae_set_error_flag(errorflag, ae_fabs(spline2dcalc(&s, 0.0, 0.0, _state) - 0.25 * (xy.xyR[0][2] + xy.xyR[1][2] + xy.xyR[2][2] + xy.xyR[3][2]), _state) > 1.0E-4, __FILE__, __LINE__, "testspline2dunit.ap:1305");

      // Check fitting without penalty
         if (solvertype == 1) {
            spline2dbuildersetalgoblocklls(&state, 0.0, _state);
         } else {
            if (solvertype == 2) {
               spline2dbuildersetalgonaivells(&state, 0.0, _state);
            } else {
               ae_assert(false, "Assertion failed", _state);
            }
         }
         spline2dfit(&state, &s2, &rep2, _state);
         ae_set_error_flag(errorflag, ae_fabs(spline2dcalc(&s2, 0.0, 0.0, _state), _state) > 1.0E-4, __FILE__, __LINE__, "testspline2dunit.ap:1318");
      }
   }

// Solve a sequence of problems with increasing value of penalty coefficient;
// test that increasing penalty coefficient results in:
// * increased residuals
// * decreased value of penalty function
   for (solvertype = 1; solvertype <= 2; solvertype++) {
      for (d = 1; d <= 3; d++) {

      // Prepare solver and dataset
         kx = 6 + hqrnduniformi(&rs, 5, _state);
         ky = 6 + hqrnduniformi(&rs, 5, _state);
         npoints = 3 * (kx + 2) * (ky + 2) + 30;
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         spline2dbuildercreate(d, &state, _state);
         spline2dbuildersetgrid(&state, kx, ky, _state);
         if (solvertype == 1) {
            spline2dbuildersetalgoblocklls(&state, 0.0, _state);
         } else {
            if (solvertype == 2) {
               spline2dbuildersetalgonaivells(&state, 0.0, _state);
            } else {
               ae_assert(false, "Assertion failed", _state);
            }
         }
         spline2dbuildersetarea(&state, 0.0, 1.0, 0.0, 1.0, _state);
         spline2dbuildersetpoints(&state, &xy, npoints, _state);
         spline2dbuildersetzeroterm(&state, _state);

      // Solve with zero penalty
         spline2dfit(&state, &s, &rep, _state);
         rmserror = rep.rmserror;
         ae_vector_set_length(&penalty, d, _state);
         for (k = 0; k < d; k++) {
            penalty.xR[k] = 0.0;
         }
         v = 1.0E-3;
         for (i = 0; i < kx; i++) {
            for (j = 0; j < ky; j++) {
               v0 = (double)i / (kx - 1);
               v1 = (double)j / (ky - 1);
               for (k = 0; k < d; k++) {
                  f0 = spline2dcalcvi(&s, v0 - v, v1, k, _state);
                  f1 = spline2dcalcvi(&s, v0, v1, k, _state);
                  f2 = spline2dcalcvi(&s, v0 + v, v1, k, _state);
                  penalty.xR[k] = penalty.xR[k] + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(v, _state), _state);
               }
               for (k = 0; k < d; k++) {
                  f0 = spline2dcalcvi(&s, v0, v1 - v, k, _state);
                  f1 = spline2dcalcvi(&s, v0, v1, k, _state);
                  f2 = spline2dcalcvi(&s, v0, v1 + v, k, _state);
                  penalty.xR[k] = penalty.xR[k] + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(v, _state), _state);
               }
               for (k = 0; k < d; k++) {
                  f0 = spline2dcalcvi(&s, v0 - v, v1 - v, k, _state);
                  f1 = spline2dcalcvi(&s, v0 + v, v1 - v, k, _state);
                  f2 = spline2dcalcvi(&s, v0 - v, v1 + v, k, _state);
                  f3 = spline2dcalcvi(&s, v0 + v, v1 + v, k, _state);
                  penalty.xR[k] = penalty.xR[k] + 2 * ae_sqr((f3 - f2 - f1 + f0) / ae_sqr(v, _state), _state);
               }
            }
         }

      // Solve with non-zero penalty
         for (pidx = -6; pidx <= 3; pidx++) {
            if (solvertype == 1) {
               spline2dbuildersetalgoblocklls(&state, ae_pow(10.0, (double)pidx / 2.0, _state), _state);
            } else {
               if (solvertype == 2) {
                  spline2dbuildersetalgonaivells(&state, ae_pow(10.0, (double)pidx / 2.0, _state), _state);
               } else {
                  ae_assert(false, "Assertion failed", _state);
               }
            }
            spline2dfit(&state, &s, &rep, _state);
            ae_vector_set_length(&penalty2, d, _state);
            for (k = 0; k < d; k++) {
               penalty2.xR[k] = 0.0;
            }
            v = 1.0E-3;
            for (i = 0; i < kx; i++) {
               for (j = 0; j < ky; j++) {
                  v0 = (double)i / (kx - 1);
                  v1 = (double)j / (ky - 1);
                  for (k = 0; k < d; k++) {
                     f0 = spline2dcalcvi(&s, v0 - v, v1, k, _state);
                     f1 = spline2dcalcvi(&s, v0, v1, k, _state);
                     f2 = spline2dcalcvi(&s, v0 + v, v1, k, _state);
                     penalty2.xR[k] = penalty2.xR[k] + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(v, _state), _state);
                  }
                  for (k = 0; k < d; k++) {
                     f0 = spline2dcalcvi(&s, v0, v1 - v, k, _state);
                     f1 = spline2dcalcvi(&s, v0, v1, k, _state);
                     f2 = spline2dcalcvi(&s, v0, v1 + v, k, _state);
                     penalty2.xR[k] = penalty2.xR[k] + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(v, _state), _state);
                  }
                  for (k = 0; k < d; k++) {
                     f0 = spline2dcalcvi(&s, v0 - v, v1 - v, k, _state);
                     f1 = spline2dcalcvi(&s, v0 + v, v1 - v, k, _state);
                     f2 = spline2dcalcvi(&s, v0 - v, v1 + v, k, _state);
                     f3 = spline2dcalcvi(&s, v0 + v, v1 + v, k, _state);
                     penalty2.xR[k] = penalty2.xR[k] + 2 * ae_sqr((f3 - f2 - f1 + f0) / ae_sqr(v, _state), _state);
                  }
               }
            }
            ae_set_error_flag(errorflag, rep.rmserror <= rmserror, __FILE__, __LINE__, "testspline2dunit.ap:1448");
            for (k = 0; k < d; k++) {
               ae_set_error_flag(errorflag, penalty2.xR[k] >= penalty.xR[k], __FILE__, __LINE__, "testspline2dunit.ap:1450");
            }
            rmserror = rep.rmserror;
            for (k = 0; k < d; k++) {
               penalty.xR[k] = penalty2.xR[k];
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Block solver test for fitting functionality
static void testspline2dunit_testfittingblocksolver(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t d;
   ae_int_t kidx;
   ae_int_t areatype;
   ae_int_t gridtype;
   ae_int_t solvertype;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double errtol;
   ae_int_t kx;
   ae_int_t ky;
   double w1;
   double xc;
   double yc;
   ae_int_t npoints;
   ae_int_t priortype;
   double mx;
   double rmserror;
   double avgerror;
   double maxerror;
   double rss;
   double tss;
   double r2;
   double v;
   double v0;
   double v1;

   ae_frame_make(_state, &_frame_block);
   NewVector(kv, 0, DT_INT, _state);
   NewObj(hqrndstate, rs, _state);
   NewObj(spline2dbuilder, state, _state);
   NewObj(spline2dinterpolant, s, _state);
   NewObj(spline2dinterpolant, s2, _state);
   NewObj(spline2dinterpolant, s3, _state);
   NewObj(spline2dfitreport, rep, _state);
   NewObj(spline2dfitreport, rep2, _state);
   NewObj(spline2dfitreport, rep3, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewMatrix(xy2, 0, 0, DT_REAL, _state);
   NewMatrix(xy3, 0, 0, DT_REAL, _state);
   NewMatrix(vterm, 0, 0, DT_REAL, _state);
   NewVector(meany, 0, DT_REAL, _state);
   NewVector(tmp0, 0, DT_REAL, _state);
   NewVector(tmp1, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);
   errtol = 1.0E-6;

// Check that default state (no dataset) results in zero model being returned.
   for (priortype = 0; priortype <= 2; priortype++) {
      for (d = 1; d <= 3; d++) {
         for (areatype = 0; areatype <= 1; areatype++) {
            for (gridtype = 0; gridtype <= 1; gridtype++) {
               for (solvertype = 1; solvertype <= 2; solvertype++) {

               // Create solver, set grid, area and other properties
               // according to chosen test mode.
                  kx = ae_maxint(4, 1 + hqrnduniformi(&rs, 10, _state), _state);
                  ky = ae_maxint(4, 1 + hqrnduniformi(&rs, 10, _state), _state);
                  spline2dbuildercreate(d, &state, _state);
                  if (gridtype == 1) {
                     spline2dbuildersetgrid(&state, kx, ky, _state);
                  }
                  if (solvertype == 1) {
                     spline2dbuildersetalgoblocklls(&state, 0.0, _state);
                  } else {
                     if (solvertype == 2) {
                        spline2dbuildersetalgonaivells(&state, 0.0, _state);
                     } else {
                        ae_assert(false, "Assertion failed", _state);
                     }
                  }
                  if (areatype == 0) {
                     spline2dbuildersetareaauto(&state, _state);
                  } else {
                     if (areatype == 1) {
                        spline2dbuildersetarea(&state, 0.0, 1.0, 0.0, 1.0, _state);
                     } else {
                        ae_assert(false, "Assertion failed", _state);
                     }
                  }
                  if (priortype == 0) {
                     spline2dbuildersetlinterm(&state, _state);
                  } else {
                     if (priortype == 1) {
                        spline2dbuildersetconstterm(&state, _state);
                     } else {
                        if (priortype == 2) {
                           spline2dbuildersetzeroterm(&state, _state);
                        } else {
                           ae_assert(false, "Assertion failed", _state);
                        }
                     }
                  }
                  spline2dfit(&state, &s, &rep, _state);

               // Test
                  for (j = 0; j < d; j++) {
                     ae_set_error_flag(errorflag, spline2dcalcvi(&s, hqrndnormal(&rs, _state), hqrndnormal(&rs, _state), j, _state) != 0.0, __FILE__, __LINE__, "testspline2dunit.ap:1550");
                  }
                  ae_set_error_flag(errorflag, rep.rmserror != 0.0, __FILE__, __LINE__, "testspline2dunit.ap:1551");
                  ae_set_error_flag(errorflag, rep.avgerror != 0.0, __FILE__, __LINE__, "testspline2dunit.ap:1552");
                  ae_set_error_flag(errorflag, rep.maxerror != 0.0, __FILE__, __LINE__, "testspline2dunit.ap:1553");
                  ae_set_error_flag(errorflag, rep.r2 != 1.0, __FILE__, __LINE__, "testspline2dunit.ap:1554");
               }
            }
         }
      }
   }

// Check properties of regression spline fitting WITHOUT nonlinearity
// penalty. Absence of penalty term allows us to perform several indirect
// tests which are impossible when working under penalty.
//
// Section below performs following tests on completely random dataset:
// * correctness of report fields
// * indirect tests for fitting solver (fitting on residuals from previous fit)
   ae_vector_set_length(&kv, 5, _state);
   kv.xZ[0] = 4;
   kv.xZ[1] = 5;
   kv.xZ[2] = 10;
   kv.xZ[3] = 12;
   kv.xZ[4] = 15;
   for (kidx = 0; kidx < kv.cnt; kidx++) {
      for (d = 1; d <= 3; d++) {
         for (areatype = 0; areatype <= 1; areatype++) {
            for (gridtype = 1; gridtype <= 1; gridtype++) {
               for (solvertype = 1; solvertype <= 2; solvertype++) {

               // Generate dataset
                  kx = ae_maxint(4, 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state), _state);
                  ky = ae_maxint(4, 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state), _state);
                  if (hqrndnormal(&rs, _state) > 0.0) {
                     kx = kv.xZ[kidx];
                  } else {
                     ky = kv.xZ[kidx];
                  }
                  w1 = ae_pow(10.0, hqrndnormal(&rs, _state), _state);
                  xc = w1 * (2 * hqrnduniformr(&rs, _state) - 1);
                  yc = w1 * (2 * hqrnduniformr(&rs, _state) - 1);
                  ae_vector_set_length(&meany, d, _state);
                  for (j = 0; j < d; j++) {
                     meany.xR[j] = 0.0;
                  }
                  npoints = ae_round((kx + 2) * (ky + 2) * (3 + hqrnduniformr(&rs, _state)), _state);
                  ae_matrix_set_length(&xy, npoints, 2 + d, _state);
                  for (i = 0; i < npoints; i++) {
                     xy.xyR[i][0] = xc + w1 * (2 * hqrnduniformr(&rs, _state) - 1) * kx;
                     xy.xyR[i][1] = yc + w1 * (2 * hqrnduniformr(&rs, _state) - 1) * ky;
                     for (j = 0; j < d; j++) {
                        xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
                        meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
                     }
                  }

               // Create solver, set grid, area and other properties
               // according to chosen test mode.
                  spline2dbuildercreate(d, &state, _state);
                  if (gridtype == 1) {
                     spline2dbuildersetgrid(&state, kx, ky, _state);
                  } else {
                     ae_assert(false, "Assertion failed", _state);
                  }
                  if (solvertype == 1) {
                     spline2dbuildersetalgoblocklls(&state, 0.0, _state);
                  } else {
                     if (solvertype == 2) {
                        spline2dbuildersetalgonaivells(&state, 0.0, _state);
                     } else {
                        ae_assert(false, "Assertion failed", _state);
                     }
                  }
                  if (areatype == 0) {
                     spline2dbuildersetareaauto(&state, _state);
                  } else {
                     if (areatype == 1) {
                        spline2dbuildersetarea(&state, xc - w1 * kx, xc + w1 * kx, yc - w1 * ky, yc + w1 * ky, _state);
                     } else {
                        ae_assert(false, "Assertion failed", _state);
                     }
                  }
                  spline2dbuildersetpoints(&state, &xy, npoints, _state);
                  spline2dfit(&state, &s, &rep, _state);

               // Test report fields
                  rmserror = 0.0;
                  avgerror = 0.0;
                  maxerror = 0.0;
                  rss = 0.0;
                  tss = 0.0;
                  for (i = 0; i < npoints; i++) {
                     spline2dcalcv(&s, xy.xyR[i][0], xy.xyR[i][1], &tmp0, _state);
                     for (j = 0; j < d; j++) {
                        v = xy.xyR[i][2 + j] - tmp0.xR[j];
                        rss = rss + v * v;
                        tss = tss + ae_sqr(xy.xyR[i][2 + j] - meany.xR[j], _state);
                        rmserror = rmserror + ae_sqr(v, _state);
                        avgerror = avgerror + ae_fabs(v, _state) / (npoints * d);
                        maxerror = ae_maxreal(maxerror, ae_fabs(v, _state), _state);
                     }
                  }
                  rmserror = ae_sqrt(rmserror / (npoints * d), _state);
                  r2 = 1.0 - rss / coalesce(tss, 1.0, _state);
                  ae_set_error_flag(errorflag, ae_fabs(rmserror - rep.rmserror, _state) > errtol, __FILE__, __LINE__, "testspline2dunit.ap:1654");
                  ae_set_error_flag(errorflag, ae_fabs(avgerror - rep.avgerror, _state) > errtol, __FILE__, __LINE__, "testspline2dunit.ap:1655");
                  ae_set_error_flag(errorflag, ae_fabs(maxerror - rep.maxerror, _state) > errtol, __FILE__, __LINE__, "testspline2dunit.ap:1656");
                  if (tss > 1.0E-3) {
                     ae_set_error_flag(errorflag, ae_fabs(r2 - rep.r2, _state) > errtol, __FILE__, __LINE__, "testspline2dunit.ap:1658");
                  }
               // Minimalistic test for fitting quality: R2 is better than zero
               // (zero value = fitting by median value).
                  ae_set_error_flag(errorflag, rep.r2 < 0.0, __FILE__, __LINE__, "testspline2dunit.ap:1664");

               // Indirect test for solver: test that fitting on residuals from
               // previous fitting does not significantly decrease residuals.
               //
               // In our test we require that residuals from residuals are
               // greater than 0.8 times original residuals.
               //
               // In the ideal world, fitting on residuals will return same
               // residuals, but during spline construction we made several
               // compromises (regularization, pre-model phase). So, we just
               // check that residuals are weakly decreased.
               //
               // NOTE: this test is performed only for significantly non-zero residuals
                  if (rep.rmserror > 0.05) {
                     ae_matrix_set_length(&xy2, npoints, 2 + d, _state);
                     for (i = 0; i < npoints; i++) {
                        xy2.xyR[i][0] = xy.xyR[i][0];
                        xy2.xyR[i][1] = xy.xyR[i][1];
                        spline2dcalcv(&s, xy.xyR[i][0], xy.xyR[i][1], &tmp1, _state);
                        for (j = 0; j < d; j++) {
                           xy2.xyR[i][2 + j] = xy.xyR[i][2 + j] - tmp1.xR[j];
                        }
                     }
                     spline2dbuildersetpoints(&state, &xy2, npoints, _state);
                     spline2dfit(&state, &s2, &rep2, _state);
                     ae_set_error_flag(errorflag, rep2.rmserror < 0.8 * rep.rmserror, __FILE__, __LINE__, "testspline2dunit.ap:1693");
                  }
               }
            }
         }
      }
   }

// Direct test:
// * points on regular grid which is exactly the same as one used
//   for regression, but with some noise added to point locations
// * we test that without penalty we get nearly-zero RMS error
   ae_vector_set_length(&kv, 5, _state);
   kv.xZ[0] = 4;
   kv.xZ[1] = 5;
   kv.xZ[2] = 6;
   kv.xZ[3] = 8;
   kv.xZ[4] = 12;
   for (kidx = 0; kidx < kv.cnt; kidx++) {
      for (d = 1; d <= 2; d++) {
         for (solvertype = 1; solvertype <= 2; solvertype++) {

         // Generate dataset
            kx = ae_maxint(4, 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state), _state);
            ky = ae_maxint(4, 1 + hqrnduniformi(&rs, kv.xZ[kidx], _state), _state);
            if (hqrndnormal(&rs, _state) > 0.0) {
               kx = kv.xZ[kidx];
            } else {
               ky = kv.xZ[kidx];
            }
            npoints = kx * ky;
            ae_matrix_set_length(&xy, npoints, 2 + d, _state);
            for (i = 0; i < npoints; i++) {
               xy.xyR[i][0] = boundval(i % kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(kx - 1), _state);
               xy.xyR[i][1] = boundval(i / kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(ky - 1), _state);
               for (j = 0; j < d; j++) {
                  xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
               }
            }

         // Create solver, set grid, area and other properties
         // according to chosen test mode.
            spline2dbuildercreate(d, &state, _state);
            spline2dbuildersetgrid(&state, kx, ky, _state);
            if (solvertype == 1) {
               spline2dbuildersetalgoblocklls(&state, 0.0, _state);
            } else {
               if (solvertype == 2) {
                  spline2dbuildersetalgonaivells(&state, 0.0, _state);
               } else {
                  ae_assert(false, "Assertion failed", _state);
               }
            }
            spline2dbuildersetareaauto(&state, _state);
            spline2dbuildersetpoints(&state, &xy, npoints, _state);
            spline2dfit(&state, &s, &rep, _state);

         // Test
            for (i = 0; i < npoints; i++) {
               for (k = 0; k < d; k++) {
                  ae_set_error_flag(errorflag, ae_fabs(spline2dcalcvi(&s, xy.xyR[i][0], xy.xyR[i][1], k, _state) - xy.xyR[i][2 + k], _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1754");
               }
            }
         }
      }
   }

// Compare results obtained with block solver vs naive one
   mx = 0.0;
   for (kx = 4; kx <= 8; kx++) {
      for (ky = 4; ky <= 8; ky++) {
         for (d = 1; d <= 2; d++) {

         // Generate dataset
            npoints = 300;
            ae_matrix_set_length(&xy, npoints, 2 + d, _state);
            for (i = 0; i < npoints; i++) {
               xy.xyR[i][0] = (kx - 1) * hqrnduniformr(&rs, _state);
               xy.xyR[i][1] = (ky - 1) * hqrnduniformr(&rs, _state);
               for (j = 0; j < d; j++) {
                  xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
               }
            }

         // Create solver, set grid, area and other properties
         // according to chosen test mode.
         //
         // NOTE: we perform just one iteration of LSQR in order
         //       to test subtle differences of two implementations -
         //       reference one (NaiveLLS) and high-performance one.
            spline2dbuildercreate(d, &state, _state);
            spline2dbuildersetgrid(&state, kx, ky, _state);
            spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
            spline2dbuildersetpoints(&state, &xy, npoints, _state);
            state.lsqrcnt = 1;
            spline2dbuildersetalgonaivells(&state, 0.0, _state);
            spline2dfit(&state, &s, &rep, _state);
            spline2dbuildersetalgoblocklls(&state, 0.0, _state);
            spline2dfit(&state, &s2, &rep2, _state);
            for (i = 0; i < npoints; i++) {
               spline2dcalcv(&s, xy.xyR[i][0], xy.xyR[i][1], &tmp0, _state);
               spline2dcalcv(&s2, xy.xyR[i][0], xy.xyR[i][1], &tmp1, _state);
               for (j = 0; j < d; j++) {
                  ae_set_error_flag(errorflag, ae_fabs(tmp0.xR[j] - tmp1.xR[j], _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1801");
                  mx = ae_maxreal(mx, ae_fabs(tmp0.xR[j] - tmp1.xR[j], _state), _state);
               }
            }
         }
      }
   }

// Investigate stability against minor perturbations
   d = 1;
   kx = 20;
   ky = 20;
   npoints = 5 * kx * ky;
   ae_matrix_set_length(&xy, npoints, 2 + d, _state);
   for (i = 0; i < npoints; i++) {
      xy.xyR[i][0] = (kx - 1) * hqrnduniformr(&rs, _state);
      xy.xyR[i][1] = (ky - 1) * hqrnduniformr(&rs, _state);
      xy.xyR[i][2] = hqrndnormal(&rs, _state);
   }
   spline2dbuildercreate(d, &state, _state);
   spline2dbuildersetgrid(&state, kx, ky, _state);
   spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
   spline2dbuildersetalgoblocklls(&state, 0.001, _state);
   spline2dbuildersetpoints(&state, &xy, npoints, _state);
   spline2dfit(&state, &s, &rep, _state);
   for (i = 0; i < npoints; i++) {
      j = i + hqrnduniformi(&rs, npoints - i, _state);
      swaprows(&xy, i, j, -1, _state);
      xy.xyR[i][2] = xy.xyR[i][2] + ae_machineepsilon * hqrndnormal(&rs, _state);
   }
   spline2dbuildersetpoints(&state, &xy, npoints, _state);
   spline2dfit(&state, &s2, &rep2, _state);
   mx = 0.0;
   maxerror = 0.0;
   for (i = 0; i < npoints; i++) {
      v0 = (kx - 1) * hqrnduniformr(&rs, _state);
      v1 = (ky - 1) * hqrnduniformr(&rs, _state);
      maxerror = ae_maxreal(maxerror, ae_fabs(spline2dcalc(&s, v0, v1, _state) - spline2dcalc(&s2, v0, v1, _state), _state), _state);
      mx = ae_maxreal(mx, ae_fabs(spline2dcalc(&s, v0, v1, _state), _state), _state);
   }
   ae_set_error_flag(errorflag, maxerror / mx > 1.0E-12, __FILE__, __LINE__, "testspline2dunit.ap:1846");
   ae_frame_leave(_state);
}

// Block solver test for fitting functionality
static void testspline2dunit_testfittingfastddmsolver(bool *errorflag, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t tilesize;
   ae_int_t interfacesize;
   ae_int_t npoints;
   ae_int_t d;
   ae_int_t kx;
   ae_int_t ky;
   ae_int_t kxcur;
   ae_int_t kycur;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   double v0;
   double v1;
   double vx;
   double vy;
   double f0;
   double f1;
   double f2;
   double f3;
   double h;
   double smax;
   double ernd;
   double ept;
   double emax;
   double eavg;
   double vmax;
   ae_int_t padding;
   ae_int_t nlayers;
   double rmserror;
   double avgerror;
   double maxerror;
   double rss;
   double tss;
   double r2;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewObj(hqrndstate, rs, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewMatrix(xy1, 0, 0, DT_REAL, _state);
   NewObj(spline2dbuilder, state, _state);
   NewObj(spline2dinterpolant, sa0, _state);
   NewObj(spline2dinterpolant, sa1, _state);
   NewObj(spline2dinterpolant, s1, _state);
   NewObj(spline2dinterpolant, s2, _state);
   NewObj(spline2dfitreport, rep0, _state);
   NewObj(spline2dfitreport, rep1, _state);
   NewObj(spline2dfitreport, rep2, _state);
   NewVector(meany, 0, DT_REAL, _state);
   NewVector(lambdaa, 0, DT_REAL, _state);
   NewVector(penaltya, 0, DT_REAL, _state);
   NewVector(rmsa, 0, DT_REAL, _state);

   hqrndrandomize(&rs, _state);

// Basic test:
// * small grid whose size is TileSize+1 or less
// * random dataset
// * compared with single run of naiveLLS
// * we also test correctness of report fields
   for (pass = 1; pass <= 3; pass++) {
      for (d = 1; d <= 3; d++) {
         tilesize = 8 + hqrnduniformi(&rs, 8, _state);
         kx = ae_maxint(1 + hqrnduniformi(&rs, tilesize + 1, _state), 4, _state);
         ky = ae_maxint(1 + hqrnduniformi(&rs, tilesize + 1, _state), 4, _state);
         npoints = 10 * kx * ky;
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         ae_vector_set_length(&meany, d, _state);
         for (j = 0; j < d; j++) {
            meany.xR[j] = 0.0;
         }
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
               meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
            }
         }

      // Create solver, set grid, area and other properties.
      // Fit with NaiveLLS first.
         spline2dbuildercreate(d, &state, _state);
         spline2dbuildersetarea(&state, 0.0, 1.0, 0.0, 1.0, _state);
         state.maxcoresize = tilesize;
         state.adddegreeoffreedom = false;
         spline2dbuildersetalgonaivells(&state, 0.0, _state);
         spline2dbuildersetpoints(&state, &xy, npoints, _state);
         spline2dbuildersetgrid(&state, kx, ky, _state);
         spline2dfit(&state, &sa0, &rep0, _state);

      // Fit with FastDDM solver with NLayers in [-2,+2]
         spline2dbuildersetpoints(&state, &xy, npoints, _state);
         spline2dbuildersetgrid(&state, kx, ky, _state);
         spline2dbuildersetalgofastddm(&state, hqrnduniformi(&rs, 5, _state) - 2, 0.0, _state);
         spline2dfit(&state, &s1, &rep1, _state);

      // Compare at random locations
         smax = 0.0;
         ernd = 0.0;
         for (k = 0; k < 20 * kx * ky; k++) {
            vx = hqrnduniformr(&rs, _state);
            vy = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               v0 = spline2dcalcvi(&sa0, vx, vy, j, _state);
               v1 = spline2dcalcvi(&s1, vx, vy, j, _state);
               smax = ae_maxreal(smax, ae_fabs(v0, _state), _state);
               ernd = ae_maxreal(ernd, ae_fabs(v0 - v1, _state), _state);
            }
         }
         ae_set_error_flag(errorflag, ernd / smax > 0.001, __FILE__, __LINE__, "testspline2dunit.ap:1964");

      // Test report fields
         rmserror = 0.0;
         avgerror = 0.0;
         maxerror = 0.0;
         rss = 0.0;
         tss = 0.0;
         for (i = 0; i < npoints; i++) {
            for (j = 0; j < d; j++) {
               v = xy.xyR[i][2 + j] - spline2dcalcvi(&s1, xy.xyR[i][0], xy.xyR[i][1], j, _state);
               rss = rss + v * v;
               tss = tss + ae_sqr(xy.xyR[i][2 + j] - meany.xR[j], _state);
               rmserror = rmserror + ae_sqr(v, _state);
               avgerror = avgerror + ae_fabs(v, _state) / (npoints * d);
               maxerror = ae_maxreal(maxerror, ae_fabs(v, _state), _state);
            }
         }
         rmserror = ae_sqrt(rmserror / (npoints * d), _state);
         r2 = 1.0 - rss / coalesce(tss, 1.0, _state);
         ae_set_error_flag(errorflag, ae_fabs(rmserror - rep1.rmserror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1988");
         ae_set_error_flag(errorflag, ae_fabs(avgerror - rep1.avgerror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1989");
         ae_set_error_flag(errorflag, ae_fabs(maxerror - rep1.maxerror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1990");
         if (tss > 1.0E-3) {
            ae_set_error_flag(errorflag, ae_fabs(r2 - rep1.r2, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:1992");
         }
      }
   }

// Basic test: two-layer task; "reference" model is built by
// subsequent fitting of two "traditional" splines; test model
// is built by FastDDM algorithm.
   tilesize = 15;
   for (d = 1; d <= 2; d++) {

   // Generate dataset
      kx = 2 * tilesize + 1;
      ky = 2 * tilesize + 1;
      npoints = kx * ky;
      ae_matrix_set_length(&xy, npoints, 2 + d, _state);
      for (i = 0; i < npoints; i++) {
         xy.xyR[i][0] = boundval(i % kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(kx - 1), _state);
         xy.xyR[i][1] = boundval(i / kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(ky - 1), _state);
         for (j = 0; j < d; j++) {
            xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
         }
      }

   // Create solver, set grid, area and other properties
      spline2dbuildercreate(d, &state, _state);
      spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
      state.maxcoresize = tilesize;

   // Fit with BlockLLS solver
      state.adddegreeoffreedom = false;
      spline2dbuildersetalgoblocklls(&state, 0.0, _state);
      spline2dbuildersetpoints(&state, &xy, npoints, _state);
      spline2dbuildersetgrid(&state, (kx - 1) / 2 + 1, (ky - 1) / 2 + 1, _state);
      spline2dfit(&state, &sa0, &rep0, _state);
      ae_matrix_set_length(&xy1, npoints, 2 + d, _state);
      for (i = 0; i < npoints; i++) {
         xy1.xyR[i][0] = xy.xyR[i][0];
         xy1.xyR[i][1] = xy.xyR[i][1];
         for (j = 0; j < d; j++) {
            xy1.xyR[i][2 + j] = xy.xyR[i][2 + j] - spline2dcalcvi(&sa0, xy.xyR[i][0], xy.xyR[i][1], j, _state);
         }
      }
      spline2dbuildersetpoints(&state, &xy1, npoints, _state);
      spline2dbuildersetgrid(&state, kx, ky, _state);
      spline2dfit(&state, &sa1, &rep0, _state);

   // Fit with another solver
      spline2dbuildersetpoints(&state, &xy, npoints, _state);
      spline2dbuildersetgrid(&state, kx, ky, _state);
      spline2dbuildersetalgofastddm(&state, 0, 0.0, _state);
      spline2dfit(&state, &s1, &rep1, _state);

   // Compare
      padding = 0;
      smax = 0.0;
      ernd = 0.0;
      for (k = 0; k < 20 * kx * ky; k++) {
         vx = (kx - 1 - 2 * padding) * hqrnduniformr(&rs, _state) + padding;
         vy = (ky - 1 - 2 * padding) * hqrnduniformr(&rs, _state) + padding;
         for (j = 0; j < d; j++) {
            v0 = spline2dcalcvi(&sa0, vx, vy, j, _state) + spline2dcalcvi(&sa1, vx, vy, j, _state);
            v1 = spline2dcalcvi(&s1, vx, vy, j, _state);
            smax = ae_maxreal(smax, ae_fabs(v0, _state), _state);
            ernd = ae_maxreal(ernd, ae_fabs(v0 - v1, _state), _state);
         }
      }
      ae_set_error_flag(errorflag, ernd / smax > 0.010, __FILE__, __LINE__, "testspline2dunit.ap:2071");
      ept = 0.0;
      for (i = 0; i < npoints; i++) {
         vx = xy.xyR[i][0];
         vy = xy.xyR[i][1];
         for (j = 0; j < d; j++) {
            v0 = spline2dcalcvi(&sa0, vx, vy, j, _state) + spline2dcalcvi(&sa1, vx, vy, j, _state);
            v1 = spline2dcalcvi(&s1, vx, vy, j, _state);
            smax = ae_maxreal(smax, ae_fabs(v0, _state), _state);
            ept = ae_maxreal(ept, ae_fabs(v0 - v1, _state), _state);
         }
      }
      ae_set_error_flag(errorflag, ept / smax > 0.001, __FILE__, __LINE__, "testspline2dunit.ap:2085");
   }

// Test correctness of error reporting in multilevel setting:
// * grid whose size is larger than TileSize+1
// * random dataset
// * we just test correctness of report fields
   for (pass = 1; pass <= 2; pass++) {
      for (d = 1; d <= 2; d++) {
         tilesize = 5 + hqrnduniformi(&rs, 3, _state);
         kx = ae_maxint(1 + hqrnduniformi(&rs, 5 * tilesize + 1, _state), 4, _state);
         ky = ae_maxint(1 + hqrnduniformi(&rs, 5 * tilesize + 1, _state), 4, _state);
         npoints = 10 * kx * ky;
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         ae_vector_set_length(&meany, d, _state);
         for (j = 0; j < d; j++) {
            meany.xR[j] = 0.0;
         }
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
               meany.xR[j] = meany.xR[j] + xy.xyR[i][2 + j] / npoints;
            }
         }

      // Create solver, set grid, area and other properties.
      // Fit with FastDDM solver
         spline2dbuildercreate(d, &state, _state);
         spline2dbuildersetarea(&state, 0.0, 1.0, 0.0, 1.0, _state);
         state.maxcoresize = tilesize;
         spline2dbuildersetpoints(&state, &xy, npoints, _state);
         spline2dbuildersetgrid(&state, kx, ky, _state);
         spline2dbuildersetalgofastddm(&state, hqrnduniformi(&rs, 5, _state) - 2, 0.0, _state);
         spline2dfit(&state, &s1, &rep1, _state);

      // Test report fields
         rmserror = 0.0;
         avgerror = 0.0;
         maxerror = 0.0;
         rss = 0.0;
         tss = 0.0;
         for (i = 0; i < npoints; i++) {
            for (j = 0; j < d; j++) {
               v = xy.xyR[i][2 + j] - spline2dcalcvi(&s1, xy.xyR[i][0], xy.xyR[i][1], j, _state);
               rss = rss + v * v;
               tss = tss + ae_sqr(xy.xyR[i][2 + j] - meany.xR[j], _state);
               rmserror = rmserror + ae_sqr(v, _state);
               avgerror = avgerror + ae_fabs(v, _state) / (npoints * d);
               maxerror = ae_maxreal(maxerror, ae_fabs(v, _state), _state);
            }
         }
         rmserror = ae_sqrt(rmserror / (npoints * d), _state);
         r2 = 1.0 - rss / coalesce(tss, 1.0, _state);
         ae_set_error_flag(errorflag, ae_fabs(rmserror - rep1.rmserror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:2152");
         ae_set_error_flag(errorflag, ae_fabs(avgerror - rep1.avgerror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:2153");
         ae_set_error_flag(errorflag, ae_fabs(maxerror - rep1.maxerror, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:2154");
         if (tss > 1.0E-3) {
            ae_set_error_flag(errorflag, ae_fabs(r2 - rep1.r2, _state) > 1.0E-6, __FILE__, __LINE__, "testspline2dunit.ap:2156");
         }
      }
   }

// Test correctness of handling NLayers>0, NLayers=0 and NLayers<0.
//
// NOTE: we use reduced tile size and interface size, because we do
//       not test model quality, just identity with NLayers <= 0
   d = 1;
   tilesize = 5;
   interfacesize = 2;
   kx = 1 + 4 * (tilesize - hqrnduniformi(&rs, 2, _state));
   ky = 1 + 4 * (tilesize - hqrnduniformi(&rs, 2, _state));
   nlayers = 3;
   npoints = kx * ky;
   ae_matrix_set_length(&xy, npoints, 2 + d, _state);
   for (i = 0; i < npoints; i++) {
      xy.xyR[i][0] = hqrnduniformr(&rs, _state);
      xy.xyR[i][1] = hqrnduniformr(&rs, _state);
      for (j = 0; j < d; j++) {
         xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
      }
   }

// Create solver, set grid, area and other properties.
   spline2dbuildercreate(d, &state, _state);
   spline2dbuildersetarea(&state, 0.0, 1.0, 0.0, 1.0, _state);
   state.maxcoresize = tilesize;
   state.interfacesize = interfacesize;
   spline2dbuildersetpoints(&state, &xy, npoints, _state);
   spline2dbuildersetgrid(&state, kx, ky, _state);

// Try different NLayers>0, compare with corresponding non-positive values
   for (k = 1; k <= nlayers + 1; k++) {
      spline2dbuildersetalgofastddm(&state, k, 0.0, _state);
      spline2dfit(&state, &s1, &rep1, _state);
      spline2dbuildersetalgofastddm(&state, imin2(k - nlayers, 0, _state), 0.0, _state);
      spline2dfit(&state, &s2, &rep2, _state);

   // Test report fields
      vmax = 0.0;
      for (i = 0; i <= 10; i++) {
         vx = hqrnduniformr(&rs, _state);
         vy = hqrnduniformr(&rs, _state);
         vmax = ae_maxreal(vmax, ae_fabs(spline2dcalc(&s1, vx, vy, _state) - spline2dcalc(&s2, vx, vy, _state), _state), _state);
      }
      ae_set_error_flag(errorflag, vmax > 1.0E-12, __FILE__, __LINE__, "testspline2dunit.ap:2212");
   }

// Ability to reproduce target function in multilayer setup:
// * small tile size
// * nearly regular KX*KY dataset, for random KX>TileSize, KY>TileSize
// * (2*KX)x(2*KY) grid, flexible enough
//
// We check that maximum and average errors at nodes are small
   vmax = 0.0;
   for (pass = 1; pass <= 10; pass++) {
      d = 1;
      tilesize = 5 + hqrnduniformi(&rs, 4, _state);
      kx = 4 + hqrnduniformi(&rs, 4 * tilesize + 1, _state);
      ky = 4 + hqrnduniformi(&rs, 4 * tilesize + 1, _state);
      npoints = kx * ky;
      ae_matrix_set_length(&xy, npoints, 2 + d, _state);
      for (i = 0; i < npoints; i++) {
         xy.xyR[i][0] = boundval(i % kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(kx - 1), _state);
         xy.xyR[i][1] = boundval(i / kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(ky - 1), _state);
         for (j = 0; j < d; j++) {
            xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
         }
      }

   // Create solver, set grid, area and other properties.
      spline2dbuildercreate(d, &state, _state);
      state.maxcoresize = tilesize;
      spline2dbuildersetpoints(&state, &xy, npoints, _state);
      spline2dbuildersetgrid(&state, ae_round(2.0 * kx, _state), ae_round(2.0 * ky, _state), _state);
      spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
      spline2dbuildersetalgofastddm(&state, 0, 0.0, _state);
      spline2dfit(&state, &s1, &rep1, _state);

   // Compare
      emax = 0.0;
      eavg = 0.0;
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < d; j++) {
            v = ae_fabs(spline2dcalcvi(&s1, xy.xyR[i][0], xy.xyR[i][1], j, _state) - xy.xyR[i][2 + j], _state);
            emax = ae_maxreal(emax, v, _state);
            eavg = eavg + v / (npoints * d);
         }
      }
      ae_set_error_flag(errorflag, emax > 0.00100, __FILE__, __LINE__, "testspline2dunit.ap:2265");
      ae_set_error_flag(errorflag, eavg > 0.00010, __FILE__, __LINE__, "testspline2dunit.ap:2266");
      vmax = ae_maxreal(vmax, ae_fabs(emax, _state), _state);
   }

// Test that increasing LambdaV results in decrease of S'' integral;
// although (unlike BlockLLS) FastDDM does NOT minimize S'' explicitly,
// it shows similar behavior with respect to regularization, so we can
// expect that penalty decreases.
//
// NOTE: we use ultra-small values of tile size and interface size because
//       precision is not important here.
   for (pass = 0; pass <= 2; pass++) {
      d = 1;
      tilesize = 5;
      kx = 1 + 4 * (tilesize - hqrnduniformi(&rs, 2, _state));
      ky = 1 + 4 * (tilesize - hqrnduniformi(&rs, 2, _state));
      npoints = -1;
      vx = 0.0;
      vy = 0.0;
      if (pass == 0) {
         npoints = 1 + ae_round(0.5 * kx * ky, _state);
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0 / (kx - 1);
         vy = 1.0 / (ky - 1);
      }
      if (pass == 1) {
         npoints = kx * ky;
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = boundval(i % kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(kx - 1), _state);
            xy.xyR[i][1] = boundval(i / kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(ky - 1), _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0;
         vy = 1.0;
      }
      if (pass == 2) {
         npoints = 1 + ae_round(3.0 * kx * ky, _state);
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0 / (kx - 1);
         vy = 1.0 / (ky - 1);
      }
      ae_assert(vx > 0.0, "Spline2D: unit test failed integrity check", _state);
      ae_assert(vy > 0.0, "Spline2D: unit test failed integrity check", _state);

   // Create solver, set grid, area and other properties.
      spline2dbuildercreate(d, &state, _state);
      spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
      state.maxcoresize = tilesize;
      spline2dbuildersetpoints(&state, &xy, npoints, _state);
      spline2dbuildersetgrid(&state, kx, ky, _state);

   // Try different values of LambdaV, calculate penalty
      ae_vector_set_length(&lambdaa, 4, _state);
      lambdaa.xR[0] = 0.0;
      lambdaa.xR[1] = 1.0E-1;
      lambdaa.xR[2] = 1.0E+1;
      lambdaa.xR[3] = 1.0E+2;
      ae_vector_set_length(&penaltya, lambdaa.cnt, _state);
      ae_vector_set_length(&rmsa, lambdaa.cnt, _state);
      for (k = 0; k < lambdaa.cnt; k++) {
         spline2dbuildersetalgofastddm(&state, 0, lambdaa.xR[k], _state);
         spline2dfit(&state, &s1, &rep1, _state);
         h = 1.0E-4;
         v = 0.0;
         for (i = 0; i < kx; i++) {
            for (j = 0; j < ky; j++) {
               v0 = i * vx;
               v1 = j * vy;
               f0 = spline2dcalc(&s1, v0 - h, v1, _state);
               f1 = spline2dcalc(&s1, v0, v1, _state);
               f2 = spline2dcalc(&s1, v0 + h, v1, _state);
               v = v + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(h, _state), _state);
               f0 = spline2dcalc(&s1, v0, v1 - h, _state);
               f1 = spline2dcalc(&s1, v0, v1, _state);
               f2 = spline2dcalc(&s1, v0, v1 + h, _state);
               v = v + ae_sqr((f0 + f2 - 2 * f1) / ae_sqr(h, _state), _state);
               f0 = spline2dcalc(&s1, v0 - h, v1 - h, _state);
               f1 = spline2dcalc(&s1, v0 + h, v1 - h, _state);
               f2 = spline2dcalc(&s1, v0 - h, v1 + h, _state);
               f3 = spline2dcalc(&s1, v0 + h, v1 + h, _state);
               v = v + 2 * ae_sqr((f3 - f2 - f1 + f0) / ae_sqr(h, _state), _state);
            }
         }
         penaltya.xR[k] = v;
         rmsa.xR[k] = rep1.rmserror;
      }
      for (k = 0; k < lambdaa.cnt - 1; k++) {
         ae_set_error_flag(errorflag, penaltya.xR[k] < penaltya.xR[k + 1], __FILE__, __LINE__, "testspline2dunit.ap:2387");
      }
   }

// Test that refining grid in "K:=2*K-1" fashion results in
// decrease of RMS error; although it seems quite silly test,
// in fact it checks various failures in the algorithm.
//
// NOTE: we set high penalty coefficient in order to avoid nearly
//       precise fits (such fits are hard to test reliably)
//
// NOTE: we use ultra-small values of tile size and interface size because
//       precision is not important here.
   for (pass = 0; pass <= 2; pass++) {
      d = 1;
      tilesize = 5;
      kx = 1 + 4 * tilesize;
      ky = 1 + 4 * tilesize;
      nlayers = 3;
      npoints = -1;
      if (pass == 0) {
         npoints = 1 + ae_round(0.1 * kx * ky, _state);
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0 / (kx - 1);
         vy = 1.0 / (ky - 1);
      }
      if (pass == 1) {
         npoints = kx * ky;
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = boundval(i % kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(kx - 1), _state);
            xy.xyR[i][1] = boundval(i / kx + 0.05 * hqrndnormal(&rs, _state), 0.0, (double)(ky - 1), _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0;
         vy = 1.0;
      }
      if (pass == 2) {
         npoints = 1 + ae_round(2.0 * kx * ky, _state);
         ae_matrix_set_length(&xy, npoints, 2 + d, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = hqrnduniformr(&rs, _state);
            xy.xyR[i][1] = hqrnduniformr(&rs, _state);
            for (j = 0; j < d; j++) {
               xy.xyR[i][2 + j] = hqrndnormal(&rs, _state);
            }
         }
         vx = 1.0 / (kx - 1);
         vy = 1.0 / (ky - 1);
      }
   // Create solver, set grid, area and other properties.
      spline2dbuildercreate(d, &state, _state);
      spline2dbuildersetarea(&state, 0.0, (double)(kx - 1), 0.0, (double)(ky - 1), _state);
      state.maxcoresize = tilesize;
      spline2dbuildersetpoints(&state, &xy, npoints, _state);

   // Try different values of LambdaV, calculate penalty
      ae_vector_set_length(&rmsa, nlayers, _state);
      kxcur = tilesize + 1;
      kycur = tilesize + 1;
      for (k = 0; k < rmsa.cnt; k++) {
         spline2dbuildersetgrid(&state, kxcur, kycur, _state);
         spline2dbuildersetalgofastddm(&state, 0, 0.0, _state);
         spline2dfit(&state, &s1, &rep1, _state);
         rmsa.xR[k] = rep1.rmserror;
         kxcur = 2 * kxcur - 1;
         kycur = 2 * kycur - 1;
      }
      for (k = 0; k < rmsa.cnt - 1; k++) {
         ae_set_error_flag(errorflag, rmsa.xR[k] <= rmsa.xR[k + 1], __FILE__, __LINE__, "testspline2dunit.ap:2479");
      }
   }
   ae_frame_leave(_state);
}

// === spline3d testing unit ===
static bool testspline3dunit_basictest(ae_state *_state);
static bool testspline3dunit_testunpack(ae_state *_state);
static bool testspline3dunit_testlintrans(ae_state *_state);
static bool testspline3dunit_testtrilinearresample(ae_state *_state);
static void testspline3dunit_buildrndgrid(bool isvect, bool reorder, ae_int_t *n, ae_int_t *m, ae_int_t *l, ae_int_t *d, RVector *x, RVector *y, RVector *z, RVector *f, ae_state *_state);

bool testspline3d(bool silence, ae_state *_state) {
   bool waserrors;
   bool basicerr;
   bool unpackerr;
   bool lintransferr;
   bool trilinreserr;
   bool result;

   basicerr = testspline3dunit_basictest(_state);
   unpackerr = testspline3dunit_testunpack(_state);
   lintransferr = testspline3dunit_testlintrans(_state);
   trilinreserr = testspline3dunit_testtrilinearresample(_state);
   waserrors = ((basicerr || unpackerr) || lintransferr) || trilinreserr;
   if (!silence) {
      printf("TESTING 3D SPLINE\n");
      printf("BASIC TEST:                              ");
      if (basicerr) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("UNPACK TEST:                             ");
      if (unpackerr) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("LIN_TRANSF TEST:                         ");
      if (lintransferr) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }
      printf("TRILINEAR RESAMPLING TEST:               ");
      if (trilinreserr) {
         printf("FAILED\n");
      } else {
         printf("OK\n");
      }

   // Summary
      if (waserrors) {
         printf("TEST FAILED");
      } else {
         printf("TEST PASSED");
      }
      printf("\n\n");
   }
   result = !waserrors;
   return result;
}

// The function does test basic functionality.
static bool testspline3dunit_basictest(ae_state *_state) {
   ae_frame _frame_block;
   double vsf;
   ae_int_t d;
   ae_int_t m;
   ae_int_t n;
   ae_int_t l;
   double eps;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t offs;
   ae_int_t di;
   double ax;
   double ay;
   double az;
   double axy;
   double ayz;
   double vx;
   double vy;
   double vz;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(spline3dinterpolant, c, _state);
   NewObj(spline3dinterpolant, cc, _state);
   NewVector(vvf, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(z, 0, DT_REAL, _state);
   NewVector(sf, 0, DT_REAL, _state);
   NewVector(vf, 0, DT_REAL, _state);

   eps = 1000 * ae_machineepsilon;

// Test spline ability to reproduce D-dimensional vector function
//     f[idx](x,y,z) = idx+AX*x + AY*y + AZ*z + AXY*x*y + AYZ*y*z
// with random AX/AY/...
//
// We generate random test function, build spline, then evaluate
// it in the random test point.
   for (d = 1; d <= 3; d++) {
      n = 2 + ae_randominteger(4, _state);
      m = 2 + ae_randominteger(4, _state);
      l = 2 + ae_randominteger(4, _state);
      ae_vector_set_length(&x, n, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = (double)i;
      }
      ae_vector_set_length(&y, m, _state);
      for (i = 0; i < m; i++) {
         y.xR[i] = (double)i;
      }
      ae_vector_set_length(&z, l, _state);
      for (i = 0; i < l; i++) {
         z.xR[i] = (double)i;
      }
      ae_vector_set_length(&vf, l * m * n * d, _state);
      offs = 0;
      ax = 2 * ae_randomreal(_state) - 1;
      ay = 2 * ae_randomreal(_state) - 1;
      az = 2 * ae_randomreal(_state) - 1;
      axy = 2 * ae_randomreal(_state) - 1;
      ayz = 2 * ae_randomreal(_state) - 1;
      for (k = 0; k < l; k++) {
         for (j = 0; j < m; j++) {
            for (i = 0; i < n; i++) {
               for (di = 0; di < d; di++) {
                  vf.xR[offs] = di + ax * i + ay * j + az * k + axy * i * j + ayz * j * k;
                  offs = offs + 1;
               }
            }
         }
      }
      spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &vf, d, &c, _state);
      vx = ae_randomreal(_state) * n;
      vy = ae_randomreal(_state) * m;
      vz = ae_randomreal(_state) * l;
      spline3dcalcv(&c, vx, vy, vz, &vf, _state);
      for (di = 0; di < d; di++) {
         if (ae_fabs(di + ax * vx + ay * vy + az * vz + axy * vx * vy + ayz * vy * vz - vf.xR[di], _state) > eps) {
            result = true;
            ae_frame_leave(_state);
            return result;
         }
      }
      if (d == 1) {
         vsf = spline3dcalc(&c, vx, vy, vz, _state);
         if (ae_fabs(ax * vx + ay * vy + az * vz + axy * vx * vy + ayz * vy * vz - vsf, _state) > eps) {
            result = true;
            ae_frame_leave(_state);
            return result;
         }
      }
   }

// Generate random grid and test function.
// Test spline ability to reproduce function values at grid nodes.
   passcount = 20;
   for (pass = 1; pass <= passcount; pass++) {

   // Prepare a model and check that functions (Spline3DBuildTrilinear,
   // Spline3DCalc,Spline3DCalcV) work correctly and
      testspline3dunit_buildrndgrid(true, true, &n, &m, &l, &d, &x, &y, &z, &vf, _state);
      rvectorsetlengthatleast(&sf, n * m * l, _state);

   // Check that the model's values are equal to the function's values
   // in grid points
      spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &vf, d, &c, _state);
      for (i = 0; i < n; i++) {
         for (j = 0; j < m; j++) {
            for (k = 0; k < l; k++) {
               spline3dcalcv(&c, x.xR[i], y.xR[j], z.xR[k], &vvf, _state);
               for (di = 0; di < d; di++) {
                  if (ae_fabs(vf.xR[d * (n * (m * k + j) + i) + di] - vvf.xR[di], _state) > eps) {
                     result = true;
                     ae_frame_leave(_state);
                     return result;
                  }
               }
            }
         }
      }
   }
   result = false;
   ae_frame_leave(_state);
   return result;
}

// Unpack/UnpackV test
static bool testspline3dunit_testunpack(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   ae_int_t l;
   ae_int_t d;
   ae_int_t sz;
   ae_int_t un;
   ae_int_t um;
   ae_int_t ul;
   ae_int_t ud;
   ae_int_t ust;
   ae_int_t uvn;
   ae_int_t uvm;
   ae_int_t uvl;
   ae_int_t uvd;
   ae_int_t uvst;
   ae_int_t ci;
   ae_int_t cj;
   ae_int_t ck;
   ae_int_t p0;
   ae_int_t p1;
   double tx;
   double ty;
   double tz;
   double v1;
   double v2;
   double err;
   ae_int_t pass;
   ae_int_t passcount;
   bool bperr;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t di;
   ae_int_t i0;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(spline3dinterpolant, c, _state);
   NewMatrix(tbl0, 0, 0, DT_REAL, _state);
   NewMatrix(tbl1, 0, 0, DT_REAL, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(z, 0, DT_REAL, _state);
   NewVector(sf, 0, DT_REAL, _state);
   NewVector(vf, 0, DT_REAL, _state);

   passcount = 20;
   err = 0.0;
   for (pass = 1; pass <= passcount; pass++) {

   // generate random grid.
   // NOTE: for this test we need ordered grid, i.e. grid
   //       with nodes in ascending order
      testspline3dunit_buildrndgrid(true, false, &n, &m, &l, &d, &x, &y, &z, &vf, _state);
      sz = n * m * l;
      rvectorsetlengthatleast(&sf, sz, _state);
      spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &vf, d, &c, _state);
      spline3dunpackv(&c, &uvn, &uvm, &uvl, &uvd, &uvst, &tbl0, _state);
      for (di = 0; di < d; di++) {

      // DI-th component copy of a vector-function to
      // a scalar function
         for (i = 0; i < sz; i++) {
            sf.xR[i] = vf.xR[d * i + di];
         }
         spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &sf, 1, &c, _state);
         spline3dunpackv(&c, &un, &um, &ul, &ud, &ust, &tbl1, _state);
         for (i = 0; i < n - 1; i++) {
            for (j = 0; j < m - 1; j++) {
               for (k = 0; k < l - 1; k++) {
                  p1 = (n - 1) * ((m - 1) * k + j) + i;
                  p0 = d * p1 + di;

               // Check that all components are correct:
               //  *first check, that unpacked componets are equal
               //   to packed components;
                  bperr = (((((((((((((((((un != n || um != m) || ul != l) || tbl1.xyR[p1][0] != x.xR[i]) || tbl1.xyR[p1][1] != x.xR[i + 1]) || tbl1.xyR[p1][2] != y.xR[j]) || tbl1.xyR[p1][3] != y.xR[j + 1]) || tbl1.xyR[p1][4] != z.xR[k]) || tbl1.xyR[p1][5] != z.xR[k + 1]) || uvn != n) || uvm != m) || uvl != l) || uvd != d) || tbl0.xyR[p0][0] != x.xR[i]) || tbl0.xyR[p0][1] != x.xR[i + 1]) || tbl0.xyR[p0][2] != y.xR[j]) || tbl0.xyR[p0][3] != y.xR[j + 1]) || tbl0.xyR[p0][4] != z.xR[k]) || tbl0.xyR[p0][5] != z.xR[k + 1];

               //  *check, that all components unpacked by Unpack
               //   function are equal to all components unpacked
               //   by UnpackV function.
                  for (i0 = 0; i0 <= 13; i0++) {
                     bperr = bperr || tbl0.xyR[p0][i0] != tbl1.xyR[p1][i0];
                  }
                  if (bperr) {
                     result = true;
                     ae_frame_leave(_state);
                     return result;
                  }
                  tx = (0.001 + 0.999 * ae_randomreal(_state)) * (tbl1.xyR[p1][1] - tbl1.xyR[p1][0]);
                  ty = (0.001 + 0.999 * ae_randomreal(_state)) * (tbl1.xyR[p1][3] - tbl1.xyR[p1][2]);
                  tz = (0.001 + 0.999 * ae_randomreal(_state)) * (tbl1.xyR[p1][5] - tbl1.xyR[p1][4]);

               // Interpolation properties for:
               //  *scalar function;
                  v1 = 0.0;
                  for (ci = 0; ci <= 1; ci++) {
                     for (cj = 0; cj <= 1; cj++) {
                        for (ck = 0; ck <= 1; ck++) {
                           v1 = v1 + tbl1.xyR[p1][6 + 2 * (2 * ck + cj) + ci] * ae_pow(tx, (double)ci, _state) * ae_pow(ty, (double)cj, _state) * ae_pow(tz, (double)ck, _state);
                        }
                     }
                  }
                  v2 = spline3dcalc(&c, tbl1.xyR[p1][0] + tx, tbl1.xyR[p1][2] + ty, tbl1.xyR[p1][4] + tz, _state);
                  err = ae_maxreal(err, ae_fabs(v1 - v2, _state), _state);

               //  *component of vector function.
                  v1 = 0.0;
                  for (ci = 0; ci <= 1; ci++) {
                     for (cj = 0; cj <= 1; cj++) {
                        for (ck = 0; ck <= 1; ck++) {
                           v1 = v1 + tbl0.xyR[p0][6 + 2 * (2 * ck + cj) + ci] * ae_pow(tx, (double)ci, _state) * ae_pow(ty, (double)cj, _state) * ae_pow(tz, (double)ck, _state);
                        }
                     }
                  }
                  v2 = spline3dcalc(&c, tbl0.xyR[p0][0] + tx, tbl0.xyR[p0][2] + ty, tbl0.xyR[p0][4] + tz, _state);
                  err = ae_maxreal(err, ae_fabs(v1 - v2, _state), _state);
               }
            }
         }
      }
   }
   result = err > 1.0E+5 * ae_machineepsilon;
   ae_frame_leave(_state);
   return result;
}

// LinTrans test
static bool testspline3dunit_testlintrans(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t m;
   ae_int_t n;
   ae_int_t l;
   ae_int_t d;
   double a1;
   double a2;
   double a3;
   double b1;
   double b2;
   double b3;
   double tx;
   double ty;
   double tz;
   double vx;
   double vy;
   double vz;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t xjob;
   ae_int_t yjob;
   ae_int_t zjob;
   double err;
   ae_int_t i;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(spline3dinterpolant, c, _state);
   NewObj(spline3dinterpolant, c2, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(z, 0, DT_REAL, _state);
   NewVector(f, 0, DT_REAL, _state);
   NewVector(v1, 0, DT_REAL, _state);
   NewVector(v2, 0, DT_REAL, _state);

   err = 0.0;
   passcount = 15;
   for (pass = 1; pass <= passcount; pass++) {
      testspline3dunit_buildrndgrid(true, false, &n, &m, &l, &d, &x, &y, &z, &f, _state);
      spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &f, d, &c, _state);
      for (xjob = 0; xjob <= 1; xjob++) {
         for (yjob = 0; yjob <= 1; yjob++) {
            for (zjob = 0; zjob <= 1; zjob++) {

            // Prepare
               do {
                  a1 = 2.0 * ae_randomreal(_state) - 1.0;
               } while (a1 == 0.0);
               a1 = a1 * xjob;
               b1 = x.xR[0] + ae_randomreal(_state) * (x.xR[n - 1] - x.xR[0] + 2.0) - 1.0;
               do {
                  a2 = 2.0 * ae_randomreal(_state) - 1.0;
               } while (a2 == 0.0);
               a2 = a2 * yjob;
               b2 = y.xR[0] + ae_randomreal(_state) * (y.xR[m - 1] - y.xR[0] + 2.0) - 1.0;
               do {
                  a3 = 2.0 * ae_randomreal(_state) - 1.0;
               } while (a3 == 0.0);
               a3 = a3 * zjob;
               b3 = z.xR[0] + ae_randomreal(_state) * (z.xR[l - 1] - z.xR[0] + 2.0) - 1.0;

            // Test XYZ
               spline3dcopy(&c, &c2, _state);
               spline3dlintransxyz(&c2, a1, b1, a2, b2, a3, b3, _state);
               tx = x.xR[0] + ae_randomreal(_state) * (x.xR[n - 1] - x.xR[0]);
               ty = y.xR[0] + ae_randomreal(_state) * (y.xR[m - 1] - y.xR[0]);
               tz = z.xR[0] + ae_randomreal(_state) * (z.xR[l - 1] - z.xR[0]);
               if (xjob == 0) {
                  tx = b1;
                  vx = x.xR[0] + ae_randomreal(_state) * (x.xR[n - 1] - x.xR[0]);
               } else {
                  vx = (tx - b1) / a1;
               }
               if (yjob == 0) {
                  ty = b2;
                  vy = y.xR[0] + ae_randomreal(_state) * (y.xR[m - 1] - y.xR[0]);
               } else {
                  vy = (ty - b2) / a2;
               }
               if (zjob == 0) {
                  tz = b3;
                  vz = z.xR[0] + ae_randomreal(_state) * (z.xR[l - 1] - z.xR[0]);
               } else {
                  vz = (tz - b3) / a3;
               }
               spline3dcalcv(&c, tx, ty, tz, &v1, _state);
               spline3dcalcv(&c2, vx, vy, vz, &v2, _state);
               for (i = 0; i < d; i++) {
                  err = ae_maxreal(err, ae_fabs(v1.xR[i] - v2.xR[i], _state), _state);
               }
               if (err > 1.0E+4 * ae_machineepsilon) {
                  result = true;
                  ae_frame_leave(_state);
                  return result;
               }
            // Test F
               spline3dcopy(&c, &c2, _state);
               spline3dlintransf(&c2, a1, b1, _state);
               tx = x.xR[0] + ae_randomreal(_state) * (x.xR[n - 1] - x.xR[0]);
               ty = y.xR[0] + ae_randomreal(_state) * (y.xR[m - 1] - y.xR[0]);
               tz = z.xR[0] + ae_randomreal(_state) * (z.xR[l - 1] - z.xR[0]);
               spline3dcalcv(&c, tx, ty, tz, &v1, _state);
               spline3dcalcv(&c2, tx, ty, tz, &v2, _state);
               for (i = 0; i < d; i++) {
                  err = ae_maxreal(err, ae_fabs(a1 * v1.xR[i] + b1 - v2.xR[i], _state), _state);
               }
            }
         }
      }
   }
   result = err > 1.0E+4 * ae_machineepsilon;
   ae_frame_leave(_state);
   return result;
}

// Resample test
static bool testspline3dunit_testtrilinearresample(ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t n;
   ae_int_t m;
   ae_int_t l;
   ae_int_t n2;
   ae_int_t m2;
   ae_int_t l2;
   double v1;
   double v2;
   double err;
   double mf;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(spline3dinterpolant, c, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewVector(z, 0, DT_REAL, _state);
   NewVector(f, 0, DT_REAL, _state);
   NewVector(fr, 0, DT_REAL, _state);

   result = false;
   passcount = 20;
   for (pass = 1; pass <= passcount; pass++) {
      n = ae_randominteger(4, _state) + 2;
      m = ae_randominteger(4, _state) + 2;
      l = ae_randominteger(4, _state) + 2;
      n2 = ae_randominteger(4, _state) + 2;
      m2 = ae_randominteger(4, _state) + 2;
      l2 = ae_randominteger(4, _state) + 2;
      rvectorsetlengthatleast(&x, n, _state);
      rvectorsetlengthatleast(&y, m, _state);
      rvectorsetlengthatleast(&z, l, _state);
      rvectorsetlengthatleast(&f, n * m * l, _state);
      for (i = 0; i < n; i++) {
         x.xR[i] = (double)i / (n - 1);
      }
      for (i = 0; i < m; i++) {
         y.xR[i] = (double)i / (m - 1);
      }
      for (i = 0; i < l; i++) {
         z.xR[i] = (double)i / (l - 1);
      }
      for (i = 0; i < n; i++) {
         for (j = 0; j < m; j++) {
            for (k = 0; k < l; k++) {
               f.xR[n * (m * k + j) + i] = 2 * ae_randomreal(_state) - 1;
            }
         }
      }
      spline3dresampletrilinear(&f, l, m, n, l2, m2, n2, &fr, _state);
      spline3dbuildtrilinearv(&x, n, &y, m, &z, l, &f, 1, &c, _state);
      err = 0.0;
      mf = 0.0;
      for (i = 0; i < n2; i++) {
         for (j = 0; j < m2; j++) {
            for (k = 0; k < l2; k++) {
               v1 = spline3dcalc(&c, (double)i / (n2 - 1), (double)j / (m2 - 1), (double)k / (l2 - 1), _state);
               v2 = fr.xR[n2 * (m2 * k + j) + i];
               err = ae_maxreal(err, ae_fabs(v1 - v2, _state), _state);
               mf = ae_maxreal(mf, ae_fabs(v1, _state), _state);
            }
         }
      }
      result = result || err / mf > 1.0E+4 * ae_machineepsilon;
      if (result) {
         ae_frame_leave(_state);
         return result;
      }
   }
   ae_frame_leave(_state);
   return result;
}

// The function does build random function on random grid with random number
// of points:
// * N, M, K   -   random from 2 to 5
// * D         -   1 in case IsVect=False, 1..3 in case IsVect=True
// * X, Y, Z   -   each variable spans from MinV to MaxV, with MinV is random
//                 number from [-1.5,0.5] and MaxV is random number from
//                 [0.5,1.5]. All nodes are well separated. All nodes are
//                 randomly reordered in case Reorder=False. When Reorder=True,
//                 nodes are returned in ascending order.
// * F         -   random values from [-1,+1]
static void testspline3dunit_buildrndgrid(bool isvect, bool reorder, ae_int_t *n, ae_int_t *m, ae_int_t *l, ae_int_t *d, RVector *x, RVector *y, RVector *z, RVector *f, ae_state *_state) {
   double st;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t di;
   double v;
   double mx;
   double maxv;
   double minv;

   *n = 0;
   *m = 0;
   *l = 0;
   *d = 0;
   SetVector(x);
   SetVector(y);
   SetVector(z);
   SetVector(f);

   st = 0.3;
   *m = ae_randominteger(4, _state) + 2;
   *n = ae_randominteger(4, _state) + 2;
   *l = ae_randominteger(4, _state) + 2;
   if (isvect) {
      *d = ae_randominteger(3, _state) + 1;
   } else {
      *d = 1;
   }
   rvectorsetlengthatleast(x, *n, _state);
   rvectorsetlengthatleast(y, *m, _state);
   rvectorsetlengthatleast(z, *l, _state);
   rvectorsetlengthatleast(f, *n * (*m) * (*l) * (*d), _state);

// Fill X
   x->xR[0] = 0.0;
   for (i = 1; i < *n; i++) {
      x->xR[i] = x->xR[i - 1] + st + ae_randomreal(_state);
   }
   minv = -0.5 - ae_randomreal(_state);
   maxv = 0.5 + ae_randomreal(_state);
   mx = x->xR[*n - 1];
   for (i = 0; i < *n; i++) {
      x->xR[i] = x->xR[i] / mx * (maxv - minv) + minv;
   }
   if (reorder) {
      for (i = 0; i < *n; i++) {
         k = ae_randominteger(*n, _state);
         v = x->xR[i];
         x->xR[i] = x->xR[k];
         x->xR[k] = v;
      }
   }
// Fill Y
   y->xR[0] = 0.0;
   for (i = 1; i < *m; i++) {
      y->xR[i] = y->xR[i - 1] + st + ae_randomreal(_state);
   }
   minv = -0.5 - ae_randomreal(_state);
   maxv = 0.5 + ae_randomreal(_state);
   mx = y->xR[*m - 1];
   for (i = 0; i < *m; i++) {
      y->xR[i] = y->xR[i] / mx * (maxv - minv) + minv;
   }
   if (reorder) {
      for (i = 0; i < *m; i++) {
         k = ae_randominteger(*m, _state);
         v = y->xR[i];
         y->xR[i] = y->xR[k];
         y->xR[k] = v;
      }
   }
// Fill Z
   z->xR[0] = 0.0;
   for (i = 1; i < *l; i++) {
      z->xR[i] = z->xR[i - 1] + st + ae_randomreal(_state);
   }
   minv = -0.5 - ae_randomreal(_state);
   maxv = 0.5 + ae_randomreal(_state);
   mx = z->xR[*l - 1];
   for (i = 0; i < *l; i++) {
      z->xR[i] = z->xR[i] / mx * (maxv - minv) + minv;
   }
   if (reorder) {
      for (i = 0; i < *l; i++) {
         k = ae_randominteger(*l, _state);
         v = z->xR[i];
         z->xR[i] = z->xR[k];
         z->xR[k] = v;
      }
   }
// Fill F
   for (i = 0; i < *n; i++) {
      for (j = 0; j < *m; j++) {
         for (k = 0; k < *l; k++) {
            for (di = 0; di < *d; di++) {
               f->xR[*d * (*n * (*m * k + j) + i) + di] = 2 * ae_randomreal(_state) - 1;
            }
         }
      }
   }
}

