   ae_int_t i2;
   ae_int_t j;
   ae_int_t k;
   double rbase;
   ae_int_t nlayers;
   ae_int_t bf;
   ae_int_t gridsize;
   bool result;

   ae_frame_make(_state, &_frame_block);
   NewObj(rbfmodel, s, _state);
   NewObj(rbfmodel, s2, _state);
   NewObj(rbfreport, rep, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewVector(testpoint, 0, DT_REAL, _state);
