// Testing clustering
bool testclustering(bool silent, ae_state *_state) {
   bool waserrors;
   bool basicahcerrors;
   bool ahcerrors;
   bool kmeansconverrors;
   bool kmeanssimpleerrors;
   bool kmeansothererrors;
   bool kmeansrestartserrors;
   ae_int_t passcount;
   ae_int_t nf;
   ae_int_t nc;
   bool result;

// AHC tests
   basicahcerrors = testclusteringunit_basicahctests(_state);
   ahcerrors = testclusteringunit_advancedahctests(_state);

// k-means tests
   passcount = 10;
   kmeansconverrors = false;
   kmeansothererrors = false;
   kmeanssimpleerrors = false;
   kmeansrestartserrors = false;
   testclusteringunit_kmeansspecialtests(&kmeansothererrors, _state);
   testclusteringunit_kmeansinfinitelooptest(&kmeansothererrors, _state);
   testclusteringunit_kmeansrestartstest(&kmeansconverrors, &kmeansrestartserrors, _state);
   for (nf = 1; nf <= 5; nf++) {
      for (nc = 1; nc <= 5; nc++) {
         testclusteringunit_kmeanssimpletest1(nf, nc, passcount, &kmeansconverrors, &kmeansothererrors, &kmeanssimpleerrors, _state);
      }
   }

// Results
   waserrors = false;
   waserrors = waserrors || (basicahcerrors || ahcerrors);
   waserrors = waserrors || (((kmeansconverrors || kmeansothererrors) || kmeanssimpleerrors) || kmeansrestartserrors);
   if (!silent) {
      printf("TESTING CLUSTERING\n");
      printf("AHC:                                \n");
      printf("* BASIC TESTS                       ");
      if (!basicahcerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* GENERAL TESTS                     ");
      if (!ahcerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("K-MEANS:                            \n");
      printf("* CONVERGENCE                       ");
      if (!kmeansconverrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* SIMPLE TASKS                      ");
      if (!kmeanssimpleerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* OTHER PROPERTIES                  ");
      if (!kmeansothererrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* RESTARTS PROPERTIES               ");
      if (!kmeansrestartserrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST FAILED\n");
      } else {
         printf("TEST PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   return result;
}

