bool testdforest(bool silent, ae_state *_state) {
   bool waserrors;
   bool basicerrors;
   bool procerrors;
   bool compressionerrors;
   bool importanceerrors;
   bool result;

// Primary settings
   basicerrors = false;
   procerrors = false;
   compressionerrors = false;
   importanceerrors = false;
   waserrors = false;

// Tests
   testdforestunit_testprocessing(&procerrors, _state);
   testdforestunit_basictest1(&basicerrors, _state);
   testdforestunit_basictest2(&basicerrors, _state);
   testdforestunit_basictest3(&basicerrors, _state);
   testdforestunit_basictest4(&basicerrors, _state);
   testdforestunit_basictest5(&basicerrors, _state);
   testdforestunit_basictestrandom(&basicerrors, _state);
   testdforestunit_basictestallsame(&basicerrors, _state);
   testdforestunit_testcompression(&compressionerrors, _state);
   testdforestunit_testimportance(&importanceerrors, _state);

// Final report
   waserrors = ((basicerrors || procerrors) || compressionerrors) || importanceerrors;
   if (!silent) {
      printf("RANDOM FOREST TEST\n");
      printf("TOTAL RESULTS:                           ");
      if (!waserrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* PROCESSING FUNCTIONS:                  ");
      if (!procerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* BASIC TESTS:                           ");
      if (!basicerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* COMPRESSION TESTS:                     ");
      if (!compressionerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      printf("* VARIABLE IMPORTANCE TESTS:             ");
      if (!importanceerrors) {
         printf("OK\n");
      } else {
         printf("FAILED\n");
      }
      if (waserrors) {
         printf("TEST SUMMARY: FAILED\n");
      } else {
         printf("TEST SUMMARY: PASSED\n");
      }
      printf("\n\n");
   }
   result = !waserrors;
   return result;
}

// Processing functions test
static void testdforestunit_testprocessing(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nvars;
   ae_int_t nclasses;
   ae_int_t ntrees;
   ae_int_t foresttype;
   ae_int_t npoints;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t i;
   ae_int_t j;
   bool allsame;
   double v;

   ae_frame_make(_state, &_frame_block);
   NewObj(decisionforest, df1, _state);
   NewObj(decisionforest, df2, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewObj(dfreport, rep, _state);
   NewObj(decisionforestbuffer, buf, _state);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewVector(x1, 0, DT_REAL, _state);
   NewVector(x2, 0, DT_REAL, _state);
   NewVector(y1, 0, DT_REAL, _state);
   NewVector(y2, 0, DT_REAL, _state);
   NewObj(hqrndstate, rs, _state);

   passcount = 100;
   hqrndrandomize(&rs, _state);

// Main cycle
   for (pass = 1; pass <= passcount; pass++) {

   // initialize parameters
      nvars = 1 + hqrnduniformi(&rs, 5, _state);
      nclasses = 1 + hqrnduniformi(&rs, 3, _state);
      ntrees = 1 + hqrnduniformi(&rs, 4, _state);
      foresttype = hqrnduniformi(&rs, 3, _state);

   // Initialize arrays and data
      npoints = 10 + ae_randominteger(50, _state);
      ae_vector_set_length(&x1, nvars, _state);
      ae_vector_set_length(&x2, nvars, _state);
      ae_vector_set_length(&y1, nclasses, _state);
      ae_vector_set_length(&y2, nclasses, _state);
      ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < nvars; j++) {
            if (j % 2 == 0) {
               xy.xyR[i][j] = 2 * ae_randomreal(_state) - 1;
            } else {
               xy.xyR[i][j] = (double)ae_randominteger(2, _state);
            }
         }
         if (nclasses == 1) {
            xy.xyR[i][nvars] = 2 * ae_randomreal(_state) - 1;
         } else {
            xy.xyR[i][nvars] = (double)ae_randominteger(nclasses, _state);
         }
      }

   // create forest
      dfbuildercreate(&builder, _state);
      dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
      dfbuildersetrdfalgo(&builder, ae_randominteger(testdforestunit_algomax + 1, _state), _state);
      dfbuildersetrdfsplitstrength(&builder, ae_randominteger(testdforestunit_splitmax + 1, _state), _state);
      if (foresttype == 0) {

      // Build uncompressed random forest
         dfbuilderbuildrandomforest(&builder, ntrees, &df1, &rep, _state);
      }
      if (foresttype == 1) {

      // Build uncompressed random forest, then perform 16-bit compression
         dfbuilderbuildrandomforest(&builder, ntrees, &df1, &rep, _state);
         dfbinarycompression(&df1, _state);
      }
      if (foresttype == 2) {

      // Build uncompressed random forest, then perform 8-bit compression
         dfbuilderbuildrandomforest(&builder, ntrees, &df1, &rep, _state);
         dfbinarycompression8(&df1, _state);
      }
      dfcreatebuffer(&df1, &buf, _state);

   // Same inputs leads to same outputs
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      for (i = 0; i < nclasses; i++) {
         y1.xR[i] = 2 * ae_randomreal(_state) - 1;
         y2.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      dfprocess(&df1, &x1, &y1, _state);
      dfprocess(&df1, &x2, &y2, _state);
      allsame = true;
      for (i = 0; i < nclasses; i++) {
         allsame = allsame && y1.xR[i] == y2.xR[i];
      }
      *err = *err || !allsame;

   // DFProcess0 works as expected
      ae_vector_set_length(&x1, nvars, _state);
      ae_vector_set_length(&x2, nvars, _state);
      ae_vector_set_length(&y1, nclasses, _state);
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = hqrndnormal(&rs, _state);
         x2.xR[i] = x1.xR[i];
      }
      for (i = 0; i < nclasses; i++) {
         y1.xR[i] = hqrndnormal(&rs, _state);
      }
      dfprocess(&df1, &x1, &y1, _state);
      ae_set_error_flag(err, ae_fabs(y1.xR[0] - dfprocess0(&df1, &x2, _state), _state) > 100 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:225");

   // DFClassify works as expected
      ae_vector_set_length(&x1, nvars, _state);
      ae_vector_set_length(&x2, nvars, _state);
      ae_vector_set_length(&y1, nclasses, _state);
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = hqrndnormal(&rs, _state);
         x2.xR[i] = x1.xR[i];
      }
      if (nclasses > 1) {
         for (i = 0; i < nclasses; i++) {
            y1.xR[i] = hqrndnormal(&rs, _state);
         }
         dfprocess(&df1, &x1, &y1, _state);
         j = dfclassify(&df1, &x2, _state);
         for (i = 0; i < nclasses; i++) {
            ae_set_error_flag(err, y1.xR[i] > y1.xR[j], __FILE__, __LINE__, "testdforestunit.ap:248");
         }
      } else {
         ae_set_error_flag(err, dfclassify(&df1, &x2, _state) != -1, __FILE__, __LINE__, "testdforestunit.ap:251");
      }

   // DFTsProcess() works as expected
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      for (i = 0; i < nclasses; i++) {
         y1.xR[i] = 2 * ae_randomreal(_state) - 1;
         y2.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      dfprocess(&df1, &x1, &y1, _state);
      dftsprocess(&df1, &buf, &x2, &y2, _state);
      allsame = true;
      for (i = 0; i < nclasses; i++) {
         allsame = allsame && y1.xR[i] == y2.xR[i];
      }
      ae_set_error_flag(err, !allsame, __FILE__, __LINE__, "testdforestunit.ap:272");

   // Same inputs on original forest leads to same outputs
   // on copy created using DFCopy
      testdforestunit_unsetdf(&df2, _state);
      dfcopy(&df1, &df2, _state);
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      for (i = 0; i < nclasses; i++) {
         y1.xR[i] = 2 * ae_randomreal(_state) - 1;
         y2.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      dfprocess(&df1, &x1, &y1, _state);
      dfprocess(&df2, &x2, &y2, _state);
      allsame = true;
      for (i = 0; i < nclasses; i++) {
         allsame = allsame && y1.xR[i] == y2.xR[i];
      }
      *err = *err || !allsame;
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      ae_set_error_flag(err, ae_fabs(dfprocess0(&df1, &x1, _state) - dfprocess0(&df2, &x2, _state), _state) > 100 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:303");
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      ae_set_error_flag(err, dfclassify(&df1, &x1, _state) != dfclassify(&df2, &x2, _state), __FILE__, __LINE__, "testdforestunit.ap:309");

   // Same inputs on original forest leads to same outputs
   // on copy created using DFSerialize
      testdforestunit_unsetdf(&df2, _state);
      {
      // This code passes data structure through serializers
      // (serializes it to string and loads back)
         ae_int_t _local_ssize;
         ae_frame _local_frame_block;

         ae_frame_make(_state, &_local_frame_block);

         NewSerializer(_local_serializer);
         ae_serializer_alloc_start(&_local_serializer);
         dfalloc(&_local_serializer, &df1, _state);
         _local_ssize = ae_serializer_get_alloc_size(&_local_serializer);
         NewBlock(_local_dynamic_block, _local_ssize + 1, _state);
         ae_serializer_sstart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
         dfserialize(&_local_serializer, &df1, _state);
         ae_serializer_stop(&_local_serializer, _state);

         ae_serializer_init(&_local_serializer);
         ae_serializer_ustart_str(&_local_serializer, (char *)_local_dynamic_block.ptr);
         dfunserialize(&_local_serializer, &df2, _state);
         ae_serializer_stop(&_local_serializer, _state);

         ae_frame_leave(_state);
      }
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      for (i = 0; i < nclasses; i++) {
         y1.xR[i] = 2 * ae_randomreal(_state) - 1;
         y2.xR[i] = 2 * ae_randomreal(_state) - 1;
      }
      dfprocess(&df1, &x1, &y1, _state);
      dfprocess(&df2, &x2, &y2, _state);
      allsame = true;
      for (i = 0; i < nclasses; i++) {
         allsame = allsame && y1.xR[i] == y2.xR[i];
      }
      *err = *err || !allsame;
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      ae_set_error_flag(err, ae_fabs(dfprocess0(&df1, &x1, _state) - dfprocess0(&df2, &x2, _state), _state) > 100 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:339");
      for (i = 0; i < nvars; i++) {
         x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         x2.xR[i] = x1.xR[i];
      }
      ae_set_error_flag(err, dfclassify(&df1, &x1, _state) != dfclassify(&df2, &x2, _state), __FILE__, __LINE__, "testdforestunit.ap:345");

   // Normalization properties
      if (nclasses > 1) {
         for (i = 0; i < nvars; i++) {
            x1.xR[i] = 2 * ae_randomreal(_state) - 1;
         }
         dfprocess(&df1, &x1, &y1, _state);
         v = 0.0;
         for (i = 0; i < nclasses; i++) {
            v = v + y1.xR[i];
            *err = *err || y1.xR[i] < 0.0;
         }
         *err = *err || ae_fabs(v - 1, _state) > 1000 * ae_machineepsilon;
      }
   }
   ae_frame_leave(_state);
}

// Basic test:  one-tree uncompressed forest built using full sample must
// remember all training cases.
//
// Special relaxed testing is performed for compressed forests.
static void testdforestunit_basictest1(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t npoints;
   ae_int_t ntrees;
   ae_int_t i;
   ae_int_t j;
   double s;
   ae_int_t info;
   ae_int_t nvars;
   ae_int_t nclasses;
   ae_int_t passcount;
   double tol16;
   double tol8;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewObj(decisionforest, df, _state);
   NewObj(decisionforest, df1, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewObj(dfreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   tol16 = 0.0001;
   tol8 = 0.01;

// Test uncompressed forest
   passcount = 10;
   for (nvars = 1; nvars <= 4; nvars++) {
      for (nclasses = 1; nclasses <= 3; nclasses++) {
         for (pass = 0; pass < passcount; pass++) {

         // select number of points
            if (pass < 3 && passcount > 3) {
               npoints = 1 + pass;
            } else {
               npoints = 100 + hqrnduniformi(&rs, 100, _state);
            }

         // Prepare task
            ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
            ae_vector_set_length(&x, nvars, _state);
            ae_vector_set_length(&y, nclasses, _state);
            for (i = 0; i < npoints; i++) {
               xy.xyR[i][0] = (double)i;
               for (j = 1; j < nvars; j++) {
                  xy.xyR[i][j] = hqrndnormal(&rs, _state);
               }
               if (nclasses > 1) {
                  xy.xyR[i][nvars] = (double)hqrnduniformi(&rs, nclasses, _state);
               } else {
                  xy.xyR[i][nvars] = hqrndnormal(&rs, _state);
               }
            }

         // Train
         //
         // NOTE: we also test obsolete forest construction functions
            ae_assert(passcount >= 2, "RDF: test integrity failed", _state);
            ntrees = 1;
            if (pass > 0) {

            // Test new interface
               dfbuildercreate(&builder, _state);
               dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
               dfbuildersetsubsampleratio(&builder, 1.0, _state);
               if (ae_randomreal(_state) > 0.5) {
                  dfbuildersetrndvars(&builder, nvars, _state);
               } else {
                  dfbuildersetrndvarsratio(&builder, 1.0, _state);
               }
               dfbuildersetrdfalgo(&builder, ae_randominteger(testdforestunit_algomax + 1, _state), _state);
               dfbuildersetrdfsplitstrength(&builder, ae_randominteger(testdforestunit_splitmax + 1, _state), _state);
               dfbuilderbuildrandomforest(&builder, ntrees, &df, &rep, _state);
            } else {

            // Test old interface
               if (hqrnduniformi(&rs, 2, _state) == 0) {
                  dfbuildrandomdecisionforest(&xy, npoints, nvars, nclasses, ntrees, 1.0, &info, &df, &rep, _state);
               } else {
                  dfbuildrandomdecisionforestx1(&xy, npoints, nvars, nclasses, ntrees, 1, 1.0, &info, &df, &rep, _state);
               }
               if (info <= 0) {
                  ae_set_error_flag(err, true, __FILE__, __LINE__, "testdforestunit.ap:467");
                  ae_frame_leave(_state);
                  return;
               }
            }

         // Test
            ae_set_error_flag(err, ae_fabs(rep.rmserror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:475");
            ae_set_error_flag(err, ae_fabs(rep.avgerror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:476");
            ae_set_error_flag(err, ae_fabs(rep.avgrelerror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:477");
            ae_set_error_flag(err, ae_fabs(rep.relclserror, _state) > 0.0, __FILE__, __LINE__, "testdforestunit.ap:478");
            ae_set_error_flag(err, ae_fabs(rep.avgce, _state) > 0.0, __FILE__, __LINE__, "testdforestunit.ap:479");
            for (i = 0; i < npoints; i++) {
               ae_v_move(x.xR, 1, xy.xyR[i], 1, nvars);
               dfprocess(&df, &x, &y, _state);
               if (nclasses > 1) {

               // Checks for classification tasks
                  s = 0.0;
                  for (j = 0; j < nclasses; j++) {
                     ae_set_error_flag(err, y.xR[j] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:492");
                     s = s + y.xR[j];
                  }
                  ae_set_error_flag(err, ae_fabs(s - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:495");
                  ae_set_error_flag(err, ae_fabs(y.xR[ae_round(xy.xyR[i][nvars], _state)] - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:496");
               } else {

               // Regression problem
                  ae_set_error_flag(err, ae_fabs(y.xR[0] - xy.xyR[i][nvars], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:503");
               }
            }
         }
      }
   }

// Test compressed forest
   nvars = 1;
   npoints = 100;
   for (nclasses = 1; nclasses <= 3; nclasses++) {
      for (pass = 0; pass < passcount; pass++) {

      // Dataset
         ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
         ae_vector_set_length(&x, nvars, _state);
         ae_vector_set_length(&y, nclasses, _state);
         for (i = 0; i < npoints; i++) {
            xy.xyR[i][0] = (double)i;
            if (nclasses > 1) {
               xy.xyR[i][nvars] = (double)hqrnduniformi(&rs, nclasses, _state);
            } else {
               xy.xyR[i][nvars] = hqrnduniformr(&rs, _state) - 0.5;
            }
         }

      // Train
         ntrees = 1;
         dfbuildercreate(&builder, _state);
         dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
         dfbuildersetsubsampleratio(&builder, 1.0, _state);
         dfbuilderbuildrandomforest(&builder, ntrees, &df, &rep, _state);
         ae_set_error_flag(err, ae_fabs(rep.rmserror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:541");
         ae_set_error_flag(err, ae_fabs(rep.avgerror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:542");
         ae_set_error_flag(err, ae_fabs(rep.avgrelerror, _state) > 1.0E-9, __FILE__, __LINE__, "testdforestunit.ap:543");
         ae_set_error_flag(err, ae_fabs(rep.relclserror, _state) > 0.0, __FILE__, __LINE__, "testdforestunit.ap:544");
         ae_set_error_flag(err, ae_fabs(rep.avgce, _state) > 0.0, __FILE__, __LINE__, "testdforestunit.ap:545");

      // Test 16-bit compression
         dfcopy(&df, &df1, _state);
         dfbinarycompression(&df1, _state);
         for (i = 0; i < npoints; i++) {
            x.xR[0] = xy.xyR[i][0];
            dfprocess(&df1, &x, &y, _state);
            if (nclasses > 1) {

            // Checks for classification tasks
               s = 0.0;
               for (j = 0; j < nclasses; j++) {
                  ae_set_error_flag(err, y.xR[j] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:564");
                  s = s + y.xR[j];
               }
               ae_set_error_flag(err, ae_fabs(s - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:567");
               ae_set_error_flag(err, ae_fabs(y.xR[ae_round(xy.xyR[i][nvars], _state)] - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:568");
            } else {

            // Regression problem
               ae_set_error_flag(err, ae_fabs(y.xR[0] - xy.xyR[i][nvars], _state) > tol16, __FILE__, __LINE__, "testdforestunit.ap:575");
            }
         }

      // Test 8-bit compression
         dfcopy(&df, &df1, _state);
         dfbinarycompression8(&df1, _state);
         for (i = 0; i < npoints; i++) {
            x.xR[0] = xy.xyR[i][0];
            dfprocess(&df1, &x, &y, _state);
            if (nclasses > 1) {

            // Checks for classification tasks
               s = 0.0;
               for (j = 0; j < nclasses; j++) {
                  ae_set_error_flag(err, y.xR[j] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:596");
                  s = s + y.xR[j];
               }
               ae_set_error_flag(err, ae_fabs(s - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:599");
               ae_set_error_flag(err, ae_fabs(y.xR[ae_round(xy.xyR[i][nvars], _state)] - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:600");
            } else {

            // Regression problem
               ae_set_error_flag(err, ae_fabs(y.xR[0] - xy.xyR[i][nvars], _state) > tol8, __FILE__, __LINE__, "testdforestunit.ap:607");
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Basic test:  tests generalization ability on a simple noisy classification
// task:
// * 0<x<1 - P(class=0)=1
// * 1<x<2 - P(class=0)=2-x
// * 2<x<3 - P(class=0)=0
static void testdforestunit_basictest2(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t npoints;
   ae_int_t ntrees;
   ae_int_t i;
   double avgerr;
   ae_int_t cnt;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(decisionforest, df, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewObj(dfreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   passcount = 1;
   for (pass = 1; pass <= passcount; pass++) {

   // select npoints and ntrees
      npoints = 3000;
      ntrees = 100;

   // Prepare task
      ae_matrix_set_length(&xy, npoints, 2, _state);
      ae_vector_set_length(&x, 1, _state);
      ae_vector_set_length(&y, 2, _state);
      for (i = 0; i < npoints; i++) {
         xy.xyR[i][0] = 3 * hqrnduniformr(&rs, _state);
         if (xy.xyR[i][0] <= 1.0) {
            xy.xyR[i][1] = 0.0;
         } else {
            if (xy.xyR[i][0] <= 2.0) {
               if (hqrnduniformr(&rs, _state) < xy.xyR[i][0] - 1) {
                  xy.xyR[i][1] = 1.0;
               } else {
                  xy.xyR[i][1] = 0.0;
               }
            } else {
               xy.xyR[i][1] = 1.0;
            }
         }
      }

   // Build forest
      dfbuildercreate(&builder, _state);
      dfbuildersetdataset(&builder, &xy, npoints, 1, 2, _state);
      dfbuildersetsubsampleratio(&builder, 0.05, _state);
      dfbuildersetrdfalgo(&builder, ae_randominteger(testdforestunit_algomax + 1, _state), _state);
      dfbuilderbuildrandomforest(&builder, ntrees, &df, &rep, _state);

   // Check report fields
      ae_set_error_flag(err, ae_fabs(rep.oobrelclserror - 1.0 / 12.0, _state) > 0.50 * (1.0 / 12.0), __FILE__, __LINE__, "testdforestunit.ap:684");
      ae_set_error_flag(err, rep.relclserror > rep.oobrelclserror, __FILE__, __LINE__, "testdforestunit.ap:685");
      ae_set_error_flag(err, rep.avgce >= rep.oobavgce, __FILE__, __LINE__, "testdforestunit.ap:686");
      ae_set_error_flag(err, rep.rmserror >= rep.oobrmserror, __FILE__, __LINE__, "testdforestunit.ap:687");
      ae_set_error_flag(err, rep.avgerror >= rep.oobavgerror, __FILE__, __LINE__, "testdforestunit.ap:688");
      ae_set_error_flag(err, rep.avgrelerror >= rep.oobavgrelerror, __FILE__, __LINE__, "testdforestunit.ap:689");

   // Check outputs
      avgerr = 0.0;
      cnt = 0;
      x.xR[0] = 0.0;
      while (x.xR[0] <= 3.0) {
         dfprocess(&df, &x, &y, _state);

      // Test for basic properties
         ae_set_error_flag(err, y.xR[0] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:704");
         ae_set_error_flag(err, y.xR[1] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:705");
         ae_set_error_flag(err, ae_fabs(y.xR[0] + y.xR[1] - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:706");

      // test for good correlation with results
         if (x.xR[0] < 1.0) {
            avgerr = avgerr + ae_fabs(1 - y.xR[0], _state);
         }
         if (x.xR[0] >= 1.0 && x.xR[0] <= 2.0) {
            avgerr = avgerr + ae_fabs(y.xR[1] - (x.xR[0] - 1), _state);
         }
         if (x.xR[0] > 2.0) {
            avgerr = avgerr + ae_fabs(1 - y.xR[1], _state);
         }
         cnt = cnt + 1;

      // Advance
         x.xR[0] = x.xR[0] + 0.01;
      }
      avgerr = avgerr / cnt;
      ae_set_error_flag(err, avgerr > 0.05, __FILE__, __LINE__, "testdforestunit.ap:725");
   }
   ae_frame_leave(_state);
}

// Basic test:  tests  generalization ability on a simple classification task
// (no noise):
// * |x|<1, |y|<1
// * x^2+y^2 <= 0.25 - P(class=0)=1
// * x^2+y^2>0.25  - P(class=0)=0
static void testdforestunit_basictest3(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t passcount;
   ae_int_t npoints;
   ae_int_t ntrees;
   ae_int_t i;
   ae_int_t j;
   ae_int_t info;
   ae_int_t testgridsize;
   double r;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewObj(decisionforest, df, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewObj(dfreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   passcount = 2;
   testgridsize = 50;
   for (pass = 0; pass < passcount; pass++) {

   // select npoints and ntrees
      npoints = 2000;
      ntrees = 100;

   // Prepare task
      ae_matrix_set_length(&xy, npoints, 3, _state);
      ae_vector_set_length(&x, 2, _state);
      ae_vector_set_length(&y, 2, _state);
      for (i = 0; i < npoints; i++) {
         xy.xyR[i][0] = 2 * hqrnduniformr(&rs, _state) - 1;
         xy.xyR[i][1] = 2 * hqrnduniformr(&rs, _state) - 1;
         if (ae_sqr(xy.xyR[i][0], _state) + ae_sqr(xy.xyR[i][1], _state) <= 0.25) {
            xy.xyR[i][2] = 0.0;
         } else {
            xy.xyR[i][2] = 1.0;
         }
      }

   // Build forest
   //
   // NOTE: we also test obsolete forest construction functions
      ae_assert(passcount >= 2, "RDF: test integrity failed", _state);
      if (pass > 0) {

      // Test new interface
         dfbuildercreate(&builder, _state);
         dfbuildersetdataset(&builder, &xy, npoints, 2, 2, _state);
         dfbuildersetsubsampleratio(&builder, 0.10, _state);
         dfbuildersetrdfalgo(&builder, ae_randominteger(testdforestunit_algomax + 1, _state), _state);
         dfbuilderbuildrandomforest(&builder, ntrees, &df, &rep, _state);
      } else {

      // Test old interface
         if (hqrnduniformi(&rs, 2, _state) == 0) {
            dfbuildrandomdecisionforest(&xy, npoints, 2, 2, ntrees, 0.10, &info, &df, &rep, _state);
         } else {
            dfbuildrandomdecisionforestx1(&xy, npoints, 2, 2, ntrees, 1, 0.10, &info, &df, &rep, _state);
         }
         if (info <= 0) {
            ae_set_error_flag(err, true, __FILE__, __LINE__, "testdforestunit.ap:813");
            ae_frame_leave(_state);
            return;
         }
      }

   // Test
      for (i = -testgridsize / 2; i <= testgridsize / 2; i++) {
         for (j = -testgridsize / 2; j <= testgridsize / 2; j++) {
            x.xR[0] = (double)i / (testgridsize / 2);
            x.xR[1] = (double)j / (testgridsize / 2);
            dfprocess(&df, &x, &y, _state);

         // Test for basic properties
            ae_set_error_flag(err, y.xR[0] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:831");
            ae_set_error_flag(err, y.xR[1] < 0.0, __FILE__, __LINE__, "testdforestunit.ap:832");
            ae_set_error_flag(err, ae_fabs(y.xR[0] + y.xR[1] - 1, _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:833");

         // test for good correlation with results
            r = ae_sqrt(ae_sqr(x.xR[0], _state) + ae_sqr(x.xR[1], _state), _state);
            if (r < 0.66 * 0.5) {
               ae_set_error_flag(err, y.xR[0] < 0.66, __FILE__, __LINE__, "testdforestunit.ap:840");
            }
            if (r > 1.33 * 0.5) {
               ae_set_error_flag(err, y.xR[1] < 0.66, __FILE__, __LINE__, "testdforestunit.ap:842");
            }
         }
      }
   }
   ae_frame_leave(_state);
}

// Basic test: simple regression task without noise:
// * |x|<1, |y|<1
