// Basic test: test correctness of error reports on completely random problem;
// test that OOB errors are roughly unbiased.
static void testdforestunit_basictestrandom(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t nvars;
   ae_int_t npoints;
   ae_int_t ntrees;
   ae_int_t nclasses;
   ae_int_t i;
   ae_int_t j;
   ae_int_t factor;
   double meanlevel;
   double sigma;
   double v;
   double refavgce;
   double refrms;
   double refavg;
   double refavgrel;
   ae_int_t relcnt;
   double oobtol;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewObj(decisionforest, df, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y, 0, DT_REAL, _state);
   NewObj(dfreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   npoints = 200;
   nvars = 10;
   ntrees = 1000;
   oobtol = 0.15;
   for (nclasses = 1; nclasses <= 3; nclasses++) {

   // Prepare task
      ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
      meanlevel = 10 * hqrndnormal(&rs, _state);
      sigma = ae_pow(10.0, hqrndnormal(&rs, _state), _state);
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < nvars; j++) {
            xy.xyR[i][j] = 0.0;
            if (j % 3 == 0) {
               xy.xyR[i][j] = hqrndnormal(&rs, _state);
            }
            if (j % 3 == 1 && hqrnduniformr(&rs, _state) < 0.05) {
               xy.xyR[i][j] = 1.0;
            }
            if (j % 3 == 2 && hqrnduniformr(&rs, _state) < 0.05) {
               xy.xyR[i][j] = (double)(1 + hqrnduniformi(&rs, 3, _state));
            }
         }
         if (nclasses == 1) {
            xy.xyR[i][nvars] = meanlevel + sigma * hqrndnormal(&rs, _state);
            if (hqrnduniformr(&rs, _state) < 0.05) {
               xy.xyR[i][nvars] = 0.0;
            }
         } else {
            xy.xyR[i][nvars] = (double)hqrnduniformi(&rs, nclasses, _state);
         }
      }
      ae_vector_set_length(&x, nvars, _state);
      ae_vector_set_length(&y, nclasses, _state);

   // Build forest using 50% and 66% of the samples.
   // Check TRN and OOB errors (OOB should be 2x and 3x larger in these cases)
      for (factor = 2; factor <= 3; factor++) {
         j = hqrnduniformi(&rs, 3, _state);
         dfbuildercreate(&builder, _state);
         dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
         dfbuildersetsubsampleratio(&builder, 1 - 1.0 / factor, _state);
         if (j > 0) {
            dfbuildersetrndvars(&builder, 1 + hqrnduniformi(&rs, 5, _state), _state);
         }
         if (j == 0) {
            dfbuildersetrndvarsauto(&builder, _state);
         }
         if (j < 0) {
            dfbuildersetrndvarsratio(&builder, hqrnduniformr(&rs, _state), _state);
         }
         dfbuildersetrdfalgo(&builder, ae_randominteger(testdforestunit_algomax + 1, _state), _state);
         dfbuildersetrdfsplitstrength(&builder, ae_randominteger(testdforestunit_splitmax + 1, _state), _state);
         ae_set_error_flag(err, dfbuildergetprogress(&builder, _state) != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1139");
         ae_set_error_flag(err, dfbuilderpeekprogress(&builder, _state) != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1140");
         dfbuilderbuildrandomforest(&builder, ntrees, &df, &rep, _state);
         ae_set_error_flag(err, dfbuildergetprogress(&builder, _state) != 1.0, __FILE__, __LINE__, "testdforestunit.ap:1142");
         ae_set_error_flag(err, dfbuilderpeekprogress(&builder, _state) != 1.0, __FILE__, __LINE__, "testdforestunit.ap:1143");
         refavgce = 0.0;
         refrms = 0.0;
         refavg = 0.0;
         refavgrel = 0.0;
         relcnt = 0;
         if (nclasses == 1) {

         // Check training set errors
            for (i = 0; i < npoints; i++) {
               for (j = 0; j < nvars; j++) {
                  x.xR[j] = xy.xyR[i][j];
               }
               dfprocess(&df, &x, &y, _state);
               v = y.xR[0] - xy.xyR[i][nvars];
               refrms = refrms + ae_sqr(v, _state);
               refavg = refavg + ae_fabs(v, _state);
               if (xy.xyR[i][nvars] != 0.0) {
                  refavgrel = refavgrel + ae_fabs(v / xy.xyR[i][nvars], _state);
                  relcnt = relcnt + 1;
               }
            }
            refrms = ae_sqrt(refrms / npoints, _state);
            refavg = refavg / npoints;
            refavgrel = refavgrel / coalesce((double)relcnt, 1.0, _state);
            ae_set_error_flag(err, rep.relclserror != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1171");
            ae_set_error_flag(err, rep.avgce != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1172");
            ae_set_error_flag(err, ae_fabs(rep.rmserror - refrms, _state) / refrms > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1173");
            ae_set_error_flag(err, ae_fabs(rep.avgerror - refavg, _state) / refavg > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1174");
            ae_set_error_flag(err, ae_fabs(rep.avgrelerror - refavgrel, _state) / refavgrel > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1175");
            ae_set_error_flag(err, dfrelclserror(&df, &xy, npoints, _state) != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1176");
            ae_set_error_flag(err, dfavgce(&df, &xy, npoints, _state) != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1177");
            ae_set_error_flag(err, ae_fabs(dfrmserror(&df, &xy, npoints, _state) - refrms, _state) / refrms > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1178");
            ae_set_error_flag(err, ae_fabs(dfavgerror(&df, &xy, npoints, _state) - refavg, _state) / refavg > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1179");
            ae_set_error_flag(err, ae_fabs(dfavgrelerror(&df, &xy, npoints, _state) - refavgrel, _state) / refavgrel > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1180");

         // Check OOB errors against training set errors:
         // * RelCLS and AvgCE are not calculated for regression problems
         // * RMS and AVG should be roughly FACTOR times larger
         // * AVG-REL should be just larger (it is more prone to sampling noise than previous two)
            ae_set_error_flag(err, rep.oobrelclserror != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1188");
            ae_set_error_flag(err, rep.oobavgce != 0.0, __FILE__, __LINE__, "testdforestunit.ap:1189");
            ae_set_error_flag(err, ae_fabs(rep.oobrmserror / rep.rmserror - factor, _state) > oobtol * factor, __FILE__, __LINE__, "testdforestunit.ap:1190");
            ae_set_error_flag(err, ae_fabs(rep.oobavgerror / rep.avgerror - factor, _state) > oobtol * factor, __FILE__, __LINE__, "testdforestunit.ap:1191");
            ae_set_error_flag(err, rep.oobavgrelerror < rep.avgrelerror, __FILE__, __LINE__, "testdforestunit.ap:1192");
         } else {

         // Check training set errors
            for (i = 0; i < npoints; i++) {
               for (j = 0; j < nvars; j++) {
                  x.xR[j] = xy.xyR[i][j];
               }
               dfprocess(&df, &x, &y, _state);
               for (j = 0; j < nclasses; j++) {
                  v = y.xR[j];
                  if ((double)j == xy.xyR[i][nvars]) {
                     v = v - 1;
                  }
                  refrms = refrms + ae_sqr(v, _state);
                  refavg = refavg + ae_fabs(v, _state);
                  if ((double)j == xy.xyR[i][nvars]) {
                     refavgrel = refavgrel + ae_fabs(v, _state);
                     refavgce = refavgce - ae_log(v, _state);
                  }
               }
            }
            refrms = ae_sqrt(refrms / (npoints * nclasses), _state);
            refavg = refavg / (npoints * nclasses);
            refavgrel = refavgrel / npoints;
            refavgce = refavgce / npoints;
            ae_set_error_flag(err, ae_fabs(rep.avgce - refavgce, _state) / refavgce > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1222");
            ae_set_error_flag(err, ae_fabs(rep.rmserror - refrms, _state) / refrms > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1223");
            ae_set_error_flag(err, ae_fabs(rep.avgerror - refavg, _state) / refavg > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1224");
            ae_set_error_flag(err, ae_fabs(rep.avgrelerror - refavgrel, _state) / refavgrel > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1225");
            ae_set_error_flag(err, ae_fabs(dfrelclserror(&df, &xy, npoints, _state) - rep.relclserror, _state) * npoints > 5.0, __FILE__, __LINE__, "testdforestunit.ap:1227");
            ae_set_error_flag(err, ae_fabs(dfavgce(&df, &xy, npoints, _state) - refavgce, _state) / refavgce > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1228");
            ae_set_error_flag(err, ae_fabs(dfrmserror(&df, &xy, npoints, _state) - refrms, _state) / refrms > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1229");
            ae_set_error_flag(err, ae_fabs(dfavgerror(&df, &xy, npoints, _state) - refavg, _state) / refavg > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1230");
            ae_set_error_flag(err, ae_fabs(dfavgrelerror(&df, &xy, npoints, _state) - refavgrel, _state) / refavgrel > 1.0E-6, __FILE__, __LINE__, "testdforestunit.ap:1231");

         // Check OOB errors against training set errors
         // * RelCLS, AvgCE and AvgRel should be just larger (too prone to sampling noise to perform more sensitive tests)
         // * RMS and AVG should be roughly FACTOR times larger
         //
            ae_set_error_flag(err, rep.oobrelclserror < rep.relclserror, __FILE__, __LINE__, "testdforestunit.ap:1239");
            ae_set_error_flag(err, rep.oobavgce < rep.avgce, __FILE__, __LINE__, "testdforestunit.ap:1240");
            ae_set_error_flag(err, rep.oobavgrelerror < rep.avgrelerror, __FILE__, __LINE__, "testdforestunit.ap:1241");
            ae_set_error_flag(err, ae_fabs(rep.oobrmserror / rep.rmserror - factor, _state) > oobtol * factor, __FILE__, __LINE__, "testdforestunit.ap:1242");
            ae_set_error_flag(err, ae_fabs(rep.oobavgerror / rep.avgerror - factor, _state) > oobtol * factor, __FILE__, __LINE__, "testdforestunit.ap:1243");
         }
      }
   }
   ae_frame_leave(_state);
}

