// Test model compression - that it works and generates forests which correctly
// reproduce original one
static void testdforestunit_testcompression(bool *err, ae_state *_state) {
   ae_frame _frame_block;
   ae_int_t pass;
   ae_int_t npoints;
   ae_int_t ntrees;
   ae_int_t i;
   ae_int_t j;
   ae_int_t k;
   ae_int_t nvars;
   ae_int_t nclasses;
   double tol16;
   double tol8;

   ae_frame_make(_state, &_frame_block);
   NewMatrix(xy, 0, 0, DT_REAL, _state);
   NewObj(decisionforestbuilder, builder, _state);
   NewObj(decisionforest, dfraw, _state);
   NewObj(decisionforest, df16, _state);
   NewObj(decisionforest, df8, _state);
   NewVector(x, 0, DT_REAL, _state);
   NewVector(y0, 0, DT_REAL, _state);
   NewVector(y1, 0, DT_REAL, _state);
   NewObj(dfreport, rep, _state);
   NewObj(hqrndstate, rs, _state);

   hqrndrandomize(&rs, _state);
   tol16 = 0.0001;
   tol8 = 0.01;

// Test compression on small-scale problems:
// * a K-dimensional input, with 3^K distinct points located at integer grid
// * we test that compressed model produces roughly same results as uncompressed
//   one (predicts same class / predicts same scalar output subject to small
//   truncation error)
   for (nvars = 1; nvars <= 5; nvars++) {
      for (nclasses = 1; nclasses <= 3; nclasses++) {
         ae_vector_set_length(&x, nvars, _state);
         ae_vector_set_length(&y0, nclasses, _state);
         ae_vector_set_length(&y1, nclasses, _state);

      // Generate dataset
         npoints = ae_round(ae_pow(3.0, (double)nvars, _state), _state);
         ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
         for (i = 0; i < npoints; i++) {
            k = i;
            for (j = 0; j < nvars; j++) {
               xy.xyR[i][j] = (double)(k % 3 - 1);
               k = k / 3;
            }
            if (nclasses == 1) {
               xy.xyR[i][nvars] = hqrnduniformr(&rs, _state) - 0.5;
            } else {
               xy.xyR[i][nvars] = (double)hqrnduniformi(&rs, nclasses, _state);
            }
         }

      // Train uncompressed
         ntrees = 1 + hqrnduniformi(&rs, 10, _state);
         dfbuildercreate(&builder, _state);
         dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
         dfbuildersetsubsampleratio(&builder, 0.66, _state);
         dfbuilderbuildrandomforest(&builder, ntrees, &dfraw, &rep, _state);

      // Test 16-bit compression
         dfcopy(&dfraw, &df16, _state);
         dfbinarycompression(&df16, _state);
         for (i = 0; i < npoints; i++) {
            for (j = 0; j < nvars; j++) {
               x.xR[j] = xy.xyR[i][j];
            }
            dfprocess(&dfraw, &x, &y0, _state);
            dfprocess(&df16, &x, &y1, _state);
            if (nclasses > 1) {

            // Compare output probabilities for classification problem
               for (j = 0; j < nclasses; j++) {
                  ae_set_error_flag(err, ae_fabs(y0.xR[j] - y1.xR[j], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:1495");
               }
            } else {

            // Compare output values for regression problem
               ae_set_error_flag(err, ae_fabs(y0.xR[0] - y1.xR[0], _state) > tol16, __FILE__, __LINE__, "testdforestunit.ap:1502");
            }
         }

      // Test 8-bit compression
         dfcopy(&dfraw, &df8, _state);
         dfbinarycompression8(&df8, _state);
         for (i = 0; i < npoints; i++) {
            for (j = 0; j < nvars; j++) {
               x.xR[j] = xy.xyR[i][j];
            }
            dfprocess(&dfraw, &x, &y0, _state);
            dfprocess(&df8, &x, &y1, _state);
            if (nclasses > 1) {

            // Compare output probabilities for classification problem
               for (j = 0; j < nclasses; j++) {
                  ae_set_error_flag(err, ae_fabs(y0.xR[j] - y1.xR[j], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:1523");
               }
            } else {

            // Compare output values for regression problem
               ae_set_error_flag(err, ae_fabs(y0.xR[0] - y1.xR[0], _state) > tol8, __FILE__, __LINE__, "testdforestunit.ap:1530");
            }
         }
      }
   }

// Test compression on large-scale problems:
// * NVars=500, NPoints=500, I-th point is zero except for I-th position
// * NClasses=1 (random output) or NClasses=400
// * NTrees is in 1...5
   for (pass = 0; pass <= 1; pass++) {

   // Set up problem
      ae_assert(pass == 0 || pass == 1, "DF: integrity check failed", _state);
      nvars = 500;
      npoints = 500;
      nclasses = 1;
      if (pass == 1) {
         nclasses = 400;
      }
      ae_matrix_set_length(&xy, npoints, nvars + 1, _state);
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < nvars; j++) {
            xy.xyR[i][j] = 0.0;
         }
         xy.xyR[i][i] = 1.0;
         if (nclasses == 1) {
            xy.xyR[i][nvars] = hqrnduniformr(&rs, _state) - 0.5;
         } else {
            xy.xyR[i][nvars] = (double)hqrnduniformi(&rs, nclasses, _state);
         }
      }
      ae_vector_set_length(&x, nvars, _state);
      ae_vector_set_length(&y0, nclasses, _state);
      ae_vector_set_length(&y1, nclasses, _state);

   // Train uncompressed
      ntrees = 1 + hqrnduniformi(&rs, 5, _state);
      dfbuildercreate(&builder, _state);
      dfbuildersetdataset(&builder, &xy, npoints, nvars, nclasses, _state);
      dfbuildersetsubsampleratio(&builder, 0.66, _state);
      dfbuilderbuildrandomforest(&builder, ntrees, &dfraw, &rep, _state);

   // Test 16-bit compression
      dfcopy(&dfraw, &df16, _state);
      dfbinarycompression(&df16, _state);
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < nvars; j++) {
            x.xR[j] = xy.xyR[i][j];
         }
         dfprocess(&dfraw, &x, &y0, _state);
         dfprocess(&df16, &x, &y1, _state);
         if (nclasses > 1) {

         // Compare output probabilities for classification problem
            for (j = 0; j < nclasses; j++) {
               ae_set_error_flag(err, ae_fabs(y0.xR[j] - y1.xR[j], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:1593");
            }
         } else {

         // Compare output values for regression problem
            ae_set_error_flag(err, ae_fabs(y0.xR[0] - y1.xR[0], _state) > tol16, __FILE__, __LINE__, "testdforestunit.ap:1600");
         }
      }

   // Test 8-bit compression
      dfcopy(&dfraw, &df8, _state);
      dfbinarycompression8(&df8, _state);
      for (i = 0; i < npoints; i++) {
         for (j = 0; j < nvars; j++) {
            x.xR[j] = xy.xyR[i][j];
         }
         dfprocess(&dfraw, &x, &y0, _state);
         dfprocess(&df8, &x, &y1, _state);
         if (nclasses > 1) {

         // Compare output probabilities for classification problem
            for (j = 0; j < nclasses; j++) {
               ae_set_error_flag(err, ae_fabs(y0.xR[j] - y1.xR[j], _state) > 1000 * ae_machineepsilon, __FILE__, __LINE__, "testdforestunit.ap:1621");
            }
         } else {

         // Compare output values for regression problem
            ae_set_error_flag(err, ae_fabs(y0.xR[0] - y1.xR[0], _state) > tol8, __FILE__, __LINE__, "testdforestunit.ap:1628");
         }
      }
   }
   ae_frame_leave(_state);
}

